import{_ as a,c as p,o as e,d as t}from"./app.e0e4ea3a.js";const _=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u5468\u7231\u6C11-JavaScript\u6838\u5FC3\u539F\u7406\u89E3\u6790","slug":"title-\u5468\u7231\u6C11-javascript\u6838\u5FC3\u539F\u7406\u89E3\u6790","link":"#title-\u5468\u7231\u6C11-javascript\u6838\u5FC3\u539F\u7406\u89E3\u6790","children":[]},{"level":2,"title":"\u95EE\u5BF9\u95EE\u9898","slug":"\u95EE\u5BF9\u95EE\u9898","link":"#\u95EE\u5BF9\u95EE\u9898","children":[]},{"level":2,"title":"\u8BED\u8A00","slug":"\u8BED\u8A00","link":"#\u8BED\u8A00","children":[]},{"level":2,"title":"\u8BED\u8A00\u7684\u7279\u6027","slug":"\u8BED\u8A00\u7684\u7279\u6027","link":"#\u8BED\u8A00\u7684\u7279\u6027","children":[]},{"level":2,"title":"\u4F53\u7CFB\u6027","slug":"\u4F53\u7CFB\u6027","link":"#\u4F53\u7CFB\u6027","children":[]},{"level":2,"title":"\u5B66\u8FD9\u95E8\u8BFE","slug":"\u5B66\u8FD9\u95E8\u8BFE","link":"#\u5B66\u8FD9\u95E8\u8BFE","children":[]}],"relativePath":"JavaScript/\u5468\u7231\u6C11-JavaScript\u6838\u5FC3\u539F\u7406\u89E3\u6790/index.md"}'),i={name:"JavaScript/\u5468\u7231\u6C11-JavaScript\u6838\u5FC3\u539F\u7406\u89E3\u6790/index.md"},r=t('<hr><h2 id="title-\u5468\u7231\u6C11-javascript\u6838\u5FC3\u539F\u7406\u89E3\u6790" tabindex="-1">title: \u5468\u7231\u6C11-JavaScript\u6838\u5FC3\u539F\u7406\u89E3\u6790 <a class="header-anchor" href="#title-\u5468\u7231\u6C11-javascript\u6838\u5FC3\u539F\u7406\u89E3\u6790" aria-hidden="true">#</a></h2><p>\u5F00\u7BC7\u8BCD | \u5982\u4F55\u89E3\u51B3\u8BED\u8A00\u95EE\u9898\uFF1F</p><p>\u4F60\u597D\uFF0C\u6211\u662F\u5468\u7231\u6C11\uFF0C\u548C\u4F60\u4E00\u6837\uFF0C\u6211\u559C\u6B22JavaScript\u3002</p><p>\u6211\u662F\u300AJavaScript\u8BED\u8A00\u7CBE\u9AD3\u4E0E\u7F16\u7A0B\u5B9E\u8DF5\u300B\u8FD9\u672C\u4E66\u7684\u4F5C\u8005\uFF0C\u8FD9\u4E2A\u4E66\u540D\u6B63\u597D\u4E5F\u523B\u753B\u4E86\u6211\u8FFD\u968FJavaScript\u7684\u8F68\u8FF9\uFF1A\u5728\u8FC7\u53BB\u7684\u4E8C\u5341\u5E74\u4E2D\uFF0C\u6211\u4E00\u9762\u7814\u7A76\u5B83\u7684\u8BED\u8A00\u7CBE\u9AD3\uFF0C\u4E00\u9762\u505A\u7F16\u7A0B\u5B9E\u8DF5\u3002</p><p>\u66FE\u7ECF\u5728\u5F88\u957F\u7684\u65F6\u95F4\u91CC\u9762\uFF0C\u6211\u7684\u8111\u6D77\u4E2D\u65F6\u5E38\u4F1A\u6709\u4E00\u4E2A\u95EA\u5FF5\uFF1A\u5982\u4F55\u89E3\u51B3\u8BED\u8A00\u95EE\u9898\uFF1F\u8FD9\u4E5F\u4F34\u968F\u7740\u5F3A\u70C8\u7684\u81EA\u6211\u5426\u5B9A\u4E0E\u8D28\u7591\uFF0C\u56E0\u4E3A\u5B83\u7684\u6F5C\u53F0\u8BCD\u662F\uFF1A\u6211\u8FD8\u6CA1\u6709\u641E\u5B9A\u8BED\u8A00\u95EE\u9898\u3002</p><h2 id="\u95EE\u5BF9\u95EE\u9898" tabindex="-1">\u95EE\u5BF9\u95EE\u9898 <a class="header-anchor" href="#\u95EE\u5BF9\u95EE\u9898" aria-hidden="true">#</a></h2><p>\u5728\u90A3\u4E4B\u524D\uFF0C\u6211\u662F\u4ECEDBASE\u8FD9\u4E2A\u6570\u636E\u5E93\u5165\u624B\uFF0C\u4ECE\u7C7B\u4F3CSQL\u7684\u8BED\u8A00\u5F00\u59CB\u5B66\u4E60\u7684\u3002\u7B2C\u4E00\u95E8\u6B63\u5F0F\u5B66\u4E60\u7684\u8BED\u8A00\u662F\u6C47\u7F16\uFF0C\u7136\u540E\u662FBasic\u548CPascal\u3002\u540E\u6765\u5728\u5546\u7528\u4EA7\u54C1\u5F00\u53D1\u7684\u73AF\u5883\u4E2D\uFF0C\u6211\u9009\u62E9\u4E86Delphi\u8FD9\u95E8\u8BED\u8A00\u3002\u8FD9\u4E00\u6BB5\u8BED\u8A00\u5B66\u4E60\u7684\u7ECF\u5386\uFF0C\u76F4\u52302003\u5E74\u524D\u540E\u621B\u7136\u800C\u6B62\uFF0C\u90A3\u65F6\u6211\u5199\u5B8C\u4E86\u6211\u7684\u7B2C\u4E00\u672C\u4E66\uFF0C\u540D\u5B57\u5C31\u53EB\u300ADelphi\u6E90\u4EE3\u7801\u5206\u6790\u300B\u3002</p><p>\u8FD9\u4E9B\u662F\u6211\u65E2\u6709\u7684\u8BED\u8A00\u77E5\u8BC6\uFF0C\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u6765\u8BF4\uFF0C\u5B83\u9650\u5236\u4E86\u6211\u5BF9JavaScript\u7684\u8FDB\u4E00\u6B65\u5B66\u4E60\uFF0C\u65E2\u6210\u4E60\u60EF\u7684\u601D\u7EF4\u65B9\u5F0F\u548C\u77E5\u8BC6\u4F53\u7CFB\u76D8\u6839\u9519\u8282\uFF0C\u6E17\u900F\u6781\u6DF1\u3002</p><p>\u6211\u662F\u4ECE1998\u5E74\u5DE6\u53F3\u5C31\u5F00\u59CB\u63A5\u89E6JavaScript\u8FD9\u95E8\u8BED\u8A00\u7684\uFF0C\u7136\u800C\u76F4\u5230\u4E94\u516D\u5E74\u4E4B\u540E\uFF0C\u6211\u4ECD\u7136\u5728\u4F7F\u7528Delphi\u4E2D\u7684\u9762\u5411\u5BF9\u8C61\u6982\u5FF5\u4E0E\u7406\u8BBA\u6765\u7406\u89E3\u4E0E\u5206\u6790JavaScript\u7684\u5BF9\u8C61\u6A21\u578B\u3002\u8FD9\u4E5F\u662F\u6211\u65E9\u671F\u505AWEUI\u8FD9\u4E2A\u9879\u76EE\u65F6\u7684\u56F0\u6270\uFF1A\u6211\u4E00\u9762\u52AA\u529B\u6539\u9020\u7740JavaScript\u8FD9\u95E8\u8BED\u8A00\uFF0C\u4E00\u9762\u53C8\u88AB\u5B83\u6240\u5DE6\u53F3\u3002</p><p>\u90A3\u4E2A\u65F6\u4EE3\uFF0C\u6709\u592A\u591A\u4EBA\u505A\u7740\u4E0E\u6211\u76F8\u4F3C\u7684\u4E8B\u60C5\uFF1A\u8981\u4E0A\u7EBF\u4E00\u4E2A\u5927\u7684\u524D\u7AEF\u4EA7\u54C1\uFF0C\u5C31\u5148\u5199\u4E00\u4E2A\u6846\u67B6\u6216\u5E93\uFF0C\u5C06\u4F20\u7EDF\u8BED\u8A00\u4E2D\u7684\u7ECF\u9A8C\u590D\u5236\u8FC7\u6765\u3002\u90A3\u4E9B\u662F\u6211\u4EEC\u7684\u65E2\u5F97\u8D22\u5BCC\uFF0C\u95EA\u70C1\u7740\u8BB0\u5FC6\u7684\u5149\u8292\u3001\u77E5\u8BC6\u7684\u706B\u82B1\uFF0C\u662F\u81EA\u6211\u4EF7\u503C\u548C\u85AA\u8D44\u7684\u4F53\u73B0\uFF0C\u6240\u4EE5\u5B83\u4EEC\u662F\u4E0D\u53EF\u629B\u5F03\u7684\u3001\u4E0D\u53EF\u5426\u8BA4\u7684\u3001\u4E0D\u53EF\u4EB5\u6E0E\u7684\u3002</p><p>\u5728\u7C7B\u4F3C\u4E8E\u6B64\u7684\u3001\u56FA\u5316\u7684\u601D\u7EF4\u91CC\u9762\uFF0C\u6211\u4EEC\u52E4\u52B3\u5730\u3001\u4E0D\u77E5\u75B2\u5026\u5730\u5199\u7740\u65B0\u4EE3\u7801\uFF0C\u800C\u7A76\u5176\u539F\u56E0\uFF0C\u53EA\u662F\u6211\u4EEC\u4E0D\u613F\u610F\u4E22\u5F03\u90A3\u4E9B\u65E7\u4EE3\u7801\u800C\u5DF2\u3002</p><p>\u5982\u6B64\u8D1F\u91CD\u524D\u884C\uFF0C\u4EE5\u81F3\u4E8E\u8BA9\u6211\u4E0D\u5F97\u4E0D\u6000\u7591\uFF0C\u81EA\u5DF1\u662F\u5426\u8FD8\u6709\u80FD\u529B\u638C\u63E1\u8FD9\u95E8\u201C\u4E16\u754C\u4E0A\u6700\u88AB\u8BEF\u89E3\u7684\u8BED\u8A00\u201D\u3002\u8FD9\u662FDouglas Crockford\uFF08\u5C31\u662F\u521B\u5EFA\u4E86JSON\u683C\u5F0F\u90A3\u4F4D\u5927\u725B\uFF09\u57282001\u5E74\u8BF4\u8FC7\u7684\u8BDD\u3002</p><p><img src="https://static001.geekbang.org/resource/image/a7/ca/a7314875afe34589031d8d183ddb8fca.jpg" alt=""></p><p>\u6211\u771F\u6B63\u4E0B\u5B9A\u51B3\u5FC3\u629B\u5F03\u6240\u6709\uFF0C\u6765\u91CD\u65B0\u7406\u89E3\u8FD9\u95E8\u8BED\u8A00\u7684\u65F6\u5019\uFF0C\u662F\u5728\u6211\u5199\u51FA\u300ADelphi\u6E90\u4EE3\u7801\u5206\u6790\u300B\u4E4B\u540E\u4E0D\u4E45\u3002\u8FD9\u4E2A\u65F6\u5019\uFF0CBorland\u7834\u4EA7\u4E86\uFF0CDelphi\u88AB\u5356\u6389\u4E86\uFF0C\u800C\u6211\u4E5F\u505A\u4E86\u67B6\u6784\u5E08\u3002\u6B64\u65F6\uFF0C\u90A3\u4E9B\u65E2\u6709\u7684\u7ECF\u9A8C\uFF0C\u4EE5\u53CA\u5BF9\u8BED\u8A00\u5B70\u4F18\u5B70\u52A3\u7684\u56FA\u6267\u5DF1\u89C1\u90FD\u53D8\u5F97\u53EF\u6709\u53EF\u65E0\u4E86\u3002\u8FD9\u65F6\uFF0C\u5BF9\u4E8E\u6211\u4E00\u76F4\u4EE5\u6765\u7684\u56F0\u60D1\uFF0C\u6211\u624D\u771F\u6B63\u5730\u95EE\u5BF9\u4E86\u7B2C\u4E00\u4E2A\u95EE\u9898\uFF1A</p><p>JavaScript\u5230\u5E95\u662F\u4E00\u95E8\u4EC0\u4E48\u6837\u7684\u8BED\u8A00\uFF1F</p><h2 id="\u8BED\u8A00" tabindex="-1">\u8BED\u8A00 <a class="header-anchor" href="#\u8BED\u8A00" aria-hidden="true">#</a></h2><p>\u6211\u5E38\u5E38\u8BF4\uFF0C\u95EE\u5BF9\u4E86\u95EE\u9898\uFF0C\u4E5F\u5C31\u6709\u4E86\u201C\u89E3\u201D\u3002</p><p>\u5728JavaScript\u8BDE\u751F\u7684\u65F6\u5019\uFF0C\u4E3B\u6D41\u7684\u5E94\u7528\u5F00\u53D1\u8BED\u8A00\u5927\u591A\u662F\u9759\u6001\u7684\uFF0C\u4EE5\u53CA\u5355\u4E00\u8BED\u8A00\u7C7B\u578B\u7684\u3002\u5F53\u65F6\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u5927\u884C\u5176\u9053\uFF0C\u4F17\u591A\u8BED\u8A00\u90FD\u7EB7\u7EB7\u4EE5\u201C<strong>\u6211\u6700OOP</strong>\u201D\u4E3A\u5BA3\u4F20\u5671\u5934\uFF0C\u4EE5\u53CA\u5C06\u5B83\u60F3\u50CF\u6210\u8BED\u8A00\u53D1\u5C55\u7684\u5FC5\u7136\u65B9\u5411\u3002\u968F\u7740Java/JVM\u7684\u6210\u719F\uFF0C\u4F7F\u7528\u4E2D\u95F4\u6307\u4EE4\u96C6+\u865A\u62DF\u673A\u6765\u8FD0\u884C\u7684\u8BED\u8A00\u73AF\u5883\u4E5F\u53D8\u5F97\u6D41\u884C\u8D77\u6765\uFF0C\u56E0\u6B64\u4E00\u4E2A\u865A\u62DF\u673A\u4E0A\u8DD1\u5F88\u591A\u79CD\u8BED\u8A00\u4E5F\u5C31\u6210\u4E86\u5E38\u6001\u3002\u4F46\u5373\u4F7F\u5982\u6B64\uFF0C\u5177\u4F53\u5230\u6BCF\u4E00\u79CD\u8BED\u8A00\uFF0C\u5176\u4E3B\u8981\u7279\u6027\u8FD8\u662F\u5355\u4E00\u7684\uFF0C\u5E76\u4E14\u901A\u5E38\u4EE5\u4FDD\u6301\u201C<strong>\u8BED\u8A00\u7279\u6027\u7684\u7EAF\u51C0</strong>\u201D\u4E3A\u5DF1\u4EFB\u3002</p><p>JavaScript\u5374\u662F\u4E00\u4E2A\u5F02\u7C7B\uFF0C\u5B83\u6700\u5F00\u59CB\u662F\u4E00\u95E8\u201C\u7B80\u5355\u201D\u7684\u5C0F\u8BED\u8A00\uFF0C\u6CA1\u6709\u4E30\u5BCC\u7684\u8BED\u8A00\u7279\u6027\uFF0C\u4E5F\u6CA1\u6709\u5927\u4E00\u7EDF\u7684\u91CE\u5FC3\uFF0C\u66F4\u6CA1\u6709\u201C\u5305\u6253\u5929\u4E0B\u201D\u7684\u865A\u62DF\u673A\u5F15\u64CE\u3002\u4E3A\u4E86\u7EF4\u62A4\u8FD9\u79CD\u201C\u5C0F\u800C\u7B80\u6D01\u201D\uFF0C\u5F53\u7136\uFF0C\u53E6\u4E00\u90E8\u5206\u539F\u56E0\u4E5F\u5728\u4E8E\u5B83\u7684\u521B\u59CB\u8005\u592A\u8FC7\u5306\u5FD9\u548C\u968F\u610F\u2014\u2014\u5B83\u53EA\u5B9E\u73B0\u4E86\u4E00\u4E9B\u57FA\u7840\u7684\u8BED\u8A00\u7279\u6027\uFF0C\u800C\u6CA1\u6709\u4ECE\u6839\u672C\u4E0A\u201C\u9648\u8FF0\u201D\u81EA\u5DF1\u7684\u8BBE\u8BA1\u539F\u5219\u4E0E\u7406\u5FF5\u3002</p><p>\u8FD9\u95E8\u8BED\u8A00\u975E\u5E38\u6447\u6446\uFF0C\u5728\u9762\u5411\u5BF9\u8C61\u706B\u7684\u65F6\u5019\uFF0C\u5B83\u8BF4\u201C\u6211\u662FOOP\u7684\u8BED\u8A00\u201D\uFF1B\u5728\u51FD\u6570\u5F0F\u8BED\u8A00\u547C\u58F0\u6E10\u9AD8\u7684\u65F6\u5019\uFF0C\u5B83\u53C8\u8BF4\u201C\u6211\u662F\u51FD\u6570\u5F0F\u7684\u201D\u3002\u53E6\u5916\uFF0C\u4F60\u77E5\u9053\u7684\uFF0C\u5B83\u5929\u751F\u8FD8\u662F\u4E00\u95E8\u52A8\u6001\u7684\u8BED\u8A00\u3002\u4E0D\u8FC7\uFF0C\u5B83\u4E5F\u8FD8\u5305\u62EC\u4E00\u4E9B\u9759\u6001\u7684\u3001\u7ED3\u6784\u5316\u7684\u8BED\u8A00\u6210\u5206\u3002</p><p>\u652F\u6301\u8FD9\u95E8\u8BED\u8A00\u6323\u624E\u6C42\u751F\u3001\u4E00\u8DEF\u884C\u6765\u7684\uFF0C\u6B63\u662F\u8FD9\u95E8\u8BED\u8A00\u6700\u521D\u7684\u3001\u6700\u7CBE\u5F69\u7684\u8BBE\u8BA1\uFF1A<strong>\u5B83\u662F\u4E00\u95E8\u591A\u8303\u578B\u8BED\u8A00\uFF0C\u6216\u8005\uFF0C\u4E5F\u79F0\u4E3A\u6DF7\u5408\u8303\u578B\u8BED\u8A00</strong>\u3002JavaScript\u7684\u7B80\u5355\u6765\u81EA\u4E8E\u6B64\uFF0C\u590D\u6742\u4E5F\u6765\u81EA\u4E8E\u6B64\uFF1B\u751F\u5B58\u80FD\u529B\u6765\u81EA\u4E8E\u6B64\uFF0C\u62A8\u51FB\u8BDF\u75C5\u4E5F\u6765\u81EA\u4E8E\u6B64\u3002</p><p>\u7684\u786E\uFF0C\u5982\u679C\u6211\u4E0D\u629B\u5F00Delphi\u8BED\u8A00\u7ED9\u6211\u7559\u4E0B\u7684\u5386\u53F2\u5305\u88B1\uFF0C\u6211\u8FD8\u662F\u80FD\u4ECEJavaScript\u4E2D\u770B\u5230\u6211\u719F\u6089\u7684\u3001\u5F15\u4EE5\u4E3A\u50B2\u7684\u7ECF\u9A8C\u95EA\u5149\uFF0C\u5E76\u8BA9\u8FD9\u4E9B\u4E1C\u897F\u8FC5\u901F\u6FC0\u6D3B\u6211\u5BF9\u8BED\u8A00\u7684\u5174\u8DA3\u548C\u638C\u63A7\u611F\u3002\u7136\u800C\u53EA\u9700\u8981\u7A0D\u7A0D\u591A\u4E00\u70B9\u70B9\u65F6\u95F4\uFF0C\u6DF7\u5408\u8BED\u8A00\u4E2D\u7684\u5176\u5B83\u7EC4\u5206\u5C31\u4F1A\u53D8\u6210\u6211\u7684\u56F0\u6270\uFF0C\u53D8\u6210\u8FD9\u95E8\u8BED\u8A00\u7ED9\u6211\u5E26\u6765\u7684\u79CD\u79CD\u9677\u9631\uFF0C\u53D8\u6210\u6211\u8FD1\u4E4E\u7EDD\u671B\u7684\u81EA\u6211\u6000\u7591\u3002</p><p>\u800C\u5176\u5B9E\u95EE\u9898\u7684\u6C42\u89E3\u4E5F\u5F88\u7B80\u5355\uFF1A\u4E0D\u8981\u8BD5\u56FE\u53BB\u7EAF\u5316\u8FD9\u95E8\u8BED\u8A00\u3002</p><h2 id="\u8BED\u8A00\u7684\u7279\u6027" tabindex="-1">\u8BED\u8A00\u7684\u7279\u6027 <a class="header-anchor" href="#\u8BED\u8A00\u7684\u7279\u6027" aria-hidden="true">#</a></h2><p>\u6240\u4EE5\u5728\u8FD9\u4E2A\u4E13\u680F\u91CC\u9762\uFF0C\u6211\u5C31\u60F3\u4E0E\u4F60\u8BB2\u4E00\u8BB2\u6211\u5BF9JavaScript\u5404\u79CD\u8BED\u8A00\u7279\u6027\u7684\u7406\u89E3\uFF0C\u8FD8\u6709\u5C55\u793A\u5C06\u8FD9\u4E9B\u8BED\u8A00\u7279\u6027\u548C\u8BED\u8A00\u8303\u578B\u878D\u5408\u5982\u4E00\u7684\u5177\u4F53\u6311\u6218\u4E0E\u6298\u8877\u3002</p><p>JavaScript\u4E3B\u8981\u5305\u62EC5\u4E2A\u65B9\u9762\u7684\u8BED\u8A00\u7279\u6027\uFF1A\u7ED3\u6784\u5316\u7F16\u7A0B\u3001\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u3001\u52A8\u6001\u8BED\u8A00\u3001\u51FD\u6570\u5F0F\u8BED\u8A00\u548C\u5E76\u884C\u8BED\u8A00\u3002\u56E0\u6B64\u5728\u8FD9\u4E2A\u4E13\u680F\u4E2D\uFF0C\u6211\u5C06\u4EE5\u201C\u8BED\u8A00\u201D\u4E3A\u6838\u5FC3\uFF0C\u4E3B\u8981\u8BA8\u8BBA\u8BED\u8A00\u8BBE\u8BA1\uFF0C\u7ED3\u6784\u5316\u548C\u9762\u5411\u5BF9\u8C61\u7279\u6027\uFF0C\u4EE5\u53CA\u90E8\u5206\u7684\u52A8\u6001\u8BED\u8A00\u7279\u6027\u3002\u8FD8\u6709\u4E00\u4E9B\u5176\u5B83\u90E8\u5206\u7684\u7279\u6027\uFF0C\u6211\u5C06\u4F1A\u5728\u4EE5\u540E\u7684\u4E13\u680F\u4E2D\u518D\u7ED9\u4F60\u8BB2\u3002</p><p>\u5728\u8BB2\u8FF0\u7684\u5185\u5BB9\u65B9\u9762\uFF0C\u5C3D\u7BA1\u6BCF\u4E00\u8BB2\u90FD\u662F\u4E00\u4E2A\u72EC\u7ACB\u7684\u6807\u9898\uFF0C\u4F46\u603B\u4F53\u6765\u8BF4\uFF0C\u8FD9\u4E9B\u5185\u5BB9\u4E5F\u662F\u5FAA\u5E8F\u6E10\u8FDB\u7684\uFF0C\u56E0\u6B64\u4F60\u6700\u597D\u4E0D\u8981\u843D\u4E0B\u8BFE\u7A0B\u3002\u5E76\u4E14\u5982\u679C\u6709\u65F6\u95F4\u3001\u6709\u673A\u4F1A\u7684\u8BDD\uFF0C\u8FD8\u662F\u5BF9\u524D\u9762\u7684\u5185\u5BB9\u505A\u4E00\u4E9B\u5206\u6790\u548C\u5DE9\u56FA\u4E3A\u597D\u3002</p><p>\u53E6\u5916\uFF0C\u4F60\u53EF\u80FD\u5DF2\u7ECF\u6CE8\u610F\u5230\u4E86\uFF1A\u6BCF\u4E00\u8BB2\u7684\u6807\u9898\u90FD\u662F\u4E00\u884C\u4EE3\u7801\u3002\u5C3D\u7BA1\u8FD9\u4E9B\u4EE3\u7801\u7EDD\u5927\u591A\u6570\u90FD\u662F\u6709\u610F\u4E49\u7684\u3001\u53EF\u4EE5\u4F7F\u7528\u7684\uFF0C\u4F46\u662F\u6211\u5E76\u4E0D\u662F\u4ECE\u5B9E\u7528\u6027\u51FA\u53D1\u6765\u5199\u51FA\u8FD9\u4E9B\u4EE3\u7801\u7684\uFF0C\u56E0\u6B64\u5B83\u4EEC\u4E0D\u89C1\u5F97\u80FD\u5F88\u597D\u5730\u4F7F\u7528\u5728\u4F60\u7684\u9879\u76EE\u4E2D\u3002\u6211\u5C3D\u91CF\u4F7F\u6BCF\u4E00\u8BB2\u7684\u6807\u9898\u5728\u8868\u8FBE\u591A\u79CD\u8BED\u8A00\u7279\u6027\u7684\u540C\u65F6\uFF0C\u6307\u5411\u4E00\u4E2A\u4E3B\u8981\u7684\u3001\u6838\u5FC3\u7684\u5185\u5BB9\u8BB2\u8FF0\u65B9\u5411\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5982\u679C\u4F60\u770B\u5230\u90A3\u6837\u7684\u4E00\u884C\u6807\u9898\u540E\uFF0C\u80FD\u731C\u51FA\u5B83\u6D89\u53CA\u5230\u54EA\u4E9B\u6DF7\u5408\u8BED\u8A00\u7279\u6027\uFF0C\u6216\u8005\u662F\u7531\u54EA\u4E9B\u7279\u6027\u5171\u540C\u4F5C\u7528\u4EE5\u5BFC\u81F4\u8FD9\u4E2A\u4EE3\u7801\u7684\u5F62\u5F0F\u98CE\u683C\u6216\u53EF\u80FD\u7684\u7ED3\u679C\uFF0C\u90A3\u4E48\u4F60\u4E5F\u5C31\u76F8\u5F53\u4E8E\u590D\u76D8\u4E86\u4F60\u7684\u77E5\u8BC6\u50A8\u5907\uFF0C\u8FD9\u6709\u52A9\u4E8E\u4F60\u5EFA\u7ACB\u81EA\u5DF1\u7684\u77E5\u8BC6\u4F53\u7CFB\u3002</p><p>\u6240\u4EE5\uFF0C\u4F60\u5927\u53EF\u4EE5\u5C06\u8FD9\u6837\u7684\u6807\u9898\u5F53\u4F5C\u4E00\u628A\u5FF5\u73E0\uFF0C\u6CA1\u4E8B\u7684\u65F6\u5019\uFF0C\u76D8\u4E00\u76D8\u3002\uFF1A\uFF09</p><h2 id="\u4F53\u7CFB\u6027" tabindex="-1">\u4F53\u7CFB\u6027 <a class="header-anchor" href="#\u4F53\u7CFB\u6027" aria-hidden="true">#</a></h2><p>\u8BF4\u5230\u4F53\u7CFB\u6027\uFF0C\u5176\u5B9E\u8FD9\u624D\u662F\u6211\u8FD9\u4E2A\u8BFE\u7A0B\u6700\u5173\u6CE8\u7684\u5730\u65B9\u3002</p><p>\u6211\u5E0C\u671B\u7EFC\u5408\u8FD9\u4E9B\u4EE3\u7801\u7684\u7279\u6B8A\u6027\uFF0C\u4EE3\u7801\u6240\u6D89\u95EE\u9898\u7684\u9886\u57DF\uFF0C\u4EE3\u7801\u7684\u9010\u6B65\u5206\u89E3\u89E3\u6790\uFF0C\u4EE5\u53CA\u8FA8\u6790\u4E0E\u8BE5\u4EE3\u7801\u76F8\u4F3C\u7684\u6216\u540C\u7C7B\u7684\u95EE\u9898\uFF0C\u4E00\u65B9\u9762\u53D1\u6398\u5B83\u4EEC\u6F5C\u5728\u7684\u5E94\u7528\uFF0C\u53E6\u4E00\u65B9\u9762\uFF0C\u5219\u5728\u4E8E\u5E2E\u52A9\u4F60\u6784\u5EFA\u4E00\u4E2A\u8BED\u8A00\u77E5\u8BC6\u7ED3\u6784\u3002\u8FD9\u6837\u7684\u8BED\u8A00\u77E5\u8BC6\u7ED3\u6784\uFF0C\u80FD\u8BA9\u4F60\u770B\u5230\u66FE\u7ECF\u6478\u8FC7\u7684\u90A3\u4E9B\u9879\u76EE\uFF0C\u5199\u8FC7\u7684\u90A3\u4E9B\u4EE3\u7801\uFF0C\u586B\u8FC7\u7684\u90A3\u4E9B\u5DE8\u5751\u7684\u5F71\u5B50\uFF0C\u5E76\u4E14\u53D1\u6398\u6697\u5F71\u80CC\u540E\u6D8C\u52A8\u7684\u8BED\u8A00\u539F\u529B\uFF0C\u627E\u5230\u5C5E\u4E8E\u81EA\u5DF1\u7684\u3001\u53EF\u89C4\u5212\u7684\u8BED\u8A00\u5B66\u4E60\u4F53\u7CFB\u3002</p><p>\u4F60\u4E0D\u9700\u8981\u7CBE\u901A\u6240\u6709\u7684\u8BED\u8A00\uFF0C\u4F46\u5982\u679C\u4F60\u4E86\u89E3\u90A3\u4E9B\u8BED\u8A00\u7C7B\u578B\u7684\u6838\u5FC3\u7684\u3001\u672C\u8D28\u7684\u5DEE\u5F02\uFF0C\u5EFA\u7ACB\u8D77\u81EA\u5DF1\u7684\u5BF9\u8BED\u8A00\u7684\u8BA4\u8BC6\u89C2\u548C\u8FA8\u6790\u529B\uFF0C\u90A3\u4E48\u5F53\u4F60\u63A5\u89E6\u5230\u4E00\u95E8\u65B0\u7684\u8BED\u8A00\u65F6\uFF0C\u4FBF\u53EF\u4EE5\u5728\u6781\u5FEB\u7684\u65F6\u95F4\u5185\u5C06\u5B83\u7EB3\u5165\u81EA\u5DF1\u7684\u8BED\u8A00\u77E5\u8BC6\u7ED3\u6784\uFF0C\u5FEB\u901F\u5730\u6620\u5C04\u5230\u90A3\u4E9B\u5386\u53F2\u9879\u76EE\u548C\u7814\u53D1\u7ECF\u9A8C\u4E2D\u3002\u4F60\u53EF\u4EE5\u901A\u8FC7\u60F3\u8C61\uFF0C\u5C06\u65B0\u7684\u8BED\u8A00\u5728\u81EA\u5DF1\u7684\u7ECF\u9A8C\u4E2D\u201C\u56DE\u653E\u4E00\u904D\u201D\uFF0C\u8FD9\u76F8\u5F53\u4E8E\u7528\u65B0\u8BED\u8A00\u91CD\u5199\u4E86\u4E00\u904D\u90A3\u4E9B\u4EE3\u7801\uFF0C\u4E5F\u76F8\u5F53\u4E8E\u5C06\u4F60\u81EA\u5DF1\u7684\u5386\u53F2\u7ECF\u9A8C\u5168\u90E8\u6D88\u5316\u5728\u8FD9\u4E2A\u65B0\u8BED\u8A00\u4E4B\u4E2D\u3002</p><p>\u8FD9\u6837\u7684\u8BED\u8A00\u5B66\u4E60\u548C\u611F\u609F\u65B9\u6CD5\uFF0C\u6536\u6548\u662F\u6781\u5FEB\u7684\u3002</p><p>\u6211\u66FE\u7ECF\u5728\u8C4C\u8C46\u835A\u7684\u5DE5\u4F5C\u4E2D\uFF0C\u5B8C\u6210\u8FC7\u4E00\u4E2A\u7528Lua\u6765\u5B9E\u73B0\u7684\u3001\u652F\u6301\u5927\u89C4\u6A21\u5E76\u53D1\u7684\u670D\u52A1\u7AEF\u9879\u76EE\uFF0C\u90A3\u662F\u4E00\u4E2A\u91D1\u878D\u7EA7\u7684\u98CE\u63A7\u4EA7\u54C1\u3002\u5728\u6211\u5BF9Lua\u4E86\u89E3\u51E0\u4E4E\u4E3A\u96F6\u7684\u60C5\u51B5\u4E0B\uFF0C\u51FA\u4E8E\u5BF9\u201C\u7F16\u7A0B\u8BED\u8A00\u6838\u5FC3\u539F\u7406\u201D\u7684\u4E86\u89E3\uFF0C\u901A\u8FC7\u4E0A\u8FF0\u77E5\u8BC6\u6620\u5C04\u7684\u65B9\u6CD5\uFF0C\u6211\u5728\u96F6\u5B66\u4E60\u7684\u60C5\u51B5\u5C31\u5F00\u59CB\u4E86\u7F16\u7801\u5DE5\u4F5C\u3002\u9664\u4E86\u5FC5\u8981\u7684\u67E5\u624B\u518C\u4E4B\u5916\uFF0C\u5207\u6362\u8BED\u8A00\u7684\u65F6\u95F4\u6210\u672C\u51E0\u4E4E\u53EF\u4EE5\u5FFD\u7565\u4E0D\u8BA1\u3002</p><p>\u5F53\u7136\uFF0C\u542C\u5230\u8FD9\u91CC\u7684\u65F6\u5019\uFF0C\u4F60\u53EF\u80FD\u4F1A\u8BF4\u201CLua\u548CJavaScript\u7684\u76F8\u4F3C\u6027\u592A\u9AD8\u201D\uFF0C\u53C8\u6216\u8005\u8BF4\u201CLua\u592A\u7B80\u5355\u4E86\u201D\u3002\u4F46\u662F\u4E8B\u5B9E\u4E0A\uFF0C\u6211\u4E4B\u524D\u5728\u5B66\u4E60Erlang\u7684\u65F6\u5019\u4E5F\u662F\u5982\u6B64\uFF0C\u4EE5\u53CA\u540E\u6765\u5728\u5B66\u4E60Scala\u7684\u65F6\u5019\u4ECD\u7136\u662F\u5982\u6B64\u3002\u5F53\u4F60\u771F\u6B63\u5730\u201C\u89E3\u51B3\u4E86\u8BED\u8A00\u95EE\u9898\u201D\u65F6\uFF0C\u201C\u65B0\u8BED\u8A00\u201D\u771F\u7684\u5BF9\u4F60\u6765\u8BF4\u5B8C\u5168\u4E0D\u80FD\u79F0\u5176\u4E3A\u95EE\u9898\u3002</p><p>\u6240\u8C13\u7684\u8BED\u8A00\u7279\u6027\uFF0C\u5176\u5B9E\u662F\u5BF9\u8BED\u8A00\u7684\u6838\u5FC3\u62BD\u8C61\u6982\u5FF5\u7684\u8BED\u6CD5\u8868\u73B0\u3002\u6240\u4EE5\uFF0C\u6240\u8C13\u7684\u201C\u5B66\u4E60\u65B0\u8BED\u8A00\u201D\uFF0C\u53EA\u4E0D\u8FC7\u662F\u5728\u73A9\u201C\u53D8\u6362\u4EE3\u7801\u98CE\u683C\u201D\u7684\u6E38\u620F\uFF0C\u800C\u5DF2\u3002</p><p>\u4E00\u65E6\u4F60\u5EFA\u7ACB\u4E86\u4F60\u7684\u4F53\u7CFB\u6027\uFF0C\u90A3\u4E48\u76F8\u5F53\u4E8E\u4F60\u521B\u5EFA\u4E86\u6E38\u620F\u89C4\u5219\uFF0C\u4F60\u5C31\u6210\u4E86\u201C\u7F16\u7A0B\u6E38\u620F\u201D\u4E2D\u7684\u4E0A\u5E1D\u3002</p><p>\u4F60\u5C06\u4F1A\u6709\u4E00\u79CD\u5207\u5B9E\u7684\u3001\u4E07\u7269\u5982\u4E00\u7684\u64CD\u63A7\u611F\u3002</p><h2 id="\u5B66\u8FD9\u95E8\u8BFE" tabindex="-1">\u5B66\u8FD9\u95E8\u8BFE <a class="header-anchor" href="#\u5B66\u8FD9\u95E8\u8BFE" aria-hidden="true">#</a></h2><p>\u6240\u4EE5\uFF0C\u8FD9\u95E8\u8BFE\u7684\u5185\u5BB9\u5927\u6982\u7387\u4E0D\u4F1A\u7528\u5728\u4F60\u7684\u4E00\u4E2A\u7EBF\u4E0A\u9879\u76EE\u4E2D\uFF0C\u4E5F\u4E0D\u4F1A\u63D0\u9AD8\u4F60\u5199\u4EE3\u7801\u7684\u901F\u5EA6\u3002\u4F46<strong>\u5B83\u7EDD\u5BF9\u80FD\u8BA9\u4F60\u63D0\u5347\u5BF9\u4EE3\u7801\u7684\u6D1E\u5BDF\u529B</strong>\uFF0C\u8BA9\u4F60\u53EF\u4EE5\u5728\u7EB7\u7E41\u7684\u4EE3\u7801\u4E2D\u5FEB\u901F\u627E\u5230\u5B83\u5728\u6027\u80FD\u3001\u7EC4\u7EC7\u3001\u903B\u8F91\u7B49\u95EE\u9898\u7684\u5173\u952E\uFF0C\u4E5F\u53EF\u4EE5\u5728\u8BED\u8A00\u5C42\u9762\u7ED9\u51FA\u5408\u7406\u7684\u89E3\u91CA\u3002</p><p>\u5F53\u7136\uFF0C\u5C31\u4E00\u4E2A\u5177\u4F53\u95EE\u9898\u7684\u5177\u4F53\u89E3\uFF0C\u8FD9\u4E00\u5207\u8FD8\u662F\u4E0D\u591F\u7684\uFF0C\u56E0\u4E3A\u8BED\u8A00\u662F\u5B9E\u73B0\u4E1A\u52A1\u7684\u5DE5\u5177\uFF0C\u800C\u4E0D\u662F\u4E1A\u52A1\u672C\u8EAB\u3002\u6240\u4EE5\uFF0C\u5728\u9762\u8BD5\u4E2D\u4E0D\u4EC5\u4EC5\u4F1A\u8003\u5BDF\u4F60\u7684\u8BED\u8A00\u80FD\u529B\uFF0C\u4E5F\u4F1A\u8003\u5BDF\u4F60\u5BF9\u66FE\u7ECF\u9879\u76EE\u7684\u7ECF\u9A8C\u4E0E\u611F\u53D7\u3002</p><p>\u65E0\u8BBA\u5982\u4F55\uFF0C\u6211\u5E0C\u671B\u4F60\u80FD\u627E\u5230\u81EA\u5DF1\u5BF9\u8BED\u8A00\u7684\u8BA4\u8BC6\uFF0C\u65E0\u8BBA\u662F\u4E0D\u662F\u901A\u8FC7\u8FD9\u95E8\u8BFE\u7A0B\uFF0C\u201C\u6784\u9020\u8BA4\u8BC6\u201D\u5BF9\u4F60\u81EA\u5DF1\u800C\u8A00\u90FD\u662F\u6781\u81F4\u91CD\u8981\u7684\u4E8B\u60C5\u3002\u5982\u679C\u5728\u8FD9\u5176\u4E2D\uFF0C\u6211\u7684\u8BFE\u7A0B\u80FD\u5BF9\u4F60\u6709\u6240\u5E2E\u52A9\uFF0C\u54EA\u6015\u4EC5\u4EC5\u662F\u4E00\u70B9\u70B9\u542F\u53D1\uFF0C\u6211\u60F3\uFF0C\u8FD9\u90FD\u662F\u6211\u975E\u5E38\u4E50\u610F\u770B\u5230\u7684\u7ED3\u679C\u4E86\u3002</p><p>\u6700\u540E\uFF0C\u591A\u8C22\u4F60\u6765\u770B\u6211\u7684\u4E13\u680F\u3002\u5173\u4E8EJavaScript\uFF0C\u4F60\u7684\u7406\u89E3\u662F\u600E\u6837\u7684\u5462\uFF1F\u4F60\u53C8\u6709\u4EC0\u4E48\u6837\u7684\u671F\u5F85\uFF1F\u6B22\u8FCE\u7559\u8A00\u3002</p>',46),c=[r];function l(n,d,s,h,o,v){return e(),p("div",null,c)}const J=a(i,[["render",l]]);export{_ as __pageData,J as default};
