import{_ as a,c as s,o as n,d as p}from"./app.e0e4ea3a.js";const v=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u674E\u5175-\u56FE\u89E3GoogleV8","slug":"title-\u674E\u5175-\u56FE\u89E3googlev8","link":"#title-\u674E\u5175-\u56FE\u89E3googlev8","children":[]},{"level":2,"title":"\u4EC0\u4E48\u662FJavaScript\u4E2D\u7684\u5BF9\u8C61\uFF1F","slug":"\u4EC0\u4E48\u662Fjavascript\u4E2D\u7684\u5BF9\u8C61\uFF1F","link":"#\u4EC0\u4E48\u662Fjavascript\u4E2D\u7684\u5BF9\u8C61\uFF1F","children":[]},{"level":2,"title":"\u51FD\u6570\u7684\u672C\u8D28","slug":"\u51FD\u6570\u7684\u672C\u8D28","link":"#\u51FD\u6570\u7684\u672C\u8D28","children":[]},{"level":2,"title":"\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11","slug":"\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11","link":"#\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11","children":[]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[]},{"level":2,"title":"\u601D\u8003\u9898","slug":"\u601D\u8003\u9898","link":"#\u601D\u8003\u9898","children":[]}],"relativePath":"JavaScript/\u674E\u5175-\u56FE\u89E3GoogleV8/02-\u51FD\u6570\u5373\u5BF9\u8C61-\u4E00\u7BC7\u6587\u7AE0\u5F7B\u5E95\u641E\u61C2JavaScript\u7684\u51FD\u6570\u7279\u70B9.md"}'),e={name:"JavaScript/\u674E\u5175-\u56FE\u89E3GoogleV8/02-\u51FD\u6570\u5373\u5BF9\u8C61-\u4E00\u7BC7\u6587\u7AE0\u5F7B\u5E95\u641E\u61C2JavaScript\u7684\u51FD\u6570\u7279\u70B9.md"},l=p(`<hr><h2 id="title-\u674E\u5175-\u56FE\u89E3googlev8" tabindex="-1">title: \u674E\u5175-\u56FE\u89E3GoogleV8 <a class="header-anchor" href="#title-\u674E\u5175-\u56FE\u89E3googlev8" aria-hidden="true">#</a></h2><p>02 | \u51FD\u6570\u5373\u5BF9\u8C61\uFF1A\u4E00\u7BC7\u6587\u7AE0\u5F7B\u5E95\u641E\u61C2JavaScript\u7684\u51FD\u6570\u7279\u70B9</p><p>\u4F60\u597D\uFF0C\u6211\u662F\u674E\u5175\u3002\u8FD9\u662F\u6211\u4EEC\u4E13\u680F\u7684\u7B2C\u4E8C\u8BB2\uFF0C\u6211\u4EEC\u6765\u770B\u4E0B\u201C\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11\u201D\u80CC\u540E\u7684\u542B\u4E49\u3002</p><p>\u5982\u679C\u4F60\u719F\u6089\u4E86\u4E00\u95E8\u5176\u4ED6\u6D41\u884C\u8BED\u8A00\uFF0C\u518D\u6765\u4F7F\u7528JavaScript\uFF0C\u90A3\u4E48JavaScript\u4E2D\u7684\u51FD\u6570\u53EF\u80FD\u4F1A\u7ED9\u4F60\u9020\u6210\u4E00\u4E9B\u8BEF\u89E3\uFF0C\u6BD4\u5982\u5728JavaScript\u4E2D\uFF0C\u4F60\u53EF\u4EE5\u5C06\u4E00\u4E2A\u51FD\u6570\u8D4B\u503C\u7ED9\u4E00\u4E2A\u53D8\u91CF\uFF0C\u8FD8\u53EF\u4EE5\u5C06\u51FD\u6570\u4F5C\u4E3A\u4E00\u4E2A\u53C2\u6570\u4F20\u9012\u7ED9\u53E6\u5916\u4E00\u4E2A\u51FD\u6570\uFF0C\u751A\u81F3\u53EF\u4EE5\u4F7F\u5F97\u4E00\u4E2A\u51FD\u6570\u8FD4\u56DE\u53E6\u5916\u4E00\u4E2A\u51FD\u6570\uFF0C\u8FD9\u5728\u4E00\u4E9B\u4E3B\u6D41\u8BED\u8A00\u4E2D\u90FD\u5F88\u96BE\u5B9E\u73B0\u3002</p><p>JavaScript\u4E2D\u7684\u51FD\u6570\u975E\u5E38\u7075\u6D3B\uFF0C\u5176\u6839\u672C\u539F\u56E0\u5728\u4E8E<strong>JavaScript\u4E2D\u7684\u51FD\u6570\u5C31\u662F\u4E00\u79CD\u7279\u6B8A\u7684\u5BF9\u8C61</strong>\uFF0C\u6211\u4EEC\u628AJavaScript\u4E2D\u7684\u51FD\u6570\u79F0\u4E3A<strong>\u4E00\u7B49\u516C\u6C11(First Class Function)\u3002</strong></p><p>\u57FA\u4E8E\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11\u7684\u8BBE\u8BA1\uFF0C\u4F7F\u5F97JavaScript\u975E\u5E38\u5BB9\u6613\u5B9E\u73B0\u4E00\u4E9B\u7279\u6027\uFF0C\u6BD4\u5982\u95ED\u5305\uFF0C\u8FD8\u6709\u51FD\u6570\u5F0F\u7F16\u7A0B\u7B49\uFF0C\u800C\u5176\u4ED6\u8BED\u8A00\u8981\u5B9E\u73B0\u8FD9\u4E9B\u7279\u6027\u5C31\u663E\u5F97\u6BD4\u8F83\u56F0\u96BE\uFF0C\u6BD4\u5982\u8981\u5728C++\u4E2D\u5B9E\u73B0\u95ED\u5305\u9700\u8981\u5B9E\u73B0\u5927\u91CF\u590D\u6742\u7684\u4EE3\u7801\uFF0C\u800C\u4E14\u4F7F\u7528\u8D77\u6765\u4E5F\u5F02\u5E38\u590D\u6742\u3002</p><p>\u51FD\u6570\u5F0F\u7F16\u7A0B\u548C\u95ED\u5305\u5728\u5B9E\u9645\u7684\u9879\u76EE\u4E2D\u4F1A\u7ECF\u5E38\u9047\u5230\uFF0C\u5982\u679C\u4E0D\u4E86\u89E3\u8FD9\u4E9B\u7279\u6027\uFF0C\u90A3\u4E48\u5728\u4F60\u4F7F\u7528\u7B2C\u4E09\u65B9\u4EE3\u7801\u65F6\u5C31\u4F1A\u975E\u5E38\u5403\u529B\uFF0C\u540C\u65F6\u81EA\u5DF1\u4E5F\u5F88\u96BE\u4F7F\u7528\u8FD9\u4E9B\u7279\u6027\u5199\u51FA\u4F18\u96C5\u7684\u4EE3\u7801\uFF0C\u56E0\u6B64\u6211\u4EEC\u5F88\u6709\u5FC5\u8981\u4E86\u89E3\u8FD9\u4E9B\u7279\u6027\u7684\u5E95\u5C42\u673A\u5236\u3002</p><p>\u53E6\u5916\uFF0C\u5728\u6211\u4EEC\u540E\u7EED\u8BFE\u7A0B\u4ECB\u7ECDV8\u5DE5\u4F5C\u673A\u5236\u65F6\uFF0C\u4F1A\u5B66\u4E60V8\u662F\u600E\u4E48\u5B9E\u73B0\u95ED\u5305\u7684\uFF0C\u8FD8\u4F1A\u5B66\u4E60V8\u662F\u5982\u4F55\u5C06JavaScript\u7684\u52A8\u6001\u7279\u6027\u9759\u6001\u5316\u4EE5\u52A0\u5FEB\u4EE3\u7801\u7684\u6267\u884C\u901F\u5EA6\uFF0C\u8FD9\u4E9B\u5185\u5BB9\u90FD\u6D89\u53CA\u5230JavaScript\u4E2D\u7684\u51FD\u6570\u5E95\u5C42\u7279\u6027\u3002</p><p>\u4ECA\u5929\uFF0C\u6211\u4EEC\u5C31\u6765\u6DF1\u5165\u5206\u6790\u4E0B\uFF0CJavaScript\u4E2D\u7684\u201C\u51FD\u6570\u201D\u5230\u5E95\u6709\u600E\u6837\u7684\u7279\u70B9\u3002</p><h2 id="\u4EC0\u4E48\u662Fjavascript\u4E2D\u7684\u5BF9\u8C61\uFF1F" tabindex="-1">\u4EC0\u4E48\u662FJavaScript\u4E2D\u7684\u5BF9\u8C61\uFF1F <a class="header-anchor" href="#\u4EC0\u4E48\u662Fjavascript\u4E2D\u7684\u5BF9\u8C61\uFF1F" aria-hidden="true">#</a></h2><p>\u65E2\u7136\u5728JavaScript\u4E2D\uFF0C\u51FD\u6570\u5C31\u662F\u4E00\u79CD\u7279\u6B8A\u7684\u5BF9\u8C61\uFF0C\u90A3\u6211\u4EEC\u9996\u5148\u8981\u660E\u767D\uFF0C\u4EC0\u4E48\u662FJavaScript\u4E2D\u7684\u201C\u5BF9\u8C61\u201D\uFF1F\u5B83\u548C\u9762\u5411\u5BF9\u8C61\u8BED\u8A00\u4E2D\u7684\u201C\u5BF9\u8C61\u201D\u6709\u4EC0\u4E48\u533A\u522B\uFF1F</p><p>\u548C\u5176\u4ED6\u4E3B\u6D41\u8BED\u8A00\u4E0D\u4E00\u6837\u7684\u662F\uFF0CJavaScript\u662F\u4E00\u95E8**\u57FA\u4E8E\u5BF9\u8C61(****Object-Based)**\u7684\u8BED\u8A00\uFF0C\u53EF\u4EE5\u8BF4JavaScript\u4E2D\u5927\u90E8\u5206\u7684\u5185\u5BB9\u90FD\u662F\u7531\u5BF9\u8C61\u6784\u6210\u7684\uFF0C\u8BF8\u5982\u51FD\u6570\u3001\u6570\u7EC4\uFF0C\u4E5F\u53EF\u4EE5\u8BF4JavaScript\u662F\u5EFA\u7ACB\u5728\u5BF9\u8C61\u4E4B\u4E0A\u7684\u8BED\u8A00\u3002</p><p><img src="https://static001.geekbang.org/resource/image/9e/f8/9e946bbdc54f5e1347f7b593f8f6fff8.jpg" alt="" title="\u57FA\u4E8E\u5BF9\u8C61\u7684\u8BBE\u8BA1"></p><p>\u800C\u8FD9\u4E9B\u5BF9\u8C61\u5728\u8FD0\u884C\u65F6\u53EF\u4EE5\u52A8\u6001\u4FEE\u6539\u5176\u5185\u5BB9\uFF0C\u8FD9\u9020\u5C31\u4E86JavaScript\u7684\u8D85\u7EA7\u7075\u6D3B\u7279\u6027\u3002\u4E0D\u8FC7\uFF0C\u56E0\u4E3AJavaScript\u592A\u7075\u6D3B\u4E86\uFF0C\u4E5F\u52A0\u5927\u4E86\u7406\u89E3\u548C\u4F7F\u7528\u8FD9\u95E8\u8BED\u8A00\u7684\u96BE\u5EA6\u3002</p><p>\u867D\u7136JavaScript\u662F\u57FA\u4E8E\u5BF9\u8C61\u8BBE\u8BA1\u7684\uFF0C\u4F46\u662F\u5B83\u5374\u4E0D\u662F\u4E00\u95E8<strong>\u9762\u5411\u5BF9\u8C61\u7684\u8BED\u8A00(Object\u2014Oriented Programming Language)</strong>\uFF0C\u56E0\u4E3A\u9762\u5411\u5BF9\u8C61\u8BED\u8A00\u5929\u751F\u652F\u6301<strong>\u5C01\u88C5\u3001\u7EE7\u627F\u3001\u591A\u6001</strong>\uFF0C\u4F46\u662FJavaScript\u5E76\u6CA1\u6709\u76F4\u63A5\u63D0\u4F9B\u591A\u6001\u7684\u652F\u6301\uFF0C\u56E0\u6B64\u8981\u5728JavaScript\u4E2D\u4F7F\u7528\u591A\u6001\u5E76\u4E0D\u662F\u4E00\u4EF6\u5BB9\u6613\u7684\u4E8B\u3002</p><p><img src="https://static001.geekbang.org/resource/image/ef/00/eff1c1c773835b79ce597a84b2f94a00.jpg" alt="" title="\u9762\u5411\u5BF9\u8C61\u7684\u8BED\u8A00"></p><p>\u9664\u4E86\u5BF9\u591A\u6001\u652F\u6301\u7684\u4E0D\u597D\uFF0CJavaScript\u5B9E\u73B0\u7EE7\u627F\u7684\u65B9\u5F0F\u548C\u9762\u5411\u5BF9\u8C61\u7684\u8BED\u8A00\u5B9E\u73B0\u7EE7\u627F\u7684\u65B9\u5F0F\u540C\u6837\u5B58\u5728\u5F88\u5927\u7684\u5DEE\u5F02\u3002</p><p>\u9762\u5411\u5BF9\u8C61\u8BED\u8A00\u662F\u7531\u8BED\u8A00\u672C\u8EAB\u5BF9\u7EE7\u627F\u505A\u4E86\u5145\u5206\u7684\u652F\u6301\uFF0C\u5E76\u63D0\u4F9B\u4E86\u5927\u91CF\u7684\u5173\u952E\u5B57\uFF0C\u5982public\u3001protected\u3001friend\u3001interface\u7B49\uFF0C\u4F17\u591A\u7684\u5173\u952E\u5B57\u4F7F\u5F97\u9762\u5411\u5BF9\u8C61\u8BED\u8A00\u7684\u7EE7\u627F\u53D8\u5F97\u5F02\u5E38\u7E41\u7410\u548C\u590D\u6742\uFF0C\u800CJavaScript\u4E2D\u5B9E\u73B0\u7EE7\u627F\u7684\u65B9\u5F0F\u5374\u975E\u5E38\u7B80\u5355\u6E05\u723D\uFF0C**\u53EA\u662F\u5728\u5BF9\u8C61\u4E2D\u6DFB\u52A0\u4E86\u4E00\u4E2A\u79F0\u4E3A\u539F\u578B\u7684\u5C5E\u6027\uFF0C\u628A\u7EE7\u627F\u7684\u5BF9\u8C61\u901A\u8FC7\u539F\u578B\u94FE\u63A5\u8D77\u6765\uFF0C\u5C31\u5B9E\u73B0\u4E86\u7EE7\u627F\uFF0C\u6211\u4EEC\u628A\u8FD9\u79CD\u7EE7\u627F\u65B9\u5F0F\u79F0\u4E3A\u57FA\u4E8E\u539F\u578B\u94FE\u7EE7\u627F\u3002**\u5173\u4E8EV8\u662F\u5982\u4F55\u652F\u6301\u539F\u578B\u7684\uFF0C\u6211\u4EEC\u4F1A\u5728\u300A<a href="https://time.geekbang.org/column/article/215425" target="_blank" rel="noreferrer">05 | \u539F\u578B\u94FE\uFF1AV8\u662F\u5982\u4F55\u5B9E\u73B0\u5BF9\u8C61\u7EE7\u627F\u7684\uFF1F</a>\u300B\u8FD9\u8282\u8BFE\u505A\u5177\u4F53\u4ECB\u7ECD\u3002</p><p>\u65E2\u7136\u201CJavaScript\u4E2D\u7684\u5BF9\u8C61\u201D\u548C\u201C\u9762\u5411\u5BF9\u8C61\u8BED\u8A00\u4E2D\u7684\u5BF9\u8C61\u201D\u5B58\u5728\u5DE8\u5927\u5DEE\u5F02\uFF0C\u90A3\u4E48\u5728JavaScript\u4E2D\uFF0C\u6211\u4EEC\u6240\u8C08\u8BBA\u7684\u5BF9\u8C61\u5230\u5E95\u662F\u6307\u4EC0\u4E48\u5462\uFF1F</p><p>\u5176\u5B9EJavaScript\u4E2D\u7684\u5BF9\u8C61\u975E\u5E38\u7B80\u5355\uFF0C\u6BCF\u4E2A\u5BF9\u8C61\u5C31\u662F\u7531\u4E00\u7EC4\u7EC4\u5C5E\u6027\u548C\u503C\u6784\u6210\u7684\u96C6\u5408\uFF0C\u6BD4\u5982\u6211\u4F7F\u7528\u4E0B\u9762\u4EE3\u7801\u521B\u5EFA\u4E86\u4E00\u4E2Aperson\u5BF9\u8C61\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var person=new Object();</span></span>
<span class="line"><span style="color:#A6ACCD;">person.firstname=&quot;John&quot;;</span></span>
<span class="line"><span style="color:#A6ACCD;">person.lastname=&quot;Doe&quot;;</span></span>
<span class="line"><span style="color:#A6ACCD;">person.age=50;</span></span>
<span class="line"><span style="color:#A6ACCD;">person.eyecolor=&quot;blue&quot;;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8FD9\u4E2A\u5BF9\u8C61\u91CC\u9762\u6709\u56DB\u4E2A\u5C5E\u6027\uFF0C\u4E3A\u4E86\u76F4\u89C2\u7406\u89E3\uFF0C\u4F60\u53EF\u4EE5\u53C2\u770B\u4E0B\u56FE\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/d0/23/d07e174001a29765a3575908e3704123.jpg" alt="" title="\u5BF9\u8C61\u7684\u6784\u6210"></p><p>\u4E0A\u56FE\u5C55\u793A\u4E86\u5BF9\u8C61person\u7684\u7ED3\u6784\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\u84DD\u8272\u7684\u5C5E\u6027\u5728\u5DE6\u8FB9\uFF0C\u9EC4\u8272\u7684\u503C\u5728\u53F3\u8FB9\uFF0C\u6709\u591A\u7EC4\u5C5E\u6027\u548C\u503C\u7EC4\u6210\uFF0C\u8FD9\u5C31\u662FJavaScript\u4E2D\u7684\u5BF9\u8C61\uFF0C\u867D\u7136JavaScript\u5BF9\u8C61\u7528\u9014\u975E\u5E38\u5E7F\u6CDB\uFF0C\u4F7F\u7528\u7684\u65B9\u5F0F\u4E5F\u975E\u5E38\u4E4B\u591A\uFF0C\u4F46\u662F\u4E07\u53D8\u4E0D\u79BB\u5176\u5B97\uFF0C\u5176\u6838\u5FC3\u672C\u8D28\u90FD\u5C31\u662F\u7531\u4E00\u7EC4\u7EC4\u5C5E\u6027\u548C\u503C\u7EC4\u6210\u7684\u96C6\u5408\uFF0C\u6293\u4F4F\u4E86\u8FD9\u4E00\u70B9\uFF0C\u5F53\u6211\u4EEC\u518D\u5206\u6790\u5BF9\u8C61\u65F6\uFF0C\u5C31\u4F1A\u8F7B\u677E\u5F88\u591A\u3002</p><p>\u4E4B\u6240\u4EE5JavaScript\u4E2D\u5BF9\u8C61\u7684\u7528\u9014\u8FD9\u4E48\u5E7F\uFF0C\u662F\u56E0\u4E3A\u5BF9\u8C61\u7684\u503C\u53EF\u4EE5\u662F\u4EFB\u610F\u7C7B\u578B\u7684\u6570\u636E\uFF0C\u6211\u4EEC\u53EF\u4EE5\u6539\u9020\u4E0B\u4E0A\u9762\u7684\u90A3\u6BB5\u4EE3\u7801\uFF0C\u6765\u770B\u770B\u5BF9\u8C61\u7684\u503C\u90FD\u6709\u54EA\u4E9B\u7C7B\u578B\uFF1F\u6539\u9020\u540E\u7684\u4EE3\u7801\u5982\u4E0B\u6240\u793A\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var person=new Object()</span></span>
<span class="line"><span style="color:#A6ACCD;">person.firstname=&quot;John&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">person.lastname=&quot;Doe&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">person.info = new Object()</span></span>
<span class="line"><span style="color:#A6ACCD;">person.info.age=50</span></span>
<span class="line"><span style="color:#A6ACCD;">person.info.eyecolor=&quot;blue&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">person.showinfo = function (){</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(/*...*/)</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6211\u4EEC\u53EF\u4EE5\u5148\u753B\u51FA\u8FD9\u6BB5\u4EE3\u7801\u7684\u5185\u5B58\u5E03\u5C40\uFF0C\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/f7/17/f73524e4cae884747ae528d999fc1117.jpg" alt="" title="\u5C5E\u6027\u503C\u7C7B\u578B"></p><p>\u89C2\u5BDF\u4E0A\u56FE\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u51FA\u6765\uFF0C\u5BF9\u8C61\u7684\u5C5E\u6027\u503C\u6709\u4E09\u79CD\u7C7B\u578B\uFF1A</p><p>\u7B2C\u4E00\u79CD\u662F<strong>\u539F\u59CB\u7C7B\u578B(primitive)</strong>\uFF0C\u6240\u8C13\u7684\u539F\u59CB\u7C7B\u7684\u6570\u636E\uFF0C\u662F\u6307\u503C\u672C\u8EAB\u65E0\u6CD5\u88AB\u6539\u53D8\uFF0C\u6BD4\u5982JavaScript\u4E2D\u7684\u5B57\u7B26\u4E32\u5C31\u662F\u539F\u59CB\u7C7B\u578B\uFF0C\u5982\u679C\u4F60\u4FEE\u6539\u4E86JavaScript\u4E2D\u5B57\u7B26\u4E32\u7684\u503C\uFF0C\u90A3\u4E48V8\u4F1A\u8FD4\u56DE\u7ED9\u4F60\u4E00\u4E2A\u65B0\u7684\u5B57\u7B26\u4E32\uFF0C\u539F\u59CB\u5B57\u7B26\u4E32\u5E76\u6CA1\u6709\u88AB\u6539\u53D8\uFF0C\u6211\u4EEC\u79F0\u8FD9\u4E9B\u7C7B\u578B\u7684\u503C\u4E3A\u201C\u539F\u59CB\u503C\u201D\u3002</p><p>JavaScript\u4E2D\u7684\u539F\u59CB\u503C\u4E3B\u8981\u5305\u62ECnull\u3001undefined\u3001boolean\u3001number\u3001string\u3001bigint\u3001symbol \u8FD9\u4E03\u79CD\u3002</p><p>\u7B2C\u4E8C\u79CD\u5C31\u662F\u6211\u4EEC\u73B0\u5728\u4ECB\u7ECD\u7684<strong>\u5BF9\u8C61\u7C7B\u578B(Object)</strong>\uFF0C\u5BF9\u8C61\u7684\u5C5E\u6027\u503C\u4E5F\u53EF\u4EE5\u662F\u53E6\u5916\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u6BD4\u5982\u4E0A\u56FE\u4E2D\u7684info\u5C5E\u6027\u503C\u5C31\u662F\u4E00\u4E2A\u5BF9\u8C61\u3002</p><p>\u7B2C\u4E09\u79CD\u662F<strong>\u51FD\u6570\u7C7B\u578B(Function)</strong>\uFF0C\u5982\u679C\u5BF9\u8C61\u4E2D\u7684\u5C5E\u6027\u503C\u662F\u51FD\u6570\uFF0C\u90A3\u4E48\u6211\u4EEC\u628A\u8FD9\u4E2A\u5C5E\u6027\u79F0\u4E3A\u65B9\u6CD5\uFF0C\u6240\u4EE5\u6211\u4EEC\u53C8\u8BF4\u5BF9\u8C61\u5177\u5907\u5C5E\u6027\u548C\u65B9\u6CD5\uFF0C\u90A3\u4E48\u4E0A\u56FE\u4E2D\u7684showinfo\u5C31\u662Fperson\u5BF9\u8C61\u7684\u4E00\u4E2A\u65B9\u6CD5\u3002</p><p><img src="https://static001.geekbang.org/resource/image/8c/a6/8c33fa6c6e0cef5795292f0a21ee36a6.jpg" alt="" title="\u5BF9\u8C61\u5C5E\u6027\u503C\u7684\u4E09\u79CD\u7C7B\u578B"></p><h2 id="\u51FD\u6570\u7684\u672C\u8D28" tabindex="-1">\u51FD\u6570\u7684\u672C\u8D28 <a class="header-anchor" href="#\u51FD\u6570\u7684\u672C\u8D28" aria-hidden="true">#</a></h2><p>\u5206\u6790\u5B8C\u5BF9\u8C61\uFF0C\u73B0\u5728\u6211\u4EEC\u5C31\u80FD\u66F4\u597D\u5730\u7406\u89E3JavaScript\u4E2D\u51FD\u6570\u7684\u6982\u5FF5\u4E86\u3002</p><p>\u5728\u8FD9\u8282\u8BFE\u5F00\u59CB\u6211\u5C31\u63D0\u5230\uFF0C\u5728JavaScript\u4E2D\uFF0C\u51FD\u6570\u662F\u4E00\u79CD\u7279\u6B8A\u7684\u5BF9\u8C61\uFF0C\u5B83\u548C\u5BF9\u8C61\u4E00\u6837\u53EF\u4EE5\u62E5\u6709\u5C5E\u6027\u548C\u503C\uFF0C\u4F46\u662F\u51FD\u6570\u548C\u666E\u901A\u5BF9\u8C61\u4E0D\u540C\u7684\u662F\uFF0C\u51FD\u6570\u53EF\u4EE5\u88AB\u8C03\u7528\u3002</p><p>\u6211\u4EEC\u5148\u6765\u770B\u4E00\u6BB5JavaScript\u4EE3\u7801\uFF0C\u5728\u8FD9\u6BB5\u4EE3\u7801\u4E2D\uFF0C\u6211\u4EEC\u5B9A\u4E49\u4E86\u4E00\u4E2A\u51FD\u6570foo\uFF0C\u63A5\u4E0B\u6765\u6211\u4EEC\u7ED9foo\u51FD\u6570\u8BBE\u7F6E\u4E86myName\u548CuName\u7684\u5C5E\u6027\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">function foo(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var test = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">foo.myName = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">foo.uName = 2</span></span>
<span class="line"><span style="color:#A6ACCD;">console.log(foo.myName)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u65E2\u7136\u662F\u51FD\u6570\uFF0C\u90A3\u4E48\u5B83\u4E5F\u53EF\u4EE5\u88AB\u8C03\u7528\u3002\u6BD4\u5982\u4F60\u5B9A\u4E49\u4E86\u4E00\u4E2A\u51FD\u6570\uFF0C\u4FBF\u53EF\u4EE5\u901A\u8FC7\u51FD\u6570\u540D\u79F0\u52A0\u5C0F\u62EC\u53F7\u6765\u5B9E\u73B0\u51FD\u6570\u7684\u8C03\u7528\uFF0C\u4EE3\u7801\u5982\u4E0B\u6240\u793A\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">function foo(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var test = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(test)</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">foo()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u9664\u4E86\u4F7F\u7528\u51FD\u6570\u540D\u79F0\u6765\u5B9E\u73B0\u51FD\u6570\u7684\u8C03\u7528\uFF0C\u8FD8\u53EF\u4EE5\u76F4\u63A5\u8C03\u7528\u4E00\u4E2A\u533F\u540D\u51FD\u6570\uFF0C\u4EE3\u7801\u5982\u4E0B\u6240\u793A\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">(function (){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var test = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(test)</span></span>
<span class="line"><span style="color:#A6ACCD;">})()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u90A3\u4E48\uFF0CV8\u5185\u90E8\u662F\u600E\u4E48\u5B9E\u73B0\u51FD\u6570\u53EF\u8C03\u7528\u7279\u6027\u7684\u5462\uFF1F</p><p>\u5176\u5B9E\u5728V8\u5185\u90E8\uFF0C\u4F1A\u4E3A\u51FD\u6570\u5BF9\u8C61\u6DFB\u52A0\u4E86\u4E24\u4E2A\u9690\u85CF\u5C5E\u6027\uFF0C\u5177\u4F53\u5C5E\u6027\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/9e/e2/9e274227d637ce8abc4a098587613de2.jpg" alt="" title="\u51FD\u6570\u5BF9\u8C61\u5177\u6709\u9690\u85CF\u5C5E\u6027"></p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u51FD\u6570\u9664\u4E86\u53EF\u4EE5\u62E5\u6709\u5E38\u7528\u7C7B\u578B\u7684\u5C5E\u6027\u503C\u4E4B\u5916\uFF0C\u8FD8\u62E5\u6709\u4E24\u4E2A\u9690\u85CF\u5C5E\u6027\uFF0C\u5206\u522B\u662Fname\u5C5E\u6027\u548Ccode\u5C5E\u6027\u3002</p><p>\u9690\u85CFname\u5C5E\u6027\u7684\u503C\u5C31\u662F\u51FD\u6570\u540D\u79F0\uFF0C\u5982\u679C\u67D0\u4E2A\u51FD\u6570\u6CA1\u6709\u8BBE\u7F6E\u51FD\u6570\u540D\uFF0C\u5982\u4E0B\u9762\u8FD9\u6BB5\u51FD\u6570\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">(function (){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var test = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(test)</span></span>
<span class="line"><span style="color:#A6ACCD;">})()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8BE5\u51FD\u6570\u5BF9\u8C61\u7684\u9ED8\u8BA4\u7684name\u5C5E\u6027\u503C\u5C31\u662Fanonymous\uFF0C\u8868\u793A\u8BE5\u51FD\u6570\u5BF9\u8C61\u6CA1\u6709\u88AB\u8BBE\u7F6E\u540D\u79F0\u3002\u53E6\u5916\u4E00\u4E2A\u9690\u85CF\u5C5E\u6027\u662Fcode\u5C5E\u6027\uFF0C\u5176\u503C\u8868\u793A\u51FD\u6570\u4EE3\u7801\uFF0C\u4EE5\u5B57\u7B26\u4E32\u7684\u5F62\u5F0F\u5B58\u50A8\u5728\u5185\u5B58\u4E2D\u3002\u5F53\u6267\u884C\u5230\u4E00\u4E2A\u51FD\u6570\u8C03\u7528\u8BED\u53E5\u65F6\uFF0CV8\u4FBF\u4F1A\u4ECE\u51FD\u6570\u5BF9\u8C61\u4E2D\u53D6\u51FAcode\u5C5E\u6027\u503C\uFF0C\u4E5F\u5C31\u662F\u51FD\u6570\u4EE3\u7801\uFF0C\u7136\u540E\u518D\u89E3\u91CA\u6267\u884C\u8FD9\u6BB5\u51FD\u6570\u4EE3\u7801\u3002</p><h2 id="\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11" tabindex="-1">\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11 <a class="header-anchor" href="#\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11" aria-hidden="true">#</a></h2><p>\u56E0\u4E3A\u51FD\u6570\u662F\u4E00\u79CD\u7279\u6B8A\u7684\u5BF9\u8C61\uFF0C\u6240\u4EE5\u5728JavaScript\u4E2D\uFF0C\u51FD\u6570\u53EF\u4EE5\u8D4B\u503C\u7ED9\u4E00\u4E2A\u53D8\u91CF\uFF0C\u4E5F\u53EF\u4EE5\u4F5C\u4E3A\u51FD\u6570\u7684\u53C2\u6570\uFF0C\u8FD8\u53EF\u4EE5\u4F5C\u4E3A\u51FD\u6570\u7684\u8FD4\u56DE\u503C\u3002**\u5982\u679C\u67D0\u4E2A\u7F16\u7A0B\u8BED\u8A00\u7684\u51FD\u6570\uFF0C\u53EF\u4EE5\u548C\u8FD9\u4E2A\u8BED\u8A00\u7684\u6570\u636E\u7C7B\u578B\u505A\u4E00\u6837\u7684\u4E8B\u60C5\uFF0C\u6211\u4EEC\u5C31\u628A\u8FD9\u4E2A\u8BED\u8A00\u4E2D\u7684\u51FD\u6570\u79F0\u4E3A\u4E00\u7B49\u516C\u6C11\u3002**\u652F\u6301\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11\u7684\u8BED\u8A00\u53EF\u4EE5\u4F7F\u5F97\u4EE3\u7801\u903B\u8F91\u66F4\u52A0\u6E05\u6670\uFF0C\u4EE3\u7801\u66F4\u52A0\u7B80\u6D01\u3002</p><p>\u4F46\u662F\u7531\u4E8E\u51FD\u6570\u7684\u201C\u53EF\u88AB\u8C03\u7528\u201D\u7684\u7279\u6027\uFF0C\u4F7F\u5F97\u5B9E\u73B0\u51FD\u6570\u7684\u53EF\u8D4B\u503C\u3001\u53EF\u4F20\u53C2\u548C\u53EF\u4F5C\u4E3A\u8FD4\u56DE\u503C\u7B49\u7279\u6027\u53D8\u5F97\u6709\u4E00\u70B9\u9EBB\u70E6\u3002\u4E3A\u4EC0\u4E48\uFF1F</p><p>\u6211\u4EEC\u77E5\u9053\uFF0C\u5728\u6267\u884CJavaScript\u51FD\u6570\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4E3A\u4E86\u5B9E\u73B0\u53D8\u91CF\u7684\u67E5\u627E\uFF0CV8\u4F1A\u4E3A\u5176\u7EF4\u62A4\u4E00\u4E2A\u4F5C\u7528\u57DF\u94FE\uFF0C\u5982\u679C\u51FD\u6570\u4E2D\u4F7F\u7528\u4E86\u67D0\u4E2A\u53D8\u91CF\uFF0C\u4F46\u662F\u5728\u51FD\u6570\u5185\u90E8\u53C8\u6CA1\u6709\u5B9A\u4E49\u8BE5\u53D8\u91CF\uFF0C\u90A3\u4E48\u51FD\u6570\u5C31\u4F1A\u6CBF\u7740\u4F5C\u7528\u57DF\u94FE\u53BB\u5916\u90E8\u7684\u4F5C\u7528\u57DF\u4E2D\u67E5\u627E\u8BE5\u53D8\u91CF\uFF0C\u5177\u4F53\u6D41\u7A0B\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/8b/fd/8bb90b190362e3a00e5a260bad6829fd.jpg" alt="" title="\u67E5\u627E\u53D8\u91CF"></p><p>\u4ECE\u56FE\u4E2D\u53EF\u4EE5\u770B\u51FA\uFF0C\u5F53\u51FD\u6570\u5185\u90E8\u5F15\u7528\u4E86\u5916\u90E8\u7684\u53D8\u91CF\u65F6\uFF0C\u4F7F\u7528\u8FD9\u4E2A\u51FD\u6570\u8FDB\u884C\u8D4B\u503C\u3001\u4F20\u53C2\u6216\u4F5C\u4E3A\u8FD4\u56DE\u503C\uFF0C\u4F60\u8FD8\u9700\u8981\u4FDD\u8BC1\u8FD9\u4E9B\u88AB\u5F15\u7528\u7684\u5916\u90E8\u53D8\u91CF\u662F\u786E\u5B9A\u5B58\u5728\u7684\uFF0C\u8FD9\u5C31\u662F\u8BA9\u51FD\u6570\u4F5C\u4E3A\u4E00\u7B49\u516C\u6C11\u9EBB\u70E6\u7684\u5730\u65B9\uFF0C\u56E0\u4E3A\u865A\u62DF\u673A\u8FD8\u9700\u8981\u5904\u7406\u51FD\u6570\u5F15\u7528\u7684\u5916\u90E8\u53D8\u91CF\u3002\u6211\u4EEC\u6765\u770B\u4E00\u6BB5\u7B80\u5355\u7684\u4EE3\u7801\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">function foo(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var number = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">    function bar(){</span></span>
<span class="line"><span style="color:#A6ACCD;">        number++</span></span>
<span class="line"><span style="color:#A6ACCD;">        console.log(number)</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    return bar</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">var mybar = foo()</span></span>
<span class="line"><span style="color:#A6ACCD;">mybar()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u89C2\u5BDF\u4E0A\u6BB5\u4EE3\u7801\u53EF\u4EE5\u770B\u5230\uFF0C\u6211\u4EEC\u5728foo\u51FD\u6570\u4E2D\u5B9A\u4E49\u4E86\u4E00\u4E2A\u65B0\u7684bar\u51FD\u6570\uFF0C\u5E76\u4E14bar\u51FD\u6570\u5F15\u7528\u4E86foo\u51FD\u6570\u4E2D\u7684\u53D8\u91CFnumber\uFF0C\u5F53\u8C03\u7528foo\u51FD\u6570\u7684\u65F6\u5019\uFF0C\u5B83\u4F1A\u8FD4\u56DEbar\u51FD\u6570\u3002</p><p>\u90A3\u4E48\u6240\u8C13\u7684\u201C\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11\u201D\u5C31\u4F53\u73B0\u5728\uFF0C\u5982\u679C\u8981\u8FD4\u56DE\u51FD\u6570bar\u7ED9\u5916\u90E8\uFF0C\u90A3\u4E48\u5373\u4FBFfoo\u51FD\u6570\u6267\u884C\u7ED3\u675F\u4E86\uFF0C\u5176\u5185\u90E8\u5B9A\u4E49\u7684number\u53D8\u91CF\u4E5F\u4E0D\u80FD\u88AB\u9500\u6BC1\uFF0C\u56E0\u4E3Abar\u51FD\u6570\u4F9D\u7136\u5F15\u7528\u4E86\u8BE5\u53D8\u91CF\u3002</p><p>\u6211\u4EEC\u4E5F\u628A\u8FD9\u79CD\u5C06\u5916\u90E8\u53D8\u91CF\u548C\u548C\u51FD\u6570\u7ED1\u5B9A\u8D77\u6765\u7684\u6280\u672F\u79F0\u4E3A\u95ED\u5305\u3002V8\u5728\u5B9E\u73B0\u95ED\u5305\u7684\u7279\u6027\u65F6\u4E5F\u505A\u4E86\u5927\u91CF\u7684\u989D\u5916\u7684\u5DE5\u4F5C\uFF0C\u5173\u4E8E\u95ED\u5305\u7684\u8BE6\u7EC6\u5B9E\u73B0\uFF0C\u6211\u4EEC\u4F1A\u5728\u300A<a href="https://time.geekbang.org/column/article/223168" target="_blank" rel="noreferrer">12 | \u5EF6\u8FDF\u89E3\u6790\uFF1AV8\u662F\u5982\u4F55\u5B9E\u73B0\u95ED\u5305\u7684\uFF1F</a>\u300B\u8FD9\u8282\u8BFE\u518D\u4ECB\u7ECD\u3002</p><p>\u53E6\u5916\u57FA\u4E8E\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8F7B\u677E\u4F7F\u7528JavaScript\u6765\u5B9E\u73B0\u76EE\u524D\u6BD4\u8F83\u6D41\u884C\u7684\u51FD\u6570\u5F0F\u7F16\u7A0B\uFF0C\u51FD\u6570\u5F0F\u7F16\u7A0B\u89C4\u5219\u5F88\u5C11\uFF0C\u975E\u5E38\u4F18\u7F8E\uFF0C\u4E0D\u8FC7\u8FD9\u5E76\u4E0D\u662F\u672C\u4E13\u680F\u7684\u91CD\u70B9\uFF0C\u6240\u4EE5\u6211\u4EEC\u5148\u7565\u5F00\u4E0D\u8BB2\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u597D\u4E86\uFF0C\u4ECA\u5929\u7684\u5185\u5BB9\u5C31\u4ECB\u7ECD\u5230\u8FD9\u91CC\uFF0C\u4E0B\u9762\u6211\u6765\u603B\u7ED3\u4E0B\u672C\u6587\u7684\u4E3B\u8981\u5185\u5BB9\u3002</p><p>\u672C\u6587\u6211\u4EEC\u56F4\u7ED5JavaScript\u4E2D\u7684\u51FD\u6570\u6765\u5C55\u5F00\u4ECB\u7ECD\uFF0CJavaScript\u4E2D\u7684\u51FD\u6570\u975E\u5E38\u7075\u6D3B\uFF0C\u65E2\u53EF\u4EE5\u88AB\u8C03\u7528\uFF0C\u8FD8\u53EF\u4EE5\u4F5C\u4E3A\u53D8\u91CF\u3001\u53C2\u6570\u548C\u8FD4\u56DE\u503C\uFF0C\u8FD9\u4E9B\u7279\u6027\u4F7F\u5F97\u51FD\u6570\u7684\u7528\u6CD5\u975E\u5E38\u591A\uFF0C\u8FD9\u4E5F\u5BFC\u81F4\u4E86\u51FD\u6570\u53D8\u5F97\u6709\u4E9B\u590D\u6742\uFF0C\u56E0\u6B64\u672C\u6587\u7684\u76EE\u7684\u5C31\u662F\u8981\u8BB2\u6E05\u695A\u51FD\u6570\u5230\u5E95\u662F\u4EC0\u4E48\uFF1F</p><p>\u56E0\u4E3A\u51FD\u6570\u662F\u4E00\u79CD\u7279\u6B8A\u7684\u5BF9\u8C61\uFF0C\u6240\u4EE5\u6211\u4EEC\u5148\u4ECB\u7ECD\u4E86JavaScript\u4E2D\u7684\u5BF9\u8C61\uFF0CJavaScript\u4E2D\u7684\u5BF9\u8C61\u5C31\u662F\u7531\u4E00\u7EC4\u4E00\u7EC4\u5C5E\u6027\u548C\u503C\u7EC4\u6210\u7684\u96C6\u5408\uFF0C\u65E2\u7136\u51FD\u6570\u4E5F\u662F\u5BF9\u8C61\uFF0C\u90A3\u4E48\u51FD\u6570\u4E5F\u662F\u7531\u4E00\u7EC4\u7EC4\u503C\u548C\u5C5E\u6027\u7EC4\u6210\u7684\u96C6\u5408\uFF0C\u6211\u4EEC\u8FD8\u5728\u6587\u4E2D\u4F7F\u7528\u4E86\u4E00\u6BB5\u4EE3\u7801\u8BC1\u660E\u4E86\u8FD9\u70B9\u3002</p><p>\u56E0\u4E3A\u51FD\u6570\u4F5C\u4E3A\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u662F\u53EF\u4EE5\u88AB\u8D4B\u503C\u3001\u4F5C\u4E3A\u53C2\u6570\uFF0C\u8FD8\u53EF\u4EE5\u4F5C\u4E3A\u8FD4\u56DE\u503C\u7684\uFF0C\u90A3\u4E48\u5982\u679C\u4E00\u4E2A\u51FD\u6570\u8FD4\u56DE\u4E86\u53E6\u5916\u4E00\u4E2A\u51FD\u6570\uFF0C\u90A3\u4E48\u5C31\u5E94\u8BE5\u8FD4\u56DE\u8BE5\u51FD\u6570\u6240\u6709\u76F8\u5173\u7684\u5185\u5BB9\u3002</p><p>\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u53C8\u4ECB\u7ECD\u4E86\u4E00\u4E2A\u51FD\u6570\u5230\u5E95\u5173\u8054\u4E86\u54EA\u4E9B\u5185\u5BB9\uFF1A</p><ul><li>\u51FD\u6570\u4F5C\u4E3A\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u5B83\u6709\u81EA\u5DF1\u7684\u5C5E\u6027\u548C\u503C\uFF0C\u6240\u4EE5\u51FD\u6570\u5173\u8054\u4E86\u57FA\u7840\u7684\u5C5E\u6027\u548C\u503C\uFF1B</li><li>\u51FD\u6570\u4E4B\u6240\u4EE5\u6210\u4E3A\u7279\u6B8A\u7684\u5BF9\u8C61\uFF0C\u8FD9\u4E2A\u7279\u6B8A\u7684\u5730\u65B9\u662F\u51FD\u6570\u53EF\u4EE5\u201C\u88AB\u8C03\u7528\u201D\uFF0C\u6240\u4EE5\u4E00\u4E2A\u51FD\u6570\u88AB\u8C03\u7528\u65F6\uFF0C\u5B83\u8FD8\u9700\u8981\u5173\u8054\u76F8\u5173\u7684\u6267\u884C\u4E0A\u4E0B\u6587\u3002</li></ul><p>\u7ED3\u5408\u4EE5\u4E0A\u4E24\u70B9\uFF0CJavaScript\u4E2D\u7684\u51FD\u6570\u5C31\u5B9E\u73B0\u4E86\u201C\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11\u201D\u7684\u7279\u6027\u3002</p><h2 id="\u601D\u8003\u9898" tabindex="-1">\u601D\u8003\u9898 <a class="header-anchor" href="#\u601D\u8003\u9898" aria-hidden="true">#</a></h2><p>\u672C\u6587\u6211\u4EEC\u4ECE\u5BF9\u8C61\u804A\u5230\u4E86\u95ED\u5305\uFF0C\u90A3\u4E48\u7559\u7ED9\u4F60\u7684\u95EE\u9898\u662F\uFF0C\u54EA\u4E9B\u8BED\u8A00\u5929\u751F\u652F\u6301\u201C\u51FD\u6570\u662F\u4E00\u7B49\u516C\u6C11\u201D\uFF1F\u6B22\u8FCE\u4F60\u5728\u7559\u8A00\u533A\u4E0E\u6211\u5206\u4EAB\u8BA8\u8BBA\u3002</p><p>\u611F\u8C22\u4F60\u7684\u9605\u8BFB\uFF0C\u5982\u679C\u4F60\u89C9\u5F97\u8FD9\u4E00\u8BB2\u7684\u5185\u5BB9\u5BF9\u4F60\u6709\u6240\u542F\u53D1\uFF0C\u4E5F\u6B22\u8FCE\u628A\u5B83\u5206\u4EAB\u7ED9\u4F60\u7684\u670B\u53CB\u3002</p>`,73),t=[l];function o(c,r,i,g,C,A){return n(),s("div",null,t)}const u=a(e,[["render",o]]);export{v as __pageData,u as default};
