import{_ as s,c as a,o as n,d as p}from"./app.e0e4ea3a.js";const d=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u674E\u5175-\u56FE\u89E3GoogleV8","slug":"title-\u674E\u5175-\u56FE\u89E3googlev8","link":"#title-\u674E\u5175-\u56FE\u89E3googlev8","children":[]},{"level":2,"title":"\u4EC0\u4E48\u662F\u51FD\u6570\u4F5C\u7528\u57DF\u548C\u5168\u5C40\u4F5C\u7528\u57DF\uFF1F","slug":"\u4EC0\u4E48\u662F\u51FD\u6570\u4F5C\u7528\u57DF\u548C\u5168\u5C40\u4F5C\u7528\u57DF\uFF1F","link":"#\u4EC0\u4E48\u662F\u51FD\u6570\u4F5C\u7528\u57DF\u548C\u5168\u5C40\u4F5C\u7528\u57DF\uFF1F","children":[]},{"level":2,"title":"\u4F5C\u7528\u57DF\u94FE\u662F\u600E\u4E48\u5DE5\u4F5C\u7684\uFF1F","slug":"\u4F5C\u7528\u57DF\u94FE\u662F\u600E\u4E48\u5DE5\u4F5C\u7684\uFF1F","link":"#\u4F5C\u7528\u57DF\u94FE\u662F\u600E\u4E48\u5DE5\u4F5C\u7684\uFF1F","children":[]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[]},{"level":2,"title":"\u601D\u8003\u9898","slug":"\u601D\u8003\u9898","link":"#\u601D\u8003\u9898","children":[]}],"relativePath":"JavaScript/\u674E\u5175-\u56FE\u89E3GoogleV8/06\uFF5C\u4F5C\u7528\u57DF\u94FE-V8\u662F\u5982\u4F55\u67E5\u627E\u53D8\u91CF\u7684.md"}'),l={name:"JavaScript/\u674E\u5175-\u56FE\u89E3GoogleV8/06\uFF5C\u4F5C\u7528\u57DF\u94FE-V8\u662F\u5982\u4F55\u67E5\u627E\u53D8\u91CF\u7684.md"},e=p(`<hr><h2 id="title-\u674E\u5175-\u56FE\u89E3googlev8" tabindex="-1">title: \u674E\u5175-\u56FE\u89E3GoogleV8 <a class="header-anchor" href="#title-\u674E\u5175-\u56FE\u89E3googlev8" aria-hidden="true">#</a></h2><p>06\uFF5C\u4F5C\u7528\u57DF\u94FE\uFF1AV8\u662F\u5982\u4F55\u67E5\u627E\u53D8\u91CF\u7684\uFF1F</p><p>\u4F60\u597D\uFF0C\u6211\u662F\u674E\u5175\u3002</p><p>\u5728\u524D\u9762\u6211\u4EEC\u4ECB\u7ECD\u4E86JavaScript\u7684\u7EE7\u627F\u662F\u57FA\u4E8E\u539F\u578B\u94FE\u7684\uFF0C\u539F\u578B\u94FE\u5C06\u4E00\u4E2A\u4E2A\u539F\u578B\u5BF9\u8C61\u4E32\u8D77\u6765\uFF0C\u4ECE\u800C\u5B9E\u73B0\u5BF9\u8C61\u5C5E\u6027\u7684\u67E5\u627E\uFF0C\u4ECA\u5929\u6211\u4EEC\u8981\u804A\u4E00\u4E2A\u548C\u539F\u578B\u94FE\u7C7B\u4F3C\u7684\u8BDD\u9898\uFF0C\u90A3\u5C31\u662F\u4F5C\u7528\u57DF\u94FE\u3002</p><p>\u4F5C\u7528\u57DF\u94FE\u5C31\u662F\u5C06\u4E00\u4E2A\u4E2A\u4F5C\u7528\u57DF\u4E32\u8D77\u6765\uFF0C\u5B9E\u73B0\u53D8\u91CF\u67E5\u627E\u7684\u8DEF\u5F84\u3002\u8BA8\u8BBA\u4F5C\u7528\u57DF\u94FE\uFF0C\u5B9E\u9645\u5C31\u662F\u5728\u8BA8\u8BBA\u6309\u7167\u4EC0\u4E48\u8DEF\u5F84\u67E5\u627E\u53D8\u91CF\u7684\u95EE\u9898\u3002</p><p>\u6211\u4EEC\u77E5\u9053\uFF0C\u4F5C\u7528\u57DF\u5C31\u662F\u5B58\u653E\u53D8\u91CF\u548C\u51FD\u6570\u7684\u5730\u65B9\uFF0C\u5168\u5C40\u73AF\u5883\u6709\u5168\u5C40\u4F5C\u7528\u57DF\uFF0C\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u5B58\u653E\u4E86\u5168\u5C40\u53D8\u91CF\u548C\u5168\u5C40\u51FD\u6570\u3002\u6BCF\u4E2A\u51FD\u6570\u4E5F\u6709\u81EA\u5DF1\u7684\u4F5C\u7528\u57DF\uFF0C\u51FD\u6570\u4F5C\u7528\u57DF\u4E2D\u5B58\u653E\u4E86\u51FD\u6570\u4E2D\u5B9A\u4E49\u7684\u53D8\u91CF\u3002</p><p>\u5F53\u5728\u51FD\u6570\u5185\u90E8\u4F7F\u7528\u4E00\u4E2A\u53D8\u91CF\u7684\u65F6\u5019\uFF0CV8\u4FBF\u4F1A\u53BB\u4F5C\u7528\u57DF\u4E2D\u53BB\u67E5\u627E\u3002\u6211\u4EEC\u901A\u8FC7\u4E00\u6BB5\u5728\u51FD\u6570\u5185\u90E8\u67E5\u627E\u53D8\u91CF\u7684\u4EE3\u7801\u6765\u5177\u4F53\u770B\u4E00\u4E0B\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var name = &#39;\u6781\u5BA2\u65F6\u95F4&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">var type = &#39;global&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">function foo(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var name = &#39;foo&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(name)</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(type)</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">function bar(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var name = &#39;bar&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    var type = &#39;function&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    foo()</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">bar()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5728\u8FD9\u6BB5\u4EE3\u7801\u4E2D\uFF0C\u6211\u4EEC\u5728\u5168\u5C40\u73AF\u5883\u4E2D\u58F0\u660E\u4E86\u53D8\u91CFname\u548Ctype\uFF0C\u540C\u65F6\u8FD8\u5B9A\u4E49\u4E86bar\u51FD\u6570\u548Cfoo\u51FD\u6570\uFF0C\u5728bar\u51FD\u6570\u4E2D\u53C8\u518D\u6B21\u5B9A\u4E49\u4E86\u53D8\u91CFname\u548Ctype\uFF0C\u5728foo\u51FD\u6570\u4E2D\u518D\u6B21\u5B9A\u4E49\u4E86\u53D8\u91CFname\u3002</p><p>\u51FD\u6570\u7684\u8C03\u7528\u5173\u7CFB\u662F\uFF1A\u5728\u5168\u5C40\u73AF\u5883\u4E2D\u8C03\u7528bar\u51FD\u6570\uFF0C\u5728bar\u51FD\u6570\u4E2D\u8C03\u7528foo\u51FD\u6570\uFF0C\u5728foo\u51FD\u6570\u4E2D\u6253\u5370\u51FA\u6765\u53D8\u91CFname\u548Ctype\u7684\u503C\u3002</p><p>\u5F53\u6267\u884C\u5230foo\u51FD\u6570\u65F6\uFF0C\u9996\u5148\u9700\u8981\u6253\u5370\u51FA\u53D8\u91CFname\u7684\u503C\uFF0C\u800C\u6211\u4EEC\u5728\u4E09\u4E2A\u5730\u65B9\u90FD\u5B9A\u4E49\u4E86\u53D8\u91CFname\uFF0C\u90A3\u4E48\u7A76\u7ADF\u5E94\u8BE5\u4F7F\u7528\u54EA\u4E2A\u53D8\u91CF\u5462\uFF1F</p><p>\u5728foo\u51FD\u6570\u4E2D\u4F7F\u7528\u4E86\u53D8\u91CFname\uFF0C\u90A3\u4E48V8\u5C31\u5E94\u8BE5\u5148\u4F7F\u7528foo\u51FD\u6570\u5185\u90E8\u5B9A\u4E49\u7684\u53D8\u91CFname\uFF0C\u6700\u7EC8\u7684\u7ED3\u679C\u786E\u5B9E\u5982\u6B64\uFF0C\u4E5F\u7B26\u5408\u6211\u4EEC\u7684\u76F4\u89C9\u3002</p><p>\u63A5\u4E0B\u6765\uFF0Cfoo\u51FD\u6570\u7EE7\u7EED\u6253\u5370\u53D8\u91CFtype\uFF0C\u4F46\u662F\u5728foo\u51FD\u6570\u5185\u90E8\u5E76\u6CA1\u6709\u5B9A\u4E49\u53D8\u91CFtype\uFF0C\u800C\u662F\u5728\u5168\u5C40\u73AF\u5883\u4E2D\u548C\u8C03\u7528foo\u51FD\u6570\u7684bar\u51FD\u6570\u4E2D\u5206\u522B\u5B9A\u4E49\u4E86\u53D8\u91CFtype\uFF0C\u90A3\u4E48\u8FD9\u65F6\u5019\u7684\u95EE\u9898\u6765\u4E86\uFF0C\u4F60\u89C9\u5F97foo\u51FD\u6570\u4E2D\u6253\u5370\u51FA\u6765\u7684\u53D8\u91CFtype\u662Fbar\u51FD\u6570\u4E2D\u7684\uFF0C\u8FD8\u662F\u5168\u5C40\u73AF\u5883\u4E2D\u7684\u5462\uFF1F</p><h2 id="\u4EC0\u4E48\u662F\u51FD\u6570\u4F5C\u7528\u57DF\u548C\u5168\u5C40\u4F5C\u7528\u57DF\uFF1F" tabindex="-1">\u4EC0\u4E48\u662F\u51FD\u6570\u4F5C\u7528\u57DF\u548C\u5168\u5C40\u4F5C\u7528\u57DF\uFF1F <a class="header-anchor" href="#\u4EC0\u4E48\u662F\u51FD\u6570\u4F5C\u7528\u57DF\u548C\u5168\u5C40\u4F5C\u7528\u57DF\uFF1F" aria-hidden="true">#</a></h2><p>\u8981\u89E3\u91CA\u6E05\u695A\u8FD9\u4E2A\u95EE\u9898\uFF0C\u6211\u4EEC\u9700\u8981\u4ECE\u4F5C\u7528\u57DF\u7684\u5DE5\u4F5C\u539F\u7406\u8BB2\u8D77\u3002</p><p>\u6BCF\u4E2A\u51FD\u6570\u5728\u6267\u884C\u65F6\u90FD\u9700\u8981\u67E5\u627E\u81EA\u5DF1\u7684\u4F5C\u7528\u57DF\uFF0C\u6211\u4EEC\u79F0\u4E3A\u51FD\u6570\u4F5C\u7528\u57DF\uFF0C\u5728\u6267\u884C\u9636\u6BB5\uFF0C\u5728\u6267\u884C\u4E00\u4E2A\u51FD\u6570\u65F6\uFF0C\u5F53\u8BE5\u51FD\u6570\u9700\u8981\u4F7F\u7528\u67D0\u4E2A\u53D8\u91CF\u6216\u8005\u8C03\u7528\u4E86\u67D0\u4E2A\u51FD\u6570\u65F6\uFF0C\u4FBF\u4F1A\u4F18\u5148\u5728\u8BE5\u51FD\u6570\u4F5C\u7528\u57DF\u4E2D\u67E5\u627E\u76F8\u5173\u5185\u5BB9\u3002</p><p>\u6211\u4EEC\u518D\u6765\u770B\u4E00\u6BB5\u4EE3\u7801\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var x = 4</span></span>
<span class="line"><span style="color:#A6ACCD;">var test</span></span>
<span class="line"><span style="color:#A6ACCD;">function test_scope() {</span></span>
<span class="line"><span style="color:#A6ACCD;">    var name = &#39;foo&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(name)</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(type)</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(test)</span></span>
<span class="line"><span style="color:#A6ACCD;">    var type = &#39;function&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    test = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(x)</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">test_scope()  </span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5728\u4E0A\u9762\u7684\u4EE3\u7801\u4E2D\uFF0C\u6211\u4EEC\u5B9A\u4E49\u4E86\u4E00\u4E2Atest_scope\u51FD\u6570\uFF0C\u90A3\u4E48\u5728V8\u6267\u884Ctest_scope\u51FD\u6570\u7684\u65F6\u5019\uFF0C\u5728\u7F16\u8BD1\u9636\u6BB5\u4F1A\u4E3Atest_scope\u51FD\u6570\u521B\u5EFA\u4E00\u4E2A\u4F5C\u7528\u57DF\uFF0C\u5728test_scope\u51FD\u6570\u4E2D\u5B9A\u4E49\u7684\u53D8\u91CF\u548C\u58F0\u660E\u7684\u51FD\u6570\u90FD\u4F1A\u4E22\u5230\u8BE5\u4F5C\u7528\u57DF\u4E2D\uFF0C\u56E0\u4E3A\u6211\u4EEC\u5728test_scope\u51FD\u6570\u4E2D\u5B9A\u4E86\u4E09\u4E2A\u53D8\u91CF\uFF0C\u90A3\u4E48\u5E38\u89C1\u7684\u4F5C\u7528\u57DF\u5C31\u5305\u542B\u6709\u8FD9\u4E09\u4E2A\u53D8\u91CF\u3002</p><p>\u4F60\u53EF\u4EE5\u901A\u8FC7Chrome\u7684\u63A7\u5236\u53F0\u6765\u76F4\u89C2\u611F\u53D7\u4E0Btest_scope\u51FD\u6570\u7684\u4F5C\u7528\u57DF\uFF0C\u5148\u6253\u5F00\u5305\u542B\u8FD9\u6BB5\u4EE3\u7801\u7684\u9875\u9762\uFF0C\u7136\u540E\u6253\u5F00\u5F00\u53D1\u8005\u5DE5\u5177\uFF0C\u63A5\u7740\u5728test_scope\u51FD\u6570\u4E2D\u7684\u7B2C\u4E8C\u6BB5\u4EE3\u7801\u52A0\u4E0A\u65AD\u70B9\uFF0C\u7136\u540E\u5237\u65B0\u8BE5\u9875\u9762\u3002\u5F53\u6267\u884C\u5230\u8BE5\u65AD\u70B9\u65F6\uFF0CV8\u4F1A\u6682\u505C\u6574\u4E2A\u6267\u884C\u6D41\u7A0B\uFF0C\u8FD9\u65F6\u5019\u6211\u4EEC\u5C31\u53EF\u4EE5\u901A\u8FC7\u53F3\u8FB9\u7684\u533A\u57DF\u9762\u677F\u6765\u67E5\u770B\u5F53\u524D\u51FD\u6570\u7684\u6267\u884C\u72B6\u6001\u3002</p><p><img src="https://static001.geekbang.org/resource/image/e3/5a/e346a7664d91a80e5d694d20ea21275a.png" alt="" title="\u89C2\u5BDF\u4F5C\u7528\u57DF"></p><p>\u4F60\u53EF\u4EE5\u53C2\u8003\u56FE\u4E2D\u53F3\u4FA7\u7684Scope\u9879\uFF0C\u7136\u540E\u70B9\u51FB\u5C55\u5F00\u8BE5\u9879\uFF0C\u8FD9\u4E2ALocal\u5C31\u662F\u5F53\u524D\u51FD\u6570test_scope\u7684\u4F5C\u7528\u57DF\u3002\u5728test_scope\u51FD\u6570\u4E2D\u5B9A\u4E49\u7684\u53D8\u91CF\u90FD\u5305\u542B\u5230\u4E86Local\u4E2D\uFF0C\u5982\u53D8\u91CFname\u3001type\uFF0C\u53E6\u5916\u7CFB\u7EDF\u8FD8\u4E3A\u6211\u4EEC\u6DFB\u52A0\u4E86\u53E6\u5916\u4E00\u4E2A\u9690\u85CF\u53D8\u91CFthis\uFF0CV8\u8FD8\u4F1A\u9ED8\u8BA4\u5C06\u9690\u85CF\u53D8\u91CFthis\u5B58\u653E\u5230\u4F5C\u7528\u57DF\u4E2D\u3002</p><p>\u53E6\u5916\u4F60\u8FD8\u9700\u8981\u6CE8\u610F\u4E0B\uFF0C\u7B2C\u4E00\u4E2Atest1\uFF0C\u6211\u5E76\u6CA1\u6709\u91C7\u7528var\u7B49\u5173\u952E\u5B57\u6765\u58F0\u660E\uFF0C\u6240\u4EE5test1\u5E76\u4E0D\u4F1A\u51FA\u73B0\u5728test_scope\u51FD\u6570\u7684\u4F5C\u7528\u57DF\u4E2D\uFF0C\u800C\u662F\u5C5E\u4E8Ethis\u6240\u6307\u5411\u7684\u5BF9\u8C61\u3002\uFF08this\u7684\u5DE5\u4F5C\u673A\u5236\u4E0D\u662F\u672C\u6587\u8BA8\u8BBA\u7684\u91CD\u70B9\uFF0C\u4E0D\u5C55\u5F00\u4ECB\u7ECD\u3002\u5982\u679C\u4F60\u611F\u5174\u8DA3\uFF0C\u53EF\u4EE5\u5728\u300A\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5\u300B\u4E13\u680F\u4E2D\u300A<a href="https://time.geekbang.org/column/article/128427" target="_blank" rel="noreferrer">11 | this\uFF1A\u4ECEJavaScript\u6267\u884C\u4E0A\u4E0B\u6587\u7684\u89C6\u89D2\u8BB2\u6E05\u695Athis</a>\u300B\u8FD9\u4E00\u8BB2\u67E5\u770B\u3002\uFF09</p><p>\u90A3\u4E48\u53E6\u4E00\u4E2A\u95EE\u9898\u6765\u4E86\uFF0C\u6211\u5728test_scope\u51FD\u6570\u4F7F\u7528\u4E86\u53D8\u91CFx\uFF0C\u4F46\u662F\u5728test_scope\u51FD\u6570\u7684\u4F5C\u7528\u57DF\u4E2D\uFF0C\u5E76\u6CA1\u6709\u5B9A\u4E49\u53D8\u91CFx\uFF0C\u90A3\u4E48V8\u5E94\u8BE5\u5982\u4F55\u83B7\u53D6\u53D8\u91CFx\uFF1F</p><p>\u5982\u679C\u5728\u5F53\u524D\u51FD\u6570\u4F5C\u7528\u57DF\u4E2D\u6CA1\u6709\u67E5\u627E\u5230\u53D8\u91CF\uFF0C\u90A3\u4E48V8\u4F1A\u53BB\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u53BB\u67E5\u627E\uFF0C\u8FD9\u4E2A\u67E5\u627E\u7684\u7EBF\u8DEF\u5C31\u79F0\u4E3A\u4F5C\u7528\u57DF\u94FE\u3002</p><p>\u5168\u5C40\u4F5C\u7528\u57DF\u548C\u51FD\u6570\u4F5C\u7528\u57DF\u7C7B\u4F3C\uFF0C\u4E5F\u662F\u5B58\u653E\u53D8\u91CF\u548C\u51FD\u6570\u7684\u5730\u65B9\uFF0C\u4F46\u662F\u5B83\u4EEC\u8FD8\u662F\u6709\u70B9\u4E0D\u4E00\u6837\uFF1A <strong>\u5168\u5C40\u4F5C\u7528\u57DF\u662F\u5728V8\u542F\u52A8\u8FC7\u7A0B\u4E2D\u5C31\u521B\u5EFA\u4E86\uFF0C\u4E14\u4E00\u76F4\u4FDD\u5B58\u5728\u5185\u5B58\u4E2D\u4E0D\u4F1A\u88AB\u9500\u6BC1\u7684\uFF0C\u76F4\u81F3V8\u9000\u51FA\u3002</strong> <strong>\u800C\u51FD\u6570\u4F5C\u7528\u57DF\u662F\u5728\u6267\u884C\u8BE5\u51FD\u6570\u65F6\u521B\u5EFA\u7684\uFF0C\u5F53\u51FD\u6570\u6267\u884C\u7ED3\u675F\u4E4B\u540E\uFF0C\u51FD\u6570\u4F5C\u7528\u57DF\u5C31\u968F\u4E4B\u88AB\u9500\u6BC1\u6389\u4E86</strong>\u3002</p><p>\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u5305\u542B\u4E86\u5F88\u591A\u5168\u5C40\u53D8\u91CF\uFF0C\u6BD4\u5982\u5168\u5C40\u7684this\u503C\uFF0C\u5982\u679C\u662F\u6D4F\u89C8\u5668\uFF0C\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u8FD8\u6709window\u3001document\u3001opener\u7B49\u975E\u5E38\u591A\u7684\u65B9\u6CD5\u548C\u5BF9\u8C61\uFF0C\u5982\u679C\u662Fnode\u73AF\u5883\uFF0C\u90A3\u4E48\u4F1A\u6709Global\u3001File\u7B49\u5185\u5BB9\u3002</p><p>V8\u542F\u52A8\u4E4B\u540E\u5C31\u8FDB\u5165\u6B63\u5E38\u7684\u6D88\u606F\u5FAA\u73AF\u72B6\u6001\uFF0C\u8FD9\u65F6\u5019\u5C31\u53EF\u4EE5\u6267\u884C\u4EE3\u7801\u4E86\uFF0C\u6BD4\u5982\u6267\u884C\u5230\u4E0A\u9762\u90A3\u6BB5\u811A\u672C\u65F6\uFF0CV8\u4F1A\u5148\u89E3\u6790\u9876\u5C42(Top Level)\u4EE3\u7801\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\uFF0C\u5728\u9876\u5C42\u4EE3\u7801\u4E2D\u5B9A\u4E49\u4E86\u53D8\u91CFx\uFF0C\u8FD9\u65F6\u5019V8\u5C31\u4F1A\u5C06\u53D8\u91CFx\u6DFB\u52A0\u5230\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u3002</p><h2 id="\u4F5C\u7528\u57DF\u94FE\u662F\u600E\u4E48\u5DE5\u4F5C\u7684\uFF1F" tabindex="-1">\u4F5C\u7528\u57DF\u94FE\u662F\u600E\u4E48\u5DE5\u4F5C\u7684\uFF1F <a class="header-anchor" href="#\u4F5C\u7528\u57DF\u94FE\u662F\u600E\u4E48\u5DE5\u4F5C\u7684\uFF1F" aria-hidden="true">#</a></h2><p>\u7406\u89E3\u4E86\u4F5C\u7528\u57DF\u548C\u4F5C\u7528\u57DF\u94FE\uFF0C\u6211\u4EEC\u518D\u56DE\u8FC7\u5934\u6765\u770B\u6587\u7AE0\u5F00\u5934\u7684\u90A3\u9053\u601D\u8003\u9898\uFF1A \u201Cfoo\u51FD\u6570\u4E2D\u6253\u5370\u51FA\u6765\u7684\u53D8\u91CFtype\u662Fbar\u51FD\u6570\u4E2D\u7684\u5462\uFF0C\u8FD8\u662F\u5168\u5C40\u73AF\u5883\u4E2D\u7684\u5462?\u201D\u6211\u628A\u8FD9\u6BB5\u4EE3\u7801\u590D\u5236\u5230\u4E0B\u9762\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var name = &#39;\u6781\u5BA2\u65F6\u95F4&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">var type = &#39;global&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">function foo(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var name = &#39;foo&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(name)</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(type)</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">function bar(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var name = &#39;bar&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    var type = &#39;function&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    foo()</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">bar()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u73B0\u5728\uFF0C\u6211\u4EEC\u7ED3\u5408V8\u6267\u884C\u8FD9\u6BB5\u4EE3\u7801\u7684\u6D41\u7A0B\u6765\u5177\u4F53\u5206\u6790\u4E0B\u3002\u9996\u5148\u5F53V8\u542F\u52A8\u65F6\uFF0C\u4F1A\u521B\u5EFA\u5168\u5C40\u4F5C\u7528\u57DF\uFF0C\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u5305\u62EC\u4E86this\u3001window\u7B49\u53D8\u91CF\uFF0C\u8FD8\u6709\u4E00\u4E9B\u5168\u5C40\u7684Web API\u63A5\u53E3\uFF0C\u521B\u5EFA\u7684\u4F5C\u7528\u57DF\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/58/77/589622b2f517ce06487d3edbe28cf277.jpg" alt="" title="\u5168\u5C40\u4F5C\u7528\u57DF"></p><p>V8\u542F\u52A8\u4E4B\u540E\uFF0C\u6D88\u606F\u5FAA\u73AF\u7CFB\u7EDF\u4FBF\u5F00\u59CB\u5DE5\u4F5C\u4E86\uFF0C\u8FD9\u65F6\u5019\uFF0C\u6211\u8F93\u5165\u4E86\u8FD9\u6BB5\u4EE3\u7801\uFF0C\u8BA9\u5176\u6267\u884C\u3002</p><p>V8\u4F1A\u5148\u7F16\u8BD1\u9876\u5C42\u4EE3\u7801\uFF0C\u5728\u7F16\u8BD1\u8FC7\u7A0B\u4E2D\u4F1A\u5C06\u9876\u5C42\u5B9A\u4E49\u7684\u53D8\u91CF\u548C\u58F0\u660E\u7684\u51FD\u6570\u90FD\u6DFB\u52A0\u5230\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\uFF0C\u6700\u7EC8\u7684\u5168\u5C40\u4F5C\u7528\u57DF\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/53/e8/532151cb2713a229550af215962deee8.jpg" alt="" title="\u5168\u5C40\u4F5C\u7528\u57DF"></p><p>\u5168\u5C40\u4F5C\u7528\u57DF\u521B\u5EFA\u5B8C\u6210\u4E4B\u540E\uFF0CV8\u4FBF\u8FDB\u5165\u4E86\u6267\u884C\u72B6\u6001\u3002\u524D\u9762\u6211\u4EEC\u4ECB\u7ECD\u4E86\u53D8\u91CF\u63D0\u5347\uFF0C\u56E0\u4E3A\u53D8\u91CF\u63D0\u5347\u7684\u539F\u56E0\uFF0C\u4F60\u53EF\u4EE5\u628A\u4E0A\u9762\u8FD9\u6BB5\u4EE3\u7801\u5206\u89E3\u4E3A\u5982\u4E0B\u4E24\u4E2A\u90E8\u5206\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">//======\u89E3\u6790\u9636\u6BB5--\u5B9E\u73B0\u53D8\u91CF\u63D0\u5347=======</span></span>
<span class="line"><span style="color:#A6ACCD;">var name = undefined</span></span>
<span class="line"><span style="color:#A6ACCD;">var type = undefined</span></span>
<span class="line"><span style="color:#A6ACCD;">function foo(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var name = &#39;foo&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(name)</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(type)</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">function bar(){</span></span>
<span class="line"><span style="color:#A6ACCD;">    var name = &#39;bar&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    var type = &#39;function&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    foo()</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">//====\u6267\u884C\u9636\u6BB5========</span></span>
<span class="line"><span style="color:#A6ACCD;">name = &#39;\u6781\u5BA2\u65F6\u95F4&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">type = &#39;global&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">bar()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u7B2C\u4E00\u90E8\u5206\u662F\u5728\u7F16\u8BD1\u8FC7\u7A0B\u4E2D\u5B8C\u6210\u7684\uFF0C\u6B64\u65F6\u5168\u5C40\u4F5C\u7528\u4E2D\u4E24\u4E2A\u53D8\u91CF\u7684\u503C\u4F9D\u7136\u662Fundefined\uFF0C\u7136\u540E\u8FDB\u5165\u6267\u884C\u9636\u6BB5\uFF1B\u7B2C\u4E8C\u90E8\u4EE3\u7801\u5C31\u662F\u6267\u884C\u65F6\u7684\u987A\u5E8F\uFF0C\u9996\u5148\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u7684\u4E24\u4E2A\u53D8\u91CF\u8D4B\u503C\u201C\u6781\u5BA2\u65F6\u95F4\u201D\u548C\u201Cglobal\u201D\uFF0C\u7136\u540E\u5C31\u5F00\u59CB\u6267\u884C\u51FD\u6570bar\u7684\u8C03\u7528\u4E86\u3002</p><p>\u5F53V8\u6267\u884Cbar\u51FD\u6570\u7684\u65F6\u5019\uFF0C\u540C\u6837\u9700\u8981\u7ECF\u5386\u4E24\u4E2A\u9636\u6BB5\uFF1A\u7F16\u8BD1\u548C\u6267\u884C\u3002\u5728\u7F16\u8BD1\u9636\u6BB5\uFF0CV8\u4F1A\u4E3Abar\u51FD\u6570\u521B\u5EFA\u51FD\u6570\u4F5C\u7528\u57DF\uFF0C\u6700\u7EC8\u6548\u679C\u5982\u4E0B\u6240\u793A\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/e2/ce/e2958bebf2ef52023c5e514259ae2cce.jpg" alt="" title="bar\u51FD\u6570\u4F5C\u7528\u57DF"></p><p>\u7136\u540E\u8FDB\u5165\u4E86bar\u51FD\u6570\u7684\u6267\u884C\u9636\u6BB5\u3002\u5728bar\u51FD\u6570\u4E2D\uFF0C\u53EA\u662F\u7B80\u5355\u5730\u8C03\u7528foo\u51FD\u6570\uFF0C\u56E0\u6B64V8\u53C8\u5F00\u59CB\u6267\u884Cfoo\u51FD\u6570\u4E86\u3002</p><p>\u540C\u6837\uFF0C\u5728\u7F16\u8BD1foo\u51FD\u6570\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4F1A\u521B\u5EFAfoo\u51FD\u6570\u7684\u4F5C\u7528\u57DF\uFF0C\u6700\u7EC8\u521B\u5EFA\u6548\u679C\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/9d/d5/9dc20e0f38d04ae96296787c7190cad5.jpg" alt="" title="foo\u51FD\u6570\u4F5C\u7528\u57DF"></p><p>\u597D\u4E86\uFF0C\u8FD9\u65F6\u5019\u6211\u4EEC\u5C31\u6709\u4E86\u4E09\u4E2A\u4F5C\u7528\u57DF\u4E86\uFF0C\u5206\u522B\u662F\u5168\u5C40\u4F5C\u7528\u57DF\u3001bar\u7684\u51FD\u6570\u4F5C\u7528\u57DF\u3001foo\u7684\u51FD\u6570\u4F5C\u7528\u57DF\u3002</p><p>\u73B0\u5728\u6211\u4EEC\u5C31\u53EF\u4EE5\u5C06\u521A\u624D\u63D0\u5230\u7684\u95EE\u9898\u8F6C\u6362\u4E3A\u4F5C\u7528\u57DF\u94FE\u7684\u95EE\u9898\u4E86\uFF1Afoo\u51FD\u6570\u67E5\u627E\u53D8\u91CF\u7684\u8DEF\u5F84\u5230\u5E95\u662F\u4EC0\u4E48\uFF1F</p><ul><li>\u6CBF\u7740foo\u51FD\u6570\u4F5C\u7528\u57DF\u2013&gt;bar\u51FD\u6570\u4F5C\u7528\u57DF\u2013&gt;\u5168\u5C40\u4F5C\u7528\u57DF;</li><li>\u8FD8\u662F\uFF0C\u6CBF\u7740foo\u51FD\u6570\u4F5C\u7528\u57DF\u2014&gt;\u5168\u5C40\u4F5C\u7528\u57DF\uFF1F</li></ul><p>\u56E0\u4E3AJavaScript\u662F\u57FA\u4E8E\u8BCD\u6CD5\u4F5C\u7528\u57DF\u7684\uFF0C\u8BCD\u6CD5\u4F5C\u7528\u57DF\u5C31\u662F\u6307\uFF0C\u67E5\u627E\u4F5C\u7528\u57DF\u7684\u987A\u5E8F\u662F\u6309\u7167\u51FD\u6570\u5B9A\u4E49\u65F6\u7684\u4F4D\u7F6E\u6765\u51B3\u5B9A\u7684\u3002bar\u548Cfoo\u51FD\u6570\u7684\u5916\u90E8\u4EE3\u7801\u90FD\u662F\u5168\u5C40\u4EE3\u7801\uFF0C\u6240\u4EE5\u65E0\u8BBA\u4F60\u662F\u5728bar\u51FD\u6570\u4E2D\u67E5\u627E\u53D8\u91CF\uFF0C\u8FD8\u662F\u5728foo\u51FD\u6570\u4E2D\u67E5\u627E\u53D8\u91CF\uFF0C\u5176\u67E5\u627E\u987A\u5E8F\u90FD\u662F\u6309\u7167\u5F53\u524D\u51FD\u6570\u4F5C\u7528\u57DF\u2013&gt;\u5168\u5C40\u4F5C\u7528\u57DF\u8FD9\u4E2A\u8DEF\u5F84\u6765\u7684\u3002</p><p>\u7531\u4E8E\u6211\u4EEC\u4EE3\u7801\u4E2D\u7684foo\u51FD\u6570\u548Cbar\u51FD\u6570\u90FD\u662F\u5728\u5168\u5C40\u4E0B\u9762\u5B9A\u4E49\u7684\uFF0C\u6240\u4EE5\u5728foo\u51FD\u6570\u4E2D\u4F7F\u7528\u4E86type\uFF0C\u6700\u7EC8\u6253\u5370\u51FA\u6765\u7684\u503C\u5C31\u662F\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u7684type\u3002</p><p>\u4F60\u53EF\u4EE5\u53C2\u8003\u4E0B\u9762\u8FD9\u5F20\u56FE\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/82/8c/82c84c81f8c94915d4965ce38d285e8c.jpg" alt=""></p><p>\u53E6\u5916\uFF0C\u6211\u518D\u5C55\u5F00\u8BF4\u4E00\u4E9B\u3002\u56E0\u4E3A\u8BCD\u6CD5\u4F5C\u7528\u57DF\u662F\u6839\u636E\u51FD\u6570\u5728\u4EE3\u7801\u4E2D\u7684\u4F4D\u7F6E\u6765\u786E\u5B9A\u7684\uFF0C\u4F5C\u7528\u57DF\u662F\u5728\u58F0\u660E\u51FD\u6570\u65F6\u5C31\u786E\u5B9A\u597D\u7684\u4E86\uFF0C\u6240\u4EE5\u6211\u4EEC\u4E5F\u5C06\u8BCD\u6CD5\u4F5C\u7528\u57DF\u79F0\u4E3A\u9759\u6001\u4F5C\u7528\u57DF\u3002</p><p>\u548C\u9759\u6001\u4F5C\u7528\u57DF\u76F8\u5BF9\u7684\u662F\u52A8\u6001\u4F5C\u7528\u57DF\uFF0C\u52A8\u6001\u4F5C\u7528\u57DF\u5E76\u4E0D\u5173\u5FC3\u51FD\u6570\u548C\u4F5C\u7528\u57DF\u662F\u5982\u4F55\u58F0\u660E\u4EE5\u53CA\u5728\u4F55\u5904\u58F0\u660E\u7684\uFF0C\u53EA\u5173\u5FC3\u5B83\u4EEC\u4ECE<strong>\u4F55\u5904\u8C03\u7528</strong>\u3002\u6362\u53E5\u8BDD\u8BF4\uFF0C\u4F5C\u7528\u57DF\u94FE\u662F\u57FA\u4E8E\u8C03\u7528\u6808\u7684\uFF0C\u800C\u4E0D\u662F\u57FA\u4E8E\u51FD\u6570\u5B9A\u4E49\u7684\u4F4D\u7F6E\u7684\u3002\uFF08\u52A8\u6001\u4F5C\u7528\u57DF\u4E0D\u662F\u672C\u6587\u8BA8\u8BBA\u7684\u91CD\u70B9\uFF0C\u5982\u679C\u4F60\u611F\u5174\u8DA3\uFF0C\u53EF\u4EE5\u53C2\u8003\u300A\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5\u300B\u4E13\u680F\u4E2D\u7684\u300A<a href="https://time.geekbang.org/column/article/127495" target="_blank" rel="noreferrer">10 | \u4F5C\u7528\u57DF\u94FE\u548C\u95ED\u5305 \uFF1A\u4EE3\u7801\u4E2D\u51FA\u73B0\u76F8\u540C\u7684\u53D8\u91CF\uFF0CJavaScript\u5F15\u64CE\u662F\u5982\u4F55\u9009\u62E9\u7684\uFF1F</a>\u300B\u8FD9\u4E00\u8282\u3002\uFF09</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u4ECA\u5929\uFF0C\u6211\u4EEC\u4E3B\u8981\u89E3\u91CA\u4E86\u4E00\u4E2A\u95EE\u9898\uFF0C\u90A3\u5C31\u662F\u5728\u4E00\u4E2A\u51FD\u6570\u4E2D\uFF0C\u5982\u679C\u4F7F\u7528\u4E86\u4E00\u4E2A\u53D8\u91CF\uFF0C\u6216\u8005\u8C03\u7528\u4E86\u53E6\u5916\u4E00\u4E2A\u51FD\u6570\uFF0CV8\u5C06\u4F1A\u600E\u4E48\u53BB\u67E5\u627E\u8BE5\u53D8\u91CF\u6216\u8005\u51FD\u6570\u3002</p><p>\u4E3A\u4E86\u89E3\u91CA\u6E05\u695A\u8FD9\u4E2A\u95EE\u9898\uFF0C\u6211\u4EEC\u5F15\u5165\u4E86\u4F5C\u7528\u57DF\u7684\u6982\u5FF5\u3002\u4F5C\u7528\u57DF\u5C31\u662F\u7528\u6765\u5B58\u653E\u53D8\u91CF\u548C\u51FD\u6570\u7684\u5730\u65B9\uFF0C\u5168\u5C40\u4F5C\u7528\u57DF\u4E2D\u5B58\u653E\u4E86\u5168\u5C40\u73AF\u5883\u4E2D\u58F0\u660E\u7684\u53D8\u91CF\u548C\u51FD\u6570\uFF0C\u51FD\u6570\u4F5C\u7528\u57DF\u4E2D\u5B58\u653E\u4E86\u51FD\u6570\u4E2D\u58F0\u660E\u7684\u53D8\u91CF\u548C\u51FD\u6570\u3002\u5F53\u5728\u67D0\u4E2A\u51FD\u6570\u4E2D\u4F7F\u7528\u67D0\u4E2A\u53D8\u91CF\u65F6\uFF0CV8\u5C31\u4F1A\u53BB\u8FD9\u4E9B\u4F5C\u7528\u57DF\u4E2D\u67E5\u627E\u76F8\u5173\u53D8\u91CF\u3002\u6CBF\u7740\u8FD9\u4E9B\u4F5C\u7528\u57DF\u67E5\u627E\u7684\u8DEF\u5F84\uFF0C\u6211\u4EEC\u5C31\u79F0\u4E3A\u4F5C\u7528\u57DF\u94FE\u3002</p><p>\u8981\u4E86\u89E3\u67E5\u627E\u8DEF\u5F84\uFF0C\u6211\u4EEC\u9700\u8981\u660E\u767D\u8BCD\u6CD5\u4F5C\u7528\u57DF\uFF0C\u8BCD\u6CD5\u4F5C\u7528\u57DF\u662F\u6309\u7167\u4EE3\u7801\u5B9A\u4E49\u65F6\u7684\u4F4D\u7F6E\u51B3\u5B9A\u7684\uFF0C\u800CJavaScript\u6240\u91C7\u7528\u7684\u4F5C\u7528\u57DF\u673A\u5236\u5C31\u662F\u8BCD\u6CD5\u4F5C\u7528\u57DF\uFF0C\u6240\u4EE5\u4F5C\u7528\u57DF\u94FE\u7684\u8DEF\u5F84\u5C31\u662F\u6309\u7167\u8BCD\u6CD5\u4F5C\u7528\u57DF\u6765\u5B9E\u73B0\u7684\u3002</p><h2 id="\u601D\u8003\u9898" tabindex="-1">\u601D\u8003\u9898 <a class="header-anchor" href="#\u601D\u8003\u9898" aria-hidden="true">#</a></h2><p>\u6211\u5C06\u6587\u7AE0\u5F00\u5934\u90A3\u6BB5\u4EE3\u7801\u7A0D\u5FAE\u8C03\u6574\u4E86\u4E0B\uFF0Cfoo\u51FD\u6570\u5E76\u4E0D\u662F\u5728\u5168\u5C40\u73AF\u5883\u4E2D\u58F0\u660E\u7684\uFF0C\u800C\u662F\u5728bar\u51FD\u6570\u4E2D\u58F0\u660E\u7684\uFF0C\u6539\u9020\u540E\u7684\u4EE3\u7801\u5982\u4E0B\u6240\u793A\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var name = &#39;\u6781\u5BA2\u65F6\u95F4&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">var type = &#39;global&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">function bar() {</span></span>
<span class="line"><span style="color:#A6ACCD;">    var type = &#39;function&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    function foo() {</span></span>
<span class="line"><span style="color:#A6ACCD;">        console.log(type)</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    foo()</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">bar()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u90A3\u4E48\u6267\u884C\u8FD9\u6BB5\u4EE3\u7801\u4E4B\u540E\uFF0C\u6253\u5370\u51FA\u6765\u7684\u5185\u5BB9\u662F\u4EC0\u4E48\uFF1F\u6B22\u8FCE\u4F60\u5728\u7559\u8A00\u533A\u4E0E\u6211\u5206\u4EAB\u8BA8\u8BBA\u3002</p><p>\u611F\u8C22\u4F60\u7684\u9605\u8BFB\uFF0C\u5982\u679C\u4F60\u89C9\u5F97\u8FD9\u4E00\u8BB2\u7684\u5185\u5BB9\u5BF9\u4F60\u6709\u6240\u542F\u53D1\uFF0C\u4E5F\u6B22\u8FCE\u628A\u5B83\u5206\u4EAB\u7ED9\u4F60\u7684\u670B\u53CB\u3002</p>`,63),o=[e];function t(c,r,i,A,C,y){return n(),a("div",null,o)}const D=s(l,[["render",t]]);export{d as __pageData,D as default};
