import{_ as s,c as n,o as a,d as p}from"./app.e5528619.js";const d=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u674E\u5175-\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5","slug":"title-\u674E\u5175-\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5","link":"#title-\u674E\u5175-\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5","children":[]},{"level":2,"title":"\u963B\u788D\u524D\u7AEF\u7EC4\u4EF6\u5316\u7684\u56E0\u7D20","slug":"\u963B\u788D\u524D\u7AEF\u7EC4\u4EF6\u5316\u7684\u56E0\u7D20","link":"#\u963B\u788D\u524D\u7AEF\u7EC4\u4EF6\u5316\u7684\u56E0\u7D20","children":[]},{"level":2,"title":"WebComponent\u7EC4\u4EF6\u5316\u5F00\u53D1","slug":"webcomponent\u7EC4\u4EF6\u5316\u5F00\u53D1","link":"#webcomponent\u7EC4\u4EF6\u5316\u5F00\u53D1","children":[]},{"level":2,"title":"\u6D4F\u89C8\u5668\u5982\u4F55\u5B9E\u73B0\u5F71\u5B50DOM","slug":"\u6D4F\u89C8\u5668\u5982\u4F55\u5B9E\u73B0\u5F71\u5B50dom","link":"#\u6D4F\u89C8\u5668\u5982\u4F55\u5B9E\u73B0\u5F71\u5B50dom","children":[]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[]},{"level":2,"title":"\u601D\u8003\u65F6\u95F4","slug":"\u601D\u8003\u65F6\u95F4","link":"#\u601D\u8003\u65F6\u95F4","children":[]}],"relativePath":"JavaScript/\u674E\u5175-\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5/28-WebComponent-\u50CF\u642D\u79EF\u6728\u4E00\u6837\u6784\u5EFAWeb\u5E94\u7528.md"}'),l={name:"JavaScript/\u674E\u5175-\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5/28-WebComponent-\u50CF\u642D\u79EF\u6728\u4E00\u6837\u6784\u5EFAWeb\u5E94\u7528.md"},e=p(`<hr><h2 id="title-\u674E\u5175-\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5" tabindex="-1">title: \u674E\u5175-\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5 <a class="header-anchor" href="#title-\u674E\u5175-\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406\u4E0E\u5B9E\u8DF5" aria-hidden="true">#</a></h2><p>28 | WebComponent\uFF1A\u50CF\u642D\u79EF\u6728\u4E00\u6837\u6784\u5EFAWeb\u5E94\u7528</p><p>\u5728<a href="https://time.geekbang.org/column/article/144983" target="_blank" rel="noreferrer">\u4E0A\u4E00\u7BC7\u6587\u7AE0</a>\u4E2D\u6211\u4EEC\u4ECE\u6280\u672F\u6F14\u53D8\u7684\u89D2\u5EA6\u4ECB\u7ECD\u4E86PWA\uFF0C\u8FD9\u662F\u4E00\u5957\u96C6\u5408\u4E86\u591A\u79CD\u6280\u672F\u7684\u7406\u5FF5\uFF0C\u8BA9\u6D4F\u89C8\u5668\u6E10\u8FDB\u5F0F\u9002\u5E94\u8BBE\u5907\u7AEF\u3002\u4ECA\u5929\u6211\u4EEC\u8981\u7AD9\u5728\u5F00\u53D1\u8005\u548C\u9879\u76EE\u89D2\u5EA6\u6765\u804A\u804AWebComponent\uFF0C\u540C\u6837\u5B83\u4E5F\u662F\u4E00\u5957\u6280\u672F\u7684\u7EC4\u5408\uFF0C\u80FD\u63D0\u4F9B\u7ED9\u5F00\u53D1\u8005\u7EC4\u4EF6\u5316\u5F00\u53D1\u7684\u80FD\u529B\u3002</p><p>\u90A3\u4EC0\u4E48\u662F\u7EC4\u4EF6\u5316\u5462\uFF1F</p><p>\u5176\u5B9E\u7EC4\u4EF6\u5316\u5E76\u6CA1\u6709\u4E00\u4E2A\u660E\u786E\u7684\u5B9A\u4E49\uFF0C\u4E0D\u8FC7\u8FD9\u91CC\u6211\u4EEC\u53EF\u4EE5\u4F7F\u752810\u4E2A\u5B57\u6765\u5F62\u5BB9\u4EC0\u4E48\u662F\u7EC4\u4EF6\u5316\uFF0C\u90A3\u5C31\u662F\uFF1A<strong>\u5BF9\u5185\u9AD8\u5185\u805A\uFF0C\u5BF9\u5916\u4F4E\u8026\u5408</strong>\u3002\u5BF9\u5185\u5404\u4E2A\u5143\u7D20\u5F7C\u6B64\u7D27\u5BC6\u7ED3\u5408\u3001\u76F8\u4E92\u4F9D\u8D56\uFF0C\u5BF9\u5916\u548C\u5176\u4ED6\u7EC4\u4EF6\u7684\u8054\u7CFB\u6700\u5C11\u4E14\u63A5\u53E3\u7B80\u5355\u3002</p><p>\u53EF\u4EE5\u8BF4\uFF0C\u7A0B\u5E8F\u5458\u5BF9\u7EC4\u4EF6\u5316\u5F00\u53D1\u6709\u7740\u5929\u751F\u7684\u9700\u6C42\uFF0C\u56E0\u4E3A\u4E00\u4E2A\u7A0D\u5FAE\u590D\u6742\u70B9\u7684\u9879\u76EE\uFF0C\u5C31\u6D89\u53CA\u5230\u591A\u4EBA\u534F\u4F5C\u5F00\u53D1\u7684\u95EE\u9898\uFF0C\u6BCF\u4E2A\u4EBA\u8D1F\u8D23\u7684\u7EC4\u4EF6\u9700\u8981\u5C3D\u53EF\u80FD\u72EC\u7ACB\u5B8C\u6210\u81EA\u5DF1\u7684\u529F\u80FD\uFF0C\u5176\u7EC4\u4EF6\u7684\u5185\u90E8\u72B6\u6001\u4E0D\u80FD\u5F71\u54CD\u5230\u522B\u4EBA\u7684\u7EC4\u4EF6\uFF0C\u5728\u9700\u8981\u548C\u5176\u4ED6\u7EC4\u4EF6\u4EA4\u4E92\u7684\u5730\u65B9\u5F97\u63D0\u524D\u534F\u5546\u597D\u63A5\u53E3\u3002\u901A\u8FC7\u7EC4\u4EF6\u5316\u53EF\u4EE5\u964D\u4F4E\u6574\u4E2A\u7CFB\u7EDF\u7684\u8026\u5408\u5EA6\uFF0C\u540C\u65F6\u4E5F\u964D\u4F4E\u7A0B\u5E8F\u5458\u4E4B\u95F4\u6C9F\u901A\u590D\u6742\u5EA6\uFF0C\u8BA9\u7CFB\u7EDF\u53D8\u5F97\u66F4\u52A0\u6613\u4E8E\u7EF4\u62A4\u3002</p><p>\u4F7F\u7528\u7EC4\u4EF6\u5316\u80FD\u5E26\u6765\u5F88\u591A\u4F18\u52BF\uFF0C\u6240\u4EE5\u5F88\u591A\u8BED\u8A00\u5929\u751F\u5C31\u5BF9\u7EC4\u4EF6\u5316\u63D0\u4F9B\u4E86\u5F88\u597D\u7684\u652F\u6301\uFF0C\u6BD4\u5982C/C++\u5C31\u53EF\u4EE5\u5F88\u597D\u5730\u5C06\u529F\u80FD\u5C01\u88C5\u6210\u6A21\u5757\uFF0C\u65E0\u8BBA\u662F\u4E1A\u52A1\u903B\u8F91\uFF0C\u8FD8\u662F\u57FA\u7840\u529F\u80FD\uFF0C\u6291\u6216\u662FUI\uFF0C\u90FD\u80FD\u5F88\u597D\u5730\u5C06\u5176\u7EC4\u5408\u5728\u4E00\u8D77\uFF0C\u5B9E\u73B0\u7EC4\u4EF6\u5185\u90E8\u7684\u9AD8\u5EA6\u5185\u805A\u3001\u7EC4\u4EF6\u4E4B\u95F4\u7684\u4F4E\u8026\u5408\u3002</p><p>\u5927\u90E8\u5206\u8BED\u8A00\u90FD\u80FD\u5B9E\u73B0\u7EC4\u4EF6\u5316\uFF0C\u5F52\u6839\u7ED3\u5E95\u5728\u4E8E\u7F16\u7A0B\u8BED\u8A00\u7279\u6027\uFF0C\u5927\u591A\u6570\u8BED\u8A00\u90FD\u6709\u81EA\u5DF1\u7684\u51FD\u6570\u7EA7\u4F5C\u7528\u57DF\u3001\u5757\u7EA7\u4F5C\u7528\u57DF\u548C\u7C7B\uFF0C\u53EF\u4EE5\u5C06\u5185\u90E8\u7684\u72B6\u6001\u6570\u636E\u9690\u85CF\u5728\u4F5C\u7528\u57DF\u4E4B\u4E0B\u6216\u8005\u5BF9\u8C61\u7684\u5185\u90E8\uFF0C\u8FD9\u6837\u5916\u90E8\u5C31\u65E0\u6CD5\u8BBF\u95EE\u4E86\uFF0C\u7136\u540E\u901A\u8FC7\u7EA6\u5B9A\u597D\u7684\u63A5\u53E3\u548C\u5916\u90E8\u8FDB\u884C\u901A\u4FE1\u3002</p><p>JavaScript\u867D\u7136\u6709\u4E0D\u5C11\u7F3A\u70B9\uFF0C\u4F46\u662F\u4F5C\u4E3A\u4E00\u95E8\u7F16\u7A0B\u8BED\u8A00\uFF0C\u5B83\u4E5F\u80FD\u5F88\u597D\u5730\u5B9E\u73B0\u7EC4\u4EF6\u5316\uFF0C\u6BD5\u7ADF\u6709\u81EA\u5DF1\u7684\u51FD\u6570\u7EA7\u4F5C\u7528\u57DF\u548C\u5757\u7EA7\u4F5C\u7528\u57DF\uFF0C\u6240\u4EE5\u5C01\u88C5\u5185\u90E8\u72B6\u6001\u6570\u636E\u5E76\u63D0\u4F9B\u63A5\u53E3\u7ED9\u5916\u90E8\u90FD\u662F\u6CA1\u6709\u95EE\u9898\u7684\u3002</p><p>\u65E2\u7136JavaScript\u53EF\u4EE5\u5F88\u597D\u5730\u5B9E\u73B0\u7EC4\u4EF6\u5316\uFF0C\u90A3\u4E48\u6211\u4EEC\u6240\u8C08\u8BBA\u7684WebComponent\u5230\u5E95\u53C8\u662F\u4EC0\u4E48\u5462\uFF1F</p><h2 id="\u963B\u788D\u524D\u7AEF\u7EC4\u4EF6\u5316\u7684\u56E0\u7D20" tabindex="-1">\u963B\u788D\u524D\u7AEF\u7EC4\u4EF6\u5316\u7684\u56E0\u7D20 <a class="header-anchor" href="#\u963B\u788D\u524D\u7AEF\u7EC4\u4EF6\u5316\u7684\u56E0\u7D20" aria-hidden="true">#</a></h2><p>\u5728\u524D\u7AEF\u867D\u7136HTML\u3001CSS\u548CJavaScript\u662F\u5F3A\u5927\u7684\u5F00\u53D1\u8BED\u8A00\uFF0C\u4F46\u662F\u5728\u5927\u578B\u9879\u76EE\u4E2D\u7EF4\u62A4\u8D77\u6765\u4F1A\u6BD4\u8F83\u56F0\u96BE\uFF0C\u5982\u679C\u5728\u9875\u9762\u4E2D\u5D4C\u5165\u7B2C\u4E09\u65B9\u5185\u5BB9\u65F6\uFF0C\u8FD8\u9700\u8981\u786E\u4FDD\u7B2C\u4E09\u65B9\u7684\u5185\u5BB9\u6837\u5F0F\u4E0D\u4F1A\u5F71\u54CD\u5230\u5F53\u524D\u5185\u5BB9\uFF0C\u540C\u6837\u4E5F\u8981\u786E\u4FDD\u5F53\u524D\u7684DOM\u4E0D\u4F1A\u5F71\u54CD\u5230\u7B2C\u4E09\u65B9\u7684\u5185\u5BB9\u3002</p><p>\u6240\u4EE5\u8981\u804AWebComponent\uFF0C\u5F97\u5148\u770B\u770BHTML\u548CCSS\u662F\u5982\u4F55\u963B\u788D\u524D\u7AEF\u7EC4\u4EF6\u5316\u7684\uFF0C\u8FD9\u91CC\u6211\u4EEC\u5C31\u901A\u8FC7\u4E0B\u9762\u8FD9\u6837\u4E00\u4E2A\u7B80\u5355\u7684\u4F8B\u5B50\u6765\u5206\u6790\u4E0B\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&lt;style&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">p {</span></span>
<span class="line"><span style="color:#A6ACCD;">      background-color: brown;</span></span>
<span class="line"><span style="color:#A6ACCD;">      color: cornsilk</span></span>
<span class="line"><span style="color:#A6ACCD;">   }</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;/style&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;p&gt;time.geekbang.org&lt;/p&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&lt;style&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">p {</span></span>
<span class="line"><span style="color:#A6ACCD;">      background-color: red;</span></span>
<span class="line"><span style="color:#A6ACCD;">      color: blue</span></span>
<span class="line"><span style="color:#A6ACCD;">   }</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;/style&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;p&gt;time.geekbang&lt;/p&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4E0A\u9762\u8FD9\u4E24\u6BB5\u4EE3\u7801\u5206\u522B\u5B9E\u73B0\u4E86\u81EA\u5DF1p\u6807\u7B7E\u7684\u5C5E\u6027\uFF0C\u5982\u679C\u4E24\u4E2A\u4EBA\u5206\u522B\u8D1F\u8D23\u5F00\u53D1\u8FD9\u4E24\u6BB5\u4EE3\u7801\u7684\u8BDD\uFF0C\u90A3\u4E48\u5728\u6D4B\u8BD5\u9636\u6BB5\u53EF\u80FD\u6CA1\u6709\u4EC0\u4E48\u95EE\u9898\uFF0C\u4E0D\u8FC7\u5F53\u6700\u7EC8\u9879\u76EE\u6574\u5408\u7684\u65F6\u5019\uFF0C\u5176\u4E2D\u5185\u90E8\u7684CSS\u5C5E\u6027\u4F1A\u5F71\u54CD\u5230\u5176\u4ED6\u5916\u90E8\u7684p\u6807\u7B7E\u7684\uFF0C\u4E4B\u6240\u4EE5\u4F1A\u8FD9\u6837\uFF0C\u662F\u56E0\u4E3ACSS\u662F\u5F71\u54CD\u5168\u5C40\u7684\u3002</p><p>\u6211\u4EEC\u5728<a href="https://time.geekbang.org/column/article/140703" target="_blank" rel="noreferrer">\u300A23 | \u6E32\u67D3\u6D41\u6C34\u7EBF\uFF1ACSS\u5982\u4F55\u5F71\u54CD\u9996\u6B21\u52A0\u8F7D\u65F6\u7684\u767D\u5C4F\u65F6\u95F4\uFF1F\u300B</a>\u8FD9\u7BC7\u6587\u7AE0\u4E2D\u5206\u6790\u8FC7\uFF0C\u6E32\u67D3\u5F15\u64CE\u4F1A\u5C06\u6240\u6709\u7684CSS\u5185\u5BB9\u89E3\u6790\u4E3ACSSOM\uFF0C\u5728\u751F\u6210\u5E03\u5C40\u6811\u7684\u65F6\u5019\uFF0C\u4F1A\u5728CSSOM\u4E2D\u4E3A\u5E03\u5C40\u6811\u4E2D\u7684\u5143\u7D20\u67E5\u627E\u6837\u5F0F\uFF0C\u6240\u4EE5\u6709\u4E24\u4E2A\u76F8\u540C\u6807\u7B7E\u6700\u7EC8\u6240\u663E\u793A\u51FA\u6765\u7684\u6548\u679C\u662F\u4E00\u6837\u7684\uFF0C\u6E32\u67D3\u5F15\u64CE\u662F\u4E0D\u80FD\u4E3A\u5B83\u4EEC\u5206\u522B\u5355\u72EC\u8BBE\u7F6E\u6837\u5F0F\u7684\u3002</p><p>\u9664\u4E86CSS\u7684\u5168\u5C40\u5C5E\u6027\u4F1A\u963B\u788D\u7EC4\u4EF6\u5316\uFF0CDOM\u4E5F\u662F\u963B\u788D\u7EC4\u4EF6\u5316\u7684\u4E00\u4E2A\u56E0\u7D20\uFF0C\u56E0\u4E3A\u5728\u9875\u9762\u4E2D\u53EA\u6709\u4E00\u4E2ADOM\uFF0C\u4EFB\u4F55\u5730\u65B9\u90FD\u53EF\u4EE5\u76F4\u63A5\u8BFB\u53D6\u548C\u4FEE\u6539DOM\u3002\u6240\u4EE5\u4F7F\u7528JavaScript\u6765\u5B9E\u73B0\u7EC4\u4EF6\u5316\u662F\u6CA1\u6709\u95EE\u9898\u7684\uFF0C\u4F46\u662FJavaScript\u4E00\u65E6\u9047\u4E0ACSS\u548CDOM\uFF0C\u90A3\u4E48\u5C31\u76F8\u5F53\u96BE\u529E\u4E86\u3002</p><h2 id="webcomponent\u7EC4\u4EF6\u5316\u5F00\u53D1" tabindex="-1">WebComponent\u7EC4\u4EF6\u5316\u5F00\u53D1 <a class="header-anchor" href="#webcomponent\u7EC4\u4EF6\u5316\u5F00\u53D1" aria-hidden="true">#</a></h2><p>\u73B0\u5728\u6211\u4EEC\u4E86\u89E3\u4E86<strong>CSS\u548CDOM\u662F\u963B\u788D\u7EC4\u4EF6\u5316\u7684\u4E24\u4E2A\u56E0\u7D20</strong>\uFF0C\u90A3\u8981\u600E\u4E48\u89E3\u51B3\u5462\uFF1F</p><p>WebComponent\u7ED9\u51FA\u4E86\u89E3\u51B3\u601D\u8DEF\uFF0C\u5B83\u63D0\u4F9B\u4E86\u5BF9\u5C40\u90E8\u89C6\u56FE\u5C01\u88C5\u80FD\u529B\uFF0C\u53EF\u4EE5\u8BA9DOM\u3001CSSOM\u548CJavaScript\u8FD0\u884C\u5728\u5C40\u90E8\u73AF\u5883\u4E2D\uFF0C\u8FD9\u6837\u5C31\u4F7F\u5F97\u5C40\u90E8\u7684CSS\u548CDOM\u4E0D\u4F1A\u5F71\u54CD\u5230\u5168\u5C40\u3002</p><p>\u4E86\u89E3\u4E86\u8FD9\u4E9B\uFF0C\u4E0B\u9762\u6211\u4EEC\u5C31\u7ED3\u5408\u5177\u4F53\u4EE3\u7801\u6765\u770B\u770BWebComponent\u662F\u600E\u4E48\u5B9E\u73B0\u7EC4\u4EF6\u5316\u7684\u3002</p><p>\u524D\u9762\u6211\u4EEC\u8BF4\u4E86\uFF0CWebComponent\u662F\u4E00\u5957\u6280\u672F\u7684\u7EC4\u5408\uFF0C\u5177\u4F53\u6D89\u53CA\u5230\u4E86<strong>Custom elements\uFF08\u81EA\u5B9A\u4E49\u5143\u7D20\uFF09\u3001Shadow DOM\uFF08\u5F71\u5B50DOM\uFF09<strong>\u548C</strong>HTML templates\uFF08HTML\u6A21\u677F\uFF09</strong>\uFF0C\u8BE6\u7EC6\u5185\u5BB9\u4F60\u53EF\u4EE5\u53C2\u8003MDN\u4E0A\u7684<a href="https://developer.mozilla.org/zh-CN/docs/Web/Web_Components" target="_blank" rel="noreferrer">\u76F8\u5173\u94FE\u63A5</a>\u3002</p><p>\u4E0B\u9762\u6211\u4EEC\u5C31\u6765\u6F14\u793A\u4E0B\u8FD93\u4E2A\u6280\u672F\u662F\u600E\u4E48\u5B9E\u73B0\u6570\u636E\u5C01\u88C5\u7684\uFF0C\u5982\u4E0B\u9762\u4EE3\u7801\u6240\u793A\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&lt;!DOCTYPE html&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;html&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;body&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;!--</span></span>
<span class="line"><span style="color:#A6ACCD;">            \u4E00\uFF1A\u5B9A\u4E49\u6A21\u677F</span></span>
<span class="line"><span style="color:#A6ACCD;">            \u4E8C\uFF1A\u5B9A\u4E49\u5185\u90E8CSS\u6837\u5F0F</span></span>
<span class="line"><span style="color:#A6ACCD;">            \u4E09\uFF1A\u5B9A\u4E49JavaScript\u884C\u4E3A</span></span>
<span class="line"><span style="color:#A6ACCD;">    --&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;template id=&quot;geekbang-t&quot;&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;style&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">            p {</span></span>
<span class="line"><span style="color:#A6ACCD;">                background-color: brown;</span></span>
<span class="line"><span style="color:#A6ACCD;">                color: cornsilk</span></span>
<span class="line"><span style="color:#A6ACCD;">            }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">            div {</span></span>
<span class="line"><span style="color:#A6ACCD;">                width: 200px;</span></span>
<span class="line"><span style="color:#A6ACCD;">                background-color: bisque;</span></span>
<span class="line"><span style="color:#A6ACCD;">                border: 3px solid chocolate;</span></span>
<span class="line"><span style="color:#A6ACCD;">                border-radius: 10px;</span></span>
<span class="line"><span style="color:#A6ACCD;">            }</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;/style&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">            &lt;p&gt;time.geekbang.org&lt;/p&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">            &lt;p&gt;time1.geekbang.org&lt;/p&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;/div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;script&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">            function foo() {</span></span>
<span class="line"><span style="color:#A6ACCD;">                console.log(&#39;inner log&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;">            }</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;/script&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;/template&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;script&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        class GeekBang extends HTMLElement {</span></span>
<span class="line"><span style="color:#A6ACCD;">            constructor() {</span></span>
<span class="line"><span style="color:#A6ACCD;">                super()</span></span>
<span class="line"><span style="color:#A6ACCD;">                //\u83B7\u53D6\u7EC4\u4EF6\u6A21\u677F</span></span>
<span class="line"><span style="color:#A6ACCD;">                const content = document.querySelector(&#39;#geekbang-t&#39;).content</span></span>
<span class="line"><span style="color:#A6ACCD;">                //\u521B\u5EFA\u5F71\u5B50DOM\u8282\u70B9</span></span>
<span class="line"><span style="color:#A6ACCD;">                const shadowDOM = this.attachShadow({ mode: &#39;open&#39; })</span></span>
<span class="line"><span style="color:#A6ACCD;">                //\u5C06\u6A21\u677F\u6DFB\u52A0\u5230\u5F71\u5B50DOM\u4E0A</span></span>
<span class="line"><span style="color:#A6ACCD;">                shadowDOM.appendChild(content.cloneNode(true))</span></span>
<span class="line"><span style="color:#A6ACCD;">            }</span></span>
<span class="line"><span style="color:#A6ACCD;">        }</span></span>
<span class="line"><span style="color:#A6ACCD;">        customElements.define(&#39;geek-bang&#39;, GeekBang)</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;/script&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;geek-bang&gt;&lt;/geek-bang&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;p&gt;time.geekbang.org&lt;/p&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;p&gt;time1.geekbang.org&lt;/p&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;/div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;geek-bang&gt;&lt;/geek-bang&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;/body&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;/html&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8BE6\u7EC6\u89C2\u5BDF\u4E0A\u9762\u8FD9\u6BB5\u4EE3\u7801\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5F97\u51FA\uFF1A\u8981\u4F7F\u7528WebComponent\uFF0C\u901A\u5E38\u8981\u5B9E\u73B0\u4E0B\u9762\u4E09\u4E2A\u6B65\u9AA4\u3002</p><p><strong>\u9996\u5148\uFF0C\u4F7F\u7528template\u5C5E\u6027\u6765\u521B\u5EFA\u6A21\u677F</strong>\u3002\u5229\u7528DOM\u53EF\u4EE5\u67E5\u627E\u5230\u6A21\u677F\u7684\u5185\u5BB9\uFF0C\u4F46\u662F\u6A21\u677F\u5143\u7D20\u662F\u4E0D\u4F1A\u88AB\u6E32\u67D3\u5230\u9875\u9762\u4E0A\u7684\uFF0C\u4E5F\u5C31\u662F\u8BF4DOM\u6811\u4E2D\u7684template\u8282\u70B9\u4E0D\u4F1A\u51FA\u73B0\u5728\u5E03\u5C40\u6811\u4E2D\uFF0C\u6240\u4EE5\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528template\u6765\u81EA\u5B9A\u4E49\u4E00\u4E9B\u57FA\u7840\u7684\u5143\u7D20\u7ED3\u6784\uFF0C\u8FD9\u4E9B\u57FA\u7840\u7684\u5143\u7D20\u7ED3\u6784\u662F\u53EF\u4EE5\u88AB\u91CD\u590D\u4F7F\u7528\u7684\u3002\u4E00\u822C\u6A21\u677F\u5B9A\u4E49\u597D\u4E4B\u540E\uFF0C\u6211\u4EEC\u8FD8\u9700\u8981\u5728\u6A21\u677F\u7684\u5185\u90E8\u5B9A\u4E49\u6837\u5F0F\u4FE1\u606F\u3002</p><p><strong>\u5176\u6B21\uFF0C\u6211\u4EEC\u9700\u8981\u521B\u5EFA\u4E00\u4E2AGeekBang\u7684\u7C7B</strong>\u3002\u5728\u8BE5\u7C7B\u7684\u6784\u9020\u51FD\u6570\u4E2D\u8981\u5B8C\u6210\u4E09\u4EF6\u4E8B\uFF1A</p><ol><li>\u67E5\u627E\u6A21\u677F\u5185\u5BB9\uFF1B</li><li>\u521B\u5EFA\u5F71\u5B50DOM\uFF1B</li><li>\u518D\u5C06\u6A21\u677F\u6DFB\u52A0\u5230\u5F71\u5B50DOM\u4E0A\u3002</li></ol><p>\u4E0A\u9762\u6700\u96BE\u7406\u89E3\u7684\u662F\u5F71\u5B50DOM\uFF0C\u5176\u5B9E\u5F71\u5B50DOM\u7684\u4F5C\u7528\u662F\u5C06\u6A21\u677F\u4E2D\u7684\u5185\u5BB9\u4E0E\u5168\u5C40DOM\u548CCSS\u8FDB\u884C\u9694\u79BB\uFF0C\u8FD9\u6837\u6211\u4EEC\u5C31\u53EF\u4EE5\u5B9E\u73B0\u5143\u7D20\u548C\u6837\u5F0F\u7684\u79C1\u6709\u5316\u4E86\u3002\u4F60\u53EF\u4EE5\u628A\u5F71\u5B50DOM\u770B\u6210\u662F\u4E00\u4E2A\u4F5C\u7528\u57DF\uFF0C\u5176\u5185\u90E8\u7684\u6837\u5F0F\u548C\u5143\u7D20\u662F\u4E0D\u4F1A\u5F71\u54CD\u5230\u5168\u5C40\u7684\u6837\u5F0F\u548C\u5143\u7D20\u7684\uFF0C\u800C\u5728\u5168\u5C40\u73AF\u5883\u4E0B\uFF0C\u8981\u8BBF\u95EE\u5F71\u5B50DOM\u5185\u90E8\u7684\u6837\u5F0F\u6216\u8005\u5143\u7D20\u4E5F\u662F\u9700\u8981\u901A\u8FC7\u7EA6\u5B9A\u597D\u7684\u63A5\u53E3\u7684\u3002</p><p>\u603B\u4E4B\uFF0C\u901A\u8FC7\u5F71\u5B50DOM\uFF0C\u6211\u4EEC\u5C31\u5B9E\u73B0\u4E86CSS\u548C\u5143\u7D20\u7684\u5C01\u88C5\uFF0C\u5728\u521B\u5EFA\u597D\u5C01\u88C5\u5F71\u5B50DOM\u7684\u7C7B\u4E4B\u540E\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5<strong>\u4F7F\u7528customElements.define\u6765\u81EA\u5B9A\u4E49\u5143\u7D20\u4E86</strong>\uFF08\u53EF\u53C2\u8003\u4E0A\u8FF0\u4EE3\u7801\u5B9A\u4E49\u5143\u7D20\u7684\u65B9\u5F0F\uFF09\u3002</p><p><strong>\u6700\u540E\uFF0C\u5C31\u5F88\u7B80\u5355\u4E86\uFF0C\u53EF\u4EE5\u50CF\u6B63\u5E38\u4F7F\u7528HTML\u5143\u7D20\u4E00\u6837\u4F7F\u7528\u8BE5\u5143\u7D20</strong>\uFF0C\u5982\u4E0A\u8FF0\u4EE3\u7801\u4E2D\u7684<code>&lt;geek-bang&gt;&lt;/geek-bang&gt;</code>\u3002</p><p>\u4E0A\u8FF0\u4EE3\u7801\u6700\u7EC8\u6E32\u67D3\u51FA\u6765\u7684\u9875\u9762\uFF0C\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/57/7c/579c65e2d2221f4e476c7846b842c27c.png" alt=""></p><p>\u4F7F\u7528\u5F71\u5B50DOM\u7684\u8F93\u51FA\u6548\u679C</p><p>\u4ECE\u56FE\u4E2D\u6211\u4EEC\u53EF\u4EE5\u770B\u51FA\uFF0C\u5F71\u5B50DOM\u5185\u90E8\u7684\u6837\u5F0F\u662F\u4E0D\u4F1A\u5F71\u54CD\u5230\u5168\u5C40CSSOM\u7684\u3002\u53E6\u5916\uFF0C\u4F7F\u7528DOM\u63A5\u53E3\u4E5F\u662F\u65E0\u6CD5\u76F4\u63A5\u67E5\u8BE2\u5230\u5F71\u5B50DOM\u5185\u90E8\u5143\u7D20\u7684\uFF0C\u6BD4\u5982\u4F60\u53EF\u4EE5\u4F7F\u7528<code>document.getElementsByTagName(&#39;div&#39;)</code>\u6765\u67E5\u627E\u6240\u6709div\u5143\u7D20\uFF0C\u8FD9\u65F6\u5019\u4F60\u4F1A\u53D1\u73B0\u5F71\u5B50DOM\u5185\u90E8\u7684\u5143\u7D20\u90FD\u662F\u65E0\u6CD5\u67E5\u627E\u7684\uFF0C\u56E0\u4E3A\u8981\u60F3\u67E5\u627E\u5F71\u5B50DOM\u5185\u90E8\u7684\u5143\u7D20\u9700\u8981\u4E13\u95E8\u7684\u63A5\u53E3\uFF0C\u6240\u4EE5\u901A\u8FC7\u8FD9\u79CD\u65B9\u5F0F\u53C8\u5C06\u5F71\u5B50\u5185\u90E8\u7684DOM\u548C\u5916\u90E8\u7684DOM\u8FDB\u884C\u4E86\u9694\u79BB\u3002</p><p>\u901A\u8FC7\u5F71\u5B50DOM\u53EF\u4EE5\u9694\u79BBCSS\u548CDOM\uFF0C\u4E0D\u8FC7\u9700\u8981\u6CE8\u610F\u4E00\u70B9\uFF0C\u5F71\u5B50DOM\u7684JavaScript\u811A\u672C\u662F\u4E0D\u4F1A\u88AB\u9694\u79BB\u7684\uFF0C\u6BD4\u5982\u5728\u5F71\u5B50DOM\u5B9A\u4E49\u7684JavaScript\u51FD\u6570\u4F9D\u7136\u53EF\u4EE5\u88AB\u5916\u90E8\u8BBF\u95EE\uFF0C\u8FD9\u662F\u56E0\u4E3AJavaScript\u8BED\u8A00\u672C\u8EAB\u5DF2\u7ECF\u53EF\u4EE5\u5F88\u597D\u5730\u5B9E\u73B0\u7EC4\u4EF6\u5316\u4E86\u3002</p><h2 id="\u6D4F\u89C8\u5668\u5982\u4F55\u5B9E\u73B0\u5F71\u5B50dom" tabindex="-1">\u6D4F\u89C8\u5668\u5982\u4F55\u5B9E\u73B0\u5F71\u5B50DOM <a class="header-anchor" href="#\u6D4F\u89C8\u5668\u5982\u4F55\u5B9E\u73B0\u5F71\u5B50dom" aria-hidden="true">#</a></h2><p>\u5173\u4E8EWebComponent\u7684\u4F7F\u7528\u65B9\u5F0F\u6211\u4EEC\u5C31\u4ECB\u7ECD\u5230\u8FD9\u91CC\u3002WebComponent\u6574\u4F53\u77E5\u8BC6\u70B9\u4E0D\u591A\uFF0C\u5185\u5BB9\u4E5F\u4E0D\u590D\u6742\uFF0C\u6211\u8BA4\u4E3A\u6838\u5FC3\u5C31\u662F\u5F71\u5B50DOM\u3002\u4E0A\u9762\u6211\u4EEC\u4ECB\u7ECD\u5F71\u5B50DOM\u7684\u4F5C\u7528\u4E3B\u8981\u6709\u4EE5\u4E0B\u4E24\u70B9\uFF1A</p><ol><li>\u5F71\u5B50DOM\u4E2D\u7684\u5143\u7D20\u5BF9\u4E8E\u6574\u4E2A\u7F51\u9875\u662F\u4E0D\u53EF\u89C1\u7684\uFF1B</li><li>\u5F71\u5B50DOM\u7684CSS\u4E0D\u4F1A\u5F71\u54CD\u5230\u6574\u4E2A\u7F51\u9875\u7684CSSOM\uFF0C\u5F71\u5B50DOM\u5185\u90E8\u7684CSS\u53EA\u5BF9\u5185\u90E8\u7684\u5143\u7D20\u8D77\u4F5C\u7528\u3002</li></ol><p>\u90A3\u4E48\u6D4F\u89C8\u5668\u662F\u5982\u4F55\u5B9E\u73B0\u5F71\u5B50DOM\u7684\u5462\uFF1F\u4E0B\u9762\u6211\u4EEC\u5C31\u6765\u5206\u6790\u4E0B\uFF0C\u5982\u4E0B\u56FE\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/5b/22/5bce3d00c8139a7fde9cc90f9d803322.png" alt=""></p><p>\u5F71\u5B50DOM\u793A\u610F\u56FE</p><p>\u8BE5\u56FE\u662F\u4E0A\u9762\u90A3\u6BB5\u793A\u4F8B\u4EE3\u7801\u5BF9\u5E94\u7684DOM\u7ED3\u6784\u56FE\uFF0C\u4ECE\u56FE\u4E2D\u53EF\u4EE5\u770B\u51FA\uFF0C\u6211\u4EEC\u4F7F\u7528\u4E86\u4E24\u6B21geek-bang\u5C5E\u6027\uFF0C\u90A3\u4E48\u5C31\u4F1A\u751F\u6210\u4E24\u4E2A\u5F71\u5B50DOM\uFF0C\u5E76\u4E14\u6BCF\u4E2A\u5F71\u5B50DOM\u90FD\u6709\u4E00\u4E2Ashadow root\u7684\u6839\u8282\u70B9\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5C06\u8981\u5C55\u793A\u7684\u6837\u5F0F\u6216\u8005\u5143\u7D20\u6DFB\u52A0\u5230\u5F71\u5B50DOM\u7684\u6839\u8282\u70B9\u4E0A\uFF0C\u6BCF\u4E2A\u5F71\u5B50DOM\u4F60\u90FD\u53EF\u4EE5\u770B\u6210\u662F\u4E00\u4E2A\u72EC\u7ACB\u7684DOM\uFF0C\u5B83\u6709\u81EA\u5DF1\u7684\u6837\u5F0F\u3001\u81EA\u5DF1\u7684\u5C5E\u6027\uFF0C\u5185\u90E8\u6837\u5F0F\u4E0D\u4F1A\u5F71\u54CD\u5230\u5916\u90E8\u6837\u5F0F\uFF0C\u5916\u90E8\u6837\u5F0F\u4E5F\u4E0D\u4F1A\u5F71\u54CD\u5230\u5185\u90E8\u6837\u5F0F\u3002</p><p>\u6D4F\u89C8\u5668\u4E3A\u4E86\u5B9E\u73B0\u5F71\u5B50DOM\u7684\u7279\u6027\uFF0C\u5728\u4EE3\u7801\u5185\u90E8\u505A\u4E86\u5927\u91CF\u7684\u6761\u4EF6\u5224\u65AD\uFF0C\u6BD4\u5982\u5F53\u901A\u8FC7DOM\u63A5\u53E3\u53BB\u67E5\u627E\u5143\u7D20\u65F6\uFF0C\u6E32\u67D3\u5F15\u64CE\u4F1A\u53BB\u5224\u65ADgeek-bang\u5C5E\u6027\u4E0B\u9762\u7684shadow-root\u5143\u7D20\u662F\u5426\u662F\u5F71\u5B50DOM\uFF0C\u5982\u679C\u662F\u5F71\u5B50DOM\uFF0C\u90A3\u4E48\u5C31\u76F4\u63A5\u8DF3\u8FC7shadow-root\u5143\u7D20\u7684\u67E5\u8BE2\u64CD\u4F5C\u3002\u6240\u4EE5\u8FD9\u6837\u901A\u8FC7DOM API\u5C31\u65E0\u6CD5\u76F4\u63A5\u67E5\u8BE2\u5230\u5F71\u5B50DOM\u7684\u5185\u90E8\u5143\u7D20\u4E86\u3002</p><p>\u53E6\u5916\uFF0C\u5F53\u751F\u6210\u5E03\u5C40\u6811\u7684\u65F6\u5019\uFF0C\u6E32\u67D3\u5F15\u64CE\u4E5F\u4F1A\u5224\u65ADgeek-bang\u5C5E\u6027\u4E0B\u9762\u7684shadow-root\u5143\u7D20\u662F\u5426\u662F\u5F71\u5B50DOM\uFF0C\u5982\u679C\u662F\uFF0C\u90A3\u4E48\u5728\u5F71\u5B50DOM\u5185\u90E8\u5143\u7D20\u7684\u8282\u70B9\u9009\u62E9CSS\u6837\u5F0F\u7684\u65F6\u5019\uFF0C\u4F1A\u76F4\u63A5\u4F7F\u7528\u5F71\u5B50DOM\u5185\u90E8\u7684CSS\u5C5E\u6027\u3002\u6240\u4EE5\u8FD9\u6837\u6700\u7EC8\u6E32\u67D3\u51FA\u6765\u7684\u6548\u679C\u5C31\u662F\u5F71\u5B50DOM\u5185\u90E8\u5B9A\u4E49\u7684\u6837\u5F0F\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u597D\u4E86\uFF0C\u4ECA\u5929\u5C31\u8BB2\u5230\u8FD9\u91CC\uFF0C\u4E0B\u9762\u6211\u6765\u603B\u7ED3\u4E0B\u672C\u6587\u7684\u4E3B\u8981\u5185\u5BB9\u3002</p><p>\u9996\u5148\uFF0C\u6211\u4EEC\u4ECB\u7ECD\u4E86\u7EC4\u4EF6\u5316\u5F00\u53D1\u662F\u7A0B\u5E8F\u5458\u7684\u521A\u9700\uFF0C\u6240\u8C13\u7EC4\u4EF6\u5316\u5C31\u662F\u529F\u80FD\u6A21\u5757\u8981\u5B9E\u73B0\u9AD8\u5185\u805A\u3001\u4F4E\u8026\u5408\u7684\u7279\u6027\u3002\u4E0D\u8FC7\u7531\u4E8EDOM\u548CCSSOM\u90FD\u662F\u5168\u5C40\u7684\uFF0C\u6240\u4EE5\u5B83\u4EEC\u662F\u5F71\u54CD\u4E86\u524D\u7AEF\u7EC4\u4EF6\u5316\u7684\u4E3B\u8981\u5143\u7D20\u3002\u57FA\u4E8E\u8FD9\u4E2A\u539F\u56E0\uFF0C\u5C31\u51FA\u73B0WebComponent\uFF0C\u5B83\u5305\u542B\u81EA\u5B9A\u4E49\u5143\u7D20\u3001\u5F71\u5B50DOM\u548CHTML\u6A21\u677F\u4E09\u79CD\u6280\u672F\uFF0C\u4F7F\u5F97\u5F00\u53D1\u8005\u53EF\u4EE5\u9694\u79BBCSS\u548CDOM\u3002\u5728\u6B64\u57FA\u7840\u4E0A\uFF0C\u6211\u4EEC\u8FD8\u91CD\u70B9\u4ECB\u7ECD\u4E86\u5F71\u5B50DOM\u5230\u5E95\u662F\u600E\u4E48\u5B9E\u73B0\u7684\u3002</p><p>\u5173\u4E8EWebComponent\u7684\u672A\u6765\u5982\u4F55\uFF0C\u8FD9\u91CC\u6211\u4EEC\u4E0D\u597D\u9884\u6D4B\u548C\u8BC4\u5224\uFF0C\u4F46\u662F\u6709\u4E00\u70B9\u53EF\u4EE5\u80AF\u5B9A\uFF0CWebComponent\u4E5F\u4F1A\u91C7\u7528\u6E10\u8FDB\u5F0F\u8FED\u4EE3\u7684\u65B9\u5F0F\u5411\u524D\u63A8\u8FDB\uFF0C\u672A\u6765\u4F9D\u7136\u6709\u5F88\u591A\u5751\u9700\u8981\u53BB\u586B\u3002</p><h2 id="\u601D\u8003\u65F6\u95F4" tabindex="-1">\u601D\u8003\u65F6\u95F4 <a class="header-anchor" href="#\u601D\u8003\u65F6\u95F4" aria-hidden="true">#</a></h2><p>\u4ECA\u5929\u7559\u7ED9\u4F60\u7684\u601D\u8003\u9898\u662F\uFF1A\u4F60\u662F\u600E\u4E48\u770B\u5F85WebComponents\u548C\u524D\u7AEF\u6846\u67B6\uFF08React\u3001Vue\uFF09\u4E4B\u95F4\u7684\u5173\u7CFB\u7684\uFF1F</p><p>\u6B22\u8FCE\u5728\u7559\u8A00\u533A\u4E0E\u6211\u5206\u4EAB\u4F60\u7684\u60F3\u6CD5\uFF0C\u4E5F\u6B22\u8FCE\u4F60\u5728\u7559\u8A00\u533A\u8BB0\u5F55\u4F60\u7684\u601D\u8003\u8FC7\u7A0B\u3002\u611F\u8C22\u9605\u8BFB\uFF0C\u5982\u679C\u4F60\u89C9\u5F97\u8FD9\u7BC7\u6587\u7AE0\u5BF9\u4F60\u6709\u5E2E\u52A9\u7684\u8BDD\uFF0C\u4E5F\u6B22\u8FCE\u628A\u5B83\u5206\u4EAB\u7ED9\u66F4\u591A\u7684\u670B\u53CB\u3002</p>`,54),t=[e];function o(c,r,C,i,A,D){return a(),n("div",null,t)}const y=s(l,[["render",o]]);export{d as __pageData,y as default};
