import{_ as n,c as a,o as s,d as l}from"./app.e0e4ea3a.js";const A=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","slug":"title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","link":"#title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","children":[]},{"level":2,"title":"Completion\u7C7B\u578B","slug":"completion\u7C7B\u578B","link":"#completion\u7C7B\u578B","children":[]},{"level":2,"title":"\u666E\u901A\u7684\u8BED\u53E5","slug":"\u666E\u901A\u7684\u8BED\u53E5","link":"#\u666E\u901A\u7684\u8BED\u53E5","children":[]},{"level":2,"title":"\u8BED\u53E5\u5757","slug":"\u8BED\u53E5\u5757","link":"#\u8BED\u53E5\u5757","children":[]},{"level":2,"title":"\u63A7\u5236\u578B\u8BED\u53E5","slug":"\u63A7\u5236\u578B\u8BED\u53E5","link":"#\u63A7\u5236\u578B\u8BED\u53E5","children":[]},{"level":2,"title":"\u5E26\u6807\u7B7E\u7684\u8BED\u53E5","slug":"\u5E26\u6807\u7B7E\u7684\u8BED\u53E5","link":"#\u5E26\u6807\u7B7E\u7684\u8BED\u53E5","children":[]},{"level":2,"title":"\u7ED3\u8BED","slug":"\u7ED3\u8BED","link":"#\u7ED3\u8BED","children":[]}],"relativePath":"JavaScript/\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF/JavaScript\u6267\u884C\uFF08\u56DB\uFF09-try\u91CC\u9762\u653Ereturn-finally\u8FD8\u4F1A\u6267\u884C\u5417.md"}'),e={name:"JavaScript/\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF/JavaScript\u6267\u884C\uFF08\u56DB\uFF09-try\u91CC\u9762\u653Ereturn-finally\u8FD8\u4F1A\u6267\u884C\u5417.md"},p=l(`<hr><h2 id="title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF" tabindex="-1">title: \u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF <a class="header-anchor" href="#title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF" aria-hidden="true">#</a></h2><p>JavaScript\u6267\u884C\uFF08\u56DB\uFF09\uFF1Atry\u91CC\u9762\u653Ereturn\uFF0Cfinally\u8FD8\u4F1A\u6267\u884C\u5417\uFF1F</p><p>\u4F60\u597D\uFF0C\u6211\u662Fwinter\u3002</p><p>\u5728\u524D\u9762\u51E0\u7BC7\u6587\u7AE0\u4E2D\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u4E86\u89E3\u4E86\u5173\u4E8E\u6267\u884C\u4E0A\u4E0B\u6587\u3001\u4F5C\u7528\u57DF\u3001\u95ED\u5305\u4E4B\u95F4\u7684\u5173\u7CFB\u3002</p><p>\u4ECA\u5929\uFF0C\u6211\u4EEC\u5219\u8981\u8BF4\u4E00\u8BF4\u66F4\u4E3A\u7EC6\u8282\u7684\u90E8\u5206\uFF1A\u8BED\u53E5\u3002</p><p>\u8BED\u53E5\u662F\u4EFB\u4F55\u7F16\u7A0B\u8BED\u8A00\u7684\u57FA\u7840\u7ED3\u6784\uFF0C\u4E0EJavaScript\u5BF9\u8C61\u4E00\u6837\uFF0CJavaScript\u8BED\u53E5\u540C\u6837\u5177\u6709\u201C\u770B\u8D77\u6765\u5F88\u50CF\u5176\u5B83\u8BED\u8A00\uFF0C\u4F46\u662F\u5176\u5B9E\u4E00\u70B9\u90FD\u4E0D\u4E00\u6837\u201D\u7684\u7279\u70B9\u3002</p><p>\u6211\u4EEC\u6BD4\u8F83\u5E38\u89C1\u7684\u8BED\u53E5\u5305\u62EC\u53D8\u91CF\u58F0\u660E\u3001\u8868\u8FBE\u5F0F\u3001\u6761\u4EF6\u3001\u5FAA\u73AF\u7B49\uFF0C\u8FD9\u4E9B\u90FD\u662F\u5927\u5BB6\u975E\u5E38\u719F\u6089\u7684\u4E1C\u897F\uFF0C\u5BF9\u4E8E\u5B83\u4EEC\u7684\u884C\u4E3A\uFF0C\u6211\u5728\u8FD9\u91CC\u5C31\u4E0D\u8D58\u8FF0\u4E86\u3002</p><p>\u4E3A\u4E86\u4E86\u89E3JavaScript\u8BED\u53E5\u6709\u54EA\u4E9B\u7279\u522B\u4E4B\u5904\uFF0C\u9996\u5148\u6211\u4EEC\u8981\u770B\u4E00\u4E2A\u4E0D\u592A\u5E38\u89C1\u7684\u4F8B\u5B50\uFF0C\u6211\u4F1A\u901A\u8FC7\u8FD9\u4E2A\u4F8B\u5B50\uFF0C\u6765\u5411\u4F60\u4ECB\u7ECDJavaScript\u8BED\u53E5\u6267\u884C\u673A\u5236\u6D89\u53CA\u7684\u4E00\u79CD\u57FA\u7840\u7C7B\u578B\uFF1ACompletion\u7C7B\u578B\u3002</p><h2 id="completion\u7C7B\u578B" tabindex="-1">Completion\u7C7B\u578B <a class="header-anchor" href="#completion\u7C7B\u578B" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u6765\u770B\u4E00\u4E2A\u4F8B\u5B50\u3002\u5728\u51FD\u6570foo\u4E2D\uFF0C\u4F7F\u7528\u4E86\u4E00\u7EC4try\u8BED\u53E5\u3002\u6211\u4EEC\u53EF\u4EE5\u5148\u6765\u505A\u4E00\u4E2A\u5C0F\u5B9E\u9A8C\uFF0C\u5728try\u4E2D\u6709return\u8BED\u53E5\uFF0Cfinally\u4E2D\u7684\u5185\u5BB9\u8FD8\u4F1A\u6267\u884C\u5417\uFF1F\u6211\u4EEC\u6765\u770B\u4E00\u6BB5\u4EE3\u7801\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">function foo(){</span></span>
<span class="line"><span style="color:#A6ACCD;">  try{</span></span>
<span class="line"><span style="color:#A6ACCD;">    return 0;</span></span>
<span class="line"><span style="color:#A6ACCD;">  } catch(err) {</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  } finally {</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(&quot;a&quot;)</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">console.log(foo());</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u901A\u8FC7\u5B9E\u9645\u8BD5\u9A8C\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\uFF0Cfinally\u786E\u5B9E\u6267\u884C\u4E86\uFF0C\u800C\u4E14return\u8BED\u53E5\u4E5F\u751F\u6548\u4E86\uFF0Cfoo()\u8FD4\u56DE\u4E86\u7ED3\u679C0\u3002</p><p>\u867D\u7136return\u6267\u884C\u4E86\uFF0C\u4F46\u662F\u51FD\u6570\u5E76\u6CA1\u6709\u7ACB\u5373\u8FD4\u56DE\uFF0C\u53C8\u6267\u884C\u4E86finally\u91CC\u9762\u7684\u5185\u5BB9\uFF0C\u8FD9\u6837\u7684\u884C\u4E3A\u8FDD\u80CC\u4E86\u5F88\u591A\u4EBA\u7684\u76F4\u89C9\u3002</p><p>\u5982\u679C\u5728\u8FD9\u4E2A\u4F8B\u5B50\u4E2D\uFF0C\u6211\u4EEC\u5728finally\u4E2D\u52A0\u5165return\u8BED\u53E5\uFF0C\u4F1A\u53D1\u751F\u4EC0\u4E48\u5462\uFF1F</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">function foo(){</span></span>
<span class="line"><span style="color:#A6ACCD;">  try{</span></span>
<span class="line"><span style="color:#A6ACCD;">    return 0;</span></span>
<span class="line"><span style="color:#A6ACCD;">  } catch(err) {</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  } finally {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return 1;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">console.log(foo());</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u901A\u8FC7\u5B9E\u9645\u6267\u884C\uFF0C\u6211\u4EEC\u770B\u5230\uFF0Cfinally\u4E2D\u7684return \u201C\u8986\u76D6\u201D\u4E86try\u4E2D\u7684return\u3002\u5728\u4E00\u4E2A\u51FD\u6570\u4E2D\u6267\u884C\u4E86\u4E24\u6B21return\uFF0C\u8FD9\u5DF2\u7ECF\u8D85\u51FA\u4E86\u5F88\u591A\u4EBA\u7684\u5E38\u8BC6\uFF0C\u4E5F\u662F\u5176\u5B83\u8BED\u8A00\u4E2D\u4E0D\u4F1A\u51FA\u73B0\u7684\u4E00\u79CD\u884C\u4E3A\u3002</p><p>\u9762\u5BF9\u5982\u6B64\u602A\u5F02\u7684\u884C\u4E3A\uFF0C\u6211\u4EEC\u5F53\u7136\u53EF\u4EE5\u628A\u5B83\u4F5C\u4E3A\u4E00\u4E2A\u5B64\u7ACB\u7684\u77E5\u8BC6\u53BB\u8BB0\u5FC6\uFF0C\u4F46\u662F\u5B9E\u9645\u4E0A\uFF0C\u8FD9\u80CC\u540E\u6709\u4E00\u5957\u673A\u5236\u5728\u8FD0\u4F5C\u3002</p><p>\u8FD9\u4E00\u673A\u5236\u7684\u57FA\u7840\u6B63\u662FJavaScript\u8BED\u53E5\u6267\u884C\u7684\u5B8C\u6210\u72B6\u6001\uFF0C\u6211\u4EEC\u7528\u4E00\u4E2A\u6807\u51C6\u7C7B\u578B\u6765\u8868\u793A\uFF1ACompletion Record\uFF08\u6211\u5728\u7C7B\u578B\u4E00\u8282\u63D0\u5230\u8FC7\uFF0CCompletion Record\u7528\u4E8E\u63CF\u8FF0\u5F02\u5E38\u3001\u8DF3\u51FA\u7B49\u8BED\u53E5\u6267\u884C\u8FC7\u7A0B\uFF09\u3002</p><p>Completion Record \u8868\u793A\u4E00\u4E2A\u8BED\u53E5\u6267\u884C\u5B8C\u4E4B\u540E\u7684\u7ED3\u679C\uFF0C\u5B83\u6709\u4E09\u4E2A\u5B57\u6BB5\uFF1A</p><ul><li>[[type]] \u8868\u793A\u5B8C\u6210\u7684\u7C7B\u578B\uFF0C\u6709break continue return throw\u548Cnormal\u51E0\u79CD\u7C7B\u578B\uFF1B</li><li>[[value]] \u8868\u793A\u8BED\u53E5\u7684\u8FD4\u56DE\u503C\uFF0C\u5982\u679C\u8BED\u53E5\u6CA1\u6709\uFF0C\u5219\u662Fempty\uFF1B</li><li>[[target]] \u8868\u793A\u8BED\u53E5\u7684\u76EE\u6807\uFF0C\u901A\u5E38\u662F\u4E00\u4E2AJavaScript\u6807\u7B7E\uFF08\u6807\u7B7E\u5728\u540E\u6587\u4F1A\u6709\u4ECB\u7ECD\uFF09\u3002</li></ul><p>JavaScript\u6B63\u662F\u4F9D\u9760\u8BED\u53E5\u7684 Completion Record\u7C7B\u578B\uFF0C\u65B9\u624D\u53EF\u4EE5\u5728\u8BED\u53E5\u7684\u590D\u6742\u5D4C\u5957\u7ED3\u6784\u4E2D\uFF0C\u5B9E\u73B0\u5404\u79CD\u63A7\u5236\u3002\u63A5\u4E0B\u6765\u6211\u4EEC\u8981\u6765\u4E86\u89E3\u4E00\u4E0BJavaScript\u4F7F\u7528Completion Record\u7C7B\u578B\uFF0C\u63A7\u5236\u8BED\u53E5\u6267\u884C\u7684\u8FC7\u7A0B\u3002</p><p>\u9996\u5148\u6211\u4EEC\u6765\u770B\u770B\u8BED\u53E5\u6709\u51E0\u79CD\u5206\u7C7B\u3002</p><p><img src="https://static001.geekbang.org/resource/image/98/d5/98ce53be306344c018cddd6c083392d5.jpg" alt=""></p><h2 id="\u666E\u901A\u7684\u8BED\u53E5" tabindex="-1">\u666E\u901A\u7684\u8BED\u53E5 <a class="header-anchor" href="#\u666E\u901A\u7684\u8BED\u53E5" aria-hidden="true">#</a></h2><p>\u5728JavaScript\u4E2D\uFF0C\u6211\u4EEC\u628A\u4E0D\u5E26\u63A7\u5236\u80FD\u529B\u7684\u8BED\u53E5\u79F0\u4E3A\u666E\u901A\u8BED\u53E5\u3002\u666E\u901A\u8BED\u53E5\u6709\u4E0B\u9762\u51E0\u79CD\u3002</p><ul><li>\u58F0\u660E\u7C7B\u8BED\u53E5 <ul><li>var\u58F0\u660E</li><li>const\u58F0\u660E</li><li>let\u58F0\u660E</li><li>\u51FD\u6570\u58F0\u660E</li><li>\u7C7B\u58F0\u660E</li></ul></li><li>\u8868\u8FBE\u5F0F\u8BED\u53E5</li><li>\u7A7A\u8BED\u53E5</li><li>debugger\u8BED\u53E5</li></ul><p>\u8FD9\u4E9B\u8BED\u53E5\u5728\u6267\u884C\u65F6\uFF0C\u4ECE\u524D\u5230\u540E\u987A\u6B21\u6267\u884C\uFF08\u6211\u4EEC\u8FD9\u91CC\u5148\u5FFD\u7565var\u548C\u51FD\u6570\u58F0\u660E\u7684\u9884\u5904\u7406\u673A\u5236\uFF09\uFF0C\u6CA1\u6709\u4EFB\u4F55\u5206\u652F\u6216\u8005\u91CD\u590D\u6267\u884C\u903B\u8F91\u3002</p><p>\u666E\u901A\u8BED\u53E5\u6267\u884C\u540E\uFF0C\u4F1A\u5F97\u5230 [[type]] \u4E3A normal \u7684 Completion Record\uFF0CJavaScript\u5F15\u64CE\u9047\u5230\u8FD9\u6837\u7684Completion Record\uFF0C\u4F1A\u7EE7\u7EED\u6267\u884C\u4E0B\u4E00\u6761\u8BED\u53E5\u3002</p><p>\u8FD9\u4E9B\u8BED\u53E5\u4E2D\uFF0C\u53EA\u6709\u8868\u8FBE\u5F0F\u8BED\u53E5\u4F1A\u4EA7\u751F [[value]]\uFF0C\u5F53\u7136\uFF0C\u4ECE\u5F15\u64CE\u63A7\u5236\u7684\u89D2\u5EA6\uFF0C\u8FD9\u4E2Avalue\u5E76\u6CA1\u6709\u4EC0\u4E48\u7528\u5904\u3002</p><p>\u5982\u679C\u4F60\u7ECF\u5E38\u4F7F\u7528Chrome\u81EA\u5E26\u7684\u8C03\u8BD5\u5DE5\u5177\uFF0C\u53EF\u4EE5\u77E5\u9053\uFF0C\u8F93\u5165\u4E00\u4E2A\u8868\u8FBE\u5F0F\uFF0C\u5728\u63A7\u5236\u53F0\u53EF\u4EE5\u5F97\u5230\u7ED3\u679C\uFF0C\u4F46\u662F\u5728\u524D\u9762\u52A0\u4E0Avar\uFF0C\u5C31\u53D8\u6210\u4E86undefined\u3002</p><p><img src="https://static001.geekbang.org/resource/image/a3/67/a35801b1b82654d17e413e51b340d767.png" alt=""></p><p>Chrome\u63A7\u5236\u53F0\u663E\u793A\u7684\u6B63\u662F\u8BED\u53E5\u7684Completion Record\u7684[[value]]\u3002</p><h2 id="\u8BED\u53E5\u5757" tabindex="-1">\u8BED\u53E5\u5757 <a class="header-anchor" href="#\u8BED\u53E5\u5757" aria-hidden="true">#</a></h2><p>\u4ECB\u7ECD\u5B8C\u4E86\u666E\u901A\u8BED\u53E5\uFF0C\u6211\u4EEC\u518D\u6765\u4ECB\u7ECD\u4E00\u4E2A\u6BD4\u8F83\u7279\u6B8A\u7684\u8BED\u53E5\uFF1A\u8BED\u53E5\u5757\u3002</p><p>\u8BED\u53E5\u5757\u5C31\u662F\u62FF\u5927\u62EC\u53F7\u62EC\u8D77\u6765\u7684\u4E00\u7EC4\u8BED\u53E5\uFF0C\u5B83\u662F\u4E00\u79CD\u8BED\u53E5\u7684\u590D\u5408\u7ED3\u6784\uFF0C\u53EF\u4EE5\u5D4C\u5957\u3002</p><p>\u8BED\u53E5\u5757\u672C\u8EAB\u5E76\u4E0D\u590D\u6742\uFF0C\u6211\u4EEC\u9700\u8981\u6CE8\u610F\u7684\u662F\u8BED\u53E5\u5757\u5185\u90E8\u7684\u8BED\u53E5\u7684Completion Record\u7684[[type]] \u5982\u679C\u4E0D\u4E3A normal\uFF0C\u4F1A\u6253\u65AD\u8BED\u53E5\u5757\u540E\u7EED\u7684\u8BED\u53E5\u6267\u884C\u3002</p><p>\u6BD4\u5982\u6211\u4EEC\u8003\u8651\uFF0C\u4E00\u4E2A[[type]]\u4E3Areturn\u7684\u8BED\u53E5\uFF0C\u51FA\u73B0\u5728\u4E00\u4E2A\u8BED\u53E5\u5757\u4E2D\u7684\u60C5\u51B5\u3002</p><p>\u4ECE\u8BED\u53E5\u7684\u8FD9\u4E2Atype\u4E2D\uFF0C\u6211\u4EEC\u5927\u6982\u53EF\u4EE5\u731C\u5230\u5B83\u7531\u54EA\u4E9B\u7279\u5B9A\u8BED\u53E5\u4EA7\u751F\uFF0C\u6211\u4EEC\u5C31\u6765\u8BF4\u8BF4\u6700\u5F00\u59CB\u7684\u4F8B\u5B50\u4E2D\u7684 return\u3002</p><p>return\u8BED\u53E5\u53EF\u80FD\u4EA7\u751Freturn\u6216\u8005throw\u7C7B\u578B\u7684Completion Record\u3002\u6211\u4EEC\u6765\u770B\u4E00\u4E2A\u4F8B\u5B50\u3002</p><p>\u5148\u7ED9\u51FA\u4E00\u4E2A\u5185\u90E8\u4E3A\u666E\u901A\u8BED\u53E5\u7684\u8BED\u53E5\u5757\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  var i = 1; // normal, empty, empty</span></span>
<span class="line"><span style="color:#A6ACCD;">  i ++; // normal, 1, empty</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(i) //normal, undefined, empty</span></span>
<span class="line"><span style="color:#A6ACCD;">} // normal, undefined, empty</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5728\u6BCF\u4E00\u884C\u7684\u6CE8\u91CA\u4E2D\uFF0C\u6211\u7ED9\u51FA\u4E86\u8BED\u53E5\u7684Completion Record\u3002</p><p>\u6211\u4EEC\u770B\u5230\uFF0C\u5728\u4E00\u4E2Ablock\u4E2D\uFF0C\u5982\u679C\u6BCF\u4E00\u4E2A\u8BED\u53E5\u90FD\u662Fnormal\u7C7B\u578B\uFF0C\u90A3\u4E48\u5B83\u4F1A\u987A\u6B21\u6267\u884C\u3002\u63A5\u4E0B\u6765\u6211\u4EEC\u52A0\u5165return\u8BD5\u8BD5\u770B\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  var i = 1; // normal, empty, empty</span></span>
<span class="line"><span style="color:#A6ACCD;">  return i; // return, 1, empty</span></span>
<span class="line"><span style="color:#A6ACCD;">  i ++; </span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(i)</span></span>
<span class="line"><span style="color:#A6ACCD;">} // return, 1, empty</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4F46\u662F\u5047\u5982\u6211\u4EEC\u5728block\u4E2D\u63D2\u5165\u4E86\u4E00\u6761return\u8BED\u53E5\uFF0C\u4EA7\u751F\u4E86\u4E00\u4E2A\u975Enormal\u8BB0\u5F55\uFF0C\u90A3\u4E48\u6574\u4E2Ablock\u4F1A\u6210\u4E3A\u975Enormal\u3002\u8FD9\u4E2A\u7ED3\u6784\u5C31\u4FDD\u8BC1\u4E86\u975Enormal\u7684\u5B8C\u6210\u7C7B\u578B\u53EF\u4EE5\u7A7F\u900F\u590D\u6742\u7684\u8BED\u53E5\u5D4C\u5957\u7ED3\u6784\uFF0C\u4EA7\u751F\u63A7\u5236\u6548\u679C\u3002</p><p>\u63A5\u4E0B\u6765\u6211\u4EEC\u5C31\u5177\u4F53\u8BB2\u8BB2\u63A7\u5236\u7C7B\u8BED\u53E5\u3002</p><h2 id="\u63A7\u5236\u578B\u8BED\u53E5" tabindex="-1">\u63A7\u5236\u578B\u8BED\u53E5 <a class="header-anchor" href="#\u63A7\u5236\u578B\u8BED\u53E5" aria-hidden="true">#</a></h2><p>\u63A7\u5236\u578B\u8BED\u53E5\u5E26\u6709 if\u3001switch\u5173\u952E\u5B57\uFF0C\u5B83\u4EEC\u4F1A\u5BF9\u4E0D\u540C\u7C7B\u578B\u7684Completion Record\u4EA7\u751F\u53CD\u5E94\u3002</p><p>\u63A7\u5236\u7C7B\u8BED\u53E5\u5206\u6210\u4E24\u90E8\u5206\uFF0C\u4E00\u7C7B\u662F\u5BF9\u5176\u5185\u90E8\u9020\u6210\u5F71\u54CD\uFF0C\u5982if\u3001switch\u3001while/for\u3001try\u3002</p><p>\u53E6\u4E00\u7C7B\u662F\u5BF9\u5916\u90E8\u9020\u6210\u5F71\u54CD\u5982break\u3001continue\u3001return\u3001throw\uFF0C\u8FD9\u4E24\u7C7B\u8BED\u53E5\u7684\u914D\u5408\uFF0C\u4F1A\u4EA7\u751F\u63A7\u5236\u4EE3\u7801\u6267\u884C\u987A\u5E8F\u548C\u6267\u884C\u903B\u8F91\u7684\u6548\u679C\uFF0C\u8FD9\u4E5F\u662F\u6211\u4EEC\u7F16\u7A0B\u7684\u4E3B\u8981\u5DE5\u4F5C\u3002</p><p>\u4E00\u822C\u6765\u8BF4\uFF0C for/while - break/continue \u548C try - throw \u8FD9\u6837\u6BD4\u8F83\u7B26\u5408\u903B\u8F91\u7684\u7EC4\u5408\uFF0C\u662F\u5927\u5BB6\u6BD4\u8F83\u719F\u6089\u7684\uFF0C\u4F46\u662F\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u6211\u4EEC\u9700\u8981\u63A7\u5236\u8BED\u53E5\u8DDFbreak \u3001continue \u3001return \u3001throw\u56DB\u79CD\u7C7B\u578B\u4E0E\u63A7\u5236\u8BED\u53E5\u4E24\u4E24\u7EC4\u5408\u4EA7\u751F\u7684\u6548\u679C\u3002</p><p><img src="https://static001.geekbang.org/resource/image/77/d3/7760027d7ee09bdc8ec140efa9caf1d3.png" alt=""></p><p>\u901A\u8FC7\u8FD9\u4E2A\u8868\uFF0C\u6211\u4EEC\u4E0D\u96BE\u53D1\u73B0\u77E5\u8BC6\u7684\u76F2\u70B9\uFF0C\u4E5F\u5C31\u662F\u6211\u4EEC\u6700\u521D\u7684\u7684case\u4E2D\u7684try\u548Creturn\u7684\u7EC4\u5408\u4E86\u3002</p><p>\u56E0\u4E3Afinally\u4E2D\u7684\u5185\u5BB9\u5FC5\u987B\u4FDD\u8BC1\u6267\u884C\uFF0C\u6240\u4EE5 try/catch\u6267\u884C\u5B8C\u6BD5\uFF0C\u5373\u4F7F\u5F97\u5230\u7684\u7ED3\u679C\u662F\u975Enormal\u578B\u7684\u5B8C\u6210\u8BB0\u5F55\uFF0C\u4E5F\u5FC5\u987B\u8981\u6267\u884Cfinally\u3002</p><p>\u800C\u5F53finally\u6267\u884C\u4E5F\u5F97\u5230\u4E86\u975Enormal\u8BB0\u5F55\uFF0C\u5219\u4F1A\u4F7Ffinally\u4E2D\u7684\u8BB0\u5F55\u4F5C\u4E3A\u6574\u4E2Atry\u7ED3\u6784\u7684\u7ED3\u679C\u3002</p><h2 id="\u5E26\u6807\u7B7E\u7684\u8BED\u53E5" tabindex="-1">\u5E26\u6807\u7B7E\u7684\u8BED\u53E5 <a class="header-anchor" href="#\u5E26\u6807\u7B7E\u7684\u8BED\u53E5" aria-hidden="true">#</a></h2><p>\u524D\u6587\u6211\u91CD\u70B9\u8BB2\u4E86type\u5728\u8BED\u53E5\u63A7\u5236\u4E2D\u7684\u4F5C\u7528\uFF0C\u63A5\u4E0B\u6765\u6211\u4EEC\u91CD\u70B9\u6765\u8BB2\u4E00\u4E0B\u6700\u540E\u4E00\u4E2A\u5B57\u6BB5\uFF1Atarget\uFF0C\u8FD9\u6D89\u53CA\u4E86JavaScript\u4E2D\u7684\u4E00\u4E2A\u8BED\u6CD5\uFF0C\u5E26\u6807\u7B7E\u7684\u8BED\u53E5\u3002</p><p>\u5B9E\u9645\u4E0A\uFF0C\u4EFB\u4F55JavaScript\u8BED\u53E5\u662F\u53EF\u4EE5\u52A0\u6807\u7B7E\u7684\uFF0C\u5728\u8BED\u53E5\u524D\u52A0\u5192\u53F7\u5373\u53EF\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">    firstStatement: var i = 1;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5927\u90E8\u5206\u65F6\u5019\uFF0C\u8FD9\u4E2A\u4E1C\u897F\u7C7B\u4F3C\u4E8E\u6CE8\u91CA\uFF0C\u6CA1\u6709\u4EFB\u4F55\u7528\u5904\u3002\u552F\u4E00\u6709\u4F5C\u7528\u7684\u65F6\u5019\u662F\uFF1A\u4E0E\u5B8C\u6210\u8BB0\u5F55\u7C7B\u578B\u4E2D\u7684target\u76F8\u914D\u5408\uFF0C\u7528\u4E8E\u8DF3\u51FA\u591A\u5C42\u5FAA\u73AF\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">    outer: while(true) {</span></span>
<span class="line"><span style="color:#A6ACCD;">      inner: while(true) {</span></span>
<span class="line"><span style="color:#A6ACCD;">          break outer;</span></span>
<span class="line"><span style="color:#A6ACCD;">      }</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(&quot;finished&quot;)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>break/continue \u8BED\u53E5\u5982\u679C\u540E\u8DDF\u4E86\u5173\u952E\u5B57\uFF0C\u4F1A\u4EA7\u751F\u5E26target\u7684\u5B8C\u6210\u8BB0\u5F55\u3002\u4E00\u65E6\u5B8C\u6210\u8BB0\u5F55\u5E26\u4E86target\uFF0C\u90A3\u4E48\u53EA\u6709\u62E5\u6709\u5BF9\u5E94label\u7684\u5FAA\u73AF\u8BED\u53E5\u4F1A\u6D88\u8D39\u5B83\u3002</p><h2 id="\u7ED3\u8BED" tabindex="-1">\u7ED3\u8BED <a class="header-anchor" href="#\u7ED3\u8BED" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u4EE5Completion Record\u7C7B\u578B\u4E3A\u7EBF\u7D22\uFF0C\u4E3A\u4F60\u8BB2\u89E3\u4E86JavaScript\u8BED\u53E5\u6267\u884C\u7684\u539F\u7406\u3002</p><p>\u56E0\u4E3AJavaScript\u8BED\u53E5\u5B58\u5728\u7740\u5D4C\u5957\u5173\u7CFB\uFF0C\u6240\u4EE5\u6267\u884C\u8FC7\u7A0B\u5B9E\u9645\u4E0A\u4E3B\u8981\u5728\u4E00\u4E2A\u6811\u5F62\u7ED3\u6784\u4E0A\u8FDB\u884C\uFF0C \u6811\u5F62\u7ED3\u6784\u7684\u6BCF\u4E00\u4E2A\u8282\u70B9\u6267\u884C\u540E\u4EA7\u751FCompletion Record\uFF0C\u6839\u636E\u8BED\u53E5\u7684\u7ED3\u6784\u548CCompletion Record\uFF0CJavaScript\u5B9E\u73B0\u4E86\u5404\u79CD\u5206\u652F\u548C\u8DF3\u51FA\u903B\u8F91\u3002</p><p>\u4F60\u9047\u5230\u54EA\u4E9B\u8BED\u53E5\u4E2D\u7684\u6267\u884C\u7684\u5B9E\u9645\u6548\u679C\uFF0C\u662F\u8DDF\u4F60\u60F3\u8C61\u7684\u6709\u6240\u51FA\u5165\u5462\uFF0C\u4F60\u53EF\u4EE5\u7ED9\u6211\u7559\u8A00\uFF0C\u6211\u4EEC\u4E00\u8D77\u8BA8\u8BBA\u3002</p>`,67),t=[p];function r(o,i,c,C,d,y){return s(),a("div",null,t)}const h=n(e,[["render",r]]);export{A as __pageData,h as default};
