import{_ as e,c as i,o as a,d as t}from"./app.e5528619.js";const f=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","slug":"title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","link":"#title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","children":[]},{"level":2,"title":"\u57FA\u672C\u6982\u5FF5","slug":"\u57FA\u672C\u6982\u5FF5","link":"#\u57FA\u672C\u6982\u5FF5","children":[]},{"level":2,"title":"\u6B63\u5E38\u6D41\u6587\u5B57\u6392\u7248","slug":"\u6B63\u5E38\u6D41\u6587\u5B57\u6392\u7248","link":"#\u6B63\u5E38\u6D41\u6587\u5B57\u6392\u7248","children":[]},{"level":2,"title":"\u6B63\u5E38\u6D41\u4E2D\u7684\u76D2","slug":"\u6B63\u5E38\u6D41\u4E2D\u7684\u76D2","link":"#\u6B63\u5E38\u6D41\u4E2D\u7684\u76D2","children":[]},{"level":2,"title":"\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20","slug":"\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20","link":"#\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20","children":[]},{"level":2,"title":"\u6D6E\u52A8\u5143\u7D20\u6392\u7248","slug":"\u6D6E\u52A8\u5143\u7D20\u6392\u7248","link":"#\u6D6E\u52A8\u5143\u7D20\u6392\u7248","children":[]},{"level":2,"title":"\u5176\u5B83\u7684\u6392\u7248","slug":"\u5176\u5B83\u7684\u6392\u7248","link":"#\u5176\u5B83\u7684\u6392\u7248","children":[]},{"level":2,"title":"\u7ED3\u8BED","slug":"\u7ED3\u8BED","link":"#\u7ED3\u8BED","children":[]}],"relativePath":"JavaScript/\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF/\u6D4F\u89C8\u5668-\u4E00\u4E2A\u6D4F\u89C8\u5668\u662F\u5982\u4F55\u5DE5\u4F5C\u7684\uFF08\u9636\u6BB5\u56DB\uFF09.md"}'),p={name:"JavaScript/\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF/\u6D4F\u89C8\u5668-\u4E00\u4E2A\u6D4F\u89C8\u5668\u662F\u5982\u4F55\u5DE5\u4F5C\u7684\uFF08\u9636\u6BB5\u56DB\uFF09.md"},l=t('<hr><h2 id="title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF" tabindex="-1">title: \u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF <a class="header-anchor" href="#title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF" aria-hidden="true">#</a></h2><p>\u6D4F\u89C8\u5668\uFF1A\u4E00\u4E2A\u6D4F\u89C8\u5668\u662F\u5982\u4F55\u5DE5\u4F5C\u7684\uFF1F\uFF08\u9636\u6BB5\u56DB\uFF09</p><p>\u4F60\u597D\uFF0C\u6211\u662Fwinter\u3002</p><p>\u6211\u4EEC\u4E66\u63A5\u4E0A\u6587\u3002\u6D4F\u89C8\u5668\u8FDB\u884C\u5230\u8FD9\u4E00\u6B65\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u7ED9DOM\u5143\u7D20\u6DFB\u52A0\u4E86\u7528\u4E8E\u5C55\u73B0\u7684CSS\u5C5E\u6027\uFF0C\u63A5\u4E0B\u6765\uFF0C\u6D4F\u89C8\u5668\u7684\u5DE5\u4F5C\u5C31\u662F\u786E\u5B9A\u6BCF\u4E00\u4E2A\u5143\u7D20\u7684\u4F4D\u7F6E\u4E86\u3002\u6211\u4EEC\u7684\u57FA\u672C\u539F\u5219\u4ECD\u7136\u4E0D\u53D8\uFF0C\u5C31\u662F\u5C3D\u53EF\u80FD\u6D41\u5F0F\u5730\u5904\u7406\u4E0A\u4E00\u6B65\u9AA4\u7684\u8F93\u51FA\u3002</p><p>\u5728\u6784\u5EFADOM\u6811\u548C\u8BA1\u7B97CSS\u5C5E\u6027\u8FD9\u4E24\u4E2A\u6B65\u9AA4\uFF0C\u6211\u4EEC\u7684\u4EA7\u51FA\u90FD\u662F\u4E00\u4E2A\u4E00\u4E2A\u7684\u5143\u7D20\uFF0C\u4F46\u662F\u5728\u6392\u7248\u8FD9\u4E2A\u6B65\u9AA4\u4E2D\uFF0C\u6709\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u5C31\u6CA1\u6CD5\u505A\u5230\u8FD9\u6837\u4E86\u3002</p><p>\u5C24\u5176\u662F\u8868\u683C\u76F8\u5173\u6392\u7248\u3001Flex\u6392\u7248\u548CGrid\u6392\u7248\uFF0C\u5B83\u4EEC\u6709\u4E00\u4E2A\u663E\u8457\u7684\u7279\u70B9\uFF0C\u90A3\u5C31\u662F\u5B50\u5143\u7D20\u4E4B\u95F4\u5177\u6709\u5173\u8054\u6027\u3002</p><h2 id="\u57FA\u672C\u6982\u5FF5" tabindex="-1">\u57FA\u672C\u6982\u5FF5 <a class="header-anchor" href="#\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a></h2><p>\u9996\u5148\u6211\u4EEC\u5148\u6765\u4ECB\u7ECD\u4E00\u4E9B\u57FA\u672C\u6982\u5FF5\uFF0C\u4F7F\u4F60\u53EF\u4EE5\u611F\u6027\u5730\u8BA4\u8BC6\u4E00\u4E0B\u6211\u4EEC\u5E73\u5E38\u8BF4\u7684\u5404\u79CD\u672F\u8BED\u3002</p><p>**\u201C\u6392\u7248\u201D**\u8FD9\u4E2A\u6982\u5FF5\u6700\u521D\u6765\u81EA\u6D3B\u5B57\u5370\u5237\uFF0C\u662F\u6307\u6211\u4EEC\u628A\u4E00\u4E2A\u4E00\u4E2A\u7684\u94C5\u5B57\u6839\u636E\u6587\u7AE0\u987A\u5E8F\uFF0C\u653E\u5165\u677F\u6846\u5F53\u4E2D\u7684\u6B65\u9AA4\uFF0C\u6392\u7248\u7684\u610F\u601D\u662F\u786E\u5B9A\u6BCF\u4E00\u4E2A\u5B57\u7684\u4F4D\u7F6E\u3002</p><p>\u5728\u73B0\u4EE3\u6D4F\u89C8\u5668\u4E2D\uFF0C\u4ECD\u7136\u501F\u7528\u4E86\u8FD9\u4E2A\u6982\u5FF5\uFF0C\u4F46\u662F\u6392\u7248\u7684\u5185\u5BB9\u66F4\u52A0\u590D\u6742\uFF0C\u5305\u62EC\u6587\u5B57\u3001\u56FE\u7247\u3001\u56FE\u5F62\u3001\u8868\u683C\u7B49\u7B49\uFF0C\u6211\u4EEC\u628A\u6D4F\u89C8\u5668\u786E\u5B9A\u5B83\u4EEC\u4F4D\u7F6E\u7684\u8FC7\u7A0B\uFF0C\u53EB\u4F5C\u6392\u7248\u3002</p><p>\u6D4F\u89C8\u5668\u6700\u57FA\u672C\u7684\u6392\u7248\u65B9\u6848\u662F<strong>\u6B63\u5E38\u6D41\u6392\u7248</strong>\uFF0C\u5B83\u5305\u542B\u4E86\u987A\u6B21\u6392\u5E03\u548C\u6298\u884C\u7B49\u89C4\u5219\uFF0C\u8FD9\u662F\u4E00\u4E2A\u8DDF\u6211\u4EEC\u63D0\u5230\u7684\u5370\u5237\u6392\u7248\u7C7B\u4F3C\u7684\u6392\u7248\u65B9\u6848\uFF0C\u4E5F\u8DDF\u6211\u4EEC\u5E73\u65F6\u4E66\u5199\u6587\u5B57\u7684\u65B9\u5F0F\u4E00\u81F4\uFF0C\u6240\u4EE5\u6211\u4EEC\u628A\u5B83\u53EB\u505A\u6B63\u5E38\u6D41\u3002</p><p>\u6D4F\u89C8\u5668\u7684\u6587\u5B57\u6392\u7248\u9075\u5FAA\u516C\u8BA4\u7684\u6587\u5B57\u6392\u7248\u89C4\u8303\uFF0C\u6587\u5B57\u6392\u7248\u662F\u4E00\u4E2A\u590D\u6742\u7684\u7CFB\u7EDF\uFF0C\u5B83\u89C4\u5B9A\u4E86\u884C\u6A21\u578B\u548C\u6587\u5B57\u5728\u884C\u6A21\u578B\u4E2D\u7684\u6392\u5E03\u3002\u884C\u6A21\u578B\u89C4\u5B9A\u4E86\u884C\u9876\u3001\u884C\u5E95\u3001\u6587\u5B57\u533A\u57DF\u3001\u57FA\u7EBF\u7B49\u5BF9\u9F50\u65B9\u5F0F\u3002\uFF08\u4F60\u8FD8\u8BB0\u5F97\u5C0F\u65F6\u5019\u5199\u82F1\u8BED\u7684\u82F1\u8BED\u672C\u5417\uFF1F\u82F1\u8BED\u672C\u4E0A\u7684\u56DB\u6761\u7EBF\u5C31\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u884C\u6A21\u578B\u3002\uFF09</p><p>\u6B64\u5916\uFF0C\u6D4F\u89C8\u5668\u652F\u6301\u4E0D\u540C\u8BED\u8A00\uFF0C\u56E0\u4E3A\u4E0D\u540C\u8BED\u8A00\u7684\u4E66\u5199\u987A\u5E8F\u4E0D\u4E00\u81F4\uFF0C\u6240\u4EE5\u6D4F\u89C8\u5668\u7684\u6587\u672C\u6392\u7248\u8FD8\u652F\u6301\u53CC\u5411\u6587\u5B57\u7CFB\u7EDF\u3002</p><p>\u6D4F\u89C8\u5668\u53C8\u53EF\u4EE5\u652F\u6301\u5143\u7D20\u548C\u6587\u5B57\u7684\u6DF7\u6392\uFF0C\u5143\u7D20\u88AB\u5B9A\u4E49\u4E3A\u5360\u636E\u957F\u65B9\u5F62\u7684\u533A\u57DF\uFF0C\u8FD8\u5141\u8BB8\u8FB9\u6846\u3001\u8FB9\u8DDD\u548C\u7559\u767D\uFF0C\u8FD9\u4E2A\u5C31\u662F\u6240\u8C13\u7684<strong>\u76D2\u6A21\u578B</strong>\u3002</p><p>\u5728\u6B63\u5E38\u6D41\u7684\u57FA\u7840\u4E0A\uFF0C\u6D4F\u89C8\u5668\u8FD8\u652F\u6301\u4E24\u7C7B\u5143\u7D20\uFF1A\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20\u548C\u6D6E\u52A8\u5143\u7D20\u3002</p><ul><li><p>\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20\u628A\u81EA\u8EAB\u4ECE\u6B63\u5E38\u6D41\u62BD\u51FA\uFF0C\u76F4\u63A5\u7531top\u548Cleft\u7B49\u5C5E\u6027\u786E\u5B9A\u81EA\u8EAB\u7684\u4F4D\u7F6E\uFF0C\u4E0D\u53C2\u52A0\u6392\u7248\u8BA1\u7B97\uFF0C\u4E5F\u4E0D\u5F71\u54CD\u5176\u5B83\u5143\u7D20\u3002\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20\u7531position\u5C5E\u6027\u63A7\u5236\u3002</p></li><li><p>\u6D6E\u52A8\u5143\u7D20\u5219\u662F\u4F7F\u5F97\u81EA\u5DF1\u5728\u6B63\u5E38\u6D41\u7684\u4F4D\u7F6E\u5411\u5DE6\u6216\u8005\u5411\u53F3\u79FB\u52A8\u5230\u8FB9\u754C\uFF0C\u5E76\u4E14\u5360\u636E\u4E00\u5757\u6392\u7248\u7A7A\u95F4\u3002\u6D6E\u52A8\u5143\u7D20\u7531float\u5C5E\u6027\u63A7\u5236\u3002</p></li></ul><p>\u9664\u4E86\u6B63\u5E38\u6D41\uFF0C\u6D4F\u89C8\u5668\u8FD8\u652F\u6301\u5176\u5B83\u6392\u7248\u65B9\u5F0F\uFF0C\u6BD4\u5982\u73B0\u5728\u975E\u5E38\u5E38\u7528\u7684Flex\u6392\u7248\uFF0C\u8FD9\u4E9B\u6392\u7248\u65B9\u5F0F\u7531\u5916\u90E8\u5143\u7D20\u7684display\u5C5E\u6027\u6765\u63A7\u5236\uFF08\u6CE8\u610F\uFF0Cdisplay\u540C\u65F6\u8FD8\u63A7\u5236\u5143\u7D20\u5728\u6B63\u5E38\u6D41\u4E2D\u5C5E\u4E8Einline\u7B49\u7EA7\u8FD8\u662Fblock\u7B49\u7EA7\uFF09\u3002</p><h2 id="\u6B63\u5E38\u6D41\u6587\u5B57\u6392\u7248" tabindex="-1">\u6B63\u5E38\u6D41\u6587\u5B57\u6392\u7248 <a class="header-anchor" href="#\u6B63\u5E38\u6D41\u6587\u5B57\u6392\u7248" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u4F1A\u5728CSS\u90E8\u5206\u8BE6\u7EC6\u4ECB\u7ECD\u6B63\u5E38\u6D41\u6392\u7248\u7684\u884C\u4E3A\uFF0C\u6211\u4EEC\u8FD9\u91CC\u4E3B\u8981\u4ECB\u7ECD\u6D4F\u89C8\u5668\u4E2D\u7684\u6B63\u5E38\u6D41\u3002\u6B63\u5E38\u6D41\u662F\u552F\u4E00\u4E00\u4E2A\u6587\u5B57\u548C\u76D2\u6DF7\u6392\u7684\u6392\u7248\u65B9\u5F0F\uFF0C\u6211\u4EEC\u5148\u4ECE\u6587\u5B57\u6765\u8BB2\u8D77\u3002</p><p>\u8981\u60F3\u7406\u89E3\u6B63\u5E38\u6D41\uFF0C\u6211\u4EEC\u9996\u5148\u8981\u56DE\u5FC6\u4E00\u4E0B\u81EA\u5DF1\u5982\u4F55\u5728\u7EB8\u4E0A\u5199\u6587\u7AE0\u3002</p><p>\u9996\u5148\uFF0C\u7EB8\u662F\u6709\u56FA\u5B9A\u5BBD\u5EA6\u7684\uFF0C\u867D\u7136\u7EB8\u6709\u56FA\u5B9A\u9AD8\u5EA6\uFF0C\u4F46\u662F\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u4E0B\u4E00\u9875\u7EB8\u7684\u65B9\u5F0F\u6765\u63A5\u7EED\uFF0C\u56E0\u6B64\u6211\u4EEC\u4E0D\u5B58\u5728\u5199\u4E0D\u4E0B\u7684\u573A\u666F\u3002</p><p>\u6211\u4EEC\u4E66\u5199\u6587\u5B57\u7684\u65F6\u5019\uFF0C\u662F\u4ECE\u5DE6\u5230\u53F3\u4F9D\u6B21\u4E66\u5199\uFF0C\u6BCF\u4E00\u4E2A\u5B57\u8DDF\u4E0A\u4E00\u4E2A\u5B57\u90FD\u4E0D\u91CD\u53E0\uFF0C\u6587\u5B57\u4E4B\u95F4\u6709\u4E00\u5B9A\u95F4\u8DDD\uFF0C\u5F53\u5199\u6EE1\u4E00\u884C\u65F6\uFF0C\u6211\u4EEC\u6362\u5230\u4E0B\u4E00\u884C\u53BB\u7EE7\u7EED\u5199\u3002</p><p>\u4E66\u5199\u4E2D\u6587\u65F6\uFF0C\u6587\u5B57\u7684\u4E0A\u3001\u4E0B\u3001\u4E2D\u8F74\u7EBF\u90FD\u5BF9\u9F50\uFF0C\u4E66\u5199\u82F1\u6587\u65F6\uFF0C\u4E0D\u540C\u5B57\u6BCD\u7684\u9AD8\u5EA6\u4E0D\u540C\uFF0C\u4F46\u662F\u6709\u4E00\u6761\u57FA\u7EBF\u5BF9\u9F50\u3002</p><p>\u5B9E\u9645\u4E0A\u6D4F\u89C8\u5668\u73AF\u5883\u4E5F\u5F88\u7C7B\u4F3C\u3002\u4F46\u662F\u56E0\u4E3A\u6D4F\u89C8\u5668\u652F\u6301\u6539\u53D8\u6392\u7248\u65B9\u5411\uFF0C\u4E0D\u4E00\u5B9A\u662F\u4ECE\u5DE6\u5230\u53F3\u4ECE\u4E0A\u5230\u4E0B\uFF0C\u6240\u4EE5\u6211\u4EEC\u628A\u6587\u5B57\u4F9D\u6B21\u4E66\u5199\u7684\u5EF6\u4F38\u65B9\u5411\u79F0\u4E3A\u4E3B\u8F74\u6216\u8005\u4E3B\u65B9\u5411\uFF0C\u6362\u884C\u5EF6\u4F38\u7684\u65B9\u5411\uFF0C\u8DDF\u4E3B\u8F74\u5782\u76F4\u4EA4\u53C9\uFF0C\u79F0\u4E3A\u4EA4\u53C9\u8F74\u6216\u8005\u4EA4\u53C9\u65B9\u5411\u3002</p><p>\u6211\u4EEC\u4E00\u822C\u4F1A\u4ECE\u67D0\u4E2A\u5B57\u4F53\u6587\u4EF6\u4E2D\u83B7\u53D6\u67D0\u4E2A\u7279\u5B9A\u6587\u5B57\u7684\u76F8\u5173\u4FE1\u606F\u3002\u6211\u4EEC\u83B7\u53D6\u5230\u7684\u4FE1\u606F\u5927\u6982\u7C7B\u4F3C\u4E0B\u9762\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/06/01/0619d38f00d539f7b6773e541ce6fa01.png" alt=""></p><p>\u7EB5\u5411\u7248\u672C\uFF1A</p><p><img src="https://static001.geekbang.org/resource/image/c3/96/c361c7ff3a11216c139ed462b9d5f196.png" alt=""></p><p>\u8FD9\u4E24\u5F20\u56FE\u7247\u6765\u81EA\u8457\u540D\u5F00\u6E90\u5B57\u4F53\u89E3\u6790\u5E93freetype\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u5404\u4E2A\u5E93\u5BF9\u5B57\u4F53\u7684\u7406\u89E3\u5927\u540C\u5C0F\u5F02\uFF0C\u6211\u4EEC\u6CE8\u610F\u4E00\u4E0B\uFF0Cadvance\u4EE3\u8868\u6BCF\u4E00\u4E2A\u6587\u5B57\u6392\u5E03\u540E\u5728\u4E3B\u8F74\u4E0A\u7684\u524D\u8FDB\u8DDD\u79BB\uFF0C\u5B83\u8DDF\u6587\u5B57\u7684\u5BBD/\u9AD8\u4E0D\u76F8\u7B49\uFF0C\u662F\u5B57\u4F53\u4E2D\u6700\u91CD\u8981\u7684\u5C5E\u6027\u3002</p><p>\u9664\u4E86\u5B57\u4F53\u63D0\u4F9B\u7684\u5B57\u5F62\u672C\u8EAB\u5305\u542B\u7684\u4FE1\u606F\uFF0C\u6587\u5B57\u6392\u7248\u8FD8\u53D7\u5230\u4E00\u4E9BCSS\u5C5E\u6027\u5F71\u54CD\uFF0C\u5982line-height\u3001letter-spacing\u3001word-spacing\u7B49\u3002</p><p>\u5728\u6B63\u5E38\u6D41\u7684\u6587\u5B57\u6392\u7248\u4E2D\uFF0C\u591A\u6570\u5143\u7D20\u88AB\u5F53\u4F5C\u957F\u65B9\u5F62\u76D2\u6765\u6392\u7248\uFF0C\u800C\u53EA\u6709display\u4E3Ainline\u7684\u5143\u7D20\uFF0C\u662F\u88AB\u62C6\u6210\u6587\u672C\u6765\u6392\u7248\u7684\uFF08\u8FD8\u6709\u4E00\u79CDrun-in\u5143\u7D20\uFF0C\u5B83\u6709\u65F6\u4F5C\u4E3A\u76D2\uFF0C\u6709\u65F6\u4F5C\u4E3A\u6587\u5B57\uFF0C\u4E0D\u592A\u5E38\u7528\uFF0C\u8FD9\u91CC\u4E0D\u8BE6\u7EC6\u8BB2\u4E86\uFF09\u3002</p><p>display\u503C\u4E3Ainline\u7684\u5143\u7D20\u4E2D\u7684\u6587\u5B57\u6392\u7248\u65F6\u4F1A\u88AB\u76F4\u63A5\u6392\u5165\u6587\u5B57\u6D41\u4E2D\uFF0Cinline\u5143\u7D20\u4E3B\u8F74\u65B9\u5411\u7684margin\u5C5E\u6027\u548Cborder\u5C5E\u6027\uFF08\u4F8B\u5982\u4E3B\u8F74\u4E3A\u6A2A\u5411\u65F6\u7684margin-left\u548Cmargin-right\uFF09\u4E5F\u4F1A\u88AB\u8BA1\u7B97\u8FDB\u6392\u7248\u524D\u8FDB\u8DDD\u79BB\u5F53\u4E2D\u3002</p><p>\u6CE8\u610F\uFF0C\u5F53\u6CA1\u6709\u5F3A\u5236\u6307\u5B9A\u6587\u5B57\u4E66\u5199\u65B9\u5411\u65F6\uFF0C\u5728\u5DE6\u5230\u53F3\u6587\u5B57\u4E2D\u63D2\u5165\u53F3\u5230\u5DE6\u5411\u6587\u5B57\uFF0C\u4F1A\u5F62\u6210\u4E00\u4E2A\u53CC\u5411\u6587\u5B57\u76D2\uFF0C\u53CD\u4E4B\u4EA6\u7136\u3002</p><p>\u8FD9\u6837\uFF0C\u5373\u4F7F\u6CA1\u6709\u5143\u7D20\u5305\u88F9\uFF0C\u6DF7\u5408\u4E66\u5199\u65B9\u5411\u7684\u6587\u5B57\u4E5F\u53EF\u4EE5\u5F62\u6210\u4E00\u4E2A\u76D2\u7ED3\u6784\uFF0C\u6211\u4EEC\u5728\u6392\u7248\u65F6\uFF0C\u9047\u5230\u8FD9\u6837\u7684\u53CC\u5411\u6587\u5B57\u76D2\uFF0C\u4F1A\u5148\u6392\u5B8C\u76D2\u5185\u518D\u6392\u76D2\u5916\u3002</p><h2 id="\u6B63\u5E38\u6D41\u4E2D\u7684\u76D2" tabindex="-1">\u6B63\u5E38\u6D41\u4E2D\u7684\u76D2 <a class="header-anchor" href="#\u6B63\u5E38\u6D41\u4E2D\u7684\u76D2" aria-hidden="true">#</a></h2><p>\u5728\u6B63\u5E38\u6D41\u4E2D\uFF0Cdisplay\u4E0D\u4E3Ainline\u7684\u5143\u7D20\u6216\u8005\u4F2A\u5143\u7D20\uFF0C\u4F1A\u4EE5\u76D2\u7684\u5F62\u5F0F\u8DDF\u6587\u5B57\u4E00\u8D77\u6392\u7248\u3002\u591A\u6570display\u5C5E\u6027\u90FD\u53EF\u4EE5\u5206\u6210\u4E24\u90E8\u5206\uFF1A\u5185\u90E8\u7684\u6392\u7248\u548C\u662F\u5426inline\uFF0C\u5E26\u6709inline-\u524D\u7F00\u7684\u76D2\uFF0C\u88AB\u79F0\u4F5C\u884C\u5185\u7EA7\u76D2\u3002</p><p>\u6839\u636E\u76D2\u6A21\u578B\uFF0C\u4E00\u4E2A\u76D2\u5177\u6709margin\u3001border\u3001padding\u3001width/height\u7B49\u5C5E\u6027\uFF0C\u5B83\u5728\u4E3B\u8F74\u65B9\u5411\u5360\u636E\u7684\u7A7A\u95F4\u662F\u7531\u5BF9\u5E94\u65B9\u5411\u7684\u8FD9\u51E0\u4E2A\u5C5E\u6027\u4E4B\u548C\u51B3\u5B9A\u7684\uFF0C\u800Cvertical-align\u5C5E\u6027\u51B3\u5B9A\u4E86\u76D2\u5728\u4EA4\u53C9\u8F74\u65B9\u5411\u7684\u4F4D\u7F6E\uFF0C\u540C\u65F6\u4E5F\u4F1A\u5F71\u54CD\u5B9E\u9645\u884C\u9AD8\u3002</p><p>\u6240\u4EE5\uFF0C\u6D4F\u89C8\u5668\u5BF9\u884C\u7684\u6392\u7248\uFF0C\u4E00\u822C\u662F\u5148\u884C\u5185\u5E03\u5C40\uFF0C\u518D\u786E\u5B9A\u884C\u7684\u4F4D\u7F6E\uFF0C\u6839\u636E\u884C\u7684\u4F4D\u7F6E\u8BA1\u7B97\u51FA\u884C\u5185\u76D2\u548C\u6587\u5B57\u7684\u6392\u7248\u4F4D\u7F6E\u3002</p><p>\u5757\u7EA7\u76D2\u6BD4\u8F83\u7B80\u5355\uFF0C\u5B83\u603B\u662F\u5355\u72EC\u5360\u636E\u4E00\u6574\u884C\uFF0C\u8BA1\u7B97\u51FA\u4EA4\u53C9\u8F74\u65B9\u5411\u7684\u9AD8\u5EA6\u5373\u53EF\u3002</p><h2 id="\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20" tabindex="-1">\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20 <a class="header-anchor" href="#\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20" aria-hidden="true">#</a></h2><p>position\u5C5E\u6027\u4E3Aabsolute\u7684\u5143\u7D20\uFF0C\u6211\u4EEC\u9700\u8981\u6839\u636E\u5B83\u7684\u5305\u542B\u5757\u6765\u786E\u5B9A\u4F4D\u7F6E\uFF0C\u8FD9\u662F\u5B8C\u5168\u8DDF\u6B63\u5E38\u6D41\u65E0\u5173\u7684\u4E00\u79CD\u72EC\u7ACB\u6392\u7248\u6A21\u5F0F\uFF0C\u9010\u5C42\u627E\u5230\u5176\u7236\u7EA7\u7684position\u975Estatic\u5143\u7D20\u5373\u53EF\u3002</p><h2 id="\u6D6E\u52A8\u5143\u7D20\u6392\u7248" tabindex="-1">\u6D6E\u52A8\u5143\u7D20\u6392\u7248 <a class="header-anchor" href="#\u6D6E\u52A8\u5143\u7D20\u6392\u7248" aria-hidden="true">#</a></h2><p>float\u5143\u7D20\u975E\u5E38\u7279\u522B\uFF0C\u6D4F\u89C8\u5668\u5BF9float\u7684\u5904\u7406\u662F\u5148\u6392\u5165\u6B63\u5E38\u6D41\uFF0C\u518D\u79FB\u52A8\u5230\u6392\u7248\u5BBD\u5EA6\u7684\u6700\u5DE6/\u6700\u53F3\uFF08\u8FD9\u91CC\u5B9E\u9645\u4E0A\u662F\u4E3B\u8F74\u7684\u6700\u524D\u548C\u6700\u540E\uFF09\u3002</p><p>\u79FB\u52A8\u4E4B\u540E\uFF0Cfloat\u5143\u7D20\u5360\u636E\u4E86\u4E00\u5757\u6392\u7248\u7684\u7A7A\u95F4\uFF0C\u56E0\u6B64\uFF0C\u5728\u6570\u884C\u4E4B\u5185\uFF0C\u4E3B\u8F74\u65B9\u5411\u7684\u6392\u7248\u8DDD\u79BB\u53D1\u751F\u4E86\u53D8\u5316\uFF0C\u76F4\u5230\u4EA4\u53C9\u8F74\u65B9\u5411\u7684\u5C3A\u5BF8\u8D85\u8FC7\u4E86\u6D6E\u52A8\u5143\u7D20\u7684\u4EA4\u53C9\u8F74\u5C3A\u5BF8\u8303\u56F4\uFF0C\u4E3B\u8F74\u6392\u7248\u5C3A\u5BF8\u624D\u4F1A\u6062\u590D\u3002float\u5143\u7D20\u6392\u5E03\u5B8C\u6210\u540E\uFF0Cfloat\u5143\u7D20\u6240\u5728\u7684\u884C\u9700\u8981\u91CD\u65B0\u786E\u5B9A\u4F4D\u7F6E\u3002</p><h2 id="\u5176\u5B83\u7684\u6392\u7248" tabindex="-1">\u5176\u5B83\u7684\u6392\u7248 <a class="header-anchor" href="#\u5176\u5B83\u7684\u6392\u7248" aria-hidden="true">#</a></h2><p>CSS\u7684\u6BCF\u4E00\u79CD\u6392\u7248\u90FD\u6709\u4E00\u4E2A\u5F88\u590D\u6742\u7684\u89C4\u5B9A\uFF0C\u5B9E\u9645\u5B9E\u73B0\u5F62\u5F0F\u4E5F\u5404\u4E0D\u76F8\u540C\u3002\u6BD4\u5982\u5982Flex\u6392\u7248\uFF0C\u652F\u6301\u4E86flex\u5C5E\u6027\uFF0Cflex\u5C5E\u6027\u5C06\u6BCF\u4E00\u884C\u6392\u7248\u540E\u7684\u5269\u4F59\u7A7A\u95F4\u5E73\u5747\u5206\u914D\u7ED9\u4E3B\u8F74\u65B9\u5411\u7684width/height\u5C5E\u6027\u3002\u6D4F\u89C8\u5668\u652F\u6301\u7684\u6BCF\u4E00\u79CD\u6392\u7248\u65B9\u5F0F\uFF0C\u90FD\u662F\u6309\u7167\u5BF9\u5E94\u7684\u6807\u51C6\u6765\u5B9E\u73B0\u7684\u3002</p><h2 id="\u7ED3\u8BED" tabindex="-1">\u7ED3\u8BED <a class="header-anchor" href="#\u7ED3\u8BED" aria-hidden="true">#</a></h2><p>\u8FD9\u4E00\u90E8\u5206\uFF0C\u6211\u4EEC\u4EE5\u6B63\u5E38\u6D41\u4E3A\u4E3B\uFF0C\u4ECB\u7ECD\u4E86\u6D4F\u89C8\u5668\u7684\u6392\u7248\u57FA\u672C\u6982\u5FF5\u53CA\u4E00\u4E9B\u7B97\u6CD5\u3002\u8FD9\u91CC\uFF0C\u6211\u4E3B\u8981\u4ECB\u7ECD\u4E86\u6B63\u5E38\u6D41\u4E2D\u7684\u6587\u5B57\u6392\u7248\u3001\u6B63\u5E38\u6D41\u4E2D\u7684\u76D2\u3001\u7EDD\u5BF9\u5B9A\u4F4D\u5143\u7D20\u3001\u6D6E\u52A8\u5143\u7D20\u6392\u7248\u8FD9\u51E0\u79CD\u60C5\u51B5\u3002\u6700\u540E\uFF0C\u6211\u8FD8\u7B80\u5355\u4ECB\u7ECD\u4E86\u4E00\u4E0BFlex\u6392\u7248\u3002\u8FD9\u5C5E\u4E8E\u8FDB\u9636\u7248\u7684\u6392\u7248\u65B9\u5F0F\u4E86\uFF0C\u4F60\u53EF\u4EE5\u4E86\u89E3\u4E00\u4E0B\u3002</p><p>\u4F60\u5E73\u65F6\u559C\u6B22\u4F7F\u7528\u65B9\u5F0F\u6392\u7248\u5462\uFF0C\u6B22\u8FCE\u7559\u8A00\u544A\u8BC9\u6211\u3002</p>',50),r=[l];function n(d,h,s,c,o,_){return a(),i("div",null,r)}const u=e(p,[["render",n]]);export{f as __pageData,u as default};
