import{_ as e,c as a,o as n,d as l}from"./app.e0e4ea3a.js";const m=JSON.parse('{"title":"\u731C\u4F60\u559C\u6B22","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","slug":"title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","link":"#title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF","children":[]},{"level":2,"title":"DOM API\u4ECB\u7ECD","slug":"dom-api\u4ECB\u7ECD","link":"#dom-api\u4ECB\u7ECD","children":[{"level":3,"title":"\u8282\u70B9","slug":"\u8282\u70B9","link":"#\u8282\u70B9","children":[]},{"level":3,"title":"Node","slug":"node","link":"#node","children":[]}]},{"level":2,"title":"Element \u4E0E Attribute","slug":"element-\u4E0E-attribute","link":"#element-\u4E0E-attribute","children":[{"level":3,"title":"\u67E5\u627E\u5143\u7D20","slug":"\u67E5\u627E\u5143\u7D20","link":"#\u67E5\u627E\u5143\u7D20","children":[]}]},{"level":2,"title":"\u904D\u5386","slug":"\u904D\u5386","link":"#\u904D\u5386","children":[]},{"level":2,"title":"Range","slug":"range","link":"#range","children":[]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[{"level":3,"title":"\u8865\u5145\u9605\u8BFB\uFF1A\u547D\u540D\u7A7A\u95F4","slug":"\u8865\u5145\u9605\u8BFB\uFF1A\u547D\u540D\u7A7A\u95F4","link":"#\u8865\u5145\u9605\u8BFB\uFF1A\u547D\u540D\u7A7A\u95F4","children":[]}]}],"relativePath":"JavaScript/\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF/\u6D4F\u89C8\u5668DOM-\u4F60\u77E5\u9053HTML\u7684\u8282\u70B9\u6709\u54EA\u51E0\u79CD\u5417.md"}'),s={name:"JavaScript/\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF/\u6D4F\u89C8\u5668DOM-\u4F60\u77E5\u9053HTML\u7684\u8282\u70B9\u6709\u54EA\u51E0\u79CD\u5417.md"},t=l(`<hr><h2 id="title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF" tabindex="-1">title: \u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF <a class="header-anchor" href="#title-\u7A0B\u52AD\u975E\uFF08winter\uFF09-\u91CD\u5B66\u524D\u7AEF" aria-hidden="true">#</a></h2><p>\u6D4F\u89C8\u5668DOM\uFF1A\u4F60\u77E5\u9053HTML\u7684\u8282\u70B9\u6709\u54EA\u51E0\u79CD\u5417\uFF1F</p><p>\u4F60\u597D\uFF0C\u6211\u662Fwinter\u3002</p><p>\u4ECA\u5929\u6211\u4EEC\u8FDB\u5165\u6D4F\u89C8\u5668API\u7684\u5B66\u4E60, \u8FD9\u4E00\u8282\u8BFE\uFF0C\u6211\u4EEC\u6765\u5B66\u4E60\u4E00\u4E0BDOM API\u3002</p><p>DOM API\u662F\u6700\u65E9\u88AB\u8BBE\u8BA1\u51FA\u6765\u7684\u4E00\u6279API\uFF0C\u4E5F\u662F\u7528\u9014\u6700\u5E7F\u7684API\uFF0C\u6240\u4EE5\u65E9\u5E74\u7684\u6280\u672F\u793E\u533A\uFF0C\u5E38\u5E38\u7528DOM\u6765\u6CDB\u6307\u6D4F\u89C8\u5668\u4E2D\u6240\u6709\u7684API\u3002\u4E0D\u8FC7\u4ECA\u5929\u8FD9\u91CC\u6211\u4EEC\u8981\u4ECB\u7ECD\u7684DOM\uFF0C\u6307\u7684\u5C31\u662F\u72ED\u4E49\u7684\u6587\u6863\u5BF9\u8C61\u6A21\u578B\u3002</p><h2 id="dom-api\u4ECB\u7ECD" tabindex="-1">DOM API\u4ECB\u7ECD <a class="header-anchor" href="#dom-api\u4ECB\u7ECD" aria-hidden="true">#</a></h2><p>\u9996\u5148\u6211\u4EEC\u5148\u6765\u8BB2\u4E00\u8BB2\u4EC0\u4E48\u53EB\u505A\u6587\u6863\u5BF9\u8C61\u6A21\u578B\u3002</p><p>\u987E\u540D\u601D\u4E49\uFF0C\u6587\u6863\u5BF9\u8C61\u6A21\u578B\u662F\u7528\u6765\u63CF\u8FF0\u6587\u6863\uFF0C\u8FD9\u91CC\u7684\u6587\u6863\uFF0C\u662F\u7279\u6307HTML\u6587\u6863\uFF08\u4E5F\u7528\u4E8EXML\u6587\u6863\uFF0C\u4F46\u662F\u672C\u8BFE\u4E0D\u8BA8\u8BBAXML\uFF09\u3002\u540C\u65F6\u5B83\u53C8\u662F\u4E00\u4E2A\u201C\u5BF9\u8C61\u6A21\u578B\u201D\uFF0C\u8FD9\u610F\u5473\u7740\u5B83\u4F7F\u7528\u7684\u662F\u5BF9\u8C61\u8FD9\u6837\u7684\u6982\u5FF5\u6765\u63CF\u8FF0HTML\u6587\u6863\u3002</p><p>\u8BF4\u8D77HTML\u6587\u6863\uFF0C\u8FD9\u662F\u5927\u5BB6\u6700\u719F\u6089\u7684\u4E1C\u897F\u4E86\uFF0C\u6211\u4EEC\u90FD\u77E5\u9053\uFF0CHTML\u6587\u6863\u662F\u4E00\u4E2A\u7531\u6807\u7B7E\u5D4C\u5957\u800C\u6210\u7684\u6811\u5F62\u7ED3\u6784\uFF0C\u56E0\u6B64\uFF0CDOM\u4E5F\u662F\u4F7F\u7528\u6811\u5F62\u7684\u5BF9\u8C61\u6A21\u578B\u6765\u63CF\u8FF0\u4E00\u4E2AHTML\u6587\u6863\u3002</p><p>DOM API\u5927\u81F4\u4F1A\u5305\u542B4\u4E2A\u90E8\u5206\u3002</p><ul><li>\u8282\u70B9\uFF1ADOM\u6811\u5F62\u7ED3\u6784\u4E2D\u7684\u8282\u70B9\u76F8\u5173API\u3002</li><li>\u4E8B\u4EF6\uFF1A\u89E6\u53D1\u548C\u76D1\u542C\u4E8B\u4EF6\u76F8\u5173API\u3002</li><li>Range\uFF1A\u64CD\u4F5C\u6587\u5B57\u8303\u56F4\u76F8\u5173API\u3002</li><li>\u904D\u5386\uFF1A\u904D\u5386DOM\u9700\u8981\u7684API\u3002</li></ul><p>\u4E8B\u4EF6\u76F8\u5173API\u548C\u4E8B\u4EF6\u6A21\u578B\uFF0C\u6211\u4EEC\u4F1A\u7528\u5355\u72EC\u7684\u8BFE\u7A0B\u8BB2\u89E3\uFF0C\u6240\u4EE5\u6211\u4EEC\u672C\u7BC7\u6587\u7AE0\u91CD\u70B9\u4F1A\u4E3A\u4F60\u4ECB\u7ECD\u8282\u70B9\u548C\u904D\u5386\u76F8\u5173API\u3002</p><p>DOM API \u6570\u91CF\u5F88\u591A\uFF0C\u6211\u5E0C\u671B\u7ED9\u4F60\u63D0\u4F9B\u4E00\u4E2A\u7406\u89E3DOM API\u8BBE\u8BA1\u7684\u601D\u8DEF\uFF0C\u907F\u514D\u5355\u9760\u673A\u68B0\u7684\u65B9\u5F0F\u53BB\u6B7B\u8BB0\u786C\u80CC\u3002</p><h3 id="\u8282\u70B9" tabindex="-1">\u8282\u70B9 <a class="header-anchor" href="#\u8282\u70B9" aria-hidden="true">#</a></h3><p>DOM\u7684\u6811\u5F62\u7ED3\u6784\u6240\u6709\u7684\u8282\u70B9\u6709\u7EDF\u4E00\u7684\u63A5\u53E3Node\uFF0C\u6211\u4EEC\u6309\u7167\u7EE7\u627F\u5173\u7CFB\uFF0C\u7ED9\u4F60\u4ECB\u7ECD\u4E00\u4E0B\u8282\u70B9\u7684\u7C7B\u578B\u3002</p><p><img src="https://static001.geekbang.org/resource/image/6e/f6/6e278e450d8cc7122da3616fd18b9cf6.png" alt=""></p><p>\u5728\u8FD9\u4E9B\u8282\u70B9\u4E2D\uFF0C\u9664\u4E86Document\u548CDocumentFrangment\uFF0C\u90FD\u6709\u4E0E\u4E4B\u5BF9\u5E94\u7684HTML\u5199\u6CD5\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u4E00\u4E0B\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">Element: &lt;tagname&gt;...&lt;/tagname&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">Text: text</span></span>
<span class="line"><span style="color:#A6ACCD;">Comment: &lt;!-- comments --&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">DocumentType: &lt;!Doctype html&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">ProcessingInstruction: &lt;?a 1?&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6211\u4EEC\u5728\u7F16\u5199HTML\u4EE3\u7801\u5E76\u4E14\u8FD0\u884C\u540E\uFF0C\u5C31\u4F1A\u5728\u5185\u5B58\u4E2D\u5F97\u5230\u8FD9\u6837\u4E00\u68F5DOM\u6811\uFF0CHTML\u7684\u5199\u6CD5\u4F1A\u88AB\u8F6C\u5316\u6210\u5BF9\u5E94\u7684\u6587\u6863\u6A21\u578B\uFF0C\u800C\u6211\u4EEC\u5219\u53EF\u4EE5\u901A\u8FC7JavaScript\u7B49\u8BED\u8A00\u53BB\u8BBF\u95EE\u8FD9\u4E2A\u6587\u6863\u6A21\u578B\u3002</p><p>\u8FD9\u91CC\u6211\u4EEC\u6BCF\u5929\u90FD\u9700\u8981\u7528\u5230\uFF0C\u8981\u91CD\u70B9\u638C\u63E1\u7684\u662F\uFF1ADocument\u3001Element\u3001Text\u8282\u70B9\u3002</p><p>DocumentFragment\u4E5F\u975E\u5E38\u6709\u7528\uFF0C\u5B83\u5E38\u5E38\u88AB\u7528\u6765\u9AD8\u6027\u80FD\u5730\u6279\u91CF\u6DFB\u52A0\u8282\u70B9\u3002\u56E0\u4E3AComment\u3001DocumentType\u548CProcessingInstruction\u5F88\u5C11\u9700\u8981\u8FD0\u884C\u65F6\u53BB\u4FEE\u6539\u548C\u64CD\u4F5C\uFF0C\u6240\u4EE5\u6709\u6240\u4E86\u89E3\u5373\u53EF\u3002</p><h3 id="node" tabindex="-1">Node <a class="header-anchor" href="#node" aria-hidden="true">#</a></h3><p>Node\u662FDOM\u6811\u7EE7\u627F\u5173\u7CFB\u7684\u6839\u8282\u70B9\uFF0C\u5B83\u5B9A\u4E49\u4E86DOM\u8282\u70B9\u5728DOM\u6811\u4E0A\u7684\u64CD\u4F5C\uFF0C\u9996\u5148\uFF0CNode\u63D0\u4F9B\u4E86\u4E00\u7EC4\u5C5E\u6027\uFF0C\u6765\u8868\u793A\u5B83\u5728DOM\u6811\u4E2D\u7684\u5173\u7CFB\uFF0C\u5B83\u4EEC\u662F\uFF1A</p><ul><li>parentNode</li><li>childNodes</li><li>firstChild</li><li>lastChild</li><li>nextSibling</li><li>previousSibling</li></ul><p>\u4ECE\u547D\u540D\u4E0A\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5F88\u6E05\u6670\u5730\u770B\u51FA\uFF0C\u8FD9\u4E00\u7EC4\u5C5E\u6027\u63D0\u4F9B\u4E86\u524D\u3001\u540E\u3001\u7236\u3001\u5B50\u5173\u7CFB\uFF0C\u6709\u4E86\u8FD9\u51E0\u4E2A\u5C5E\u6027\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5F88\u65B9\u4FBF\u5730\u6839\u636E\u76F8\u5BF9\u4F4D\u7F6E\u83B7\u53D6\u5143\u7D20\u3002\u5F53\u7136\uFF0CNode\u4E2D\u4E5F\u63D0\u4F9B\u4E86\u64CD\u4F5CDOM\u6811\u7684API\uFF0C\u4E3B\u8981\u6709\u4E0B\u9762\u51E0\u79CD\u3002</p><ul><li>appendChild</li><li>insertBefore</li><li>removeChild</li><li>replaceChild</li></ul><p>\u8FD9\u4E2A\u547D\u540D\u8DDF\u4E0A\u9762\u4E00\u6837\uFF0C\u6211\u4EEC\u57FA\u672C\u53EF\u4EE5\u77E5\u9053API\u7684\u4F5C\u7528\u3002\u8FD9\u51E0\u4E2AAPI\u7684\u8BBE\u8BA1\u53EF\u4EE5\u8BF4\u662F\u9971\u53D7\u8BDF\u75C5\u3002\u5176\u4E2D\u6700\u4E3B\u8981\u7684\u6279\u8BC4\u662F\u5B83\u4E0D\u5BF9\u79F0\u2014\u2014\u53EA\u6709before\uFF0C\u6CA1\u6709after\uFF0C\u800CjQuery\u7B49\u6846\u67B6\u90FD\u5BF9\u5176\u505A\u4E86\u8865\u5145\u3002</p><p>\u5B9E\u9645\u4E0A\uFF0CappendChild\u548CinsertBefore\u7684\u8FD9\u4E2A\u8BBE\u8BA1\uFF0C\u662F\u4E00\u4E2A\u201C\u6700\u5C0F\u539F\u5219\u201D\u7684\u8BBE\u8BA1\uFF0C\u8FD9\u4E24\u4E2AAPI\u662F\u6EE1\u8DB3\u63D2\u5165\u4EFB\u610F\u4F4D\u7F6E\u7684\u5FC5\u8981API\uFF0C\u800CinsertAfter\uFF0C\u5219\u53EF\u4EE5\u7531\u8FD9\u4E24\u4E2AAPI\u5B9E\u73B0\u51FA\u6765\u3002</p><p>\u6211\u4E2A\u4EBA\u5176\u5B9E\u4E0D\u592A\u559C\u6B22\u8FD9\u4E2A\u8BBE\u8BA1\uFF0C\u5BF9\u6211\u800C\u8A00\uFF0CinsertAt(pos) \u66F4\u7B26\u5408\u5BA1\u7F8E\u4E00\u4E9B\u3002\u5F53\u7136\uFF0C\u4E0D\u8BBA\u559C\u4E0D\u559C\u6B22\uFF0C\u8FD9\u4E2A\u6807\u51C6\u5DF2\u7ECF\u786E\u5B9A\uFF0C\u6211\u4EEC\u8FD8\u662F\u5FC5\u987B\u8981\u638C\u63E1\u5B83\u3002</p><p>\u8FD9\u91CC\u4ECE\u8BBE\u8BA1\u7684\u89D2\u5EA6\u8FD8\u60F3\u8981\u8C08\u4E00\u70B9\uFF0C\u90A3\u5C31\u662F\uFF0C\u6240\u6709\u8FD9\u51E0\u4E2A\u4FEE\u6539\u578B\u7684API\uFF0C\u5168\u90FD\u662F\u5728\u7236\u5143\u7D20\u4E0A\u64CD\u4F5C\u7684\uFF0C\u6BD4\u5982\u6211\u4EEC\u8981\u60F3\u5B9E\u73B0\u201C\u5220\u9664\u4E00\u4E2A\u5143\u7D20\u7684\u4E0A\u4E00\u4E2A\u5143\u7D20\u201D\uFF0C\u5FC5\u987B\u8981\u5148\u7528parentNode\u83B7\u53D6\u5176\u7236\u5143\u7D20\u3002</p><p>\u8FD9\u6837\u7684\u8BBE\u8BA1\u662F\u7B26\u5408\u9762\u5411\u5BF9\u8C61\u7684\u57FA\u672C\u539F\u5219\u7684\u3002\u8FD8\u8BB0\u5F97\u6211\u4EEC\u5728JavaScript\u5BF9\u8C61\u90E8\u5206\u8BB2\u7684\u5BF9\u8C61\u57FA\u672C\u7279\u5F81\u5417\uFF1F\u201C\u62E5\u6709\u54EA\u4E9B\u5B50\u5143\u7D20\u201D\u662F\u7236\u5143\u7D20\u7684\u4E00\u79CD\u72B6\u6001\uFF0C\u6240\u4EE5\u4FEE\u6539\u72B6\u6001\uFF0C\u5E94\u8BE5\u662F\u7236\u5143\u7D20\u7684\u884C\u4E3A\u3002\u8FD9\u4E2A\u8BBE\u8BA1\u6211\u8BA4\u4E3A\u662FDOM API\u4E2D\u597D\u7684\u90E8\u5206\u3002</p><p>\u5230\u6B64\u4E3A\u6B62\uFF0CNode\u63D0\u4F9B\u7684API\u5DF2\u7ECF\u53EF\u4EE5\u5F88\u65B9\u4FBF\uFF08\u5927\u6982\u5427\uFF09\u5730\u5BF9\u6811\u8FDB\u884C\u589E\u3001\u5220\u3001\u904D\u5386\u7B49\u64CD\u4F5C\u4E86\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0CNode\u8FD8\u63D0\u4F9B\u4E86\u4E00\u4E9B\u9AD8\u7EA7API\uFF0C\u6211\u4EEC\u6765\u8BA4\u8BC6\u4E00\u4E0B\u5B83\u4EEC\u3002</p><ul><li>compareDocumentPosition \u662F\u4E00\u4E2A\u7528\u4E8E\u6BD4\u8F83\u4E24\u4E2A\u8282\u70B9\u4E2D\u5173\u7CFB\u7684\u51FD\u6570\u3002</li><li>contains \u68C0\u67E5\u4E00\u4E2A\u8282\u70B9\u662F\u5426\u5305\u542B\u53E6\u4E00\u4E2A\u8282\u70B9\u7684\u51FD\u6570\u3002</li><li>isEqualNode \u68C0\u67E5\u4E24\u4E2A\u8282\u70B9\u662F\u5426\u5B8C\u5168\u76F8\u540C\u3002</li><li>isSameNode \u68C0\u67E5\u4E24\u4E2A\u8282\u70B9\u662F\u5426\u662F\u540C\u4E00\u4E2A\u8282\u70B9\uFF0C\u5B9E\u9645\u4E0A\u5728JavaScript\u4E2D\u53EF\u4EE5\u7528\u201C===\u201D\u3002</li><li>cloneNode \u590D\u5236\u4E00\u4E2A\u8282\u70B9\uFF0C\u5982\u679C\u4F20\u5165\u53C2\u6570true\uFF0C\u5219\u4F1A\u8FDE\u540C\u5B50\u5143\u7D20\u505A\u6DF1\u62F7\u8D1D\u3002</li></ul><p>DOM\u6807\u51C6\u89C4\u5B9A\u4E86\u8282\u70B9\u5FC5\u987B\u4ECE\u6587\u6863\u7684create\u65B9\u6CD5\u521B\u5EFA\u51FA\u6765\uFF0C\u4E0D\u80FD\u591F\u4F7F\u7528\u539F\u751F\u7684JavaScript\u7684new\u8FD0\u7B97\u3002\u4E8E\u662Fdocument\u5BF9\u8C61\u6709\u8FD9\u4E9B\u65B9\u6CD5\u3002</p><ul><li>createElement</li><li>createTextNode</li><li>createCDATASection</li><li>createComment</li><li>createProcessingInstruction</li><li>createDocumentFragment</li><li>createDocumentType</li></ul><p>\u4E0A\u9762\u7684\u8FD9\u4E9B\u65B9\u6CD5\u90FD\u662F\u7528\u4E8E\u521B\u5EFA\u5BF9\u5E94\u7684\u8282\u70B9\u7C7B\u578B\u3002\u4F60\u53EF\u4EE5\u81EA\u5DF1\u5C1D\u8BD5\u4E00\u4E0B\u3002</p><h2 id="element-\u4E0E-attribute" tabindex="-1">Element \u4E0E Attribute <a class="header-anchor" href="#element-\u4E0E-attribute" aria-hidden="true">#</a></h2><p>Node\u63D0\u4F9B\u4E86\u6811\u5F62\u7ED3\u6784\u4E0A\u8282\u70B9\u76F8\u5173\u7684\u64CD\u4F5C\u3002\u800C\u5927\u90E8\u5206\u65F6\u5019\uFF0C\u6211\u4EEC\u6BD4\u8F83\u5173\u6CE8\u7684\u662F\u5143\u7D20\u3002Element\u8868\u793A\u5143\u7D20\uFF0C\u5B83\u662FNode\u7684\u5B50\u7C7B\u3002</p><p>\u5143\u7D20\u5BF9\u5E94\u4E86HTML\u4E2D\u7684\u6807\u7B7E\uFF0C\u5B83\u65E2\u6709\u5B50\u8282\u70B9\uFF0C\u53C8\u6709\u5C5E\u6027\u3002\u6240\u4EE5Element\u5B50\u7C7B\u4E2D\uFF0C\u6709\u4E00\u7CFB\u5217\u64CD\u4F5C\u5C5E\u6027\u7684\u65B9\u6CD5\u3002</p><p>\u6211\u4EEC\u9700\u8981\u6CE8\u610F\uFF0C\u5BF9DOM\u800C\u8A00\uFF0CAttribute\u548CProperty\u662F\u5B8C\u5168\u4E0D\u540C\u7684\u542B\u4E49\uFF0C\u53EA\u6709\u7279\u6027\u573A\u666F\u4E0B\uFF0C\u4E24\u8005\u624D\u4F1A\u4E92\u76F8\u5173\u8054\uFF08\u8FD9\u91CC\u5728\u540E\u9762\u6211\u4F1A\u8BE6\u7EC6\u8BB2\u89E3\uFF0C\u4ECA\u5929\u7684\u6587\u7AE0\u91CC\u6211\u5C31\u4E0D\u5C55\u5F00\u4E86\uFF09\u3002</p><p>\u9996\u5148\uFF0C\u6211\u4EEC\u53EF\u4EE5\u628A\u5143\u7D20\u7684Attribute\u5F53\u4F5C\u5B57\u7B26\u4E32\u6765\u770B\u5F85\uFF0C\u8FD9\u6837\u5C31\u6709\u4EE5\u4E0B\u7684API\uFF1A</p><ul><li>getAttribute</li><li>setAttribute</li><li>removeAttribute</li><li>hasAttribute</li></ul><p>\u5982\u679C\u4F60\u8FFD\u6C42\u6781\u81F4\u7684\u6027\u80FD\uFF0C\u8FD8\u53EF\u4EE5\u628AAttribute\u5F53\u4F5C\u8282\u70B9\uFF1A</p><ul><li>getAttributeNode</li><li>setAttributeNode</li></ul><p>\u6B64\u5916\uFF0C\u5982\u679C\u4F60\u559C\u6B22property\u4E00\u6837\u7684\u8BBF\u95EEattribute\uFF0C\u8FD8\u53EF\u4EE5\u4F7F\u7528 attributes \u5BF9\u8C61\uFF0C\u6BD4\u5982 document.body.attributes.class = \u201Ca\u201D \u7B49\u6548\u4E8E document.body.setAttribute(\u201Cclass\u201D, \u201Ca\u201D)\u3002</p><h3 id="\u67E5\u627E\u5143\u7D20" tabindex="-1">\u67E5\u627E\u5143\u7D20 <a class="header-anchor" href="#\u67E5\u627E\u5143\u7D20" aria-hidden="true">#</a></h3><p>document\u8282\u70B9\u63D0\u4F9B\u4E86\u67E5\u627E\u5143\u7D20\u7684\u80FD\u529B\u3002\u6BD4\u5982\u6709\u4E0B\u9762\u7684\u51E0\u79CD\u3002</p><ul><li>querySelector</li><li>querySelectorAll</li><li>getElementById</li><li>getElementsByName</li><li>getElementsByTagName</li><li>getElementsByClassName</li></ul><p>\u6211\u4EEC\u9700\u8981\u6CE8\u610F\uFF0CgetElementById\u3001getElementsByName\u3001getElementsByTagName\u3001getElementsByClassName\uFF0C\u8FD9\u51E0\u4E2AAPI\u7684\u6027\u80FD\u9AD8\u4E8EquerySelector\u3002</p><p>\u800C getElementsByName\u3001getElementsByTagName\u3001getElementsByClassName \u83B7\u53D6\u7684\u96C6\u5408\u5E76\u975E\u6570\u7EC4\uFF0C\u800C\u662F\u4E00\u4E2A\u80FD\u591F\u52A8\u6001\u66F4\u65B0\u7684\u96C6\u5408\u3002</p><p>\u6211\u4EEC\u770B\u4E00\u4E2A\u4F8B\u5B50\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var collection = document.getElementsByClassName(&#39;winter&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">console.log(collection.length);</span></span>
<span class="line"><span style="color:#A6ACCD;">var winter = document.createElement(&#39;div&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">winter.setAttribute(&#39;class&#39;, &#39;winter&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;">document.documentElement.appendChild(winter)</span></span>
<span class="line"><span style="color:#A6ACCD;">console.log(collection.length);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5728\u8FD9\u6BB5\u4EE3\u7801\u4E2D\uFF0C\u6211\u4EEC\u5148\u83B7\u53D6\u4E86\u9875\u9762\u7684className\u4E3Awinter\u7684\u5143\u7D20\u96C6\u5408\uFF0C\u4E0D\u51FA\u610F\u5916\u7684\u8BDD\uFF0C\u5E94\u8BE5\u662F\u7A7A\u3002</p><p>\u6211\u4EEC\u901A\u8FC7console.log\u53EF\u4EE5\u770B\u5230\u96C6\u5408\u7684\u5927\u5C0F\u4E3A0\u3002\u4E4B\u540E\u6211\u4EEC\u6DFB\u52A0\u4E86\u4E00\u4E2Aclass\u4E3Awinter\u7684div\uFF0C\u8FD9\u65F6\u5019\u6211\u4EEC\u518D\u770B\u96C6\u5408\uFF0C\u53EF\u4EE5\u53D1\u73B0\uFF0C\u96C6\u5408\u4E2D\u51FA\u73B0\u4E86\u65B0\u6DFB\u52A0\u7684\u5143\u7D20\u3002</p><p>\u8FD9\u8BF4\u660E\u6D4F\u89C8\u5668\u5185\u90E8\u662F\u6709\u9AD8\u901F\u7684\u7D22\u5F15\u673A\u5236\uFF0C\u6765\u52A8\u6001\u66F4\u65B0\u8FD9\u6837\u7684\u96C6\u5408\u7684\u3002\u6240\u4EE5\uFF0C\u5C3D\u7BA1querySelector\u7CFB\u5217\u7684API\u975E\u5E38\u5F3A\u5927\uFF0C\u6211\u4EEC\u8FD8\u662F\u5E94\u8BE5\u5C3D\u91CF\u4F7F\u7528getElement\u7CFB\u5217\u7684API\u3002</p><h2 id="\u904D\u5386" tabindex="-1">\u904D\u5386 <a class="header-anchor" href="#\u904D\u5386" aria-hidden="true">#</a></h2><p>\u524D\u9762\u5DF2\u7ECF\u63D0\u5230\u8FC7\uFF0C\u901A\u8FC7Node\u7684\u76F8\u5173\u5C5E\u6027\uFF0C\u6211\u4EEC\u53EF\u4EE5\u7528JavaScript\u904D\u5386\u6574\u4E2A\u6811\u3002\u5B9E\u9645\u4E0A\uFF0CDOM API\u4E2D\u8FD8\u63D0\u4F9B\u4E86NodeIterator \u548C TreeWalker \u6765\u904D\u5386\u6811\u3002</p><p>\u6BD4\u8D77\u76F4\u63A5\u7528\u5C5E\u6027\u6765\u904D\u5386\uFF0CNodeIterator \u548C TreeWalker \u63D0\u4F9B\u4E86\u8FC7\u6EE4\u529F\u80FD\uFF0C\u8FD8\u53EF\u4EE5\u628A\u5C5E\u6027\u8282\u70B9\u4E5F\u5305\u542B\u5728\u904D\u5386\u4E4B\u5185\u3002</p><p>NodeIterator\u7684\u57FA\u672C\u7528\u6CD5\u793A\u4F8B\u5982\u4E0B\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var iterator = document.createNodeIterator(document.body, NodeFilter.SHOW_TEXT | NodeFilter.SHOW_COMMENT, null, false);</span></span>
<span class="line"><span style="color:#A6ACCD;">var node;</span></span>
<span class="line"><span style="color:#A6ACCD;">while(node = iterator.nextNode())</span></span>
<span class="line"><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(node);</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8FD9\u4E2AAPI\u7684\u8BBE\u8BA1\u975E\u5E38\u8001\u6D3E\uFF0C\u8FD9\u4E48\u8BB2\u7684\u539F\u56E0\u4E3B\u8981\u6709\u4E24\u70B9\uFF0C\u4E00\u662F\u5FAA\u73AF\u5E76\u6CA1\u6709\u7C7B\u4F3C\u201ChasNext\u201D\u8FD9\u6837\u7684\u65B9\u6CD5\uFF0C\u800C\u662F\u76F4\u63A5\u4EE5nextNode\u8FD4\u56DEnull\u6765\u6807\u5FD7\u7ED3\u675F\uFF0C\u4E8C\u662F\u7B2C\u4E8C\u4E2A\u53C2\u6570\u662F\u63A9\u7801\uFF0C\u8FD9\u4E24\u4E2A\u8BBE\u8BA1\u90FD\u662F\u4F20\u7EDFC\u8BED\u8A00\u91CC\u6BD4\u8F83\u5E38\u89C1\u7684\u7528\u6CD5\u3002</p><p>\u653E\u5230\u4ECA\u5929\u770B\uFF0C\u8FD9\u4E2A\u8FED\u4EE3\u5668\u65E0\u6CD5\u5339\u914DJavaScript\u7684\u8FED\u4EE3\u5668\u8BED\u6CD5\uFF0C\u800C\u4E14JavaScript\u4F4D\u8FD0\u7B97\u5E76\u4E0D\u9AD8\u6548\uFF0C\u63A9\u7801\u7684\u8BBE\u8BA1\u5C31\u5F92\u589E\u590D\u6742\u6027\u4E86\u3002</p><p>\u8FD9\u91CC\u8BF7\u4F60\u6CE8\u610F\u4E00\u4E0B\u8FD9\u4E2A\u4F8B\u5B50\u4E2D\u7684\u5904\u7406\u65B9\u6CD5\uFF0C\u901A\u5E38\u63A9\u7801\u578B\u53C2\u6570\uFF0C\u6211\u4EEC\u90FD\u662F\u7528\u6309\u4F4D\u6216\u8FD0\u7B97\u6765\u53E0\u52A0\u3002\u800C\u9488\u5BF9\u8FD9\u79CD\u8FD4\u56DEnull\u8868\u793A\u7ED3\u675F\u7684\u8FED\u4EE3\u5668\uFF0C\u6211\u4F7F\u7528\u4E86\u5728while\u5FAA\u73AF\u6761\u4EF6\u4E2D\u8D4B\u503C\uFF0C\u6765\u4FDD\u8BC1\u5FAA\u73AF\u6B21\u6570\u548C\u8C03\u7528next\u6B21\u6570\u4E25\u683C\u4E00\u81F4\uFF08\u4F46\u8FD9\u6837\u5199\u53EF\u80FD\u8FDD\u53CD\u4E86\u67D0\u4E9B\u7F16\u7801\u89C4\u8303\uFF09\u3002</p><p>\u6211\u4EEC\u518D\u6765\u770B\u4E00\u4E0BTreeWalker\u7684\u7528\u6CD5\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var walker = document.createTreeWalker(document.body, NodeFilter.SHOW_ELEMENT, null, false)</span></span>
<span class="line"><span style="color:#A6ACCD;">var node;</span></span>
<span class="line"><span style="color:#A6ACCD;">while(node = walker.nextNode())</span></span>
<span class="line"><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">    if(node.tagName === &quot;p&quot;)</span></span>
<span class="line"><span style="color:#A6ACCD;">        node.nextSibling();</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(node);</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6BD4\u8D77NodeIterator\uFF0CTreeWalker\u591A\u4E86\u5728DOM\u6811\u4E0A\u81EA\u7531\u79FB\u52A8\u5F53\u524D\u8282\u70B9\u7684\u80FD\u529B\uFF0C\u4E00\u822C\u6765\u8BF4\uFF0C\u8FD9\u79CDAPI\u7528\u4E8E\u201C\u8DF3\u8FC7\u201D\u67D0\u4E9B\u8282\u70B9\uFF0C\u6216\u8005\u91CD\u590D\u904D\u5386\u67D0\u4E9B\u8282\u70B9\u3002</p><p>\u603B\u7684\u6765\u8BF4\uFF0C\u6211\u4E2A\u4EBA\u4E0D\u592A\u559C\u6B22TreeWalker\u548CNodeIterator\u8FD9\u4E24\u4E2AAPI\uFF0C\u5EFA\u8BAE\u9700\u8981\u904D\u5386DOM\u7684\u65F6\u5019\uFF0C\u76F4\u63A5\u4F7F\u7528\u9012\u5F52\u548CNode\u7684\u5C5E\u6027\u3002</p><h2 id="range" tabindex="-1">Range <a class="header-anchor" href="#range" aria-hidden="true">#</a></h2><p>Range API \u662F\u4E00\u4E2A\u6BD4\u8F83\u4E13\u4E1A\u7684\u9886\u57DF\uFF0C\u5982\u679C\u4E0D\u505A\u5BCC\u6587\u672C\u7F16\u8F91\u7C7B\u7684\u4E1A\u52A1\uFF0C\u4E0D\u9700\u8981\u592A\u6DF1\u5165\u3002\u8FD9\u91CC\u6211\u4EEC\u5C31\u4EC5\u4ECB\u7ECD\u6982\u5FF5\u548C\u7ED9\u51FA\u57FA\u672C\u7528\u6CD5\u7684\u793A\u4F8B\uFF0C\u4F60\u53EA\u8981\u638C\u63E1\u5373\u53EF\u3002</p><p>Range API \u8868\u793A\u4E00\u4E2AHTML\u4E0A\u7684\u8303\u56F4\uFF0C\u8FD9\u4E2A\u8303\u56F4\u662F\u4EE5\u6587\u5B57\u4E3A\u6700\u5C0F\u5355\u4F4D\u7684\uFF0C\u6240\u4EE5Range\u4E0D\u4E00\u5B9A\u5305\u542B\u5B8C\u6574\u7684\u8282\u70B9\uFF0C\u5B83\u53EF\u80FD\u662FText\u8282\u70B9\u4E2D\u7684\u4E00\u6BB5\uFF0C\u4E5F\u53EF\u4EE5\u662F\u5934\u5C3E\u4E24\u4E2AText\u7684\u4E00\u90E8\u5206\u52A0\u4E0A\u4E2D\u95F4\u7684\u5143\u7D20\u3002</p><p>\u6211\u4EEC\u901A\u8FC7 Range API \u53EF\u4EE5\u6BD4\u8282\u70B9 API \u66F4\u7CBE\u786E\u5730\u64CD\u4F5C DOM \u6811\uFF0C\u51E1\u662F \u8282\u70B9 API \u80FD\u505A\u5230\u7684\uFF0CRange API\u90FD\u53EF\u4EE5\u505A\u5230\uFF0C\u800C\u4E14\u53EF\u4EE5\u505A\u5230\u66F4\u9AD8\u6027\u80FD\uFF0C\u4F46\u662F Range API \u4F7F\u7528\u8D77\u6765\u6BD4\u8F83\u9EBB\u70E6\uFF0C\u6240\u4EE5\u5728\u5B9E\u9645\u9879\u76EE\u4E2D\uFF0C\u5E76\u4E0D\u5E38\u7528\uFF0C\u53EA\u6709\u505A\u5E95\u5C42\u6846\u67B6\u548C\u5BCC\u6587\u672C\u7F16\u8F91\u5BF9\u5B83\u6709\u5F3A\u9700\u6C42\u3002</p><p>\u521B\u5EFARange\u4E00\u822C\u662F\u901A\u8FC7\u8BBE\u7F6E\u5B83\u7684\u8D77\u6B62\u6765\u5B9E\u73B0\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u4E00\u4E2A\u4F8B\u5B50\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var range = new Range(),</span></span>
<span class="line"><span style="color:#A6ACCD;">    firstText = p.childNodes[1],</span></span>
<span class="line"><span style="color:#A6ACCD;">    secondText = em.firstChild</span></span>
<span class="line"><span style="color:#A6ACCD;">range.setStart(firstText, 9) // do not forget the leading space</span></span>
<span class="line"><span style="color:#A6ACCD;">range.setEnd(secondText, 4)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6B64\u5916\uFF0C\u901A\u8FC7 Range \u4E5F\u53EF\u4EE5\u4ECE\u7528\u6237\u9009\u4E2D\u533A\u57DF\u521B\u5EFA\uFF0C\u8FD9\u6837\u7684Range\u7528\u4E8E\u5904\u7406\u7528\u6237\u9009\u4E2D\u533A\u57DF:</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var range = document.getSelection().getRangeAt(0);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u66F4\u6539 Range \u9009\u4E2D\u533A\u6BB5\u5185\u5BB9\u7684\u65B9\u5F0F\u4E3B\u8981\u662F\u53D6\u51FA\u548C\u63D2\u5165\uFF0C\u5206\u522B\u7531extractContents\u548CinsertNode\u6765\u5B9E\u73B0\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var fragment = range.extractContents()</span></span>
<span class="line"><span style="color:#A6ACCD;">range.insertNode(document.createTextNode(&quot;aaaa&quot;))</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6700\u540E\u6211\u4EEC\u770B\u4E00\u4E2A\u5B8C\u6574\u7684\u4F8B\u5B50\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var range = new Range(),</span></span>
<span class="line"><span style="color:#A6ACCD;">    firstText = p.childNodes[1],</span></span>
<span class="line"><span style="color:#A6ACCD;">    secondText = em.firstChild</span></span>
<span class="line"><span style="color:#A6ACCD;">range.setStart(firstText, 9) // do not forget the leading space</span></span>
<span class="line"><span style="color:#A6ACCD;">range.setEnd(secondText, 4)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">var fragment = range.extractContents()</span></span>
<span class="line"><span style="color:#A6ACCD;">range.insertNode(document.createTextNode(&quot;aaaa&quot;))</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8FD9\u4E2A\u4F8B\u5B50\u5C55\u793A\u4E86\u5982\u4F55\u4F7F\u7528range\u6765\u53D6\u51FA\u5143\u7D20\u548C\u5728\u7279\u5B9A\u4F4D\u7F6E\u6DFB\u52A0\u65B0\u5143\u7D20\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u5728\u4ECA\u5929\u7684\u6587\u7AE0\u4E2D\uFF0C\u6211\u4EEC\u4E00\u8D77\u4E86\u89E3\u4E86DOM API\u7684\u5185\u5BB9\u3002DOM API\u5927\u81F4\u4F1A\u5305\u542B4\u4E2A\u90E8\u5206\u3002</p><ul><li>\u8282\u70B9\uFF1ADOM\u6811\u5F62\u7ED3\u6784\u4E2D\u7684\u8282\u70B9\u76F8\u5173API\u3002</li><li>\u4E8B\u4EF6\uFF1A\u89E6\u53D1\u548C\u76D1\u542C\u4E8B\u4EF6\u76F8\u5173API\u3002</li><li>Range\uFF1A\u64CD\u4F5C\u6587\u5B57\u8303\u56F4\u76F8\u5173API\u3002</li><li>\u904D\u5386\uFF1A\u904D\u5386DOM\u9700\u8981\u7684API\u3002</li></ul><p>DOM API\u4E2D\u8FD8\u63D0\u4F9B\u4E86NodeIterator \u548C TreeWalker \u6765\u904D\u5386\u6811\u3002\u6BD4\u8D77\u76F4\u63A5\u7528\u5C5E\u6027\u6765\u904D\u5386\uFF0CNodeIterator \u548C TreeWalker \u63D0\u4F9B\u4E86\u8FC7\u6EE4\u529F\u80FD\uFF0C\u8FD8\u53EF\u4EE5\u628A\u5C5E\u6027\u8282\u70B9\u4E5F\u5305\u542B\u5728\u904D\u5386\u4E4B\u5185\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u6211\u4EEC\u8FD8\u8C08\u5230\u4E86Range\u7684\u4E00\u4E9B\u57FA\u7840\u77E5\u8BC6\u70B9\uFF0C\u8FD9\u91CC\u4F60\u638C\u63E1\u5373\u53EF\u3002</p><p>\u6700\u540E\uFF0C\u6211\u7ED9\u4F60\u7559\u4E86\u4E00\u4E2A\u9898\u76EE\uFF0C\u8BF7\u4F60\u7528DOM API\u6765\u5B9E\u73B0\u904D\u5386\u6574\u4E2ADOM\u6811\uFF0C\u628A\u6240\u6709\u7684\u5143\u7D20\u7684tagName\u6253\u5370\u51FA\u6765\u3002</p><hr><h3 id="\u8865\u5145\u9605\u8BFB\uFF1A\u547D\u540D\u7A7A\u95F4" tabindex="-1">\u8865\u5145\u9605\u8BFB\uFF1A\u547D\u540D\u7A7A\u95F4 <a class="header-anchor" href="#\u8865\u5145\u9605\u8BFB\uFF1A\u547D\u540D\u7A7A\u95F4" aria-hidden="true">#</a></h3><p>\u6211\u4EEC\u672C\u8BFE\u4ECB\u7ECD\u7684\u6240\u6709API\uFF0C\u7279\u610F\u5FFD\u7565\u4E86\u547D\u540D\u7A7A\u95F4\u3002</p><p>\u5728HTML\u573A\u666F\u4E2D\uFF0C\u9700\u8981\u8003\u8651\u547D\u540D\u7A7A\u95F4\u7684\u573A\u666F\u4E0D\u591A\u3002\u6700\u4E3B\u8981\u7684\u573A\u666F\u662FSVG\u3002\u521B\u5EFA\u5143\u7D20\u548C\u5C5E\u6027\u76F8\u5173\u7684API\u90FD\u6709\u5E26\u547D\u540D\u7A7A\u95F4\u7684\u7248\u672C\uFF1A</p><ul><li>document <ul><li>createElementNS</li><li>createAttributeNS</li></ul></li><li>Element <ul><li>getAttributeNS</li><li>setAttributeNS</li><li>getAttributeNodeNS</li><li>setAttributeNodeNS</li><li>removeAttributeNS</li><li>hasAttributeNS</li><li>attributes.setNamedItemNS</li><li>attributes.getNamedItemNS</li><li>attributes.removeNamedItemNS</li></ul></li></ul><p>\u82E5\u8981\u521B\u5EFADocument\u6216\u8005Doctype\uFF0C\u4E5F\u5FC5\u987B\u8981\u8003\u8651\u547D\u540D\u7A7A\u95F4\u95EE\u9898\u3002DOM\u8981\u6C42\u4ECEdocument.implementation\u6765\u521B\u5EFA\u3002</p><ul><li>document.implementation.createDocument</li><li>document.implementation.createDocumentType</li></ul><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u8FD8\u63D0\u4F9B\u4E86\u4E00\u4E2A\u5FEB\u6377\u65B9\u5F0F\uFF0C\u4F60\u4E5F\u53EF\u4EE5\u52A8\u624B\u5C1D\u8BD5\u4E00\u4E0B\u3002</p><ul><li>document.implementation.createHTMLDocument</li></ul><h1 id="\u731C\u4F60\u559C\u6B22" tabindex="-1">\u731C\u4F60\u559C\u6B22 <a class="header-anchor" href="#\u731C\u4F60\u559C\u6B22" aria-hidden="true">#</a></h1><p><a href="https://time.geekbang.org/course/intro/163?utm_term=zeusMTA7L&amp;utm_source=app&amp;utm_medium=chongxueqianduan&amp;utm_campaign=163-presell" target="_blank" rel="noreferrer"><img src="https://static001.geekbang.org/resource/image/1a/08/1a49758821bdbdf6f0a8a1dc5bf39f08.jpg" alt="unpreview"></a></p>`,99),p=[t];function i(r,o,c,d,A,u){return n(),a("div",null,p)}const C=e(s,[["render",i]]);export{m as __pageData,C as default};
