import{_ as e,c as o,o as t,d as a}from"./app.c4f45da1.js";const _=JSON.parse('{"title":"\u8BBE\u8BA1\u7BC7-\u9700\u6C42\u5206\u6790","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u524D\u8A00","slug":"\u524D\u8A00","link":"#\u524D\u8A00","children":[]},{"level":2,"title":"\u5E94\u7528\u573A\u666F","slug":"\u5E94\u7528\u573A\u666F","link":"#\u5E94\u7528\u573A\u666F","children":[]},{"level":2,"title":"\u4EC0\u4E48\u662F\u7F51\u5173\u7CFB\u7EDF\uFF08Gateway\uFF09","slug":"\u4EC0\u4E48\u662F\u7F51\u5173\u7CFB\u7EDF\uFF08gateway\uFF09","link":"#\u4EC0\u4E48\u662F\u7F51\u5173\u7CFB\u7EDF\uFF08gateway\uFF09","children":[]},{"level":2,"title":"Gateway \u529F\u80FD\u62C6\u89E3","slug":"gateway-\u529F\u80FD\u62C6\u89E3","link":"#gateway-\u529F\u80FD\u62C6\u89E3","children":[]},{"level":2,"title":"\u6211\u4EEC\u7684\u7F51\u5173\u7CFB\u7EDF\u8BBE\u8BA1","slug":"\u6211\u4EEC\u7684\u7F51\u5173\u7CFB\u7EDF\u8BBE\u8BA1","link":"#\u6211\u4EEC\u7684\u7F51\u5173\u7CFB\u7EDF\u8BBE\u8BA1","children":[]},{"level":2,"title":"\u5199\u5728\u6700\u540E","slug":"\u5199\u5728\u6700\u540E","link":"#\u5199\u5728\u6700\u540E","children":[]}],"relativePath":"Node.js/NestJS \u9879\u76EE\u5B9E\u6218/\u8BBE\u8BA1\u7BC7-\u9700\u6C42\u5206\u6790.md"}'),i={name:"Node.js/NestJS \u9879\u76EE\u5B9E\u6218/\u8BBE\u8BA1\u7BC7-\u9700\u6C42\u5206\u6790.md"},r=a('<h1 id="\u8BBE\u8BA1\u7BC7-\u9700\u6C42\u5206\u6790" tabindex="-1">\u8BBE\u8BA1\u7BC7-\u9700\u6C42\u5206\u6790 <a class="header-anchor" href="#\u8BBE\u8BA1\u7BC7-\u9700\u6C42\u5206\u6790" aria-hidden="true">#</a></h1><hr><h2 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h2><p>\u4E00\u822C\u5E38\u89C4\u7684\u9879\u76EE\u7ACB\u9879\u4E4B\u521D\u4F1A\u6709\u4E00\u4EFD <strong>MRD</strong>\uFF08Market Requirements Document\uFF0C\u5E02\u573A\u9700\u6C42\u6587\u6863\uFF09\u7528\u6765\u5224\u65AD\u4EA7\u54C1\u7684\u5FC5\u9700\u6027\u4EE5\u53CA\u4EF7\u503C\u7B49\u3002</p><p>\u5BF9\u4E8E\u57FA\u7840\u9879\u76EE\u5F00\u53D1\u6765\u8BF4\uFF0C\u4F7F\u7528 <strong>MRD</strong> \u53EF\u80FD\u6709\u4E9B\u91CD\u91CF\u7EA7\uFF0C\u4F46\u6211\u4EEC\u4E5F\u9700\u8981\u5BF9\u4E00\u4E2A\u65B0\u7684\u57FA\u5EFA\u7C7B\u578B\u9879\u76EE\u505A\u4E00\u4E2A\u7B80\u5355\u8BC4\u4F30\uFF0C\u5305\u62EC\u7814\u53D1\u5FC5\u9700\u6027\u3001\u6295\u5165\u7684\u6210\u672C\u4EE5\u53CA\u4EA7\u751F\u7684\u4EF7\u503C\u7B49\u7B49\u3002\u6709\u4E9B\u8F6E\u5B50\u662F\u5FC5\u9020\uFF0C\u800C\u6709\u4E9B\u8F6E\u5B50\u4E0D\u662F\u3002</p><p>\u8FD9\u4E00\u7AE0\uFF0C\u6211\u4EEC\u5C31\u6765\u63A2\u8BA8\u4E00\u4E0B\uFF1A<strong>\u4F60\u7684\u56E2\u961F\u9700\u8981\u4E00\u4E2A\u7F51\u5173\u7CFB\u7EDF\u5417\uFF1F</strong></p><h2 id="\u5E94\u7528\u573A\u666F" tabindex="-1">\u5E94\u7528\u573A\u666F <a class="header-anchor" href="#\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a></h2><p>\u5BF9\u4E8E\u73B0\u5728\u4E3B\u6D41\u7684\u540E\u7AEF\u67B6\u6784\u6765\u8BF4\uFF0C\u5FAE\u670D\u52A1\u7684\u666E\u53CA\u8303\u56F4\u8FD8\u662F\u6BD4\u8F83\u5E7F\u7684\uFF0C\u6BD5\u7ADF\u5DE8\u77F3\u9879\u76EE\u7684\u7EF4\u62A4\u4E0E\u5F00\u53D1\u90FD\u4E0D\u592A\u7075\u6D3B\u3002</p><p>\u4EE5\u7535\u5546\u670D\u52A1\u4E3A\u4F8B\u5B50\u6765\u8BF4\uFF0C\u4E00\u4E2A\u7CFB\u7EDF\u53EF\u4EE5\u62C6\u5206\u6210<strong>\u7528\u6237\u3001\u4EA4\u6613\u3001\u8BA2\u5355\u3001\u5546\u54C1\u3001\u6D3B\u52A8</strong>\u7B49\u591A\u4E2A\u529F\u80FD\u6A21\u5757\uFF0C\u5982\u679C\u5168\u90E8\u7684\u529F\u80FD\u90FD\u7EF4\u62A4\u5728\u4E00\u4E2A\u9879\u76EE\u91CC\u9762\uFF0C\u67D0\u4E9B\u53EF\u4EE5\u516C\u7528\u7684\u6A21\u5757\uFF08\u4F8B\u5982<strong>\u7528\u6237\u3001\u6743\u9650</strong>\u7B49\uFF09\u5C31\u6CA1\u529E\u6CD5\u5171\u4EAB\u7ED9\u5176\u4ED6\u9879\u76EE\uFF0C\u9879\u76EE\u7684\u4F53\u79EF\u4E0E\u4EE3\u7801\u590D\u6742\u5EA6\u4E5F\u4F1A\u9010\u6B65\u4E0A\u5347\uFF0C\u5BFC\u81F4\u540E\u671F\u7EF4\u62A4\u4E0E\u534F\u540C\u7684\u6210\u672C\u4F1A\u9010\u6B65\u589E\u52A0\u3002</p><p><strong>\u4F46\u4E0A\u8FF0\u7F3A\u70B9\u90FD\u4E0D\u662F\u6700\u4E3B\u8981\u7684\u95EE\u9898\uFF0C\u6700\u4E3B\u8981\u7684\u95EE\u9898\u662F\u6240\u6709\u529F\u80FD\u90FD\u653E\u5728\u4E00\u4E2A\u7CFB\u7EDF\u91CC\u9762\u5F00\u53D1\u90E8\u7F72\uFF0C\u5176\u4E2D\u4EFB\u610F\u4E00\u4E2A\u6A21\u5757\u51FA\u73B0\u4E86\u95EE\u9898\u90FD\u53EF\u80FD\u4F1A\u5BFC\u81F4\u6574\u4E2A\u7CFB\u7EDF\u96EA\u5D29</strong>\u3002</p><p><strong>\u5BF9\u4E8E\u4E00\u4E2A\u5E94\u7528\u7684\u7A33\u5B9A\u6027\u6765\u8BF4\uFF0C\u5982\u679C\u6CA1\u529E\u6CD5\u5BF9\u5355\u4E00\u7684\u6A21\u5757\u505A\u7194\u65AD\u3001\u5347\u7EA7\u3001\u56DE\u6EDA\u7B49\u64CD\u4F5C\uFF0C\u7EBF\u4E0A\u4E0D\u53EF\u63A7\u7684\u6982\u7387\u6781\u5927\uFF0C\u8FD9\u4E5F\u662F\u76EE\u524D\u4E3B\u6D41\u91C7\u7528\u5FAE\u670D\u52A1\u67B6\u6784\u6700\u5927\u7684\u539F\u56E0\u4E4B\u4E00</strong>\u3002</p><p>\u4F46\u662F\uFF0C\u5F53\u4E00\u4E2A\u7CFB\u7EDF\u7684\u5FAE\u670D\u52A1\u6A21\u5757\u6570\u91CF\u975E\u5E38\u591A\u7684\u60C5\u51B5\u4E0B\uFF0C\u4E5F\u7ECF\u5E38\u4F1A\u51FA\u73B0\u4EE5\u4E0B\u95EE\u9898\uFF1A</p><ol><li>\u901A\u7528\u6027\u7684\u8BA4\u8BC1\u3001\u9274\u6743\u3001\u9650\u6D41\u7B49\u529F\u80FD\u4F1A\u5BFC\u81F4\u6BCF\u4E2A\u5FAE\u670D\u52A1\u90FD\u5B58\u5728\u9020\u8F6E\u5B50\u7684\u884C\u4E3A\uFF1B</li><li>\u4E1A\u52A1\u590D\u6742\u5EA6\u4E0A\u5347\u4E4B\u540E\uFF0C\u5B58\u5728\u57DF\u540D\u5206\u914D\u7684\u95EE\u9898\uFF0C\u6CA1\u529E\u6CD5\u5BF9\u6BCF\u4E2A\u670D\u52A1\u90FD\u5206\u914D\u4E00\u4E2A\u65B0\u7684\u57DF\u540D\uFF0C\u540C\u65F6\u6BCF\u4E00\u4E2A\u65B0\u7684\u670D\u52A1\u4E0A\u7EBF\uFF0C\u8FD0\u7EF4\u91CD\u590D\u914D\u7F6E\u7684\u5DE5\u4F5C\u91CF\u591A\u4E0D\u5C11\uFF1B</li><li>\u592A\u591A\u7684\u57DF\u540D\u670D\u52A1\u5BF9\u5BA2\u6237\u7AEF\u5E76\u4E0D\u53CB\u597D\uFF0C\u7279\u522B\u662F\u8BF7\u6C42\u5C42\u6CA1\u6709\u505A <a href="https://zhuanlan.zhihu.com/p/463196408" target="_blank" rel="noreferrer">BFF</a> \u7684\u8BDD\uFF0C\u6BCF\u4E00\u6B21\u62C6\u5206\u65B0\u7684\u5FAE\u670D\u52A1\u51FA\u6765\u90FD\u53EF\u80FD\u4F1A\u5F15\u8D77\u524D\u7AEF\u7684\u6539\u9020\uFF1B</li><li>\u5E76\u975E\u6BCF\u4E2A\u670D\u52A1\u90FD\u662F\u540C\u4E00\u79CD\u8BED\u8A00\u6216\u8005\u6846\u67B6\u6240\u5F00\u53D1\uFF0C\u524D\u9762\u63D0\u5230\u7684\u516C\u5171\u7684\u63D2\u4EF6\u5E76\u4E0D\u80FD\u6EE1\u8DB3\u6240\u6709\u7684\u670D\u52A1\uFF0C\u8FD9\u4E2A\u60C5\u51B5\u53EF\u80FD\u5728 <code>DevOps</code> \u7CFB\u7EDF\u4E2D\u6BD4\u8F83\u5E38\u89C1\u3002</li></ol><p>\u4E3A\u4E86\u89E3\u51B3\u4E0A\u8FF0\u7684\u95EE\u9898\uFF0C\u7F51\u5173\u7CFB\u7EDF\u968F\u4E4B\u8BDE\u751F\u3002\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u7F51\u5173\u7684\u7EDF\u4E00\u5165\u53E3\u6765\u8C03\u5EA6\u5404\u4E2A\u5FAE\u670D\u52A1\u529F\u80FD\u6A21\u5757\uFF0C\u4F7F\u5F97\u6BCF\u4E2A\u5FAE\u670D\u52A1\u53EF\u4EE5\u5173\u6CE8\u4E8E\u81EA\u8EAB\u7684\u4E1A\u52A1\u529F\u80FD\u5F00\u53D1\u3002</p><h2 id="\u4EC0\u4E48\u662F\u7F51\u5173\u7CFB\u7EDF\uFF08gateway\uFF09" tabindex="-1">\u4EC0\u4E48\u662F\u7F51\u5173\u7CFB\u7EDF\uFF08Gateway\uFF09 <a class="header-anchor" href="#\u4EC0\u4E48\u662F\u7F51\u5173\u7CFB\u7EDF\uFF08gateway\uFF09" aria-hidden="true">#</a></h2><p><strong>\u7F51\u5173\u7CFB\u7EDF\u6839\u636E\u8BF7\u6C42\u7C7B\u578B\u53EF\u4EE5\u5206\u4E3A</strong>\uFF1A</p><ol><li>\u9759\u6001\u8D44\u6E90\u7F51\u5173\uFF1A\u5904\u7406\u524D\u7AEF\u8D44\u6E90\u6570\u636E\u5305\u62EC <strong>CSR</strong>\u3001<strong>SSR</strong> \u7B49\uFF1B</li><li><strong>API</strong> \u7F51\u5173\uFF1A\u968F\u7740\u5FAE\u670D\u52A1\u67B6\u6784\uFF08<strong>MSA</strong>\uFF09\u7684\u666E\u53CA\uFF0C\u901A\u8FC7\u7EDF\u4E00\u7684 <strong>API</strong> \u7F51\u5173\u53EF\u4EE5\u805A\u5408\u6240\u6709\u96F6\u6563\u7684\u5FAE\u670D\u52A1\u8D44\u6E90\uFF0C\u4FDD\u6301\u7EDF\u4E00\u7684\u51FA\u5165\u53E3\uFF0C\u964D\u4F4E\u591A\u9879\u76EE\u7684\u63A5\u5165\u6210\u672C\u4EE5\u53CA\u5176\u4ED6\u9879\u76EE\u7684\u4F7F\u7528\u6210\u672C\u3002</li></ol><p><strong>\u4ECE\u529F\u80FD\u5C5E\u6027\u4E0A\u53EF\u4EE5\u5206\u4E3A</strong>\uFF1A</p><ol><li>\u6D41\u91CF\u7F51\u5173\uFF1A\u65E0\u5173\u4E1A\u52A1\u5C5E\u6027\uFF0C\u5355\u7EAF\u505A\u5B89\u5168\uFF08\u9ED1\u767D\u540D\u5355\uFF09\u3001\u5206\u6D41\uFF08\u8D1F\u8F7D\u5747\u8861\uFF09\u7B49\uFF1B</li><li>\u4E1A\u52A1\u7F51\u5173\uFF1A\u7528\u6237\uFF08\u8BA4\u8BC1\u3001\u9274\u6743\uFF09\u3001\u670D\u52A1\u7A33\u5B9A\u6027\uFF08\u964D\u7EA7\u3001\u5BB9\u707E\uFF09\u3001\u4E1A\u52A1\u5C5E\u6027\u7070\u5EA6\u3001\u4EE3\u7406\uFF08\u8D44\u6E90\u4EE3\u7406\u3001\u7F13\u5B58\uFF09\u3001\u7EDF\u4E00\u524D\u7F6E\uFF08\u65E5\u5FD7\u3001\u6570\u636E\u6821\u9A8C\uFF09\u7B49\u3002</li></ol><p>\u6240\u4EE5\uFF0C\u5E02\u9762\u4E0A\u5E38\u89C1\u7684\u7F51\u5173\u7CFB\u7EDF\u9664\u4E86\u63D0\u4F9B<strong>\u8BF7\u6C42\u805A\u5408\u529F\u80FD</strong>\u4E4B\u5916\u57FA\u672C\u90FD\u5305\u542B\u6240\u6709\u901A\u7528\u529F\u80FD\uFF1A</p><ul><li>\u8BA4\u8BC1\uFF08\u9A8C\u8BC1\u767B\u5F55\u6001\uFF0C\u4E00\u822C\u4E0D\u505A\u9274\u6743\uFF09</li><li>\u5206\u6D41</li><li>\u4EE3\u7406\uFF08\u9759\u6001\u8D44\u6E90\u3001<strong>API</strong> \u7B49\uFF09</li><li><strong>AB test</strong> \uFF08\u6D41\u91CF\u7070\u5EA6\uFF0C\u4E00\u822C\u6839\u636E <strong>IP</strong> \u6216\u8005\u7528\u6237\u4FE1\u606F\u7070\u5EA6\uFF09</li><li>\u7F13\u5B58\uFF08\u6210\u672C\u4E0D\u4F4E\uFF0C\u770B\u770B\u5C31\u884C\uFF09</li><li>\u7B49\u7B49</li></ul><h2 id="gateway-\u529F\u80FD\u62C6\u89E3" tabindex="-1">Gateway \u529F\u80FD\u62C6\u89E3 <a class="header-anchor" href="#gateway-\u529F\u80FD\u62C6\u89E3" aria-hidden="true">#</a></h2><p>\u901A\u8FC7\u4E0A\u9762\u5BF9\u7F51\u5173\u7CFB\u7EDF\u7684\u7B80\u5355\u4E86\u89E3\u548C\u5206\u6790\uFF0C\u6211\u4EEC\u80FD\u591F\u77E5\u9053\uFF0C\u62E5\u6709\u7F51\u5173\u7CFB\u7EDF\u5BF9\u56E2\u961F\u6280\u672F\u7684\u4EF7\u503C\u8D21\u732E\u4E0D\u5C0F\u3002\u90A3\u4E48\u5982\u4F55\u5B9E\u73B0\u4E00\u4E2A\u7F51\u5173\u7CFB\u7EDF\u5462\uFF1F\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u56E2\u961F\u60C5\u51B5\u4E0E\u9700\u6C42\uFF0C\u5BF9\u5C06\u8981\u5B9E\u73B0\u7684\u7F51\u5173\u529F\u80FD\u8FDB\u884C\u62C6\u89E3\uFF0C\u65B9\u4FBF\u540E\u671F\u4E1A\u52A1\u5F00\u53D1\u3002</p><blockquote><p>\u524D\u6587\u4E5F\u63D0\u5230\u4E86\uFF0C\u4E1A\u52A1\u7F51\u5173\u6700\u5927\u7684\u4EF7\u503C\u662F\u4E0E\u5FAE\u670D\u52A1\u67B6\u6784\u7684\u914D\u5408\uFF0C\u5982\u679C\u540E\u7AEF\u670D\u52A1\u6CA1\u6709\u4F7F\u7528\u5FAE\u670D\u52A1\u67B6\u6784\uFF0C\u7F51\u5173\u7684\u4EF7\u503C\u4F1A\u6253\u4E00\u5B9A\u7684\u6298\u6263\uFF0C\u6240\u4EE5\u5177\u4F53\u662F\u5426\u9700\u8981\u7F51\u5173\u670D\u52A1\u8FD8\u662F\u9700\u8981\u7ED3\u5408\u56E2\u961F\u7684\u67B6\u6784\u8BBE\u8BA1\u6765\u8003\u8651\u3002\u540C\u65F6\u5728\u9700\u6C42\u62C6\u89E3\u7684\u8FC7\u7A0B\u4E2D\u8981\u8003\u8651\u4FA7\u91CD\u70B9\uFF0C\u4F8B\u5982\u5F53\u524D\u53EA\u9700\u8981\u5B8C\u6210\u524D\u7AEF\u9759\u6001\u8D44\u6E90\u8F6C\u53D1\u5C31\u6CA1\u5FC5\u8981\u53BB\u5F00\u53D1\u540E\u7AEF <strong>API</strong> \u8F6C\u53D1\u7684\u903B\u8F91\uFF0C\u53EF\u4EE5\u628A\u67B6\u6784\u8BBE\u8BA1\u65B9\u6848\u505A\u5927\u4E00\u70B9\uFF0C\u540E\u9762\u6709\u9700\u6C42\u65B9\u4FBF\u62D3\u5C55\uFF0C\u4F46\u6CA1\u5FC5\u8981\u4E00\u6B21\u6027\u5168\u90E8\u505A\u5B8C\uFF0C\u4ECE\u56E2\u961F\u7684\u89D2\u5EA6\u6765\u8003\u8651\uFF0C\u5BFB\u6C42 <strong>ROI\uFF08\u6295\u8D44\u56DE\u62A5\u7387\uFF09</strong> \u7684\u6700\u5927\u5316\u3002</p></blockquote><h4 id="nginx" tabindex="-1">Nginx <a class="header-anchor" href="#nginx" aria-hidden="true">#</a></h4><p><code>Nginx</code> \u4F5C\u4E3A\u4E13\u4E1A\u7684 <code>WEB</code> \u4EE3\u7406\u670D\u52A1\u5668\uFF0C\u5728\u4EE3\u7406\u65B9\u9762\u80FD\u591F\u63D0\u4F9B<strong>\u8D1F\u8F7D\u5747\u8861\u3001\u6D41\u91CF\u5207\u6362</strong>\u7B49\u529F\u80FD\uFF0C\u811A\u672C\u8BED\u8A00\u4E5F\u6709 <code>lua</code> \u652F\u6301\u3002</p><p>\u90A3\u4E48 <code>Nginx</code> \u505A\u4E0D\u5230\u4EC0\u4E48\u5462\uFF1F</p><ol><li><code>Nginx</code> \u4F5C\u4E3A\u4E13\u4E1A\u7684\u8F6C\u53D1\u670D\u52A1\u5668\uFF0C\u5BF9 <code>Session</code> \u4EE5\u53CA <code>Cookie</code> \u7684\u5904\u7406\u6BD4\u8F83\u5F31\u3002</li><li><code>Nginx</code>\u4EC5\u4EC5\u652F\u6301 <code>HTTP</code> \u534F\u8BAE\uFF08<code>Email</code> \u4E0D\u7B97\u5E38\u7528\u529F\u80FD\uFF09\u3002</li><li>\u867D\u7136\u53EF\u4EE5\u901A\u8FC7 <code>Lua</code> \u811A\u672C\u6765\u5904\u7406\u4E00\u4E9B\u62D3\u5C55\u7684\u529F\u80FD\uFF0C\u4F46\u662F <code>Lua</code> \u811A\u672C\u7684\u53D8\u66F4\u4EE5\u53CA\u4FEE\u6539 <code>Nginx</code> \u7684\u914D\u7F6E\u90FD\u9700\u8981\u91CD\u65B0\u542F\u52A8\u65E0\u6CD5\u505A\u5230\u70ED\u66F4\u65B0\uFF0C\u6BD4\u8F83\u9EBB\u70E6\u3002</li><li>\u6CA1\u6709\u53EF\u89C6\u5316\u7BA1\u7406\u754C\u9762\u4E5F\u662F\u4E00\u4E2A\u6BD4\u8F83\u5927\u7684\u786C\u4F24\uFF08\u5F00\u6E90\u7684\u6709\u4E00\u4E9B\u53EF\u89C6\u5316\u914D\u7F6E\u9879\u76EE\uFF0C\u4F46\u8DDF\u53EF\u89C6\u5316\u7BA1\u7406\u6709\u4E00\u5B9A\u7684\u533A\u522B\u4E0E\u5DEE\u8DDD\uFF09\u3002</li></ol><h4 id="gateway" tabindex="-1">Gateway <a class="header-anchor" href="#gateway" aria-hidden="true">#</a></h4><p>\u4E1A\u52A1\u6027\u7684 <code>Gateway</code> \u9700\u8981\u505A\u70B9\u5565\uFF1A</p><ol><li>\u7EDF\u4E00\u9274\u6743\u6536\u53E3\uFF0C\u901A\u8FC7\u7EDF\u4E00\u914D\u7F6E\u7ED9\u63A5\u53E3\u8D44\u6E90\u6DFB\u52A0\u6743\u9650\uFF1B</li><li>\u652F\u6301 <code>RPC</code> \u5FAE\u670D\u52A1\u8C03\u7528\uFF0C\u51CF\u5C11\u8D44\u6E90\u6D88\u8017\uFF1B</li><li>\u7CFB\u7EDF\u6613\u4E8E\u76D1\u63A7\uFF0C\u540C\u65F6\u53EF\u4EE5\u91C7\u96C6\u6536\u53E3\u8FDB\u6765\u7684\u4FE1\u606F\u3002</li></ol><p>\u901A\u8FC7\u4E24\u8005\u7684\u5BF9\u6BD4\u53EF\u4EE5\u770B\u51FA\uFF0C<code>Nginx</code> \u66F4\u5173\u6CE8<strong>\u8D1F\u8F7D\u5747\u8861\u4EE5\u53CA\u53CD\u5411\u4EE3\u7406</strong>\uFF0C\u5BF9\u4E1A\u52A1\u90E8\u5206\u7684\u4FB5\u5165\u5F88\u4F4E\uFF0C\u800C <code>Gateway</code> \u4F5C\u4E3A\u540E\u7AEF\u5E94\u7528\uFF0C\u53EF\u4EE5\u643A\u5E26\u4E1A\u52A1\u5C5E\u6027\uFF0C\u4E24\u8005\u53EF\u4EE5\u5F88\u597D\u7684\u4E92\u8865\u3002</p><p>\u5728\u7CFB\u7EDF\u67B6\u6784\u8BBE\u8BA1\u4E0A\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528 <code>Nginx</code> \u4F5C\u4E3A\u4E0A\u6587\u6240\u8BF4\u7684\u6D41\u91CF\u7F51\u5173\uFF0C\u7531 <code>Nginx</code> \u505A\u4E00\u5C42\u6D41\u91CF\u4EE3\u7406\uFF0C\u901A\u8FC7\u8D1F\u8F7D\u5747\u8861\u5230 <code>Gateway</code> \u505A\u4E1A\u52A1\u5C42\u7684\u8F6C\u53D1\u5904\u7406\uFF0C\u8FD9\u6837\u53EF\u4EE5\u51CF\u5C11\u6211\u4EEC\u81EA\u5EFA\u7F51\u5173\u7CFB\u7EDF\u7684\u5DE5\u4F5C\u91CF\u3002</p><p><img src="https://p6-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/e15b1e4bc0b842a1affeba55594b232d~tplv-k3u1fbpfcp-watermark.image?" alt="\u7F51\u5173\u7CFB\u7EDF\u6574\u4F53\u67B6\u6784.png"></p><h2 id="\u6211\u4EEC\u7684\u7F51\u5173\u7CFB\u7EDF\u8BBE\u8BA1" tabindex="-1">\u6211\u4EEC\u7684\u7F51\u5173\u7CFB\u7EDF\u8BBE\u8BA1 <a class="header-anchor" href="#\u6211\u4EEC\u7684\u7F51\u5173\u7CFB\u7EDF\u8BBE\u8BA1" aria-hidden="true">#</a></h2><p>\u4E00\u4E2A\u5B8C\u6574\u7684\u7F51\u5173\u7CFB\u7EDF\u662F\u5927\u800C\u5168\u7684\uFF0C\u63A5\u4E0B\u6765\u6211\u4EEC\u5C06\u6311\u9009\u51E0\u4E2A\u6BD4\u8F83\u5E38\u89C1\u7684\u6A21\u5757\u6765\u5B8C\u6210\u81EA\u7814 <code>Gateway</code> \u5F00\u53D1\uFF08\u5982\u679C\u76EE\u524D\u56E2\u961F\u6B20\u7F3A\u6216\u8005\u81EA\u5DF1\u6709\u9700\u6C42\u7684\u8BDD\uFF0C\u53EF\u4EE5\u63A5\u7740\u4F7F\u7528 <code>demo</code> \u9879\u76EE\u7EE7\u7EED\u4F18\u5316\uFF0C\u62D3\u5C55\u9700\u8981\u7684\u6A21\u5757\uFF0C\u8FBE\u5230\u7406\u60F3\u53EF\u7528\u7684\u72B6\u6001\uFF09\uFF1A</p><p><img src="https://p1-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/f73f00d3e2aa4b779c6539089252c54e~tplv-k3u1fbpfcp-watermark.image?" alt="image.png"></p><p>\u7531\u4E0A\u56FE\u53EF\u4EE5\u770B\u51FA\uFF0C\u6211\u4EEC\u7684\u7F51\u5173\u7CFB\u7EDF\u67B6\u6784\u53EF\u4EE5\u5206\u4E3A\u4E24\u5927\u6A21\u5757\uFF0C\u5206\u522B\u662F<strong>\u4EE3\u7406\u8F6C\u53D1\u7684\u57FA\u7840\u6A21\u5757</strong>\u4EE5\u53CA\u72EC\u7ACB\u7684<strong>\u7528\u6237\u6A21\u5757</strong>\u3002</p><ul><li><strong>\u7F51\u5173\u57FA\u7840\u670D\u52A1</strong></li></ul><p>\u56E0\u4E3A\u6D41\u91CF\u5165\u53E3\u5DF2\u7ECF\u6709 <code>Nginx</code> \u4F5C\u8D1F\u8F7D\u5747\u8861\uFF0C\u6211\u4EEC\u7F51\u5173\u7684\u57FA\u7840\u670D\u52A1\u5C31\u53EF\u4EE5\u4E13\u6CE8\u4E8E\u4EE3\u7406\u6A21\u5757\u7684\u5F00\u53D1\uFF1A</p><ol><li>\u4E13\u6CE8\u4E8E\u524D\u540E\u7AEF\u8D44\u6E90\u5206\u53D1\u4EE5\u53CA\u4E0D\u540C\u7C7B\u578B\u7684\u9879\u76EE <strong>API</strong> \u5206\u53D1\uFF1B</li><li>\u5E38\u7528\u8D44\u6E90\u7F13\u5B58\u6A21\u5757\uFF1B</li><li><strong>AB Test</strong> \u6A21\u5757\uFF1B</li><li>\u901A\u7528\u65E5\u5FD7\u6A21\u5757\u3002</li></ol><ul><li><strong>\u7EDF\u4E00\u7528\u6237\u4E2D\u5FC3\u7CFB\u7EDF</strong></li></ul><p>\u7528\u6237\u7CFB\u7EDF\u9700\u8981\u63D0\u4F9B\u7684\u529F\u80FD\u6709\uFF1A</p><ol><li>\u7528\u6237\u767B\u5F55\u3001\u8BA4\u8BC1\u7B49\u57FA\u7840\u529F\u80FD\uFF1B</li><li>\u6743\u9650\u7CFB\u7EDF\uFF08\u57FA\u4E8E <strong>RBAC</strong> \u5305\u62EC\u89D2\u8272\u3001\u7CFB\u7EDF\u3001\u8D44\u6E90\u7B49\u6743\u9650\u63A7\u5236\uFF09\u3002</li></ol><blockquote><p>\u5982\u679C\u5F53\u524D\u56E2\u961F\u4E2D\u6CA1\u6709\u7EDF\u4E00\u7528\u6237\u4E2D\u5FC3\u7684\u8BDD\uFF0C\u5EFA\u8BAE\u5C06\u7528\u6237\u4E2D\u5FC3\u7CFB\u7EDF\u4F18\u5148\u7EA7\u63D0\u9AD8\uFF0C\u4F5C\u4E3A\u7B2C\u4E00\u4F18\u5148\u7EA7\u7684\u57FA\u5EFA\u9879\u76EE\uFF0C\u5B8C\u6210\u4E4B\u540E\u53EF\u4EE5\u8D4B\u80FD\u7ED9\u4E88\u5176\u4ED6\u540E\u7AEF\u9879\u76EE\u7528\u6237\u767B\u5F55\u3001\u9274\u6743\u7684\u529F\u80FD\uFF0C\u53EF\u4EE5\u51CF\u5C11\u5176\u4ED6\u540E\u7AEF\u57FA\u5EFA\u7684\u5F88\u591A\u91CD\u590D\u5DE5\u4F5C\u91CF\u3002</p></blockquote><ul><li><strong>\u7269\u6599\u7CFB\u7EDF</strong></li></ul><p>\u7269\u6599\u7CFB\u7EDF\u4E3B\u8981\u662F\u9488\u5BF9\u4E8E\u9759\u6001\u8D44\u6E90\u7684\u7BA1\u7406\uFF0C\u4E00\u822C\u7269\u6599\u7CFB\u7EDF\u4F1A\u8DDF <strong>DevOps</strong> \u4F53\u7CFB\u5173\u8054\u6BD4\u8F83\u5927\uFF0C\u6BD5\u7ADF\u7269\u6599\u4F1A\u6D89\u53CA\u6784\u5EFA\u90E8\u7F72\u7684\u8FC7\u7A0B\uFF0C\u4F46\u6211\u4EEC\u7684\u4E3B\u9898\u5E76\u4E0D\u662F <strong>DevOps</strong>\uFF0C\u6240\u4EE5\u7269\u6599\u7CFB\u7EDF\u5728\u5C0F\u518C\u7684\u5360\u6BD4\u4E0D\u4F1A\u5F88\u9AD8\uFF0C\u53EA\u662F\u4F5C\u4E3A\u4E00\u4E2A\u8F85\u52A9\u7C7B\u578B\u7684\u9879\u76EE\u4E3A\u7F51\u5173\u670D\u52A1\u63D0\u4F9B\u9759\u6001\u8D44\u6E90\u8DEF\u7531\u7684\u914D\u7F6E\u3001\u8D44\u6E90\u7248\u672C\u7684\u7BA1\u7406\u7B49\u529F\u80FD\u3002</p><h2 id="\u5199\u5728\u6700\u540E" tabindex="-1">\u5199\u5728\u6700\u540E <a class="header-anchor" href="#\u5199\u5728\u6700\u540E" aria-hidden="true">#</a></h2><p>\u672C\u7AE0\u4E3B\u8981\u9488\u5BF9\u7F51\u5173\u7CFB\u7EDF\u7684\u5FC5\u8981\u6027\u505A\u4E86\u7B80\u5355\u5206\u6790\uFF0C\u4ECB\u7ECD\u4E86\u7F51\u5173\u7CFB\u7EDF\u5E94\u7528\u7684\u573A\u666F\u4EE5\u53CA\u7F51\u5173\u7684\u7C7B\u578B\u3001\u4F5C\u7528\u7B49\uFF0C\u6700\u540E\u9488\u5BF9\u6211\u4EEC\u8981\u505A\u7684\u7CFB\u7EDF\u8FDB\u884C\u67B6\u6784\u8BBE\u8BA1\u4E0E\u529F\u80FD\u62C6\u89E3\u3002</p><p>\u6309\u7167\u4E00\u4E2A\u5B8C\u6574\u7684\u9879\u76EE\u8FED\u4EE3\u6765\u8BF4\uFF0C\u5728\u67B6\u6784\u8BBE\u8BA1\u4E0E\u9700\u6C42\u6A21\u5757\u90FD\u6572\u5B9A\u4E4B\u540E\uFF0C\u63A5\u4E0B\u6765\u5C31\u9700\u8981\u5F00\u53D1\u540C\u5B66\u51FA\u6280\u672F\u65B9\u6848\u8FDB\u884C\u9879\u76EE\u5F00\u53D1\uFF0C\u6240\u4EE5\u4E0B\u4E00\u7AE0\u6211\u4EEC\u5C06\u5BF9\u6280\u672F\u65B9\u9762\u7684\u5185\u5BB9\u8FDB\u884C\u8BBE\u8BA1\u4E0E\u89C4\u5212\u3002</p><p>\u5982\u679C\u4F60\u6709\u4EC0\u4E48\u7591\u95EE\uFF0C\u6B22\u8FCE\u5728\u8BC4\u8BBA\u533A\u63D0\u51FA\u6216\u8005\u52A0\u7FA4\u6C9F\u901A\u3002 \u{1F44F}</p>',51),l=[r];function n(s,d,c,g,p,h){return t(),o("div",null,l)}const f=e(i,[["render",n]]);export{_ as __pageData,f as default};
