import{_ as s,c as n,o as a,d as p}from"./app.c4f45da1.js";const d=JSON.parse('{"title":"\u5B9E\u6218\u7BC7 1-\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316 \u2014\u2014 \u4F7F\u7528 hapi","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u57FA\u7840\u9879\u76EE\u5DE5\u7A0B\u642D\u5EFA","slug":"\u57FA\u7840\u9879\u76EE\u5DE5\u7A0B\u642D\u5EFA","link":"#\u57FA\u7840\u9879\u76EE\u5DE5\u7A0B\u642D\u5EFA","children":[]},{"level":2,"title":"\u7ED3\u6784\u5316\u9879\u76EE\u5DE5\u7A0B\u91CD\u6784","slug":"\u7ED3\u6784\u5316\u9879\u76EE\u5DE5\u7A0B\u91CD\u6784","link":"#\u7ED3\u6784\u5316\u9879\u76EE\u5DE5\u7A0B\u91CD\u6784","children":[{"level":3,"title":"\u9879\u76EE\u5DE5\u7A0B\u5316\u8BBE\u8BA1\u539F\u5219","slug":"\u9879\u76EE\u5DE5\u7A0B\u5316\u8BBE\u8BA1\u539F\u5219","link":"#\u9879\u76EE\u5DE5\u7A0B\u5316\u8BBE\u8BA1\u539F\u5219","children":[]},{"level":3,"title":"\u91CD\u6784\u6B65\u9AA4","slug":"\u91CD\u6784\u6B65\u9AA4","link":"#\u91CD\u6784\u6B65\u9AA4","children":[]},{"level":3,"title":"\u73AF\u5883\u914D\u7F6E","slug":"\u73AF\u5883\u914D\u7F6E","link":"#\u73AF\u5883\u914D\u7F6E","children":[]}]},{"level":2,"title":"\u5C0F\u7ED3","slug":"\u5C0F\u7ED3","link":"#\u5C0F\u7ED3","children":[]}],"relativePath":"Node.js/\u57FA\u4E8E hapi \u7684 Node.js \u5C0F\u7A0B\u5E8F\u540E\u7AEF\u5F00\u53D1\u5B9E\u8DF5\u6307\u5357/\u5B9E\u6218\u7BC7 1-\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316 \u2014\u2014 \u4F7F\u7528 hapi.md"}'),l={name:"Node.js/\u57FA\u4E8E hapi \u7684 Node.js \u5C0F\u7A0B\u5E8F\u540E\u7AEF\u5F00\u53D1\u5B9E\u8DF5\u6307\u5357/\u5B9E\u6218\u7BC7 1-\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316 \u2014\u2014 \u4F7F\u7528 hapi.md"},e=p(`<h1 id="\u5B9E\u6218\u7BC7-1-\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316-\u2014\u2014-\u4F7F\u7528-hapi" tabindex="-1">\u5B9E\u6218\u7BC7 1-\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316 \u2014\u2014 \u4F7F\u7528 hapi <a class="header-anchor" href="#\u5B9E\u6218\u7BC7-1-\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316-\u2014\u2014-\u4F7F\u7528-hapi" aria-hidden="true">#</a></h1><hr><h1 id="\u5B9E\u6218\u7BC7-1-\uFF1A\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316-\u2014\u2014-\u4F7F\u7528-hapi" tabindex="-1">\u5B9E\u6218\u7BC7 1 \uFF1A\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316 \u2014\u2014 \u4F7F\u7528 hapi <a class="header-anchor" href="#\u5B9E\u6218\u7BC7-1-\uFF1A\u9879\u76EE\u5DE5\u7A0B\u521D\u59CB\u5316-\u2014\u2014-\u4F7F\u7528-hapi" aria-hidden="true">#</a></h1><p>\u7EC8\u4E8E\u6765\u5230\u4E86\u9879\u76EE\u5B9E\u6218\u7684\u7AE0\u8282\uFF0C\u5199\u4EE3\u7801\u662F\u6211\u4EEC\u7A0B\u5E8F\u7231\u597D\u8005\u6700\u6709\u5E78\u798F\u611F\u7684\u4E00\u523B\u3002\u672C\u5C0F\u8282\u5C06\u5E26\u540C\u5B66\u4EEC\u638C\u63E1 hapi \u4F5C\u4E3A\u5E94\u7528\u6846\u67B6\u7684\u57FA\u7840\u4F7F\u7528\u65B9\u6CD5\uFF0C\u4EE5\u53CA\u9879\u76EE\u5DE5\u7A0B\u5316\u7ED3\u6784\u5E03\u5C40\u7684\u8BBE\u8BA1\u539F\u5219\u3002\u5E26\u4F60\u4F53\u9A8C\u7A0B\u5E8F\u9879\u76EE\u5DE5\u7A0B\u642D\u5EFA\u4E2D\u7684\u9759\u4E0E\u52A8\u3002</p><h2 id="\u57FA\u7840\u9879\u76EE\u5DE5\u7A0B\u642D\u5EFA" tabindex="-1">\u57FA\u7840\u9879\u76EE\u5DE5\u7A0B\u642D\u5EFA <a class="header-anchor" href="#\u57FA\u7840\u9879\u76EE\u5DE5\u7A0B\u642D\u5EFA" aria-hidden="true">#</a></h2><p><strong>1. \u521D\u59CB\u5316 Node.js \u9879\u76EE</strong></p><p>\u4F7F\u7528 npm init \u6765\u751F\u6210\u9879\u76EE\u5DE5\u7A0B\u7684\u521D\u59CB\u5316 package.json \u6587\u4EF6\uFF0C\u91CC\u9762\u53EF\u4EE5\u8865\u5168\u4E00\u4E9B\u5F00\u53D1\u8005\u4FE1\u606F\u3001\u9879\u76EE\u63CF\u8FF0\u3001\u4EE3\u7801\u4ED3\u5E93\u5730\u5740\u7B49\u57FA\u7840\u63CF\u8FF0\uFF0C\u6700\u6700\u91CD\u8981\u7684\u662F\uFF0C\u540E\u7EED\u7684\u524D\u7AEF\u9879\u76EE\u6240\u4F9D\u8D56\u7684\u5916\u90E8\u6A21\u5757\uFF0C\u90FD\u5C06\u6301\u7EED\u66F4\u65B0\u51FA\u73B0\u5728 package.json \u4E2D\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">npm init</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><strong>2. \u5B89\u88C5 hapi \u6A21\u5757</strong></p><p>\u8FD9\u91CC\uFF0C\u6211\u4EEC\u4F7F\u7528 hapi \u7684 v16 \u7248\u672C\uFF0C\u7531\u4E8E hapi \u6700\u65B0\u7684\u7248\u672C\u662F v17 \uFF0C\u6240\u4EE5\u901A\u8FC7\u8865\u4E00\u4E2A @16 \u6765\u5E2E\u52A9\u6211\u4EEC\u660E\u786E\u6240\u9700\u5B89\u88C5\u7684\u6A21\u5757\u7248\u672C\u53F7\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">npm i hapi@16</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><strong>3. \u6700\u57FA\u7840\u7684 hapi \u670D\u52A1\u4EE3\u7801</strong></p><p>\u5728\u9879\u76EE\u5DE5\u7A0B\u76EE\u5F55\u4E2D\uFF0C\u521B\u5EFA\u4E00\u4E2A app.js \u6587\u4EF6\u4F5C\u4E3A\u670D\u52A1\u7684\u542F\u52A8\u5165\u53E3\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// app.js</span></span>
<span class="line"><span style="color:#A6ACCD;">const Hapi = require(&#39;hapi&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">const server = new Hapi.Server();</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u914D\u7F6E\u670D\u52A1\u5668\u542F\u52A8 host \u4E0E\u7AEF\u53E3</span></span>
<span class="line"><span style="color:#A6ACCD;">server.connection({</span></span>
<span class="line"><span style="color:#A6ACCD;">  port: 3000,</span></span>
<span class="line"><span style="color:#A6ACCD;">  host: &#39;127.0.0.1&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">const init = async () =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">  server.route([</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u521B\u5EFA\u4E00\u4E2A\u7B80\u5355\u7684 hello hapi \u63A5\u53E3</span></span>
<span class="line"><span style="color:#A6ACCD;">    {</span></span>
<span class="line"><span style="color:#A6ACCD;">      method: &#39;GET&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">      path: &#39;/&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">      handler: (request, reply) =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">        reply(&#39;hello hapi&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">      },</span></span>
<span class="line"><span style="color:#A6ACCD;">    },</span></span>
<span class="line"><span style="color:#A6ACCD;">  ]);</span></span>
<span class="line"><span style="color:#A6ACCD;">  // \u542F\u52A8\u670D\u52A1</span></span>
<span class="line"><span style="color:#A6ACCD;">  await server.start();</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(\`Server running at: \${server.info.uri}\`);</span></span>
<span class="line"><span style="color:#A6ACCD;">};</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">init();</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><blockquote><p><strong>GitHub \u53C2\u8003\u4EE3\u7801</strong> <a href="https://github.com/yeshengfei/hapi-tutorial/tree/master/chapter5/hapi-tutorial-1" target="_blank" rel="noreferrer">chapter5/hapi-tutorial-1</a></p></blockquote><p><strong>4. \u542F\u52A8 hapi \u670D\u52A1</strong></p><p>\u5728 terminal \u7EC8\u7AEF\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">node app.js</span></span>
<span class="line"><span style="color:#A6ACCD;"># \u6216\u8005\u5E0C\u671B\u5728\u7F16\u8F91\u5B8C\u6E90\u4EE3\u7801\u540E\uFF0C\u670D\u52A1\u81EA\u52A8\u91CD\u542F\uFF0C\u4F7F\u7528 supervisor</span></span>
<span class="line"><span style="color:#A6ACCD;">supervisor app.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><strong>5. \u9A8C\u8BC1 hapi \u670D\u52A1</strong></p><p>\u6253\u5F00\u6D4F\u89C8\u5668\uFF0C\u8BBF\u95EE <a href="http://127.0.0.1:3000" target="_blank" rel="noreferrer">http://127.0.0.1:3000</a> \u6211\u4EEC\u5C06\u770B\u5230\u6D4F\u89C8\u5668\u4E2D\u770B\u5230 hello hapi \u7684\u5B57\u6837\uFF1A</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2018/8/14/165379de186adeac~tplv-t2oaga2asx-image.image" alt=""></p><p>\u6216\u8005\u901A\u8FC7 terminal \u4E2D\u6784\u9020\u4E00\u4E2A cURL \u8BF7\u6C42\uFF0C\u67E5\u770B\u6570\u636E\u8FD4\u56DE\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">curl &#39;http://127.0.0.1:3000&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;"># \u8F93\u51FA hello hapi</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6B64\u65F6\uFF0C\u4E00\u4E2A\u6700\u57FA\u7840\u7684 API \u63A5\u53E3\u670D\u52A1\u5DF2\u7ECF\u5F97\u5230\u4E86\u3002</p><h2 id="\u7ED3\u6784\u5316\u9879\u76EE\u5DE5\u7A0B\u91CD\u6784" tabindex="-1">\u7ED3\u6784\u5316\u9879\u76EE\u5DE5\u7A0B\u91CD\u6784 <a class="header-anchor" href="#\u7ED3\u6784\u5316\u9879\u76EE\u5DE5\u7A0B\u91CD\u6784" aria-hidden="true">#</a></h2><p>\u57FA\u7840 API \u670D\u52A1\u7684\u642D\u5EFA\uFF0C\u5C06\u6240\u6709\u7684\u4EE3\u7801\u4FE1\u606F\u90FD\u96C6\u4E2D\u5728\u4E86\u4E00\u4E2A\u6587\u4EF6\u4E4B\u4E2D\uFF0C\u8FD9\u6837\u5F88\u96BE\u6EE1\u8DB3\u65E5\u540E\u9879\u76EE\u89C4\u6A21\u6269\u5927\u6240\u8981\u6C42\u7684\u4EE3\u7801\u53EF\u7EF4\u62A4\u6027\u3002 \u4E3A\u9879\u76EE\u5DE5\u7A0B\u4EE3\u7801\u7684\u4E66\u5199\uFF0C\u7EA6\u5B9A\u5F62\u6210\u4E00\u5957\u826F\u597D\u7684\u6A21\u5757\u5316\u6587\u4EF6\u7ED3\u6784\u4E0E\u89C4\u8303\uFF0C\u4EE5\u9884\u7559\u8DB3\u591F\u7684\u53EF\u6269\u5C55\u7A7A\u95F4\uFF0C\u8FD9\u662F\u4E00\u95E8\u67B6\u6784\u8BBE\u8BA1\u7684\u827A\u672F\uFF0C\u5B9B\u82E5\u4E00\u573A\u5927\u6C14\u7684\u68CB\u5C40\uFF0C\u68CB\u624B\u65E9\u65E9\u5730\u9884\u77E5\u4E86\u672A\u6765\u800C\u6709\u6240\u5E03\u8BBE\u3002\u4E00\u5927\u6CE2\u8D76\u6765\u7684\u540E\u7EED\u4E1A\u52A1\u9700\u6C42\uFF0C\u90FD\u53EF\u4EE5\u5728\u8FD9\u5957\u89C4\u5219\u4F53\u7CFB\u4E0B\u5404\u5F97\u5176\u6240\uFF0C\u4ECE\u5BB9\u4E0D\u8FEB\uFF0C\u6E38\u5203\u6709\u4F59\u3002</p><h3 id="\u9879\u76EE\u5DE5\u7A0B\u5316\u8BBE\u8BA1\u539F\u5219" tabindex="-1">\u9879\u76EE\u5DE5\u7A0B\u5316\u8BBE\u8BA1\u539F\u5219 <a class="header-anchor" href="#\u9879\u76EE\u5DE5\u7A0B\u5316\u8BBE\u8BA1\u539F\u5219" aria-hidden="true">#</a></h3><ul><li><p>\u5355\u4E1A\u52A1\u6A21\u5757\u5316</p><p>\u5927\u591A\u6570\u7684\u5185\u52A1\u9700\u6C42\uFF0C\u90FD\u6700\u7EC8\u53EF\u4EE5\u88AB\u5F52\u7EB3\u8FDB\u5355\u4E2A\u6A21\u5757\uFF0C\u6A21\u5757\u4E0E\u6A21\u5757\u95F4\u5177\u6709\u7269\u7406\u6587\u4EF6\u72EC\u7ACB\u6027\uFF0C\u80FD\u66F4\u597D\u5730\u4FBF\u4E8E\u591A\u4EBA\u7684\u9879\u76EE\u56E2\u961F\u4EBA\u5458\u540C\u65F6\u7EF4\u62A4\u4EE3\u7801\u3002</p></li><li><p>\u6A21\u5757\u4E8C\u767E\u884C\u539F\u5219</p><p>\u4E8C\u767E\u884C\u662F\u4E2A\u865A\u6570\u6CDB\u6307\uFF0C\u4F46\u4E5F\u5728\u5927\u91CF\u7684\u9879\u76EE\u5B9E\u8DF5\u540E\u53D1\u73B0\uFF0C\u5927\u591A\u6570\u5355\u6587\u4EF6\u7684\u6A21\u5757\u4EE3\u7801\uFF0C\u5728\u826F\u597D\u7684\u8BBE\u8BA1\u89C4\u5212\u4E0B\uFF0C\u5F80\u5F80\u5F88\u96BE\u7A81\u7834\u4E24\u767E\u884C\u7684\u6570\u76EE\uFF0C\u800C\u4E24\u767E\u884C\u5185\u7684\u4EE3\u7801\u7684\u9605\u8BFB\u4E0E\u7EF4\u62A4\uFF0C\u4E5F\u90FD\u662F\u4E00\u4E2A\u5FC3\u7406\u538B\u529B\u8F83\u5C0F\u7684\u91CF\u7EA7\u3002</p><p>\u6B63\u6240\u8C13\u5408\u4E45\u5FC5\u5206\uFF0C\u5355\u4E2A\u6A21\u5757\u7684\u4EE3\u7801\u91CF\u5927\u91CF\u589E\u957F\uFF0C\u53EF\u4EE5\u8003\u8651\u5206\u79BB\u90E8\u5206\u4EE3\u7801\u5230\u5916\u90E8\u6587\u4EF6\u6765\u7BA1\u7406\uFF0C\u901A\u8FC7\u6A21\u5757 require \u7684\u65B9\u5F0F\u5F62\u6210\u5173\u8054\uFF0C\u4EE5\u4FDD\u6301\u5404\u6A21\u5757\u4EE3\u7801\u7684\u76F8\u5BF9\u7CBE\u7B80\u4E0E\u53EF\u7EF4\u62A4\u6027\u3002</p></li><li><p>\u540C\u7C7B\u6A21\u5757\u5206\u7EC4\u5316</p><p>\u5206\u7EC4\u5728\u672C\u6587\u4E2D\u4E00\u822C\u6307\u5F62\u6210\u4E00\u4E2A\u6587\u4EF6\u76EE\u5F55\uFF0C\u6765\u5BF9\u4E00\u4E9B\u529F\u80FD\u76F8\u4F3C\u7684\u4E1A\u52A1\u6A21\u5757\u5F62\u6210\u7BA1\u7406\uFF0C\u6BD4\u5982\u5E38\u89C1\u7684\u8DEF\u7531\u6A21\u5757\uFF0C\u4F1A\u6709\u5927\u91CF\u4E0D\u540C\u7684\u57FA\u4E8E resource \u7684\u8D44\u6E90\u8DEF\u7531\uFF0C\u88AB\u5206\u79BB\u5728\u5355\u4E2A\u6A21\u5757\u6587\u4EF6\u4E2D\uFF0C\u6700\u7EC8\u6C47\u603B\u5728 router \u7684\u76EE\u5F55\u3002\u518D\u6BD4\u5982\u5B9A\u4E49\u6570\u636E\u5E93\u8868\u7ED3\u6784\u6620\u5C04\u5173\u7CFB\u7684 model \u6A21\u578B\u7C7B\u6A21\u5757\uFF0C\u6700\u7EC8\u4E5F\u5F80\u5F80\u4F1A\u4E60\u60EF\u6C47\u603B\u5230\u4E00\u4E2A model \u7684\u76EE\u5F55\u4E2D\uFF0C\u5F62\u6210\u7EDF\u4E00\u7BA1\u7406\u3002</p></li><li><p>\u914D\u7F6E\u6587\u4EF6\u5206\u79BB</p><p>\u7CFB\u7EDF\u4E2D\u5F80\u5F80\u4F1A\u5B58\u5728\u4E00\u4E9B\u8BF8\u5982\u670D\u52A1\u542F\u52A8\u7684\u7AEF\u53E3\u53F7\u3001\u670D\u52A1\u540D\u79F0\u3001\u6570\u636E\u5E93\u8FDE\u63A5\u914D\u7F6E\u3001\u670D\u52A1\u542F\u52A8\u7684\u6027\u80FD\u53C2\u6570\u57DF\u503C\u914D\u7F6E\u7B49\u7B49\uFF0C\u8FD9\u4E9B\u53C2\u6570\u7684\u4E1A\u52A1\u903B\u8F91\u5F80\u5F80\u79BB\u6563\u5728\u7CFB\u7EDF\u4E2D\u7684\u5404\u4E2A\u72EC\u7ACB\u6A21\u5757\u4E2D\uFF0C\u5C06\u5176\u62BD\u53D6\u5728\u4E00\u4E2A\u7EDF\u4E00\u7684\u7C7B\u4F3C config.js \u7684\u914D\u7F6E\u6587\u4EF6\u4E2D\u8FDB\u884C\u6574\u4F53\u7BA1\u7406\uFF0C\u53EF\u4EE5\u907F\u514D\u65E5\u540E\u6253\u5730\u9F20\u5F0F\u7684\u5728\u6A21\u5757\u4E2D\u56DB\u5904\u627E\u5BFB\uFF0C\u4EE5\u53CA\u4FEE\u6539\u7F16\u8F91\u758F\u6F0F\u3002</p></li></ul><p>\u57FA\u4E8E\u4E0A\u8FF0\u539F\u5219\uFF0C\u6211\u4EEC\u5C31\u57FA\u7840\u9879\u76EE\u5DE5\u7A0B\u8FDB\u884C\u62C6\u5206\u91CD\u6784\u3002</p><h3 id="\u91CD\u6784\u6B65\u9AA4" tabindex="-1">\u91CD\u6784\u6B65\u9AA4 <a class="header-anchor" href="#\u91CD\u6784\u6B65\u9AA4" aria-hidden="true">#</a></h3><p><strong>1. \u91CD\u65B0\u68B3\u7406\u9879\u76EE\u5DE5\u7A0B\u76EE\u5F55</strong></p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 config                       # \u9879\u76EE\u914D\u7F6E\u76EE\u5F55</span></span>
<span class="line"><span style="color:#A6ACCD;">|   \u251C\u2500\u2500 index.js                 # \u914D\u7F6E\u9879\u76EE\u4E2D\u7684\u914D\u7F6E\u4FE1\u606F</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 models                       # \u6570\u636E\u5E93 model</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 node_modules                 # node.js \u7684\u4F9D\u8D56\u76EE\u5F55</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 plugins                      # \u63D2\u4EF6\u76EE\u5F55</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 routes                       # \u8DEF\u7531\u76EE\u5F55</span></span>
<span class="line"><span style="color:#A6ACCD;">\u2502   \u251C\u2500\u2500 hello-world.js           # \u6D4B\u8BD5\u63A5\u53E3 hello-world</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 utils                        # \u5DE5\u5177\u7C7B\u76F8\u5173\u76EE\u5F55</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 app.js                       # \u9879\u76EE\u5165\u53E3\u6587\u4EF6</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 package.json                 # JS \u9879\u76EE\u5DE5\u7A0B\u4F9D\u8D56\u5E93</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500\u2500 readme.md                    # \u9879\u76EE\u5DE5\u7A0B\u5982\u4F55\u88AB\u4F7F\u7528\u7684\u8BF4\u660E\u624B\u518C</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><strong>2. \u5206\u79BB\u8DEF\u7531\u914D\u7F6E</strong></p><p>\u5728 routes \u76EE\u5F55\u6DFB\u52A0\u4E00\u4E2A hello-hapi.js \u6587\u4EF6:</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// routes/hello-hapi.js</span></span>
<span class="line"><span style="color:#A6ACCD;">module.exports = [</span></span>
<span class="line"><span style="color:#A6ACCD;">  {</span></span>
<span class="line"><span style="color:#A6ACCD;">    method: &#39;GET&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">    path: &#39;/&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">    handler: (request, reply) =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">      reply(&#39;hapi&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">]</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><strong>3. \u5206\u79BB config \u57FA\u7840\u53C2\u6570\u914D\u7F6E</strong></p><p>\u5728 config \u76EE\u5F55\u6DFB\u52A0\u4E00\u4E2A index.js \u6587\u4EF6\uFF0C\u5C06\u670D\u52A1\u542F\u52A8\u7684\u76F8\u5173\u914D\u7F6E\uFF0C\u79FB\u52A8\u5230 config \u4E2D\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// config/index.js</span></span>
<span class="line"><span style="color:#A6ACCD;">module.exports = {</span></span>
<span class="line"><span style="color:#A6ACCD;">  host: &#39;127.0.0.1&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">  port: 3000,</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><strong>4. \u5173\u8054 config \u4E0E route \u8DEF\u7531\u6A21\u5757</strong></p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// APP \u5165\u53E3\u7684 JS</span></span>
<span class="line"><span style="color:#A6ACCD;">const Hapi = require(&#39;hapi&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">const config = require(&#39;./config&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">const routesHelloHapi = require(&#39;./routes/hello-hapi&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">const server = new Hapi.Server();</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u914D\u7F6E\u670D\u52A1\u5668\u542F\u52A8 host \u4E0E\u7AEF\u53E3</span></span>
<span class="line"><span style="color:#A6ACCD;">server.connection({</span></span>
<span class="line"><span style="color:#A6ACCD;">  port: config.port,</span></span>
<span class="line"><span style="color:#A6ACCD;">  host: config.host,</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">const init = async () =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">  server.route([</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u521B\u5EFA\u4E00\u4E2A\u7B80\u5355\u7684 hello hapi \u63A5\u53E3</span></span>
<span class="line"><span style="color:#A6ACCD;">    ...routesHelloHapi,</span></span>
<span class="line"><span style="color:#A6ACCD;">  ]);</span></span>
<span class="line"><span style="color:#A6ACCD;">  // \u542F\u52A8\u670D\u52A1</span></span>
<span class="line"><span style="color:#A6ACCD;">  await server.start();</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(\`Server running at: \${server.info.uri}\`);</span></span>
<span class="line"><span style="color:#A6ACCD;">};</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">init();</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><blockquote><p><strong>GitHub \u53C2\u8003\u4EE3\u7801</strong> <a href="https://github.com/yeshengfei/hapi-tutorial/tree/master/chapter5/hapi-tutorial-2" target="_blank" rel="noreferrer">chapter5/hapi-tutorial-2</a></p></blockquote><h3 id="\u73AF\u5883\u914D\u7F6E" tabindex="-1">\u73AF\u5883\u914D\u7F6E <a class="header-anchor" href="#\u73AF\u5883\u914D\u7F6E" aria-hidden="true">#</a></h3><p><strong>\u73AF\u5883\u914D\u7F6E\u7684\u91CD\u8981\u6027</strong></p><p>\u7406\u8BBA\u4E0A\uFF0C\u57FA\u7840\u9879\u76EE\u4EE3\u7801\u5206\u79BB\u81F3\u4E0A\u8FF0\u60C5\u51B5\uFF0C\u5DF2\u7ECF\u521D\u6B65\u5177\u5907\u4E86\u652F\u6491\u4E0D\u65AD\u589E\u957F\u7684\u4E1A\u52A1\u9700\u6C42\u7684\u8BBE\u8BA1\u5F39\u6027\u3002\u4F46\u662F\u5BF9\u4E8E\u5E94\u7528\u7A0B\u5E8F\u8FD0\u884C\u7684\u73AF\u5883\u6765\u8BF4\uFF0C\u4E0D\u540C\u7684\u73AF\u5883\u5F80\u5F80\u6709\u4E0D\u540C\u7684\u914D\u7F6E\u3002</p><p>\u4F8B\u5982\uFF0C\u6211\u4EEC\u7684\u672C\u5730\u6570\u636E\u5E93\u8FDE\u63A5\u7528\u6237\u540D\u5BC6\u7801\u4E0E\u670D\u52A1\u5668\u73AF\u5883\u7684\u7528\u6237\u540D\u5BC6\u7801\u5E76\u4E0D\u4E00\u6837\u3002\u53C8\u4F8B\u5982\uFF0C\u6211\u4EEC\u672C\u5730\u542F\u52A8\u670D\u52A1\u7684 IP \u4E0E\u7AEF\u53E3\u4E0E\u670D\u52A1\u5668\u73AF\u5883\u6709\u6240\u4E0D\u540C\u3002\u7F3A\u4E4F\u73AF\u5883\u914D\u7F6E\u6587\u4EF6\u7684\u786C\u7F16\u7801\u5F00\u53D1\uFF0C\u867D\u7136\u4E66\u5199\u4EE3\u7801\u7701\u4E8B\uFF0C\u4F46\u4F1A\u7ED9\u6211\u4EEC\u540E\u7EED\u957F\u671F\u7EF4\u62A4\u9879\u76EE\u4EE3\u7801\u5E26\u6765\u6781\u5927\u7684\u9EBB\u70E6\u4E0E\u7F16\u8F91\u4FEE\u6539\u914D\u7F6E\u7684\u98CE\u9669\u9690\u60A3\u3002</p><p>\u6211\u4EEC\u5728\u9879\u76EE\u7684\u5B9E\u8DF5\u8FC7\u7A0B\u4E2D\uFF0C\u5F80\u5F80\u4E5F\u4F1A\u548C\u4E00\u4E9B\u654F\u611F\u7684\u6570\u636E\u4FE1\u606F\u6253\u4EA4\u9053\uFF0C\u6BD4\u5982\u6570\u636E\u5E93\u7684\u8FDE\u63A5\u7528\u6237\u540D\u3001\u5BC6\u7801\uFF0C\u7B2C\u4E09\u65B9 SDK \u7684 secret \u7B49\u3002\u8FD9\u4E9B\u53C2\u6570\u7684\u914D\u7F6E\u4FE1\u606F\uFF0C\u539F\u5219\u4E0A\u662F\u7981\u6B62\u8FDB\u5165\u5230 git \u7248\u672C\u4ED3\u5E93\u7684\u3002\u4E00\u6765\u5728\u5F00\u53D1\u73AF\u5883\u4E2D\uFF0C\u4E0D\u540C\u7684\u5F00\u53D1\u4EBA\u5458\u672C\u5730\u7684\u5F00\u53D1\u914D\u7F6E\u5404\u6709\u4E0D\u540C\uFF0C\u4E0D\u4F9D\u8D56\u4E8E git \u7248\u672C\u5E93\u914D\u7F6E\u3002\u4E8C\u6765\u654F\u611F\u6570\u636E\u7684\u5165\u5E93\uFF0C\u589E\u52A0\u4E86\u4EBA\u4E3A\u6CC4\u6F0F\u914D\u7F6E\u6570\u636E\u7684\u98CE\u9669\uFF0C\u4EFB\u4F55\u53EF\u4EE5\u8BBF\u95EE git \u4ED3\u5E93\u7684\u5F00\u53D1\u4EBA\u5458\uFF0C\u90FD\u53EF\u4EE5\u4ECE\u4E2D\u83B7\u53D6\u5230\u751F\u4EA7\u73AF\u5883\u7684 secret key\u3002\u4E00\u65E6\u88AB\u6076\u610F\u5229\u7528\uFF0C\u540E\u679C\u4E0D\u582A\u8BBE\u60F3\u3002</p><p>\u4E8E\u662F\u6211\u4EEC\u8003\u8651\u5F15\u5165\u4E00\u4E2A\u88AB .gitignore \u7684 .env \u7684\u6587\u4EF6\uFF0C\u4EE5 key-value \u7684\u65B9\u5F0F\uFF0C\u8BB0\u5F55\u7CFB\u7EDF\u4E2D\u6240\u9700\u8981\u7684\u53EF\u914D\u7F6E\u73AF\u5883\u53C2\u6570\u3002\u5E76\u540C\u65F6\u914D\u5957\u4E00\u4E2A.env.example \u7684\u793A\u4F8B\u914D\u7F6E\u6587\u4EF6\u7528\u6765\u653E\u7F6E\u5360\u4F4D\uFF0C.env.example \u53EF\u4EE5\u653E\u5FC3\u5730\u8FDB\u5165 git \u7248\u672C\u4ED3\u5E93\u3002</p><p><strong>\u4EE5\u5F53\u524D\u7684\u9879\u76EE\u4E3A\u4F8B</strong></p><p>\u6211\u4EEC\u5728\u672C\u5730\u521B\u5EFA\u4E00\u4E2A .env.example \u6587\u4EF6\u4F5C\u4E3A\u914D\u7F6E\u6A21\u677F\uFF0C\u5185\u5BB9\u5982\u4E0B\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;"># .env.expamle</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># \u670D\u52A1\u7684\u542F\u52A8\u540D\u5B57\u548C\u7AEF\u53E3\uFF0C\u4F46\u4E5F\u53EF\u4EE5\u7F3A\u7701\u4E0D\u586B\u503C\uFF0C\u9ED8\u8BA4\u503C\u7684\u586B\u5199\u53EA\u662F\u4E00\u5B9A\u7A0B\u5EA6\u51CF\u5C11\u8D77\u59CB\u6570\u636E\u914D\u7F6E\u5DE5\u4F5C</span></span>
<span class="line"><span style="color:#A6ACCD;">HOST = 127.0.0.1</span></span>
<span class="line"><span style="color:#A6ACCD;">PORT = 3000</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u7136\u540E\u590D\u5236\u51FA\u4E00\u4EFD\u771F\u5B9E\u7684 .env \u6587\u4EF6\uFF0C\u6765\u4F9B\u7CFB\u7EDF\u6700\u7EC8\u4F7F\u7528\uFF0C\u586B\u5165\u771F\u5B9E\u53EF\u7528\u7684\u914D\u7F6E\u4FE1\u606F\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;"># .env</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># \u670D\u52A1\u7684\u542F\u52A8\u540D\u5B57\u548C\u7AEF\u53E3</span></span>
<span class="line"><span style="color:#A6ACCD;">HOST = 127.0.0.1</span></span>
<span class="line"><span style="color:#A6ACCD;">PORT = 3000</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><strong>\u5982\u4F55\u8BFB\u53D6 .env \u4E2D\u7684\u914D\u7F6E\u503C</strong></p><p>Node.js \u53EF\u4EE5\u901A\u8FC7 env2 \u7684\u63D2\u4EF6\uFF0C\u6765\u8BFB\u53D6 .env \u914D\u7F6E\u6587\u4EF6\uFF0C\u52A0\u8F7D\u540E\u7684\u73AF\u5883\u914D\u7F6E\u53C2\u6570\uFF0C\u53EF\u4EE5\u901A\u8FC7\u4F8B\u5982 p<wbr>rocess.env.PORT \u6765\u8BFB\u53D6\u7AEF\u53E3\u4FE1\u606F\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;"># \u7CFB\u7EDF\u5168\u5C40\u5B89\u88C5</span></span>
<span class="line"><span style="color:#A6ACCD;">supervisor</span></span>
<span class="line"><span style="color:#A6ACCD;">npm i env2</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// app.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">require(&#39;env2&#39;)(&#39;./.env&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>config/index.js\u4E2D\u7684\u914D\u7F6E\u53C2\u6570\u6700\u7EC8\u53D8\u4E3A\u5982\u4E0B\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// config/index.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">const { env } = process;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">module.exports = {</span></span>
<span class="line"><span style="color:#A6ACCD;">  host: env.HOST,</span></span>
<span class="line"><span style="color:#A6ACCD;">  port: env.PORT,  </span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6700\u540E\uFF0C\u8BB0\u5F97\u5728 .gitignore \u6587\u4EF6\u4E2D\u589E\u52A0\u4E00\u884C .env\uFF0C\u7528\u6765\u907F\u514D\u8BE5\u6587\u4EF6\u7684 git \u7248\u672C\u5165\u5E93\u3002\u540C\u65F6\u5728\u672A\u6765\u4EA4\u4ED8\u751F\u4EA7\u73AF\u5883\u7684\u65F6\u5019\uFF0C\u73AF\u5883\u53C2\u6570\u7684\u914D\u7F6E\u4EA4\u7531\u751F\u4EA7\u73AF\u5883\u7684\u8FD0\u7EF4\u4EBA\u5458\u5904\u7406\uFF0C\u9879\u76EE\u7684\u654F\u611F\u914D\u7F6E\u6570\u636E\u7684\u5B89\u5168\u6027\u53EF\u4EE5\u5F97\u5230\u4E00\u5B9A\u7A0B\u5EA6\u7684\u4FDD\u8BC1\u3002</p><blockquote><p><strong>GitHub \u53C2\u8003\u4EE3\u7801</strong> <a href="https://github.com/yeshengfei/hapi-tutorial/tree/master/chapter5/hapi-tutorial-3" target="_blank" rel="noreferrer">chapter5/hapi-tutorial-3</a></p></blockquote><h2 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3 <a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a></h2><p>\u5173\u952E\u8BCD\uFF1Ahapi \u63A5\u53E3\u670D\u52A1\uFF0C\u9879\u76EE\u5DE5\u7A0B\u5316\u8BBE\u8BA1\uFF0C\u73AF\u5883\u914D\u7F6E</p><p>\u901A\u8FC7\u672C\u5C0F\u8282\u7684\u5B66\u4E60\uFF0C\u6211\u4EEC\u4E00\u5B9A\u4F1A\u4E3A\u5B9E\u73B0\u4E86\u7B2C\u4E00\u4E2A\u57FA\u4E8E hapi \u7684 API \u63A5\u53E3\u800C\u5174\u594B\u3002\u4F46\u7B14\u8005\u66F4\u5E0C\u671B\u80FD\u901A\u8FC7\u672C\u8282\u7684\u8BDD\u9898\u5185\u5BB9\u8BBE\u8BA1\uFF0C\u5E26\u7ED9\u540C\u5B66\u4EEC\u4E00\u79CD\u9879\u76EE\u5DE5\u7A0B\u5316\u7684\u683C\u5C40\u610F\u8BC6\u3002\u5927\u591A\u6570\u7684\u8F6F\u4EF6\u5DE5\u7A0B\u9879\u76EE\uFF0C\u968F\u7740\u4E1A\u52A1\u7684\u6DF1\u5165\uFF0C\u90FD\u4F1A\u4F9D\u8D56\u4E8E\u66F4\u591A\u7684\u5DE5\u7A0B\u5E08\u52A0\u5165\uFF0C\u80FD\u5426\u5177\u5907\u826F\u597D\u7684\u4E1A\u52A1\u4E0E\u4EBA\u5458\u7684\u53EF\u6269\u5C55\u6027\uFF0C\u76F4\u63A5\u4F53\u73B0\u4E86\u9879\u76EE\u5DE5\u7A0B\u5316\u8BBE\u8BA1\u7684\u4F18\u52A3\u9AD8\u4F4E\u3002</p><p>\u601D\u8003\uFF1A\u6848\u4F8B\u4E2D\u9879\u76EE\u5DE5\u7A0B\u76EE\u5F55\u7684\u9884\u7559\uFF0C\u5916\u5356\u7CFB\u7EDF\u4E2D\u6240\u6D89\u53CA\u7684\u4E1A\u52A1\u529F\u80FD\u7684\u5B9E\u73B0\uFF0C\u5728\u4F60\u7684\u8111\u6D77\u4E2D\uFF0C\u80FD\u5426\u627E\u5230\u4E00\u4E9B\u5BF9\u53F7\u5165\u5EA7\u7684\u4ECE\u5BB9\u586B\u5751\u611F\uFF1F</p><p>\u6211\u4EEC\u5728\u540E\u9762\u7684\u5185\u5BB9\u91CC\uFF0C\u6765\u6301\u7EED\u4F5C\u51FA\u9A8C\u8BC1\u3002</p><p><strong>\u672C\u5C0F\u8282\u53C2\u8003\u4EE3\u7801\u6C47\u603B</strong></p><p>\u57FA\u7840\u9879\u76EE\u5DE5\u7A0B\u642D\u5EFA - hapi \u670D\u52A1\u4EE3\u7801\uFF1A <a href="https://github.com/yeshengfei/hapi-tutorial/tree/master/chapter5/hapi-tutorial-1" target="_blank" rel="noreferrer">chapter5/hapi-tutorial-1</a></p><p>\u7ED3\u6784\u5316\u9879\u76EE\u5DE5\u7A0B\u91CD\u6784 - \u91CD\u6784\u6B65\u9AA4\uFF1A <a href="https://github.com/yeshengfei/hapi-tutorial/tree/master/chapter5/hapi-tutorial-2" target="_blank" rel="noreferrer">chapter5/hapi-tutorial-2</a></p><p>\u7ED3\u6784\u5316\u9879\u76EE\u5DE5\u7A0B\u91CD\u6784 - \u73AF\u5883\u914D\u7F6E\uFF1A <a href="https://github.com/yeshengfei/hapi-tutorial/tree/master/chapter5/hapi-tutorial-3" target="_blank" rel="noreferrer">chapter5/hapi-tutorial-3</a></p>`,69),o=[e];function t(c,r,i,A,C,h){return a(),n("div",null,o)}const g=s(l,[["render",t]]);export{d as __pageData,g as default};
