import{_ as s,c as n,o as a,d as e}from"./app.c4f45da1.js";const D=JSON.parse('{"title":"\u5B9E\u6218\u7BC7 9-\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8 \u2014\u2014 \u5FAE\u4FE1\u652F\u4ED8","description":"","frontmatter":{},"headers":[{"level":3,"title":"1.\u5C0F\u7A0B\u5E8F\u5185\u8C03\u7528\u767B\u5F55\u63A5\u53E3","slug":"_1-\u5C0F\u7A0B\u5E8F\u5185\u8C03\u7528\u767B\u5F55\u63A5\u53E3","link":"#_1-\u5C0F\u7A0B\u5E8F\u5185\u8C03\u7528\u767B\u5F55\u63A5\u53E3","children":[]},{"level":3,"title":"2. \u5546\u6237 server \u8C03\u7528\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355","slug":"_2-\u5546\u6237-server-\u8C03\u7528\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355","link":"#_2-\u5546\u6237-server-\u8C03\u7528\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355","children":[]},{"level":3,"title":"3. \u5546\u6237 server \u8C03\u7528\u518D\u6B21\u7B7E\u540D","slug":"_3-\u5546\u6237-server-\u8C03\u7528\u518D\u6B21\u7B7E\u540D","link":"#_3-\u5546\u6237-server-\u8C03\u7528\u518D\u6B21\u7B7E\u540D","children":[]},{"level":3,"title":"4. \u5546\u6237 server \u63A5\u6536\u652F\u4ED8\u901A\u77E5","slug":"_4-\u5546\u6237-server-\u63A5\u6536\u652F\u4ED8\u901A\u77E5","link":"#_4-\u5546\u6237-server-\u63A5\u6536\u652F\u4ED8\u901A\u77E5","children":[]},{"level":2,"title":"\u5C0F\u7ED3","slug":"\u5C0F\u7ED3","link":"#\u5C0F\u7ED3","children":[]}],"relativePath":"Node.js/\u57FA\u4E8E hapi \u7684 Node.js \u5C0F\u7A0B\u5E8F\u540E\u7AEF\u5F00\u53D1\u5B9E\u8DF5\u6307\u5357/\u5B9E\u6218\u7BC7 9-\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8 \u2014\u2014 \u5FAE\u4FE1\u652F\u4ED8.md"}'),p={name:"Node.js/\u57FA\u4E8E hapi \u7684 Node.js \u5C0F\u7A0B\u5E8F\u540E\u7AEF\u5F00\u53D1\u5B9E\u8DF5\u6307\u5357/\u5B9E\u6218\u7BC7 9-\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8 \u2014\u2014 \u5FAE\u4FE1\u652F\u4ED8.md"},l=e(`<h1 id="\u5B9E\u6218\u7BC7-9-\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8-\u2014\u2014-\u5FAE\u4FE1\u652F\u4ED8" tabindex="-1">\u5B9E\u6218\u7BC7 9-\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8 \u2014\u2014 \u5FAE\u4FE1\u652F\u4ED8 <a class="header-anchor" href="#\u5B9E\u6218\u7BC7-9-\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8-\u2014\u2014-\u5FAE\u4FE1\u652F\u4ED8" aria-hidden="true">#</a></h1><hr><h1 id="\u5B9E\u6218\u7BC7-9\uFF1A\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8-\u2014\u2014-\u5C0F\u7A0B\u5E8F\u652F\u4ED8" tabindex="-1">\u5B9E\u6218\u7BC7 9\uFF1A\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8 \u2014\u2014 \u5C0F\u7A0B\u5E8F\u652F\u4ED8 <a class="header-anchor" href="#\u5B9E\u6218\u7BC7-9\uFF1A\u5C0F\u7A0B\u5E8F\u8BA2\u5355\u652F\u4ED8-\u2014\u2014-\u5C0F\u7A0B\u5E8F\u652F\u4ED8" aria-hidden="true">#</a></h1><p>\u521B\u5EFA\u5B8C\u8BA2\u5355\uFF0C\u8FD9\u4E00\u8282\uFF0C\u6211\u4EEC\u6765\u5B9E\u73B0\u5C0F\u7A0B\u5E8F\u7684\u652F\u4ED8\u529F\u80FD\uFF0C\u4EE5\u5B8C\u6210\u4E00\u4E2A\u5546\u4E1A\u5E94\u7528\u7684\u4E1A\u52A1\u7ECF\u8425\u80FD\u529B\u95ED\u73AF\u3002</p><p>\u5FAE\u4FE1\u5C0F\u7A0B\u5E8F\u7684\u4E3B\u8981\u4EA4\u4E92\u56FE\u5982\u4E0B\uFF1A</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2018/8/30/16586e676781031e~tplv-t2oaga2asx-image.image" alt=""></p><p>\u8981\u60F3\u5B9E\u73B0\u652F\u4ED8\u7684\u7CFB\u7EDF\u903B\u8F91\uFF0C\u6700\u4E3B\u8981\u7684\u662F\u5B8C\u6210\u63A5\u4E0B\u6765\u7684 4 \u4E2A\u6B65\u9AA4\uFF1A</p><h3 id="_1-\u5C0F\u7A0B\u5E8F\u5185\u8C03\u7528\u767B\u5F55\u63A5\u53E3" tabindex="-1">1.\u5C0F\u7A0B\u5E8F\u5185\u8C03\u7528\u767B\u5F55\u63A5\u53E3 <a class="header-anchor" href="#_1-\u5C0F\u7A0B\u5E8F\u5185\u8C03\u7528\u767B\u5F55\u63A5\u53E3" aria-hidden="true">#</a></h3><p>\u5C0F\u7A0B\u5E8F\u5185\u8C03\u7528\u767B\u5F55\u63A5\u53E3\uFF0C\u83B7\u53D6\u5230\u7528\u6237\u7684 openid\uFF0CAPI \u53C2\u89C1 <a href="https://developers.weixin.qq.com/miniprogram/dev/api/api-login.html?t=20161122" target="_blank" rel="noreferrer">\u5C0F\u7A0B\u5E8F\u767B\u5F55 API</a> \u3002</p><p>\u5728\u9762\u5411\u5C0F\u7A0B\u5E8F\u7684 JWT \u767B\u5F55\u7528\u6237\u9A8C\u8BC1\u7AE0\u8282\u4E2D\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u638C\u63E1\u4E86\u5982\u4F55\u83B7\u53D6\u7528\u6237 openid \u7684\u8C03\u7528\u6D41\u7A0B\u4E0E\u65B9\u6CD5\u3002</p><h3 id="_2-\u5546\u6237-server-\u8C03\u7528\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355" tabindex="-1">2. \u5546\u6237 server \u8C03\u7528\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355 <a class="header-anchor" href="#_2-\u5546\u6237-server-\u8C03\u7528\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355" aria-hidden="true">#</a></h3><p>\u83B7\u53D6\u4E86\u7528\u6237\u7684 openid \u540E\uFF0C\u9700\u8981\u5728\u5546\u6237\u7684 server \u8C03\u7528\u5FAE\u4FE1\u7684\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355\uFF0C\u4EE5\u521B\u5EFA\u4E00\u6761\u5F85\u652F\u4ED8\u7684\u8BB0\u5F55\u8FD4\u56DE\u7ED9\u5C0F\u7A0B\u5E8F\uFF0C\u4EE5\u5B8C\u6210\u5C0F\u7A0B\u5E8F\u5BA2\u6237\u7AEF\u7684\u652F\u4ED8\u80FD\u529B\u5524\u8D77\uFF0C\u8FDB\u5165\u5230\u540E\u7EED\u6B65\u9AA4\u3002\u5546\u6237 server \u8C03\u7528\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355\uFF0CAPI \u53C2\u89C1 <a href="https://pay.weixin.qq.com/wiki/doc/api/wxa/wxa_api.php?chapter=9_1&amp;index=1" target="_blank" rel="noreferrer">\u7EDF\u4E00\u4E0B\u5355 API</a> \u3002</p><p>\u770B\u8FC7\u63A5\u53E3\u6587\u6863\uFF0C\u6211\u4EEC\u53D1\u73B0\u5FAE\u4FE1\u63A5\u6536\u7684\u6570\u636E\u4E0E\u8FD4\u56DE\u7684\u683C\u5F0F\u90FD\u662F\u4EE5 text/xml \u7684\u683C\u5F0F\uFF0C\u800C\u975E application/json \uFF0C\u6240\u4EE5\uFF0C\u6211\u4EEC\u9700\u8981\u5F15\u5165 xml2js \u7684\u63D2\u4EF6\u5E2E\u52A9\u6211\u4EEC\u5728 JavaScript \u7684 Ojbect \u4E0E XML \u7684 Object \u6570\u636E\u5173\u7CFB\u4E4B\u95F4\u5FEB\u901F\u8F6C\u6362\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">$ npm i xml2js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// routers/order.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  method: &#39;POST&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">  path: \`/\${GROUP_NAME}/{orderId}/pay\`,</span></span>
<span class="line"><span style="color:#A6ACCD;">  handler: async (request, reply) =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u4ECE\u7528\u6237\u8868\u4E2D\u83B7\u53D6 openid</span></span>
<span class="line"><span style="color:#A6ACCD;">    const user = await models.users.findOne({ where: { id: request.auth.credentials.userId } });</span></span>
<span class="line"><span style="color:#A6ACCD;">      const { openid } = user;</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u6784\u9020 unifiedorder \u6240\u9700\u5165\u53C2</span></span>
<span class="line"><span style="color:#A6ACCD;">    const unifiedorderObj = {</span></span>
<span class="line"><span style="color:#A6ACCD;">      appid: config.wxAppid, // \u5C0F\u7A0B\u5E8F id</span></span>
<span class="line"><span style="color:#A6ACCD;">      body: &#39;\u5C0F\u7A0B\u5E8F\u652F\u4ED8&#39;, // \u5546\u54C1\u7B80\u5355\u63CF\u8FF0</span></span>
<span class="line"><span style="color:#A6ACCD;">      mch_id: config.wxMchid, // \u5546\u6237\u53F7</span></span>
<span class="line"><span style="color:#A6ACCD;">      nonce_str: Math.random().toString(36).substr(2, 15), // \u968F\u673A\u5B57\u7B26\u4E32</span></span>
<span class="line"><span style="color:#A6ACCD;">      notify_url: &#39;https://yourhost.com/orders/pay/notify&#39;, // \u652F\u4ED8\u6210\u529F\u7684\u56DE\u8C03\u5730\u5740</span></span>
<span class="line"><span style="color:#A6ACCD;">      openid, // \u7528\u6237 openid</span></span>
<span class="line"><span style="color:#A6ACCD;">      out_trade_no: request.params.orderId, // \u5546\u6237\u8BA2\u5355\u53F7</span></span>
<span class="line"><span style="color:#A6ACCD;">      spbill_create_ip: request.info.remoteAddress, // \u8C03\u7528\u652F\u4ED8\u63A5\u53E3\u7684\u7528\u6237 ip</span></span>
<span class="line"><span style="color:#A6ACCD;">      total_fee: 1, // \u603B\u91D1\u989D\uFF0C\u5355\u4F4D\u4E3A\u5206</span></span>
<span class="line"><span style="color:#A6ACCD;">      trade_type: &#39;JSAPI&#39;, // \u4EA4\u6613\u7C7B\u578B\uFF0C\u9ED8\u8BA4</span></span>
<span class="line"><span style="color:#A6ACCD;">    };</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u7B7E\u540D\u7684\u6570\u636E</span></span>
<span class="line"><span style="color:#A6ACCD;">    const getSignData = (rawData, apiKey) =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">      let keys = Object.keys(rawData);</span></span>
<span class="line"><span style="color:#A6ACCD;">      keys = keys.sort();</span></span>
<span class="line"><span style="color:#A6ACCD;">      let string = &#39;&#39;;</span></span>
<span class="line"><span style="color:#A6ACCD;">      keys.forEach((key) =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">        string += \`&amp;\${key}=\${rawData[key]}\`;</span></span>
<span class="line"><span style="color:#A6ACCD;">      });</span></span>
<span class="line"><span style="color:#A6ACCD;">      string = string.substr(1);</span></span>
<span class="line"><span style="color:#A6ACCD;">      return crypto.createHash(&#39;md5&#39;).update(\`\${string}&amp;key=\${apiKey}\`).digest(&#39;hex&#39;).toUpperCase();</span></span>
<span class="line"><span style="color:#A6ACCD;">    };</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u5C06\u57FA\u7840\u6570\u636E\u4FE1\u606F sign \u7B7E\u540D</span></span>
<span class="line"><span style="color:#A6ACCD;">    const sign = getSignData(unifiedorderObj, config.wxPayApiKey);</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u9700\u8981\u88AB post \u7684\u6570\u636E\u6E90</span></span>
<span class="line"><span style="color:#A6ACCD;">    const unifiedorderWithSign = {</span></span>
<span class="line"><span style="color:#A6ACCD;">      ...unifiedorderObj,</span></span>
<span class="line"><span style="color:#A6ACCD;">      sign,</span></span>
<span class="line"><span style="color:#A6ACCD;">    };</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u5C06\u9700\u8981 post \u51FA\u53BB\u7684\u8BA2\u5355\u53C2\u6570\uFF0C\u8F6C\u6362\u4F4D xml \u683C\u5F0F</span></span>
<span class="line"><span style="color:#A6ACCD;">    const builder = new xml2js.Builder({ rootName: &#39;xml&#39;, headless: true });</span></span>
<span class="line"><span style="color:#A6ACCD;">    const unifiedorderXML = builder.buildObject(unifiedorderWithSign);</span></span>
<span class="line"><span style="color:#A6ACCD;">    const result = await axios({</span></span>
<span class="line"><span style="color:#A6ACCD;">      url: &#39;https://api.mch.weixin.qq.com/pay/unifiedorder&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">      method: &#39;POST&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">      data: unifiedorderXML,</span></span>
<span class="line"><span style="color:#A6ACCD;">      headers: { &#39;content-type&#39;: &#39;text/xml&#39; },</span></span>
<span class="line"><span style="color:#A6ACCD;">    });</span></span>
<span class="line"><span style="color:#A6ACCD;">    // result \u662F\u4E00\u4E2A xml \u7ED3\u6784\u7684 response\uFF0C\u8F6C\u6362\u4E3A jsonObject\uFF0C\u5E76\u8FD4\u56DE\u524D\u7AEF</span></span>
<span class="line"><span style="color:#A6ACCD;">    xml2js.parseString(result.data, (err, parsedResult) =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">      if (parsedResult.xml) {</span></span>
<span class="line"><span style="color:#A6ACCD;">        if (parsedResult.xml.return_code[0] === &#39;SUCCESS&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &amp;&amp; parsedResult.xml.result_code[0] === &#39;SUCCESS&#39;) {</span></span>
<span class="line"><span style="color:#A6ACCD;">          // \u5F85\u7B7E\u540D\u7684\u539F\u59CB\u652F\u4ED8\u6570\u636E</span></span>
<span class="line"><span style="color:#A6ACCD;">          const replyData = {</span></span>
<span class="line"><span style="color:#A6ACCD;">            appId: parsedResult.xml.appid[0],</span></span>
<span class="line"><span style="color:#A6ACCD;">            timeStamp: (Date.now() / 1000).toString(),</span></span>
<span class="line"><span style="color:#A6ACCD;">            nonceStr: parsedResult.xml.nonce_str[0],</span></span>
<span class="line"><span style="color:#A6ACCD;">            package: \`prepay_id=\${parsedResult.xml.prepay_id[0]}\`,</span></span>
<span class="line"><span style="color:#A6ACCD;">            signType: &#39;MD5&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">          };</span></span>
<span class="line"><span style="color:#A6ACCD;">          replyData.paySign = getSignData(replyData, config.wxPayApiKey);</span></span>
<span class="line"><span style="color:#A6ACCD;">          reply(replyData);</span></span>
<span class="line"><span style="color:#A6ACCD;">        }</span></span>
<span class="line"><span style="color:#A6ACCD;">      }</span></span>
<span class="line"><span style="color:#A6ACCD;">    });</span></span>
<span class="line"><span style="color:#A6ACCD;">  },</span></span>
<span class="line"><span style="color:#A6ACCD;">  config: {</span></span>
<span class="line"><span style="color:#A6ACCD;">    tags: [&#39;api&#39;, GROUP_NAME],</span></span>
<span class="line"><span style="color:#A6ACCD;">    description: &#39;\u652F\u4ED8\u67D0\u6761\u8BA2\u5355&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">    validate: {</span></span>
<span class="line"><span style="color:#A6ACCD;">      params: {</span></span>
<span class="line"><span style="color:#A6ACCD;">        orderId: Joi.string().required(),</span></span>
<span class="line"><span style="color:#A6ACCD;">      },</span></span>
<span class="line"><span style="color:#A6ACCD;">      ...jwtHeaderDefine,</span></span>
<span class="line"><span style="color:#A6ACCD;">    },</span></span>
<span class="line"><span style="color:#A6ACCD;">  },</span></span>
<span class="line"><span style="color:#A6ACCD;">},</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h3 id="_3-\u5546\u6237-server-\u8C03\u7528\u518D\u6B21\u7B7E\u540D" tabindex="-1">3. \u5546\u6237 server \u8C03\u7528\u518D\u6B21\u7B7E\u540D <a class="header-anchor" href="#_3-\u5546\u6237-server-\u8C03\u7528\u518D\u6B21\u7B7E\u540D" aria-hidden="true">#</a></h3><p>\u5546\u6237 server \u8C03\u7528\u518D\u6B21\u7B7E\u540D\uFF0C\u516C\u5171 API \u53C2\u89C1 <a href="https://pay.weixin.qq.com/wiki/doc/api/wxa/wxa_api.php?chapter=7_7&amp;index=3" target="_blank" rel="noreferrer">\u518D\u6B21\u7B7E\u540D API</a>\u3002</p><p>\u6B64\u6B65\u9AA4\u7531\u5FAE\u4FE1\u5C0F\u7A0B\u5E8F\u524D\u7AEF\u5BA2\u6237\u7AEF\u5B9E\u73B0\uFF0C\u5C06\u6B65\u9AA4 2 \u4E2D\uFF0C/orders/{orderId}/pay \u63A5\u53E3\u8FD4\u56DE\u7684\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355\u7B7E\u540D\u6570\u636E\uFF0C\u4F9D\u6B21\u586B\u5165 wx.requestPayment\uFF0C\u5373\u53EF\u5728\u5C0F\u7A0B\u5E8F\u7684\u5BA2\u6237\u7AEF\u5524\u8D77\u652F\u4ED8\u754C\u9762\uFF0C\u5E76\u5B8C\u6210\u540E\u7EED\u7684\u652F\u4ED8\u64CD\u4F5C\u6D41\u7A0B\u3002\u7528\u6237\u652F\u4ED8\u6210\u529F\u540E\uFF0C\u5FAE\u4FE1\u5E73\u53F0\u4F1A\u81EA\u52A8\u89E6\u53D1\u6B65\u9AA4 4 \u4E2D\u7684\u652F\u4ED8\u6210\u529F\u63A8\u9001\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">wx.requestPayment(</span></span>
<span class="line"><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  &#39;timeStamp&#39;: &#39;&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">  &#39;nonceStr&#39;: &#39;&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">  &#39;package&#39;: &#39;&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">  &#39;signType&#39;: &#39;MD5&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">  &#39;paySign&#39;: &#39;&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">  &#39;success&#39;:function(res){},</span></span>
<span class="line"><span style="color:#A6ACCD;">  &#39;fail&#39;:function(res){},</span></span>
<span class="line"><span style="color:#A6ACCD;">  &#39;complete&#39;:function(res){}</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h3 id="_4-\u5546\u6237-server-\u63A5\u6536\u652F\u4ED8\u901A\u77E5" tabindex="-1">4. \u5546\u6237 server \u63A5\u6536\u652F\u4ED8\u901A\u77E5 <a class="header-anchor" href="#_4-\u5546\u6237-server-\u63A5\u6536\u652F\u4ED8\u901A\u77E5" aria-hidden="true">#</a></h3><p>\u7528\u6237\u5B8C\u6210\u652F\u4ED8\u884C\u4E3A\u540E\uFF0C\u5546\u6237 server \u63A5\u6536\u652F\u4ED8\u901A\u77E5\uFF0CAPI \u53C2\u89C1 <a href="https://pay.weixin.qq.com/wiki/doc/api/wxa/wxa_api.php?chapter=9_7" target="_blank" rel="noreferrer">\u652F\u4ED8\u7ED3\u679C\u901A\u77E5 API</a> \u3002</p><p>\u5FAE\u4FE1\u5BF9\u5546\u6237\u540E\u53F0\u901A\u77E5\u4EA4\u4E92\u65F6\uFF0C\u5982\u679C\u5FAE\u4FE1\u6536\u5230\u5546\u6237\u7684\u5E94\u7B54\u4E0D\u662F\u6210\u529F\u6216\u8D85\u65F6\uFF0C\u5FAE\u4FE1\u8BA4\u4E3A\u901A\u77E5\u5931\u8D25\uFF0C\u5FAE\u4FE1\u4F1A\u901A\u8FC7\u4E00\u5B9A\u7684\u7B56\u7565\u5B9A\u671F\u91CD\u65B0\u53D1\u8D77\u901A\u77E5\uFF0C\u5C3D\u53EF\u80FD\u63D0\u9AD8\u901A\u77E5\u7684\u6210\u529F\u7387\uFF0C\u4F46\u4E0D\u4FDD\u8BC1\u901A\u77E5\u6700\u7EC8\u80FD\u6210\u529F\u3002\uFF08\u901A\u77E5\u9891\u7387\u4E3A 15/15/30/180/1800/1800/1800/1800/3600\uFF0C\u5355\u4F4D\uFF1A\u79D2\u3002\uFF09</p><p>\u5546\u6237\u5E94\u7B54\u6210\u529F\u7684\u8FD4\u56DE\u6570\u636E\u7ED3\u6784\u662F\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&lt;xml&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  &lt;return_code&gt;&lt;![CDATA[SUCCESS]]&gt;&lt;/return_code&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  &lt;return_msg&gt;&lt;![CDATA[OK]]&gt;&lt;/return_msg&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;/xml&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5B9E\u73B0\u63A5\u53E3 POST /orders/pay/notify\uFF0C\u5728\u6838\u5BF9\u8BA2\u5355\u4FE1\u606F\u6821\u9A8C\u6210\u529F\u540E\uFF0C\u9700\u8981\u8FD4\u56DE\u5FAE\u4FE1\u4E0A\u8FF0\u7684 XML \u5B57\u7B26\u4E32\u4FE1\u606F\uFF0C\u5426\u5219\u8FD4\u56DE return_code \u4E3A <code>FAIL</code>\uFF0C\u5E76\u5728 return_msg \u4E2D\u9644\u5E26\u53C2\u6570\u6821\u9A8C\u9519\u8BEF\u8BF4\u660E\u3002/orders/pay/notify \u5728 hapi \u7684 API \u63A5\u53E3\u4E2D config.auth \u5E94\u8BE5\u8BBE\u7F6E\u4E3A <code>false</code>\uFF0C\u4E0D\u8FDB\u5165 JWT \u7684\u7528\u6237\u8BA4\u8BC1\u6D41\u7A0B\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  method: &#39;POST&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">  path: \`/\${GROUP_NAME}/pay/notify\`,</span></span>
<span class="line"><span style="color:#A6ACCD;">  handler: async (request, reply) =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">    xml2js.parseString(request.payload, async (err, parsedResult) =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">      if (parsedResult.xml.return_code[0] === &#39;SUCCESS&#39;) {</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u5FAE\u4FE1\u7EDF\u4E00\u652F\u4ED8\u72B6\u6001\u6210\u529F\uFF0C\u9700\u8981\u68C0\u9A8C\u672C\u5730\u6570\u636E\u7684\u903B\u8F91\u4E00\u81F4\u6027</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u7701\u7565...\u7EC6\u8282\u903B\u8F91\u6821\u9A8C</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u66F4\u65B0\u8BE5\u8BA2\u5355\u7F16\u53F7\u4E0B\u7684\u652F\u4ED8\u72B6\u6001\u672A\u5DF2\u652F\u4ED8</span></span>
<span class="line"><span style="color:#A6ACCD;">        const orderId = parsedResult.xml.out_trade_no[0];</span></span>
<span class="line"><span style="color:#A6ACCD;">        const orderResult = await models.orders.findOne({ where: { id: orderId } });</span></span>
<span class="line"><span style="color:#A6ACCD;">        orderResult.payment_status = &#39;1&#39;;</span></span>
<span class="line"><span style="color:#A6ACCD;">        await orderResult.save();</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u8FD4\u56DE\u5FAE\u4FE1\uFF0C\u6821\u9A8C\u6210\u529F</span></span>
<span class="line"><span style="color:#A6ACCD;">        const retVal = {</span></span>
<span class="line"><span style="color:#A6ACCD;">          return_code: &#39;SUCCESS&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">          return_msg: &#39;OK&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">        };</span></span>
<span class="line"><span style="color:#A6ACCD;">        const builder = new xml2js.Builder({</span></span>
<span class="line"><span style="color:#A6ACCD;">          rootName: &#39;xml&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">          headless: true,</span></span>
<span class="line"><span style="color:#A6ACCD;">        });</span></span>
<span class="line"><span style="color:#A6ACCD;">        reply(builder.buildObject(retVal));</span></span>
<span class="line"><span style="color:#A6ACCD;">      }</span></span>
<span class="line"><span style="color:#A6ACCD;">    });</span></span>
<span class="line"><span style="color:#A6ACCD;">  },</span></span>
<span class="line"><span style="color:#A6ACCD;">  config: {</span></span>
<span class="line"><span style="color:#A6ACCD;">    tags: [&#39;api&#39;, GROUP_NAME],</span></span>
<span class="line"><span style="color:#A6ACCD;">    description: &#39;\u5FAE\u4FE1\u652F\u4ED8\u6210\u529F\u7684\u6D88\u606F\u63A8\u9001&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">    auth: false,</span></span>
<span class="line"><span style="color:#A6ACCD;">  },</span></span>
<span class="line"><span style="color:#A6ACCD;">},</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><blockquote><p><strong>GitHub \u53C2\u8003\u4EE3\u7801</strong> <a href="https://github.com/yeshengfei/hapi-tutorial/tree/master/chapter13/hapi-tutorial-1" target="_blank" rel="noreferrer">chapter13/hapi-tutorial-1</a></p></blockquote><h2 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3 <a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a></h2><p>\u5173\u952E\u8BCD\uFF1A\u5FAE\u4FE1\u652F\u4ED8\uFF0C\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355\uFF0C\u652F\u4ED8\u901A\u77E5\uFF0CXML \u6570\u636E\u901A\u4FE1</p><p>\u672C\u5C0F\u8282\u56F4\u7ED5\u5FAE\u4FE1\u652F\u4ED8\u63A5\u5165\u7684\u56DB\u6B65\u9AA4\uFF0C\u505A\u4E86\u63A5\u5165\u6D41\u7A0B\u4E0A\u7684\u8BB2\u89E3\u3002\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u5C24\u5176\u6CE8\u610F\u652F\u4ED8\u63A5\u5165\u4EE5 XML \u683C\u5F0F\u6570\u636E\u8FDB\u884C\u6570\u636E\u4EA4\u6362\uFF0C\u7B7E\u540D\u6570\u636E\u7684\u7B97\u6CD5\u4E00\u81F4\u6027\u3002\u5269\u4E0B\u7684\u5BF9\u7167\u7740\u6587\u6863\uFF0C\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u5904\u7406\u597D\u5B57\u6BB5\u7684\u5BF9\u5E94\uFF0C\u4FBF\u80FD\u987A\u5229\u628A\u6D41\u7A0B\u8D70\u5B8C\u3002</p><p><strong>\u672C\u5C0F\u8282\u53C2\u8003\u4EE3\u7801\u6C47\u603B</strong></p><p>\u5C0F\u7A0B\u5E8F\u5185\u8C03\u7528\u767B\u5F55\u63A5\u53E3\uFF1A<a href="https://developers.weixin.qq.com/miniprogram/dev/api/api-login.html?t=20161122" target="_blank" rel="noreferrer">\u5C0F\u7A0B\u5E8F\u767B\u5F55 API</a></p><p>\u5546\u6237 server \u8C03\u7528\u652F\u4ED8\u7EDF\u4E00\u4E0B\u5355\uFF1A<a href="https://pay.weixin.qq.com/wiki/doc/api/wxa/wxa_api.php?chapter=9_1&amp;index=1" target="_blank" rel="noreferrer">\u7EDF\u4E00\u4E0B\u5355 API</a></p><p>\u5546\u6237 server \u8C03\u7528\u518D\u6B21\u7B7E\u540D\uFF1A<a href="https://pay.weixin.qq.com/wiki/doc/api/wxa/wxa_api.php?chapter=7_7&amp;index=3" target="_blank" rel="noreferrer">\u518D\u6B21\u7B7E\u540D API</a></p><p>\u5546\u6237 server \u63A5\u6536\u652F\u4ED8\u901A\u77E5\uFF1A<a href="https://pay.weixin.qq.com/wiki/doc/api/wxa/wxa_api.php?chapter=9_7" target="_blank" rel="noreferrer">\u652F\u4ED8\u7ED3\u679C\u901A\u77E5 API</a></p><p>GitHub\u53C2\u8003\u4EE3\u7801\uFF1A<a href="https://github.com/yeshengfei/hapi-tutorial/tree/master/chapter13/hapi-tutorial-1" target="_blank" rel="noreferrer">chapter13/hapi-tutorial-1</a></p>`,36),r=[l];function o(t,c,i,A,C,d){return a(),n("div",null,r)}const h=s(p,[["render",o]]);export{D as __pageData,h as default};
