import{_ as s,c as a,o as n,d as e}from"./app.c4f45da1.js";const h=JSON.parse('{"title":"\u62D3\u5C55\u7BC7 2-\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5 \u2014\u2014 \u4F7F\u7528 Lab & Code","description":"","frontmatter":{},"headers":[{"level":2,"title":"Lab & Code","slug":"lab-code","link":"#lab-code","children":[{"level":3,"title":"Lab","slug":"lab","link":"#lab","children":[]},{"level":3,"title":"Code","slug":"code","link":"#code","children":[]},{"level":3,"title":"\u4F7F\u7528 Lab & Code \u6D4B\u8BD5\u6C42\u548C\u6A21\u5757","slug":"\u4F7F\u7528-lab-code-\u6D4B\u8BD5\u6C42\u548C\u6A21\u5757","link":"#\u4F7F\u7528-lab-code-\u6D4B\u8BD5\u6C42\u548C\u6A21\u5757","children":[]}]},{"level":2,"title":"\u5728 hapi \u4E2D\u6D4B\u8BD5 API \u63A5\u53E3","slug":"\u5728-hapi-\u4E2D\u6D4B\u8BD5-api-\u63A5\u53E3","link":"#\u5728-hapi-\u4E2D\u6D4B\u8BD5-api-\u63A5\u53E3","children":[]},{"level":2,"title":"\u5E38\u7528\u7684\u65AD\u8A00\u8868\u8FBE\u5F0F","slug":"\u5E38\u7528\u7684\u65AD\u8A00\u8868\u8FBE\u5F0F","link":"#\u5E38\u7528\u7684\u65AD\u8A00\u8868\u8FBE\u5F0F","children":[]},{"level":2,"title":"coverage","slug":"coverage","link":"#coverage","children":[]},{"level":2,"title":"\u4F7F\u7528 .labrc.js \u6765\u8FDB\u884C\u914D\u7F6E","slug":"\u4F7F\u7528-labrc-js-\u6765\u8FDB\u884C\u914D\u7F6E","link":"#\u4F7F\u7528-labrc-js-\u6765\u8FDB\u884C\u914D\u7F6E","children":[]},{"level":2,"title":"\u5C0F\u7ED3","slug":"\u5C0F\u7ED3","link":"#\u5C0F\u7ED3","children":[]}],"relativePath":"Node.js/\u57FA\u4E8E hapi \u7684 Node.js \u5C0F\u7A0B\u5E8F\u540E\u7AEF\u5F00\u53D1\u5B9E\u8DF5\u6307\u5357/\u62D3\u5C55\u7BC7 2-\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5 \u2014\u2014 \u4F7F\u7528 Lab and Code.md"}'),l={name:"Node.js/\u57FA\u4E8E hapi \u7684 Node.js \u5C0F\u7A0B\u5E8F\u540E\u7AEF\u5F00\u53D1\u5B9E\u8DF5\u6307\u5357/\u62D3\u5C55\u7BC7 2-\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5 \u2014\u2014 \u4F7F\u7528 Lab and Code.md"},p=e(`<h1 id="\u62D3\u5C55\u7BC7-2-\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5-\u2014\u2014-\u4F7F\u7528-lab-code" tabindex="-1">\u62D3\u5C55\u7BC7 2-\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5 \u2014\u2014 \u4F7F\u7528 Lab &amp; Code <a class="header-anchor" href="#\u62D3\u5C55\u7BC7-2-\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5-\u2014\u2014-\u4F7F\u7528-lab-code" aria-hidden="true">#</a></h1><hr><h1 id="\u62D3\u5C55\u7BC7-2\uFF1A\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5-\u2014\u2014-\u4F7F\u7528-lab-code" tabindex="-1">\u62D3\u5C55\u7BC7 2\uFF1A\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5 \u2014\u2014 \u4F7F\u7528 Lab &amp; Code <a class="header-anchor" href="#\u62D3\u5C55\u7BC7-2\uFF1A\u7CFB\u7EDF\u7A33\u5B9A\u6027\u6D4B\u8BD5-\u2014\u2014-\u4F7F\u7528-lab-code" aria-hidden="true">#</a></h1><p>\u6D4B\u8BD5\u6846\u67B6\uFF0C\u662F\u8FD0\u884C\u6D4B\u8BD5\u7684\u5DE5\u5177\u3002\u901A\u8FC7\u5B83\uFF0C\u53EF\u4EE5\u4E3A JavaScript \u5E94\u7528\u6DFB\u52A0\u6D4B\u8BD5\uFF0C\u4ECE\u800C\u4FDD\u8BC1\u4EE3\u7801\u7684\u8D28\u91CF\u3002\u73B0\u884C\u7684 Javascript \u5E38\u7528\u6D41\u884C\u6D4B\u8BD5\u5E93\u6709 Jasmine\uFF0CMocha\uFF0C Karma \u7B49\uFF0C\u867D\u7136\u6846\u67B6\u7684\u540D\u79F0\u4E0D\u540C\uFF0C\u4F46\u80CC\u540E\u7684\u6838\u5FC3\u5957\u4EF6\u5374\u5927\u540C\u5C0F\u5F02\u3002</p><p>\u4EE5\u5E38\u89C1\u7684 JavaScript \u5355\u5143\u6D4B\u8BD5\u6846\u67B6 Mocha \u4E3A\u4F8B\u3002\u6211\u4EEC\u8981\u4E3A\u4E00\u4E2A\u6C42\u548C\u6A21\u5757 <code>sum</code> \u65B9\u6CD5\u6765\u505A\u4E00\u4E2A\u7B80\u5355\u7684\u5355\u5143\u6D4B\u8BD5\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// sum.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">module.exports = (x, y) =&gt; x + y</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// sum.test.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">const sum = require(&#39;./sum.js&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">const expect = require(&#39;chai&#39;).expect;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u6D4B\u8BD5\u5957\u4EF6(test suite) -- describe</span></span>
<span class="line"><span style="color:#A6ACCD;">describe(&#39;\u6C42\u548C\u6A21\u5757\u7684\u6D4B\u8BD5&#39;, () =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">  // \u6D4B\u8BD5\u7528\u4F8B(test case) -- it</span></span>
<span class="line"><span style="color:#A6ACCD;">  it(&#39;1 \u52A0 2 \u5E94\u8BE5\u7B49\u4E8E 3&#39;, () =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u6D4B\u8BD5\u65AD\u8A00(test expect) -- expect</span></span>
<span class="line"><span style="color:#A6ACCD;">    expect(sum(1, 2)).to.be.equal(3);</span></span>
<span class="line"><span style="color:#A6ACCD;">  });</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u7531\u6848\u4F8B\u4E2D\u7684\u6CE8\u89C6\u6807\u8BB0\u53EF\u4EE5\u5927\u81F4\u4E86\u89E3\u5230\u5355\u5143\u6D4B\u8BD5\u7684\u4E09\u4E2A\u5173\u952E\u672F\u8BED\uFF1A</p><ul><li><strong>describe</strong>\uFF1A\u6D4B\u8BD5\u5957\u4EF6</li><li><strong>it</strong>\uFF1A\u6D4B\u8BD5\u7528\u4F8B</li><li><strong>expect</strong>\uFF1A\u6D4B\u8BD5\u65AD\u8A00</li></ul><p>\u4E00\u4E2A\u5B8C\u6574\u7684\u6D4B\u8BD5\uFF0C\u53EF\u4EE5\u7531\u591A\u4E2A describe \u7EC4\u6210\uFF1B \u6BCF\u4E2A describe \u91CC\u53EF\u4EE5\u5305\u542B\u591A\u4E2A describe \u6216\u8005\u591A\u4E2A it \uFF1B \u6BCF\u4E2A it \u91CC\u53EF\u4EE5\u5305\u6DB5\u591A\u4E2A expect\uFF0C\u6765\u6700\u7EC8\u63CF\u8FF0\u6BCF\u4E2A\u6D4B\u8BD5\u7528\u4F8B\uFF0C\u5F97\u5230\u6700\u7EC8\u7684\u7A0B\u5E8F\u6267\u884C\u7ED3\u679C\u6821\u9A8C\u3002</p><p>\u5176\u4ED6\u8FD8\u6709\u4E00\u4E9B\u9AD8\u7EA7\u7684\u5C5E\u6027\uFF0C\u8BF8\u5982 before / after / beforeEach / afterEach \u7B49\u6D4B\u8BD5\u7528\u4F8B\u751F\u547D\u5468\u671F\u7684\u94A9\u5B50\uFF0C\u4EE5\u53CA\u5176\u4ED6\u7279\u6027\uFF0C\u6211\u4EEC\u53EF\u4EE5\u540E\u7EED\u518D\u7CFB\u7EDF\u5B66\u4E60\u3002</p><h2 id="lab-code" tabindex="-1">Lab &amp; Code <a class="header-anchor" href="#lab-code" aria-hidden="true">#</a></h2><p>Lab &amp; Code \u662F hapi \u7684\u914D\u5957\u6D4B\u8BD5\u6846\u67B6\u3002</p><h3 id="lab" tabindex="-1">Lab <a class="header-anchor" href="#lab" aria-hidden="true">#</a></h3><p>Lab \u5E93\u652F\u6301 async/await\uFF0C\u5C3D\u53EF\u80FD\u4FDD\u6301\u6D4B\u8BD5\u5F15\u64CE\u7684\u8DB3\u591F\u7B80\u5355\uFF0C\u5E76\u5305\u542B\u4E86\u6211\u4EEC\u5E0C\u671B\u4ECE\u73B0\u4EE3 Node.js \u6D4B\u8BD5\u6846\u67B6\u7A0B\u5E8F\u4E2D\u9700\u8981\u7684\u6240\u6709\u7279\u6027\u3002\u63D0\u4F9B\u4E86 describe \u548C it\uFF0C\u4EE5\u53CA\u751F\u547D\u5468\u671F\u7684\u94A9\u5B50\u7B49\u529F\u80FD\u3002</p><p>Lab \u4EC5\u4F7F\u7528 async/await \u529F\u80FD\uFF0C\u5E76\u5305\u542B\u4E86\u4F60\u5E0C\u671B\u4ECE\u73B0\u4EE3 Node.js\u6D4B\u8BD5\u6846\u67B6\u7A0B\u5E8F\u4E2D\u9700\u8981\u7684\u6240\u6709\u7279\u6027\u3002</p><h3 id="code" tabindex="-1">Code <a class="header-anchor" href="#code" aria-hidden="true">#</a></h3><p>Code \u5E93\u7528\u4E8E\u63D0\u4F9B expect \u65AD\u8A00\u7684\u76F8\u5173\u51FD\u6570\u5E93\uFF0Ccode-expect \u4E0E mocha-expect \u7528\u6CD5\u4E0A\u51E0\u4E4E\u5B8C\u5168\u4E00\u81F4\u3002</p><h3 id="\u4F7F\u7528-lab-code-\u6D4B\u8BD5\u6C42\u548C\u6A21\u5757" tabindex="-1">\u4F7F\u7528 Lab &amp; Code \u6D4B\u8BD5\u6C42\u548C\u6A21\u5757 <a class="header-anchor" href="#\u4F7F\u7528-lab-code-\u6D4B\u8BD5\u6C42\u548C\u6A21\u5757" aria-hidden="true">#</a></h3><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// sum.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">module.exports = (x, y) =&gt; x + y</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// sum.test.js</span></span>
<span class="line"><span style="color:#A6ACCD;">var const = require(&#39;./sum.js&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">// requires for testing</span></span>
<span class="line"><span style="color:#A6ACCD;">const Code        = require(&#39;code&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">const Lab         = require(&#39;lab&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">const lab         = Lab.script();</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u6D4B\u8BD5\u6846\u67B6\u65B9\u6CD5\u63D0\u53D6</span></span>
<span class="line"><span style="color:#A6ACCD;">const describe    = lab.describe;</span></span>
<span class="line"><span style="color:#A6ACCD;">const it          = lab.it;</span></span>
<span class="line"><span style="color:#A6ACCD;">const expect      = Code.expect;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u6D4B\u8BD5\u5957\u4EF6(test suite) -- describe</span></span>
<span class="line"><span style="color:#A6ACCD;">describe(&#39;\u6C42\u548C\u6A21\u5757\u7684\u6D4B\u8BD5&#39;, () =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">  // \u6D4B\u8BD5\u7528\u4F8B(test case) -- it</span></span>
<span class="line"><span style="color:#A6ACCD;">  it(&#39;1 \u52A0 2 \u5E94\u8BE5\u7B49\u4E8E 3&#39;, () =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u6D4B\u8BD5\u65AD\u8A00(test expect) -- expect</span></span>
<span class="line"><span style="color:#A6ACCD;">    expect(sum(1, 2)).to.equal(3);</span></span>
<span class="line"><span style="color:#A6ACCD;">  });</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6267\u884C\u5355\u5143\u6D4B\u8BD5\u547D\u4EE4 <code>node node_modules/lab/bin/lab \\-v</code>\u3002 <code>-v</code> \u7684\u53C2\u6570\u6253\u5370\u6D4B\u8BD5\u660E\u7EC6\uFF0C\u5728\u53EA\u60F3\u5173\u6CE8\u9519\u8BEF\u7684\u6D4B\u8BD5\u7528\u4F8B\u7ED3\u679C\u65F6\uFF0C\u53EF\u4EE5\u4E0D\u5E26\u4E0A\u6B64\u53C2\u6570\uFF0C\u4FDD\u6301\u63A7\u5236\u53F0\u4FE1\u606F\u8F93\u51FA\u7684\u7B80\u6D01\u6027\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">  # \u6267\u884C\u7ED3\u679C</span></span>
<span class="line"><span style="color:#A6ACCD;">  \u6C42\u548C\u6A21\u5757\u7684\u6D4B\u8BD5</span></span>
<span class="line"><span style="color:#A6ACCD;">    \u2713 1 \u52A0 2 \u5E94\u8BE5\u7B49\u4E8E 3</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  1 tests complete</span></span>
<span class="line"><span style="color:#A6ACCD;">  Test duration: 236 ms</span></span>
<span class="line"><span style="color:#A6ACCD;">  No global variable leaks detected</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="\u5728-hapi-\u4E2D\u6D4B\u8BD5-api-\u63A5\u53E3" tabindex="-1">\u5728 hapi \u4E2D\u6D4B\u8BD5 API \u63A5\u53E3 <a class="header-anchor" href="#\u5728-hapi-\u4E2D\u6D4B\u8BD5-api-\u63A5\u53E3" aria-hidden="true">#</a></h2><p>\u4EE5\u6D4B\u8BD5\u7528\u6237\u767B\u5F55\u4E3A\u4F8B\uFF0C\u6211\u4EEC\u5E0C\u671B\u6D4B\u8BD5\u63A5\u53E3\u7684\u5F02\u6B65\u8C03\u7528\u662F\u5426\u8FD4\u56DE 200 \u7684\u72B6\u6001\u7F16\u7801\uFF0C\u5E76\u4E14\u8FD4\u56DE\u7684 response \u7684 result \u4E2D\uFF0C\u7B7E\u53D1\u7684 JWT payload \u4E2D\u7684\u4FE1\u606F\u5982 userid\u3001openid \u7B26\u5408\u9884\u671F\u3002</p><p>hapi \u7684\u6D4B\u8BD5\u7528\u4F8B\u4E2D\uFF0C\u76F4\u63A5\u4F7F\u7528 app.inject\uFF0C\u5373\u670D\u52A1\u5668 server \u81EA\u8EAB\u7684 API\uFF0C\u6765\u5B9E\u73B0\u6307\u5B9A\u63A5\u53E3\u7684\u8C03\u7528\u3002\u4EE3\u7801\u5982\u4E0B\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// users.test.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">const app = require(&#39;../app.js&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// appJWT \u7528\u4E8E\u6682\u5B58\u5DF2\u767B\u5F55\u7684\u7528\u6237jwt\uFF0C\u4F9B\u540E\u7EED\u9700\u8981 jwt \u767B\u5F55\u8BA4\u8BC1\u7684\u6D4B\u8BD5\u5957\u4EF6\u4F7F\u7528</span></span>
<span class="line"><span style="color:#A6ACCD;">let appJWT = &#39;&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">describe(&quot;POST: /user/login-jwt-test&quot;, () =&gt; {  </span></span>
<span class="line"><span style="color:#A6ACCD;">  it(&quot;\u72B6\u6001\u7801200\u5E76\u4E14\u8FD4\u56DE\u4E86\u6B63\u786E\u7684jwt&quot;, async() =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">    const response = await app.inject({</span></span>
<span class="line"><span style="color:#A6ACCD;">      method: &#39;POST&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">      url: &#39;/users/login-jwt-test&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">    })</span></span>
<span class="line"><span style="color:#A6ACCD;">    expect(response.statusCode).to.equal(200)</span></span>
<span class="line"><span style="color:#A6ACCD;">    const JWT = require(&#39;jsonwebtoken&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;">    appJWT = response.result</span></span>
<span class="line"><span style="color:#A6ACCD;">    const jwtPayload = JWT.decode(response.result)</span></span>
<span class="line"><span style="color:#A6ACCD;">    expect(jwtPayload.userId).to.equal(1)</span></span>
<span class="line"><span style="color:#A6ACCD;">    expect(jwtPayload.openId).to.equal(1)</span></span>
<span class="line"><span style="color:#A6ACCD;">  })</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u83B7\u53D6\u5E97\u94FA\u5217\u8868\u9700\u8981\u7528\u5230 authorization \u7684 jwt \u9A8C\u8BC1\uFF0C\u8BED\u6CD5\u5B9E\u73B0\u5982\u4E0B</span></span>
<span class="line"><span style="color:#A6ACCD;">describe(&quot;GET /shops&quot;, () =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">  it(&quot;\u72B6\u6001\u7801200&quot;, async() =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">    const response = await app.inject({</span></span>
<span class="line"><span style="color:#A6ACCD;">      method: &#39;GET&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">      url: &#39;/shops&#39;,</span></span>
<span class="line"><span style="color:#A6ACCD;">      headers: {</span></span>
<span class="line"><span style="color:#A6ACCD;">        authorization: appJWT</span></span>
<span class="line"><span style="color:#A6ACCD;">      }</span></span>
<span class="line"><span style="color:#A6ACCD;">    })</span></span>
<span class="line"><span style="color:#A6ACCD;">    expect(response.statusCode).to.equal(200)  </span></span>
<span class="line"><span style="color:#A6ACCD;">  })</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>hapi \u7684\u5355\u5143\u6D4B\u8BD5\u6846\u67B6\u8FD8\u4F1A\u5728\u6267\u884C\u57FA\u7840\u6D4B\u8BD5\u540E\uFF0C\u8D34\u5FC3\u5730\u68C0\u6D4B\u7CFB\u7EDF\u4E2D\u662F\u5426\u5B58\u5728\u6709\u5168\u5C40\u53D8\u91CF\uFF0C\u4F8B\u5982\u4E0B\u8FF0\u7684\u5168\u5C40\u53D8\u91CF\u58F0\u660E\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">foo = &#39;bar&#39;;</span></span>
<span class="line"><span style="color:#A6ACCD;">hello = &#39;hapi&#39;;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5728\u8FD0\u884C\u5B8C\u6D4B\u8BD5\u540E\uFF0C\u4F1A\u7EA2\u5B57\u63D0\u793A\uFF1A <em>The following leaks were detected:foo\uFF0Chello</em>\uFF0C\u5F15\u8D77\u5F00\u53D1\u8005\u7684\u91CD\u89C6\u3002</p><p>\u800C <em>No global variable leaks detected</em> \u7684\u7ED3\u679C\uFF0C\u5219\u8868\u793A\u7CFB\u7EDF\u4E2D\u4E0D\u5B58\u5728\u4EFB\u4F55\u7684\u5168\u5C40\u53D8\u91CF\uFF0C\u8F83\u597D\u5730\u786E\u4FDD\u4E86\u53D8\u91CF\u4F5C\u7528\u57DF\u7684\u5B89\u5168\u53EF\u9760\u6027\u3002</p><h2 id="\u5E38\u7528\u7684\u65AD\u8A00\u8868\u8FBE\u5F0F" tabindex="-1">\u5E38\u7528\u7684\u65AD\u8A00\u8868\u8FBE\u5F0F <a class="header-anchor" href="#\u5E38\u7528\u7684\u65AD\u8A00\u8868\u8FBE\u5F0F" aria-hidden="true">#</a></h2><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u76F8\u7B49\u6216\u4E0D\u76F8\u7B49 equal()</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(1 + 2).to.be.equal(3);</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(1 + 2).to.be.not.equal(4);</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(hello).to.be.deep.equal({ hapi: &#39;hapi&#39; });</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u5E03\u5C14\u503C\u68C0\u6D4B boolean</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(true).to.be.true();</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(false).to.not.be.false();</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u6570\u636E\u7C7B\u578B\u68C0\u6D4B type</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(&#39;hapi&#39;).to.be.a.string();</span></span>
<span class="line"><span style="color:#A6ACCD;">expect({ hello: &#39;hapi&#39; }).to.be.an.object();</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(hello).to.be.an.instanceof(Hello);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u5305\u542B\u68C0\u6D4B include</span></span>
<span class="line"><span style="color:#A6ACCD;">expect([1,2,3]).to.include(2);</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(&#39;hello hapi&#39;).to.contain(&#39;hapi&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">expect({ hello: &#39;hapi&#39; }).to.include.keys(&#39;hello&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u5224\u7A7A\u68C0\u6D4B empty()</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(&#39;&#39;).to.be.empty();</span></span>
<span class="line"><span style="color:#A6ACCD;">expect([]).to.be.empty();</span></span>
<span class="line"><span style="color:#A6ACCD;">expect({}).to.be.empty();</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u6B63\u5219\u5339\u914D match()</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(&#39;foobar&#39;).to.match(/^foo/);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u65AD\u8A00\u6761\u4EF6\u4E0E and</span></span>
<span class="line"><span style="color:#A6ACCD;">expect(&#39;hello hapi&#39;).to.be.a.string().and.contain(&#39;hapi&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><blockquote><p><a href="https://github.com/hapijs/code/blob/master/API.md" target="_blank" rel="noreferrer">\u65AD\u8A00\u8868\u8FBE\u5F0F\u8BE6\u7EC6\u624B\u518C</a>\u3002</p></blockquote><h2 id="coverage" tabindex="-1">coverage <a class="header-anchor" href="#coverage" aria-hidden="true">#</a></h2><p>coverage \u7528\u4E8E\u91CF\u5316\u4EE3\u7801\u7684\u88AB\u6D4B\u8BD5\u7684\u6BD4\u4F8B\u4E0E\u7A0B\u5EA6\u3002\u88AB\u6D4B\u8BD5\u7684\u4EE3\u7801\u6570\u91CF\u5904\u4EE5\u603B\u4EE3\u7801\u91CF\u6240\u5F97\u7684\u6BD4\u4F8B\u5373\u4E3A\u8986\u76D6\u7387\u503C\u3002</p><p>\u5F00\u542F\u6D4B\u8BD5\u8986\u76D6\u7387\uFF0C\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u5FEB\u901F\u5B9A\u4F4D\u6D4B\u8BD5\u7528\u4F8B\u6240\u672A\u6DB5\u76D6\u7684\u4EE3\u7801\u533A\u57DF\uFF0C\u51B3\u5B9A\u662F\u5426\u8FFD\u52A0\u6D4B\u8BD5\u7528\u4F8B\uFF0C\u5E76\u4E14\u9AD8\u8986\u76D6\u7387\u7684\u6D4B\u8BD5\u7528\u4F8B\uFF0C\u80FD\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u786E\u4FDD\u6211\u4EEC\u7684\u7CFB\u7EDF\u57FA\u7840\u53EF\u6267\u884C\u6027\u3002</p><p>\u6267\u884C <code>node node_modules/lab/bin/lab \\-v \\-c</code>\uFF0C\u8FFD\u52A0\u7684 <code>-c</code> \u53EF\u4EE5\u989D\u5916\u7EDF\u8BA1\u6D4B\u8BD5\u7528\u4F8B\u7684\u8986\u76D6\u7387\u3002\u8986\u76D6\u7387\u7684\u6D4B\u8BD5\u7ED3\u679C\u4F1A\u4EE5\u6BD4\u4F8B\u4E0E\u672A\u8986\u76D6\u660E\u7EC6\u7684\u65B9\u5F0F\u8F93\u51FA\u663E\u793A\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">Coverage: 92.26% (57/736)</span></span>
<span class="line"><span style="color:#A6ACCD;">models/index.js missing coverage on line(s): 35, 41, 49, 50</span></span>
<span class="line"><span style="color:#A6ACCD;">routes/hello-hapi.js missing coverage on line(s): 8, 9</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5F53\u7136\uFF0C\u5B9E\u9645\u7684\u9879\u76EE\u5B9E\u8DF5\u4E2D\uFF0C\u4E1A\u52A1\u578B\u7CFB\u7EDF\u505A\u5F97\u8D8A\u6DF1\uFF0C\u4EE3\u7801\u8986\u76D6\u7387 100% \u7684\u5B9E\u73B0\u6210\u672C\u4E5F\u4F1A\u8D8A\u6765\u8D8A\u9AD8\u3002\u80FD\u6548\u4E0E\u7A33\u5B9A\u6027\u4E4B\u95F4\u7684\u5E73\u8861\u70B9\u4F1A\u5B58\u5728\u4E00\u4E2A\u5206\u5BF8\u7684\u95EE\u9898\uFF0C\u76F2\u76EE\u8FFD\u6C42 100% \u662F\u6781\u5EA6\u4E0D\u53EF\u53D6\u7684\u3002\u6211\u4EEC\u6709\u51E0\u79CD\u624B\u6CD5\u53EF\u4EE5\u5E2E\u52A9\u4F18\u5316 coverage \u7684\u8D28\u91CF\u3002</p><p>1\uFF09\u4F7F\u7528 threshold</p><p>\u9488\u5BF9 coverage\uFF0C\u53EF\u4EE5\u8BBE\u5B9A\u4E00\u4E2A <code>-t (--threshold)</code> \u7684\u53C2\u6570\uFF0C\u6765\u8BBE\u5B9A\u4E00\u4E2A\u57DF\u503C\u3002\u6BD4\u5982 <code>-t 80</code> \u8868\u5F81\u8986\u76D6\u7387\u9AD8\u4E8E 80% \u5373\u6EE1\u8DB3\u8981\u6C42\uFF0C\u7528\u6765\u7C97\u9897\u7C92\u5EA6\u964D\u4F4E\u8986\u76D6\u6807\u51C6\u3002\u7B14\u8005\u4EE5\u4E3A\uFF0C\u8FD9\u6837\u7684\u57DF\u503C\u8C03\u6574\u610F\u4E49\u4E0D\u5927\uFF0C\u66F4\u591A\u7684\u662F\u4E00\u79CD\u5FC3\u7406\u6697\u793A\u3002</p><p>2\uFF09 \u4F7F\u7528 <code>--coverage-exclude</code> / <code>--coverage-path</code></p><p><code>--coverage-exclude</code> \u4E0E <code>--coverage-exclude</code> \u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u76F4\u63A5\u6392\u9664\u6216\u6307\u5B9A\u4E00\u4E9B\u76EE\u5F55\u6216\u662F\u5355\u4E2A\u6587\u4EF6\uFF0C\u6765\u66F4\u52A0\u660E\u786E\u8986\u76D6\u7387\u6240\u8981\u8986\u76D6\u7684\u76EE\u6807\uFF0C\u4F46\u7CBE\u5EA6\u4EC5\u9650\u4E8E\u6587\u4EF6\u3002\u4E00\u4E9B\u5FEB\u901F\u8FED\u4EE3\u7684\u5C40\u90E8\u4E1A\u52A1\u7EA7\u4EE3\u7801\uFF0C\u6216\u662F\u975E\u6838\u5FC3\u4E3B\u5E72\u7684\u4EE3\u7801\uFF0C\u53EF\u4EE5\u91C7\u7528\u8FD9\u79CD\u65B9\u5F0F\u3002</p><p>3\uFF09 \u4F7F\u7528 <code>/* $lab:coverage:(off|on)$ */</code></p><p>\u4F7F\u7528 <code>/* $lab:coverage:(off|on)$ */</code> \u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u5728\u6700\u7EC8\u5355\u4E2A\u6587\u4EF6\u4E2D\u7684\u4EFB\u610F\u4EE3\u7801\u90E8\u5206\uFF0C\u589E\u52A0\u4E00\u4E2A\u7279\u6B8A\u7EA6\u5B9A\u7684\u8868\u8FBE\u5F0F\uFF0C\u6765\u6807\u8BB0\u4E0D\u9700\u8981\u6D4B\u8BD5\u8986\u76D6\u7684\u5C40\u90E8\u4EE3\u7801\uFF0C\u4ECE\u800C\u63D0\u5347\u6D4B\u8BD5\u7684\u6709\u6548\u8986\u76D6\u7387\u3002\u4F8B\u5982\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">/* $lab:coverage:off$ */</span></span>
<span class="line"><span style="color:#A6ACCD;">if (typeof value === &#39;symbol&#39;) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // do something with value</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">/* $lab:coverage:on$ */</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="\u4F7F\u7528-labrc-js-\u6765\u8FDB\u884C\u914D\u7F6E" tabindex="-1">\u4F7F\u7528 .labrc.js \u6765\u8FDB\u884C\u914D\u7F6E <a class="header-anchor" href="#\u4F7F\u7528-labrc-js-\u6765\u8FDB\u884C\u914D\u7F6E" aria-hidden="true">#</a></h2><p>.labrc.js \u662F Lab \u7684\u76F4\u89C2\u914D\u7F6E\u6587\u4EF6\uFF0C\u6211\u4EEC\u628A .labrc.js \u653E\u5728\u5F53\u524D\u5DE5\u7A0B\u76EE\u5F55\u7684\u6839\u76EE\u5F55\uFF0C\u540E\u7EED\u6267\u884C Lab \u7684\u6307\u4EE4\uFF0C\u5C06\u81EA\u52A8\u4ECE .labrc.js \u4E2D\u63D0\u53D6\u76F8\u5E94\u7684\u8FD0\u884C\u53C2\u6570\u3002\u4E0B\u9762\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u4F8B\u5B50\u7B49\u4EF7\u4E8E\u6267\u884C\u4E86 <code>lab \\-c \\-t 100</code>\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">module.exports = {</span></span>
<span class="line"><span style="color:#A6ACCD;">  coverage: true, //\u5F00\u542F\u8986\u76D6\u7387\u6D4B\u8BD5</span></span>
<span class="line"><span style="color:#A6ACCD;">  threshold: 100,  //\u8986\u76D6\u7387\u6401\u503C\u4E3A 100%</span></span>
<span class="line"><span style="color:#A6ACCD;">};</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><blockquote><p><a href="https://github.com/hapijs/lab" target="_blank" rel="noreferrer">Lab \u8BF4\u660E\u624B\u518C</a>\u3002</p></blockquote><h2 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3 <a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a></h2><p>\u5173\u952E\u8BCD\uFF1A\u5355\u5143\u6D4B\u8BD5\uFF0CLab\uFF0CCode\uFF0Ccoverage \u8986\u76D6\u7387</p><p>\u672C\u5C0F\u8282\uFF0C\u6211\u4EEC\u4ECB\u7ECD\u4E86\u5982\u4F55\u4F7F\u7528 Lab &amp; Code\uFF0C\u89E3\u51B3\u6D4B\u8BD5\u7528\u4F8B\u8FC7\u7A0B\u4E2D\u6240\u9700\u8981\u89E3\u51B3\u7684\u5E38\u89C1\u95EE\u9898\uFF0C\u4ECE\u5957\u4EF6\u5B9A\u4E49\u3001\u7528\u4F8B\u58F0\u660E\uFF0C\u5230\u6700\u7EC8\u7684\u65AD\u8A00\u9A8C\u8BC1\uFF0C\u5E76\u901A\u8FC7 coverage \u91CF\u5316\u6D4B\u8BD5\u7528\u4F8B\u7684\u8986\u76D6\u7387\u3002</p><p>\u57FA\u4E8E REST \u63A5\u53E3\u7684\u5F00\u53D1\uFF0C\u4E00\u4E2A\u76F8\u5BF9\u597D\u7684\u4E60\u60EF\u662F\u5148\u5B8C\u6210\u63A5\u53E3\u6587\u6863\u7684\u4E66\u5199\u3002\u5229\u7528 hapi-swagger \u548C Joi\uFF0C\u53EF\u4EE5\u9AD8\u6548\u5730\u5E2E\u52A9\u6211\u4EEC\u5B8C\u6210\u63A5\u53E3\u6587\u6863\u5316\u4EFB\u52A1\u3002\u63A5\u4E0B\u6765\u4FBF\u662F\u4E66\u5199\u6D4B\u8BD5\u7528\u4F8B\uFF0C\u5C06\u6D4B\u8BD5\u7528\u4F8B\u901A\u8FC7 BDD \u6216\u8005 TDD \u7684\u65B9\u5F0F\uFF0C\u8FDB\u884C\u7EC4\u5408\u4E66\u5199\uFF0C\u6700\u7EC8\u5F97\u5230\u4E00\u5957\u6587\u6863\u5B8C\u5907\u3001\u6D4B\u8BD5\u914D\u5957\u7684\u826F\u597D\u63A5\u53E3\u3002\u8FD9\u662F\u4E00\u4E2A\u503C\u5F97\u53BB\u6301\u7EED\u5B9E\u8DF5\u4F18\u5316\u4E0E\u4F53\u4F1A\u7684\u597D\u4E60\u60EF\u3002</p><p><strong>\u672C\u5C0F\u8282\u53C2\u8003\u4EE3\u7801\u6C47\u603B</strong></p><p><a href="https://github.com/hapijs/code/blob/master/API.md" target="_blank" rel="noreferrer">\u65AD\u8A00\u8868\u8FBE\u5F0F\u8BE6\u7EC6\u624B\u518C</a></p><p><a href="https://github.com/hapijs/lab" target="_blank" rel="noreferrer">Lab \u8BF4\u660E\u624B\u518C</a></p>`,56),o=[p];function c(t,r,i,C,A,d){return n(),a("div",null,o)}const b=s(l,[["render",c]]);export{h as __pageData,b as default};
