import{_ as a,c as e,o as t,d as p}from"./app.e0e4ea3a.js";const _=JSON.parse('{"title":"\u524D\u7F6E\u57FA\u7840-\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u524D\u8A00","slug":"\u524D\u8A00","link":"#\u524D\u8A00","children":[]},{"level":2,"title":"\u91CF\u5316\u4EA4\u6613\u7684\u672C\u8D28","slug":"\u91CF\u5316\u4EA4\u6613\u7684\u672C\u8D28","link":"#\u91CF\u5316\u4EA4\u6613\u7684\u672C\u8D28","children":[]},{"level":2,"title":"\u91CF\u5316\u4EA4\u6613\u7684\u53D1\u5C55","slug":"\u91CF\u5316\u4EA4\u6613\u7684\u53D1\u5C55","link":"#\u91CF\u5316\u4EA4\u6613\u7684\u53D1\u5C55","children":[]},{"level":2,"title":"\u91CF\u5316\u4EA4\u6613\u7684\u4F18\u52BF","slug":"\u91CF\u5316\u4EA4\u6613\u7684\u4F18\u52BF","link":"#\u91CF\u5316\u4EA4\u6613\u7684\u4F18\u52BF","children":[]},{"level":2,"title":"\u672C\u5C0F\u518C\u5E94\u7528\u573A\u666F","slug":"\u672C\u5C0F\u518C\u5E94\u7528\u573A\u666F","link":"#\u672C\u5C0F\u518C\u5E94\u7528\u573A\u666F","children":[]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[]}],"relativePath":"\u5176\u4ED6/Python\u6570\u636E\u5206\u6790\u5B9E\u6218\uFF1A\u6784\u5EFA\u80A1\u7968\u91CF\u5316\u4EA4\u6613\u7CFB\u7EDF/\u524D\u7F6E\u57FA\u7840-\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB.md"}'),s={name:"\u5176\u4ED6/Python\u6570\u636E\u5206\u6790\u5B9E\u6218\uFF1A\u6784\u5EFA\u80A1\u7968\u91CF\u5316\u4EA4\u6613\u7CFB\u7EDF/\u524D\u7F6E\u57FA\u7840-\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB.md"},i=p('<h1 id="\u524D\u7F6E\u57FA\u7840-\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB" tabindex="-1">\u524D\u7F6E\u57FA\u7840-\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB <a class="header-anchor" href="#\u524D\u7F6E\u57FA\u7840-\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB" aria-hidden="true">#</a></h1><hr><h1 id="\u524D\u7F6E\u57FA\u7840\uFF1A\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB" tabindex="-1">\u524D\u7F6E\u57FA\u7840\uFF1A\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB <a class="header-anchor" href="#\u524D\u7F6E\u57FA\u7840\uFF1A\u91CF\u5316\u4EA4\u6613\u53CA\u5E94\u7528\u573A\u666F\u7B80\u4ECB" aria-hidden="true">#</a></h1><h2 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h2><p>\u8FD1\u5E74\u6765\u5728\u4EBA\u5DE5\u667A\u80FD\u3001\u4E92\u8054\u7F51\u3001\u5927\u6570\u636E\u8FD9\u4E9B\u7099\u624B\u53EF\u70ED\u7684\u9AD8\u65B0\u6280\u672F\u63A8\u52A8\u4E0B\uFF0C\u4F20\u7EDF\u884C\u4E1A\u5728\u4E0D\u65AD\u5730\u8F6C\u578B\u5347\u7EA7\uFF0C\u800C\u91CF\u5316\u4EA4\u6613\u4F5C\u4E3A\u4E00\u79CD\u65B0\u5174\u7684\u91D1\u878D\u6295\u8D44\u65B9\u6CD5\u6DF1\u53D7\u91D1\u878D\u884C\u4E1A\u4EBA\u58EB\u7684\u5173\u6CE8\u3002</p><p>\u7EB5\u89C2\u5168\u7403\uFF0C\u91CF\u5316\u4EA4\u6613\u5728\u6D77\u5916\u91D1\u878D\u5E02\u573A\u5C5E\u4E8E\u8F83\u4E3A\u6210\u719F\u7684\u6295\u8D44\u7406\u5FF5\u548C\u65B9\u6CD5\uFF0C\u5728\u56FD\u5185\u867D\u7136\u8D77\u6B65\u8F83\u665A\uFF0C\u4F46\u53D1\u5C55\u8FC5\u901F\uFF0C\u76EE\u524D\u4E3B\u6D41\u7684\u91D1\u878D\u673A\u6784\u51E0\u4E4E\u90FD\u5EFA\u7ACB\u8D77\u4E86\u201C\u91CF\u5316\u4EA4\u6613\u201D\u56E2\u961F\uFF0C\u5404\u7C7B\u201C\u91CF\u5316\u4EA4\u6613\u201D\u76F8\u5173\u7684\u4EA7\u54C1\u4E5F\u5982\u96E8\u540E\u6625\u7B0B\u822C\u5730\u6D8C\u73B0\u3002</p><p>\u5BF9\u4E8E\u666E\u901A\u7684\u6295\u8D44\u4EA4\u6613\u8005\u800C\u8A00\uFF0C\u91CF\u5316\u4EA4\u6613\u4F9D\u7136\u662F\u6234\u7740\u795E\u79D8\u7684\u9762\u7EB1\uFF0C\u591A\u6570\u4EBA\u7684\u7B2C\u4E00\u5370\u8C61\u662F\u201C\u9AD8\u5927\u4E0A\u7684\u6280\u672F\u201D\u3001\u201C\u53EF\u4EE5\u8EBA\u7740\u8D5A\u94B1\u7684\u5DE5\u5177\u201D\uFF0C\u6709\u4E9B\u4EBA\u5374\u8BA4\u4E3A\u5B83\u5B8C\u5168\u4E0D\u9760\u8C31\u2026\u2026</p><p>\u53EF\u662F\u201C\u91CF\u5316\u4EA4\u6613\u201D\u5230\u5E95\u662F\u4EC0\u4E48\uFF1F\u80FD\u5E2E\u52A9\u6211\u4EEC\u505A\u4E9B\u4EC0\u4E48\uFF1F\u7B14\u8005\u8BA4\u4E3A\u5728\u5F00\u542F\u201C\u91CF\u5316\u4EA4\u6613\u201D\u5B66\u4E60\u524D\uFF0C\u6211\u4EEC\u6709\u5FC5\u8981\u5148\u7406\u89E3\u201C\u91CF\u5316\u4EA4\u6613\u201D\uFF0C\u6E05\u695A\u201C\u91CF\u5316\u4EA4\u6613\u201D\u6240\u5E26\u7ED9\u6211\u4EEC\u7684\u4EF7\u503C\u3002</p><p>\u672C\u8282\u6211\u4EEC\u5C31\u4ECE\u91CF\u5316\u4EA4\u6613<strong>\u672C\u8D28</strong>\u3001<strong>\u53D1\u5C55</strong>\u3001<strong>\u4F18\u52BF</strong>\u3001\u5E94\u7528\u573A\u666F\u8FD9\u4E9B\u7EF4\u5EA6\u6765\u5256\u6790\u91CF\u5316\u4EA4\u6613\u3002</p><h2 id="\u91CF\u5316\u4EA4\u6613\u7684\u672C\u8D28" tabindex="-1">\u91CF\u5316\u4EA4\u6613\u7684\u672C\u8D28 <a class="header-anchor" href="#\u91CF\u5316\u4EA4\u6613\u7684\u672C\u8D28" aria-hidden="true">#</a></h2><p>\u91CF\u5316\u4EA4\u6613\u5C5E\u4E8E\u4EBA\u5DE5\u667A\u80FD\u7684\u4E00\u4E2A\u5E94\u7528\u5206\u652F\uFF0C\u5B83\u7EFC\u5408\u4E86\u591A\u4E2A\u5B66\u79D1\u7684\u77E5\u8BC6\u548C\u65B9\u6CD5\uFF0C\u7528\u5148\u8FDB\u7684\u6570\u5B66\u6A21\u578B\u4EE3\u66FF\u4EBA\u7684\u4E3B\u89C2\u601D\u7EF4\u5236\u5B9A\u4EA4\u6613\u7B56\u7565\uFF0C\u5229\u7528\u8BA1\u7B97\u673A\u5F3A\u5927\u8FD0\u7B97\u529B\u4ECE\u5E9E\u5927\u7684\u80A1\u7968\u3001\u503A\u5238\u3001\u671F\u8D27\u7B49\u5386\u53F2\u6570\u636E\u4E2D\u56DE\u6D4B\u83B7\u53D6\u4EA4\u6613\u7B56\u7565\u7684\u76C8\u4E8F\u201C\u6982\u7387\u201D\uFF0C\u901A\u8FC7\u7BA1\u7406\u76C8\u4E8F\u7684\u201C\u6982\u7387\u201D\u5E2E\u52A9\u6295\u8D44\u8005\u505A\u51FA\u51C6\u786E\u7684\u51B3\u7B56\u3002</p><p>\u6240\u8C13\u7684\u91CF\u5316\uFF0C\u8868\u9762\u4E0A\u662F\u7BA1\u7406\u76C8\u4E8F\u7684\u201C\u6982\u7387\u201D\uFF0C\u672C\u8D28\u4E0A\u662F\u201C\u968F\u65F6\u8C03\u6574\uFF0C\u5408\u7406\u5E94\u53D8\u201D\u7684\u7B56\u7565\u3002</p><blockquote><p>\u201C\u4E0D\u7BA1\u591A\u4E48\u590D\u6742\u7684\u6A21\u578B\uFF0C\u6CA1\u6709\u4E00\u4E2A\u80FD\u957F\u671F\u4E0D\u53D8\u5730\u4E00\u76F4\u8D5A\u94B1\uFF0C\u56E0\u4E3A\u5E02\u573A\u5728\u53D8\u5316\uFF0C\u4FE1\u606F\u5728\u53D8\u5316\uFF0C\u6211\u4EEC\u4E0D\u662F\u673A\u5668\u7684\u5974\u96B6\uFF0C\u53EA\u6709\u901A\u8FC7\u4E0D\u65AD\u5B66\u4E60\uFF0C\u6301\u7EED\u4E0D\u65AD\u7684\u66F4\u65B0\u81EA\u5DF1\u7684\u6A21\u578B\u548C\u7B56\u7565\uFF0C\u5BFB\u627E\u5E02\u573A\u4E0A\u7684\u89C4\u5F8B\uFF0C\u8BA9\u4F60\u7684\u4EA4\u6613\u7CFB\u7EDF\u8DDF\u4E0A\u53D8\u5316\u672C\u8EAB\uFF0C\u624D\u80FD\u5728\u4EA4\u6613\u5E02\u573A\u4E2D\u7ACB\u4E8E\u4E0D\u8D25\u4E4B\u5730\u3002\u201D \u2014\u2014 \u897F\u8499\u65AF</p></blockquote><p>\u8FD9\u6BB5\u8BDD\u7684\u542B\u4E49\u662F\u8BF4\u4EA4\u6613\u7B56\u7565\u4E0D\u5E94\u8BE5\u662F\u4E00\u6210\u4E0D\u53D8\u7684\uFF0C\u6211\u4EEC\u5BF9\u5386\u53F2\u6570\u636E\u4F5C\u68C0\u9A8C\u53EA\u662F\u628A\u8FC7\u53BB\u7684\u7ECF\u9A8C\u4F5C\u4E3A\u4E00\u79CD\u53C2\u8003\u6307\u5357\uFF0C\u4ECE\u8FC7\u53BB\u7684\u89E3\u8BFB\u4E2D\u53D1\u6398\u51FA\u8574\u85CF\u76C8\u5229\u673A\u4F1A\u7684\u91CD\u590D\u6027\u6A21\u5F0F\uFF0C\u4F46\u662F\u8FC7\u53BB\u5E76\u4E0D\u4EE3\u8868\u672A\u6765\uFF0C\u5386\u53F2\u6D4B\u8BD5\u7ED3\u679C\u548C\u5B9E\u9645\u7ED3\u679C\u4E00\u5B9A\u5B58\u5728\u7740\u5DEE\u5F02\u3002\u5F15\u8D77\u5DEE\u5F02\u7684\u4E3B\u8981\u56E0\u7D20\u53EF\u6982\u62EC\u4E3A\u4EE5\u4E0B\u51E0\u4E2A\uFF1A</p><ul><li><p>\u4EA4\u6613\u8005\u6548\u5E94\uFF0C\u5176\u4ED6\u4EA4\u6613\u8005\u6CE8\u610F\u5230\u4E86\u8FD9\u4E2A\u7B56\u7565\u65B9\u6CD5\u5E76\u5F00\u59CB\u6A21\u4EFF\u5B83\uFF0C\u5BFC\u81F4\u8FD9\u79CD\u65B9\u6CD5\u6548\u679C\u4E0B\u964D\uFF1B</p></li><li><p>\u968F\u673A\u6548\u5E94\uFF0C\u5386\u53F2\u6D4B\u8BD5\u7684\u7ED3\u679C\u53EF\u80FD\u662F\u4E00\u79CD\u968F\u673A\u6027\u7684\u73B0\u8C61\uFF1B</p></li><li><p>\u6700\u4F18\u5316\u77DB\u76FE\uFF0C\u9009\u62E9\u7279\u5B9A\u7684\u53C2\u6570\u964D\u4F4E\u4E86\u4E8B\u540E\u6D4B\u8BD5\u7684\u4EF7\u503C\uFF1B</p></li><li><p>\u8FC7\u5EA6\u62DF\u5408\uFF0C\u4E0E\u5386\u53F2\u6570\u636E\u7684\u543B\u5408\u5EA6\u592A\u9AD8\uFF0C\u5E02\u573A\u884C\u4E3A\u8F7B\u5FAE\u7684\u53D8\u5316\u9020\u6210\u6548\u679C\u7684\u6076\u5316\u3002</p></li></ul><p>\u5BF9\u6B64\uFF0C\u5728\u56DE\u6D4B\u9636\u6BB5\u6211\u4EEC\u5E94\u8BE5\u4ECE\u6839\u6E90\u4E0A\u91CD\u70B9\u5173\u6CE8\u5386\u53F2\u6D4B\u8BD5\u7684\u5C40\u9650\u6027\uFF0C\u907F\u514D\u6C89\u6D78\u4E8E\u5386\u53F2\u56DE\u6D4B\u7684\u5047\u8C61\u4E4B\u4E2D\u3002</p><h2 id="\u91CF\u5316\u4EA4\u6613\u7684\u53D1\u5C55" tabindex="-1">\u91CF\u5316\u4EA4\u6613\u7684\u53D1\u5C55 <a class="header-anchor" href="#\u91CF\u5316\u4EA4\u6613\u7684\u53D1\u5C55" aria-hidden="true">#</a></h2><p>\u5173\u4E8E\u91CF\u5316\u4EA4\u6613\u7684\u53D1\u5C55\uFF0C\u56FD\u5916\u5148\u8FDB\u7684\u8D44\u672C\u5E02\u573A\u4E2D\u91CF\u5316\u4EA4\u6613\u5DF2\u7ECF\u65E5\u8D8B\u6210\u719F\u3002</p><p>\u91D1\u878D\u9738\u4E3B\u7F8E\u56FD\u5728\u8FD9\u4E2A\u9886\u57DF\u53EF\u8C13\u4EBA\u624D\u8F88\u51FA\uFF0C\u8FD9\u91CC\u6211\u4EEC\u4E0D\u5F97\u4E0D\u63D0\u5230\uFF1A</p><blockquote><p>\u534E\u5C14\u8857\u201C\u5BBD\u5BA2\u6559\u7236\u201D\u2014\u2014\u7231\u5FB7\u534E\xB7\u7D22\u666E(Edward Thorp)</p></blockquote><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2020/4/18/1718bb45d0b24ca2~tplv-t2oaga2asx-image.image" alt=""></p><blockquote><p>\u201C\u91CF\u5316\u4EA4\u6613\u4E4B\u795E\u201D\u2014\u2014\u8A79\u59C6\u65AF\xB7\u897F\u8499\u65AF(James Simons)</p></blockquote><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2020/4/18/1718bb57ef5966d6~tplv-t2oaga2asx-image.image" alt=""></p><p>\u4E0B\u9762\u4EE5\u8FD9\u4E24\u4F4D\u5927\u4F6C\u7684\u4F20\u5947\u4E8B\u8FF9\u4E3A\u4E3B\u7EBF\u6765\u4E86\u89E3\u4E0B\u91CF\u5316\u4EA4\u6613\u7684\u53D1\u5C55\u3002</p><p>\u5728\u534E\u5C14\u8857\uFF0C\u5BBD\u5BA2\uFF08quant\uFF09\u5C31\u662F\u6307\u90A3\u4E9B\u4ECE\u4E8B\u91CF\u5316\u4EA4\u6613\u7684\u4EBA\u3002\u4ED6\u4EEC\u5927\u591A\u6570\u662F\u5B66\u6570\u5B66\u6216\u662F\u7269\u7406\u51FA\u8EAB\u7684\uFF0C\u4E0D\u540C\u4E8E\u4F20\u7EDF\u7684\u4F9D\u9760\u76F4\u89C9\u548C\u57FA\u672C\u9762\u5206\u6790\u6295\u8D44\u4EA4\u6613\uFF0C\u4ED6\u4EEC\u8FD0\u7528\u590D\u6742\u7684\u6570\u5B66\u6A21\u578B\u548C\u8BA1\u7B97\u673A\u7684\u8D85\u7EA7\u8FD0\u7B97\u80FD\u529B\u5728\u7A0D\u7EB5\u5373\u901D\u7684\u8BC1\u5238\u5E02\u573A\u4E2D\u76C8\u5229\u3002</p><p>\u7231\u5FB7\u534E\xB7\u7D22\u666E\u5728 1962 \u5E74\u51FA\u7248\u4E86\u300A\u51FB\u8D25\u5E84\u5BB6\u300B\u8FD9\u672C\u4E66\uFF0C\u63CF\u8FF0\u4E86\u4ED6\u5728 21 \u70B9\u6E38\u620F\u4E0A\u6A2A\u626B\u62C9\u65AF\u7EF4\u52A0\u65AF\u591A\u5BB6\u8D4C\u573A\u7684\u5236\u80DC\u6A21\u578B\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2020/4/18/1718bb792638ba20~tplv-t2oaga2asx-image.image" alt=""></p><p>\u4F5C\u4E3A\u4E00\u540D\u6570\u5B66\u5BB6\uFF0C\u7D22\u666E\u7684\u7B56\u7565\u5C31\u662F\u8BA1\u7B9721\u70B9\u6E38\u620F\u8FC7\u7A0B\u4E2D\u83B7\u80DC\u7684\u6982\u7387\u548C\u8D54\u7387\uFF0C\u6839\u636E\u51EF\u5229\u516C\u5F0F\u7BA1\u7406\u4E0B\u6CE8\u8D44\u91D1\uFF0C\u4EE5\u80DC\u7B97\u5927\u5C0F\u91CF\u5316\u4E0B\u6CE8\u8D44\u91D1\uFF0C\u8FD9\u5C31\u662F\u65E9\u671F\u91CF\u5316\u4EA4\u6613\u7684\u96CF\u5F62\u3002</p><p>1965 \u5E74\u7D22\u666E\u628A\u76EE\u5149\u8F6C\u5411\u4E86\u534E\u5C14\u8857\u65B0\u751F\u7684\u80A1\u7968\u6743\u8BC1\u5B9A\u4EF7\uFF0C\u7814\u7A76\u51FA\u4E86\u4E00\u79CD\u53EB\u201C\u79D1\u5B66\u80A1\u7968\u5E02\u573A\u7CFB\u7EDF\u201D\u7684\u91CF\u5316\u4EA4\u6613\u7B56\u7565\u3002</p><p>\u5728 1967 \u5E74\u7D22\u666E\u51FA\u7248\u4E86\u300ABeat the market\uFF1AA Scientific Stock Market System\u300B\u4E00\u4E66\uFF0C\u8BE5\u4E66\u662F\u91CF\u5316\u4EA4\u6613\u7684\u5F00\u5C71\u4E4B\u4F5C\uFF0C\u662F\u7B2C\u4E00\u4E2A\u7CBE\u786E\u7684\u7EAF\u91CF\u5316\u4EA4\u6613\u7B56\u7565\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2020/4/18/1718bc288e77e388~tplv-t2oaga2asx-image.image" alt=""></p><p>1969 \u5E74\u7D22\u666E\u4E0E\u5408\u4F19\u4EBA\u6210\u7ACB\u4E86\u53F2\u4E0A\u7B2C\u4E00\u5BB6\u91CF\u5316\u5BF9\u51B2\u57FA\u91D1\u201C\u53EF\u8F6C\u6362\u5BF9\u51B2\u5408\u4F19\u57FA\u91D1\u201D\u5E76\u5168\u5FC3\u81F4\u529B\u4E8E\u6295\u8D44\u3002</p><p>\u968F\u540E\u91CF\u5316\u4EA4\u6613\u4FBF\u5728\u5168\u7403\u8303\u56F4\u5185\u5FEB\u901F\u53D1\u5C55\uFF0C\u6D8C\u73B0\u51FA\u6307\u6570\u57FA\u91D1\u3001\u5BF9\u51B2\u57FA\u91D1\u3001SmartBeta \u548CFund of Funds\uFF08FOF\uFF09\u7B49\u91CF\u5316\u521B\u65B0\u4EA7\u54C1\u3002</p><p>\u8A79\u59C6\u65AF\xB7\u897F\u8499\u65AF\u662F\u91CF\u5316\u4EA4\u6613\u7684\u53E6\u4E00\u4F4D\u4F20\u5947\u4EBA\u7269\uFF0C\u5982\u679C\u8BF4\u7D22\u666E\u5F00\u521B\u4E86\u91CF\u5316\u4EA4\u6613\uFF0C\u90A3\u4E48\u897F\u8499\u65AF\u5219\u662F\u5C06\u91CF\u5316\u4EA4\u6613\u5E26\u5230\u8FD9\u4E2A\u65F6\u4EE3\u7684\u4EBA\u3002</p><p>\u897F\u8499\u65AF\u5728 1988 \u5E74\u6210\u7ACB\u4E86\u6587\u827A\u590D\u5174\u79D1\u6280\u516C\u53F8\uFF0C\u4F5C\u4E3A\u6709\u53F2\u4EE5\u6765\u6700\u6210\u529F\u7684\u5BF9\u51B2\u57FA\u91D1\uFF0C\u6587\u827A\u590D\u5174\u79D1\u6280\u516C\u53F8\u7684\u65D7\u8230\u57FA\u91D1\u2014\u2014\u5927\u5956\u7AE0\u57FA\u91D1\u57281989-2009 \u7684\u4E8C\u5341\u5E74\u95F4\u53D6\u5F97\u5E74\u5E73\u5747 35% \u5DE6\u53F3\u7684\u56DE\u62A5\uFF0C\u6536\u76CA\u8FDC\u8D85\u5DF4\u83F2\u7279\u3001\u7D22\u7F57\u65AF\u8FD9\u4E9B\u4F20\u7EDF\u4EA4\u6613\u7684\u6295\u8D44\u5927\u5E08 10 \u591A\u4E2A\u767E\u5206\u70B9,\u6BD4\u540C\u671F\u6807\u51C6\u666E\u5C14 500 \u6307\u6570\u7684\u5E74\u5747\u56DE\u62A5\u7387\u8981\u9AD8\u51FA 20 \u591A\u4E2A\u767E\u5206\u70B9\u3002</p><p>\u7EB5\u7136\u662F\u5728 2008 \u5E74\u7684\u6B21\u8D37\u5371\u673A\u65F6\u671F\uFF0C\u8BE5\u57FA\u91D1\u7684\u6295\u8D44\u56DE\u62A5\u7387\u4ECD\u7136\u4FDD\u6301\u7A33\u5B9A\u7684\u6C34\u51C6\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2020/4/18/1718bc44876b4d4a~tplv-t2oaga2asx-image.image" alt=""></p><p>\u4E0D\u8FC7\u91CF\u5316\u4EA4\u6613\u5E76\u4E0D\u662F\u4E07\u80FD\u6CD5\u5B9D\uFF0C2012 \u5E74\u6587\u827A\u590D\u5174\u65D7\u4E0B\u201C\u6587\u827A\u590D\u5174\u673A\u6784\u671F\u8D27\u57FA\u91D1\u201D\uFF08RIFF\uFF09\u4E8F\u635F 3.17%\uFF0C2011 \u5E74\u76C8\u5229\u4EC5\u589E\u957F 1.84%\uFF0C\u5C31\u50CF\u897F\u8499\u65AF\u81EA\u5DF1\u8BF4\u7684\u90A3\u6837\uFF0C\u201C\u6CA1\u6709\u4E00\u4E2A\u7B56\u7565\u80FD\u957F\u671F\u4E0D\u53D8\u5730\u4E00\u76F4\u8D5A\u94B1\uFF0C\u53EA\u6709\u968F\u65F6\u8C03\u6574\u3001\u5408\u7406\u5E94\u53D8\u624D\u80FD\u5728\u4EA4\u6613\u5E02\u573A\u4E2D\u7ACB\u4E8E\u4E0D\u8D25\u4E4B\u5730\u3002\u201D</p><p>\u91CF\u5316\u4EA4\u6613\u6539\u53D8\u4E86\u5168\u7403\u8D44\u4EA7\u7BA1\u7406\u683C\u5C40\uFF0C\u6210\u4E3A\u4E3B\u6D41\u7684\u6295\u8D44\u65B9\u6CD5\uFF0C\u5176\u7BA1\u7406\u89C4\u6A21\u4E5F\u5728\u5FEB\u901F\u589E\u957F\u3002\u76EE\u524D\uFF0C\u5168\u7403\u6700\u5927\u7684\u8D44\u4EA7\u7BA1\u7406\u516C\u53F8\u548C\u5BF9\u51B2\u57FA\u91D1\u90FD\u662F\u57FA\u4E8E\u91CF\u5316\u548C\u6307\u6570\u6295\u8D44\u7684\u673A\u6784\u3002</p><p>\u6211\u4EEC\u90FD\u77E5\u9053\u7F8E\u56FD\u7684\u91D1\u878D\u5E02\u573A\u6210\u719F\u7A0B\u5EA6\u662F\u8FDC\u8D85\u4E2D\u56FD\u7684\uFF0C\u7F8E\u56FD\u7684\u80A1\u5E02\u6709\u8FD1\u4E00\u767E\u591A\u5E74\u7684\u5386\u53F2\uFF0C\u800C\u4E2D\u56FD\u80A1\u5E02\u6210\u7ACB\u81F3\u4ECA\u8FD8\u4E0D\u5230 30 \u5E74\u3002</p><p>\u56DE\u5230\u56FD\u5185\u6765\u770B\uFF0C\u968F\u7740\u56FD\u5BB6\u5C42\u9762\u63A8\u52A8\u4EBA\u5DE5\u667A\u80FD\u3001\u5927\u6570\u636E\u6280\u672F\u7684\u53D1\u5C55\uFF0C\u5927\u91CF\u7684\u91CF\u5316\u57FA\u91D1\u4EA7\u54C1\u548C\u5E73\u53F0\u5982\u96E8\u540E\u6625\u7B0B\u4E00\u822C\u5174\u8D77\uFF0C\u91CF\u5316\u4EA4\u6613\u5728\u56FD\u5185\u4E5F\u6380\u8D77\u70ED\u6F6E\u3002</p><p>\u5C31\u50CF\u4E92\u8054\u7F51\u6280\u672F\u6539\u53D8\u7740\u4F20\u7EDF\u884C\u4E1A\u7684\u6A21\u5F0F\u4E00\u6837\uFF0C\u672A\u6765\u91CF\u5316\u4EA4\u6613\u5FC5\u5B9A\u662F\u56FD\u5185\u91D1\u878D\u5E02\u573A\u7684\u4E00\u4E2A\u5927\u7684\u53D1\u5C55\u8D8B\u52BF\u3002</p><h2 id="\u91CF\u5316\u4EA4\u6613\u7684\u4F18\u52BF" tabindex="-1">\u91CF\u5316\u4EA4\u6613\u7684\u4F18\u52BF <a class="header-anchor" href="#\u91CF\u5316\u4EA4\u6613\u7684\u4F18\u52BF" aria-hidden="true">#</a></h2><p>\u5728\u8FC7\u53BB\u7684\u5168\u7403\u91D1\u878D\u5E02\u573A\u4E2D\uFF0C\u91CF\u5316\u4EA4\u6613\u5F97\u5230\u4E86\u5982\u706B\u5982\u837C\u7684\u53D1\u5C55\uFF0C\u6210\u4E3A\u548C\u4EF7\u503C\u6295\u8D44\u5E76\u5217\u7684\u4E24\u5927\u6295\u8D44\u7406\u8BBA\u4E4B\u4E00\u3002\u90A3\u4E48\u91CF\u5316\u4EA4\u6613\u8FD9\u79CD\u65B0\u7684\u6295\u8D44\u7406\u8BBA\u548C\u6A21\u5F0F\uFF0C\u5F97\u5230\u5927\u529B\u53D1\u5C55\u7684\u91CD\u8981\u539F\u56E0\u662F\u4EC0\u4E48\u5462\uFF1F\u4E0E\u4F20\u7EDF\u7684\u4EA4\u6613\u76F8\u6BD4\u91CF\u5316\u4EA4\u6613\u6709\u54EA\u4E9B\u4F18\u52BF\u5462\uFF1F</p><p>\u4F20\u7EDF\u7684\u4EA4\u6613\u65E0\u8BBA\u662F\u6280\u672F\u9762\u5206\u6790\u8FD8\u662F\u57FA\u672C\u9762\u5206\u6790\u90FD\u5C5E\u4E8E\u5B9A\u6027\u5206\u6790\uFF0C\u5B9A\u6027\u5206\u6790\u901A\u4FD7\u7684\u8BF4\u5C31\u662F\u4EBA\u7684\u601D\u7EF4\u53BB\u505A\u51B3\u7B56\uFF0C\u8FD9\u79CD\u9AD8\u5EA6\u62BD\u8C61\u7684\u6A21\u5F0F\u5728\u6DF1\u5EA6\u4E0A\u4F1A\u6709\u7EDD\u5BF9\u7684\u4F18\u52BF\uFF0C\u540C\u65F6\u4E5F\u4E0E\u4EA4\u6613\u8005\u4E2A\u4EBA\u7684\u7ECF\u9A8C\u548C\u76D8\u611F\u5177\u6709\u6781\u5927\u76F8\u5173\u6027\u3002\u5728\u5F53\u524D\u4E00\u76F4\u6269\u5BB9\u7684\u5E02\u573A\u4E2D\uFF0C\u4F20\u7EDF\u7684\u4EBA\u4E3A\u5206\u6790\u4E0E\u4EA4\u6613\u5F88\u96BE\u5B9E\u73B0\u5168\u5E02\u573A\u7684\u8DDF\u8E2A\u76D1\u6D4B\uFF0C\u5E76\u4E14\u65F6\u523B\u53D7\u5230\u4EA4\u6613\u8005\u4E3B\u89C2\u60C5\u7EEA\u7684\u5F71\u54CD\u3002\u8FD9\u4E24\u70B9\u6210\u4E3A\u4E86\u4F20\u7EDF\u4EA4\u6613\u65B9\u5F0F\u7684\u201C\u786C\u4F24\u201D\u3002</p><p>\u800C\u91CF\u5316\u4EA4\u6613\u662F\u4E00\u79CD\u5B9A\u91CF\u7684\u5206\u6790\uFF0C\u6240\u8C13\u5B9A\u91CF\u5206\u6790\u5C31\u662F\u521A\u624D\u8BB2\u5230\u7684\u83B7\u5F97\u4E00\u4E2A\u5177\u4F53\u7684\u83B7\u5229\u6982\u7387\uFF0C\u5E76\u4F9D\u636E\u6982\u7387\u9009\u62E9\u4EA4\u6613\u7684\u7B56\u7565\uFF0C\u5728\u6781\u5927\u7A0B\u5EA6\u4E0A\u51CF\u5C11\u4E86\u4EA4\u6613\u8005\u60C5\u7EEA\u6CE2\u52A8\u7684\u5F71\u54CD\uFF0C\u907F\u514D\u5728\u5E02\u573A\u6781\u5EA6\u72C2\u70ED\u6216\u60B2\u89C2\u7684\u60C5\u51B5\u4E0B\u4F5C\u51FA\u975E\u7406\u6027\u7684\u6295\u8D44\u51B3\u7B56\u3002\u540C\u65F6\u5229\u7528\u4E86\u8BA1\u7B97\u673A\u7684\u5F3A\u5927\u8FD0\u7B97\u80FD\u529B\uFF0C\u6240\u4EE5\u5728\u5E7F\u5EA6\u4E0A\u5360\u6709\u7EDD\u5BF9\u7684\u4F18\u52BF\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u628A\u91CF\u5316\u4EA4\u6613\u548C\u4F20\u7EDF\u7684\u4EA4\u6613\u7C7B\u6BD4\u4E3A\u897F\u533B\u548C\u4E2D\u533B\uFF0C\u4F20\u7EDF\u7684\u6295\u8D44\u4EA4\u6613\u9760\u7684\u662F\u4E3B\u89C2\u7ECF\u9A8C\u5224\u65AD\uFF0C\u65E0\u6CD5\u7ED9\u51FA\u5177\u4F53\u7684\u53C2\u6570\u6307\u6807\uFF0C\u91CF\u5316\u4EA4\u6613\u50CF\u897F\u533B\u90A3\u6837\uFF0C\u9760\u7684\u662F\u4EEA\u5668\u3001\u8BBE\u5907\u3001\u8F6F\u4EF6\uFF0C\u628A\u73B0\u8C61\u8F6C\u6362\u6210\u53C2\u8003\u6570\u636E\u4E3A\u75C5\u4EBA\u6CBB\u7597\u63D0\u4F9B\u4F9D\u636E\u3002</p><p>\u7B14\u8005\u7684\u89C2\u70B9\u662F\u628A\u91CF\u5316\u4EA4\u6613\u548C\u4F20\u7EDF\u7684\u4EA4\u6613\u76F8\u7ED3\u5408\uFF0C\u628A\u4EBA\u7684\u601D\u7EF4\u6A21\u578B\u62BD\u8C61\u6210\u4E3A\u6570\u5B66\u6A21\u578B\uFF0C\u4F5C\u4E3A\u4EA4\u6613\u7B56\u7565\u8FD0\u7528\u5230\u91CF\u5316\u4EA4\u6613\u4E2D\uFF0C\u5C06\u4E24\u8005\u53D6\u957F\u8865\u77ED\uFF0C\u65E2\u4E0D\u5931\u5206\u6790\u6DF1\u5EA6\uFF0C\u4E5F\u80FD\u4FDD\u8BC1\u5206\u6790\u7684\u5E7F\u5EA6\u3002</p><h2 id="\u672C\u5C0F\u518C\u5E94\u7528\u573A\u666F" tabindex="-1">\u672C\u5C0F\u518C\u5E94\u7528\u573A\u666F <a class="header-anchor" href="#\u672C\u5C0F\u518C\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a></h2><p>\u672C\u5C0F\u518C\u7684\u5E94\u7528\u573A\u666F\u5951\u5408\u4E8E\u91CF\u5316\u4EA4\u6613\u7684\u6574\u4F53\u6D41\u7A0B\u800C\u8BBE\u8BA1\uFF0C\u5C06\u6570\u636E\u3001\u7B56\u7565\u3001\u7CFB\u7EDF\u3001\u6267\u884C\u7B49\u65B9\u9762\u7EFC\u5408\u8D77\u6765\u5F62\u6210\u4E00\u4E2A\u6709\u673A\u7684\u6574\u4F53\u3002\u91CF\u5316\u4EA4\u6613\u7684\u6574\u4F53\u6D41\u7A0B\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2019/3/17/1698ae6ef334a460~tplv-t2oaga2asx-image.image" alt=""></p><p>\u9996\u5148\u662F\u628A\u5386\u53F2\u884C\u60C5\u3001\u57FA\u672C\u9762\u4FE1\u606F\u3001\u65B0\u95FB\u8D44\u8BAF\u7B49\u6570\u636E\u8FDB\u884C\u521D\u6B65\u6E05\u6D17\u548C\u5904\u7406\uFF0C\u800C\u540E\u5728\u5927\u6570\u636E\u7684\u57FA\u7840\u4E0A\u4EE5\u6570\u5B66\u5EFA\u6A21\u3001\u7A0B\u5E8F\u8BBE\u8BA1\u7B49\u65B9\u6CD5\u5EFA\u7ACB\u4EA4\u6613\u7B56\u7565\uFF0C\u5728\u5BF9\u5386\u53F2\u6570\u636E\u7684\u56DE\u6D4B\u4E2D\u8BC4\u4EF7\u4EA4\u6613\u7B56\u7565\u7684\u6548\u679C\uFF0C\u6700\u7EC8\u7B5B\u9009\u51FA\u5927\u6982\u7387\u4E0B\u76C8\u5229\u7684\u7B56\u7565\uFF0C\u5E76\u5C06\u5176\u5E94\u7528\u4E8E\u5B9E\u9645\u4EA4\u6613\u4E2D\u4EA7\u751F\u51FA\u4EA4\u6613\u7684\u4FE1\u53F7\uFF0C\u6BD4\u5982\u4E70\u4EC0\u4E48\u80A1\u3001\u4EC0\u4E48\u65F6\u5019\u4E70\u3001\u4E70\u591A\u5C11\u3001\u4EC0\u4E48\u65F6\u5019\u5356\u7B49\u4FE1\u606F\u3002</p><p>\u91CF\u5316\u4EA4\u6613\u7684\u6838\u5FC3\u662F\u4EA4\u6613\u7B56\u7565\u7684\u5B9E\u73B0\uFF0C\u5E94\u7528\u573A\u666F\u4E2D\u4F1A\u4ECE\u62E9\u65F6\u3001\u9009\u80A1\u3001\u4ED3\u4F4D\u7BA1\u7406\u3001\u53C2\u6570\u4F18\u5316\u3001\u5EA6\u91CF\u5206\u6790\u7B49\u89D2\u5EA6\u8BB2\u89E3\u4EA4\u6613\u7B56\u7565\u7684\u5B9E\u73B0\u65B9\u6CD5\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u5728\u6B63\u5F0F\u5F00\u59CB\u5B66\u4E60 Python \u91CF\u5316\u4EA4\u6613\u524D\uFF0C\u6211\u4EEC\u4ECE\u91CF\u5316\u4EA4\u6613\u672C\u8D28\u3001\u53D1\u5C55\u3001\u4F18\u52BF\u3001\u573A\u666F\u7EF4\u5EA6\u5256\u6790\u4E86\u91CF\u5316\u4EA4\u6613\uFF0C\u63ED\u5F00\u4E86\u91CF\u5316\u4EA4\u6613\u7684\u795E\u79D8\u9762\u7EB1\uFF0C\u76EE\u7684\u662F\u60F3\u8BA9\u540C\u5B66\u4EEC\u80FD\u591F\u6B63\u786E\u7406\u89E3\u91CF\u5316\u4EA4\u6613\u3002</p><p>\u6700\u540E\uFF0C\u7ED9\u5927\u5BB6\u7559\u4E00\u9053\u601D\u8003\u9898\uFF1A</p><p>\u65E2\u7136\u91CF\u5316\u4EA4\u6613\u672C\u8D28\u4E0A\u662F\u4E00\u79CD\u5E2E\u52A9\u6211\u4EEC\u9AD8\u6548\u5FEB\u901F\u5730\u7BA1\u7406\u4EA4\u6613\u80DC\u7B97\u7684\u5DE5\u5177\uFF0C\u90A3\u4E48\u5BF9\u4E8E\u4E00\u4E2A\u4E3B\u89C2\u4EA4\u6613\u5E76\u4E0D\u80FD\u76C8\u5229\u7684\u4EBA\uFF0C\u662F\u5426\u80FD\u591F\u626D\u4E8F\u4E3A\u76C8\u5462\uFF1F\u5176\u6839\u672C\u7684\u539F\u56E0\u53C8\u662F\u4EC0\u4E48\u5462\uFF1F</p><p>\u6B22\u8FCE\u5927\u5BB6\u5728\u7559\u8A00\u533A\u7559\u8A00\uFF0C\u6211\u4EEC\u4E00\u8D77\u8BA8\u8BBA\u3002</p>',58),r=[i];function l(o,n,d,c,h,g){return t(),e("div",null,r)}const u=a(s,[["render",l]]);export{_ as __pageData,u as default};
