import{_ as s,c as n,o as a,d as l}from"./app.e5528619.js";const u=JSON.parse('{"title":"\u5E94\u7528 1-\u5343\u5E06\u7ADE\u53D1 \u2014\u2014 \u5206\u5E03\u5F0F\u9501","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u5206\u5E03\u5F0F\u9501","slug":"\u5206\u5E03\u5F0F\u9501","link":"#\u5206\u5E03\u5F0F\u9501","children":[]},{"level":2,"title":"\u8D85\u65F6\u95EE\u9898","slug":"\u8D85\u65F6\u95EE\u9898","link":"#\u8D85\u65F6\u95EE\u9898","children":[]},{"level":2,"title":"\u53EF\u91CD\u5165\u6027","slug":"\u53EF\u91CD\u5165\u6027","link":"#\u53EF\u91CD\u5165\u6027","children":[]},{"level":2,"title":"\u601D\u8003\u9898","slug":"\u601D\u8003\u9898","link":"#\u601D\u8003\u9898","children":[]}],"relativePath":"\u5176\u4ED6/Redis \u6DF1\u5EA6\u5386\u9669\uFF1A\u6838\u5FC3\u539F\u7406\u4E0E\u5E94\u7528\u5B9E\u8DF5/\u5E94\u7528 1-\u5343\u5E06\u7ADE\u53D1 \u2014\u2014 \u5206\u5E03\u5F0F\u9501.md"}'),e={name:"\u5176\u4ED6/Redis \u6DF1\u5EA6\u5386\u9669\uFF1A\u6838\u5FC3\u539F\u7406\u4E0E\u5E94\u7528\u5B9E\u8DF5/\u5E94\u7528 1-\u5343\u5E06\u7ADE\u53D1 \u2014\u2014 \u5206\u5E03\u5F0F\u9501.md"},p=l(`<h1 id="\u5E94\u7528-1-\u5343\u5E06\u7ADE\u53D1-\u2014\u2014-\u5206\u5E03\u5F0F\u9501" tabindex="-1">\u5E94\u7528 1-\u5343\u5E06\u7ADE\u53D1 \u2014\u2014 \u5206\u5E03\u5F0F\u9501 <a class="header-anchor" href="#\u5E94\u7528-1-\u5343\u5E06\u7ADE\u53D1-\u2014\u2014-\u5206\u5E03\u5F0F\u9501" aria-hidden="true">#</a></h1><hr><h1 id="\u5E94\u7528-1\uFF1A\u5343\u5E06\u7ADE\u53D1-\u2014\u2014-\u5206\u5E03\u5F0F\u9501" tabindex="-1">\u5E94\u7528 1\uFF1A\u5343\u5E06\u7ADE\u53D1 \u2014\u2014 \u5206\u5E03\u5F0F\u9501 <a class="header-anchor" href="#\u5E94\u7528-1\uFF1A\u5343\u5E06\u7ADE\u53D1-\u2014\u2014-\u5206\u5E03\u5F0F\u9501" aria-hidden="true">#</a></h1><p>\u5206\u5E03\u5F0F\u5E94\u7528\u8FDB\u884C\u903B\u8F91\u5904\u7406\u65F6\u7ECF\u5E38\u4F1A\u9047\u5230\u5E76\u53D1\u95EE\u9898\u3002</p><p>\u6BD4\u5982\u4E00\u4E2A\u64CD\u4F5C\u8981\u4FEE\u6539\u7528\u6237\u7684\u72B6\u6001\uFF0C\u4FEE\u6539\u72B6\u6001\u9700\u8981\u5148\u8BFB\u51FA\u7528\u6237\u7684\u72B6\u6001\uFF0C\u5728\u5185\u5B58\u91CC\u8FDB\u884C\u4FEE\u6539\uFF0C\u6539\u5B8C\u4E86\u518D\u5B58\u56DE\u53BB\u3002\u5982\u679C\u8FD9\u6837\u7684\u64CD\u4F5C\u540C\u65F6\u8FDB\u884C\u4E86\uFF0C\u5C31\u4F1A\u51FA\u73B0\u5E76\u53D1\u95EE\u9898\uFF0C\u56E0\u4E3A\u8BFB\u53D6\u548C\u4FDD\u5B58\u72B6\u6001\u8FD9\u4E24\u4E2A\u64CD\u4F5C\u4E0D\u662F\u539F\u5B50\u7684\u3002\uFF08Wiki \u89E3\u91CA\uFF1A\u6240\u8C13<strong>\u539F\u5B50\u64CD\u4F5C</strong>\u662F\u6307\u4E0D\u4F1A\u88AB\u7EBF\u7A0B\u8C03\u5EA6\u673A\u5236\u6253\u65AD\u7684\u64CD\u4F5C\uFF1B\u8FD9\u79CD\u64CD\u4F5C\u4E00\u65E6\u5F00\u59CB\uFF0C\u5C31\u4E00\u76F4\u8FD0\u884C\u5230\u7ED3\u675F\uFF0C\u4E2D\u95F4\u4E0D\u4F1A\u6709\u4EFB\u4F55 context switch \u7EBF\u7A0B\u5207\u6362\u3002\uFF09</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2018/7/10/164824791aa796fa~tplv-t2oaga2asx-image.image" alt=""></p><p>\u8FD9\u4E2A\u65F6\u5019\u5C31\u8981\u4F7F\u7528\u5230\u5206\u5E03\u5F0F\u9501\u6765\u9650\u5236\u7A0B\u5E8F\u7684\u5E76\u53D1\u6267\u884C\u3002Redis \u5206\u5E03\u5F0F\u9501\u4F7F\u7528\u975E\u5E38\u5E7F\u6CDB\uFF0C\u5B83\u662F\u9762\u8BD5\u7684\u91CD\u8981\u8003\u70B9\u4E4B\u4E00\uFF0C\u5F88\u591A\u540C\u5B66\u90FD\u77E5\u9053\u8FD9\u4E2A\u77E5\u8BC6\uFF0C\u4E5F\u5927\u81F4\u77E5\u9053\u5206\u5E03\u5F0F\u9501\u7684\u539F\u7406\uFF0C\u4F46\u662F\u5177\u4F53\u5230\u7EC6\u8282\u7684\u4F7F\u7528\u4E0A\u5F80\u5F80\u5E76\u4E0D\u5B8C\u5168\u6B63\u786E\u3002</p><h2 id="\u5206\u5E03\u5F0F\u9501" tabindex="-1">\u5206\u5E03\u5F0F\u9501 <a class="header-anchor" href="#\u5206\u5E03\u5F0F\u9501" aria-hidden="true">#</a></h2><p>\u5206\u5E03\u5F0F\u9501\u672C\u8D28\u4E0A\u8981\u5B9E\u73B0\u7684\u76EE\u6807\u5C31\u662F\u5728 Redis \u91CC\u9762\u5360\u4E00\u4E2A\u201C\u8305\u5751\u201D\uFF0C\u5F53\u522B\u7684\u8FDB\u7A0B\u4E5F\u8981\u6765\u5360\u65F6\uFF0C\u53D1\u73B0\u5DF2\u7ECF\u6709\u4EBA\u8E72\u5728\u90A3\u91CC\u4E86\uFF0C\u5C31\u53EA\u597D\u653E\u5F03\u6216\u8005\u7A0D\u540E\u518D\u8BD5\u3002</p><p>\u5360\u5751\u4E00\u822C\u662F\u4F7F\u7528 setnx(set if not exists) \u6307\u4EE4\uFF0C\u53EA\u5141\u8BB8\u88AB\u4E00\u4E2A\u5BA2\u6237\u7AEF\u5360\u5751\u3002\u5148\u6765\u5148\u5360\uFF0C \u7528\u5B8C\u4E86\uFF0C\u518D\u8C03\u7528 del \u6307\u4EE4\u91CA\u653E\u8305\u5751\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u8FD9\u91CC\u7684\u5192\u53F7:\u5C31\u662F\u4E00\u4E2A\u666E\u901A\u7684\u5B57\u7B26\uFF0C\u6CA1\u7279\u522B\u542B\u4E49\uFF0C\u5B83\u53EF\u4EE5\u662F\u4EFB\u610F\u5176\u5B83\u5B57\u7B26\uFF0C\u4E0D\u8981\u8BEF\u89E3</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; setnx lock:codehole true</span></span>
<span class="line"><span style="color:#A6ACCD;">OK</span></span>
<span class="line"><span style="color:#A6ACCD;">... do something critical ...</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; del lock:codehole</span></span>
<span class="line"><span style="color:#A6ACCD;">(integer) 1</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4F46\u662F\u6709\u4E2A\u95EE\u9898\uFF0C\u5982\u679C\u903B\u8F91\u6267\u884C\u5230\u4E2D\u95F4\u51FA\u73B0\u5F02\u5E38\u4E86\uFF0C\u53EF\u80FD\u4F1A\u5BFC\u81F4 del \u6307\u4EE4\u6CA1\u6709\u88AB\u8C03\u7528\uFF0C\u8FD9\u6837\u5C31\u4F1A\u9677\u5165\u6B7B\u9501\uFF0C\u9501\u6C38\u8FDC\u5F97\u4E0D\u5230\u91CA\u653E\u3002</p><p>\u4E8E\u662F\u6211\u4EEC\u5728\u62FF\u5230\u9501\u4E4B\u540E\uFF0C\u518D\u7ED9\u9501\u52A0\u4E0A\u4E00\u4E2A\u8FC7\u671F\u65F6\u95F4\uFF0C\u6BD4\u5982 5s\uFF0C\u8FD9\u6837\u5373\u4F7F\u4E2D\u95F4\u51FA\u73B0\u5F02\u5E38\u4E5F\u53EF\u4EE5\u4FDD\u8BC1 5 \u79D2\u4E4B\u540E\u9501\u4F1A\u81EA\u52A8\u91CA\u653E\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&gt; setnx lock:codehole true</span></span>
<span class="line"><span style="color:#A6ACCD;">OK</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; expire lock:codehole 5</span></span>
<span class="line"><span style="color:#A6ACCD;">... do something critical ...</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; del lock:codehole</span></span>
<span class="line"><span style="color:#A6ACCD;">(integer) 1</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4F46\u662F\u4EE5\u4E0A\u903B\u8F91\u8FD8\u6709\u95EE\u9898\u3002\u5982\u679C\u5728 setnx \u548C expire \u4E4B\u95F4\u670D\u52A1\u5668\u8FDB\u7A0B\u7A81\u7136\u6302\u6389\u4E86\uFF0C\u53EF\u80FD\u662F\u56E0\u4E3A\u673A\u5668\u6389\u7535\u6216\u8005\u662F\u88AB\u4EBA\u4E3A\u6740\u6389\u7684\uFF0C\u5C31\u4F1A\u5BFC\u81F4 expire \u5F97\u4E0D\u5230\u6267\u884C\uFF0C\u4E5F\u4F1A\u9020\u6210\u6B7B\u9501\u3002</p><p>\u8FD9\u79CD\u95EE\u9898\u7684\u6839\u6E90\u5C31\u5728\u4E8E setnx \u548C expire \u662F\u4E24\u6761\u6307\u4EE4\u800C\u4E0D\u662F\u539F\u5B50\u6307\u4EE4\u3002\u5982\u679C\u8FD9\u4E24\u6761\u6307\u4EE4\u53EF\u4EE5\u4E00\u8D77\u6267\u884C\u5C31\u4E0D\u4F1A\u51FA\u73B0\u95EE\u9898\u3002\u4E5F\u8BB8\u4F60\u4F1A\u60F3\u5230\u7528 Redis \u4E8B\u52A1\u6765\u89E3\u51B3\u3002\u4F46\u662F\u8FD9\u91CC\u4E0D\u884C\uFF0C\u56E0\u4E3A expire \u662F\u4F9D\u8D56\u4E8E setnx \u7684\u6267\u884C\u7ED3\u679C\u7684\uFF0C\u5982\u679C setnx \u6CA1\u62A2\u5230\u9501\uFF0Cexpire \u662F\u4E0D\u5E94\u8BE5\u6267\u884C\u7684\u3002\u4E8B\u52A1\u91CC\u6CA1\u6709 if-else \u5206\u652F\u903B\u8F91\uFF0C\u4E8B\u52A1\u7684\u7279\u70B9\u662F\u4E00\u53E3\u6C14\u6267\u884C\uFF0C\u8981\u4E48\u5168\u90E8\u6267\u884C\u8981\u4E48\u4E00\u4E2A\u90FD\u4E0D\u6267\u884C\u3002</p><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E2A\u7591\u96BE\uFF0CRedis \u5F00\u6E90\u793E\u533A\u6D8C\u73B0\u4E86\u4E00\u5806\u5206\u5E03\u5F0F\u9501\u7684 library\uFF0C\u4E13\u95E8\u7528\u6765\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u3002\u5B9E\u73B0\u65B9\u6CD5\u6781\u4E3A\u590D\u6742\uFF0C\u5C0F\u767D\u7528\u6237\u4E00\u822C\u8981\u8D39\u5F88\u5927\u7684\u7CBE\u529B\u624D\u53EF\u4EE5\u641E\u61C2\u3002\u5982\u679C\u4F60\u9700\u8981\u4F7F\u7528\u5206\u5E03\u5F0F\u9501\uFF0C\u610F\u5473\u7740\u4F60\u4E0D\u80FD\u4EC5\u4EC5\u4F7F\u7528 Jedis \u6216\u8005 redis-py \u5C31\u884C\u4E86\uFF0C\u8FD8\u5F97\u5F15\u5165\u5206\u5E03\u5F0F\u9501\u7684 library\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2018/7/2/16459b393843f7ae~tplv-t2oaga2asx-image.image" alt=""></p><p>\u4E3A\u4E86\u6CBB\u7406\u8FD9\u4E2A\u4E71\u8C61\uFF0CRedis 2.8 \u7248\u672C\u4E2D\u4F5C\u8005\u52A0\u5165\u4E86 set \u6307\u4EE4\u7684\u6269\u5C55\u53C2\u6570\uFF0C\u4F7F\u5F97 setnx \u548C expire \u6307\u4EE4\u53EF\u4EE5\u4E00\u8D77\u6267\u884C\uFF0C\u5F7B\u5E95\u89E3\u51B3\u4E86\u5206\u5E03\u5F0F\u9501\u7684\u4E71\u8C61\u3002\u4ECE\u6B64\u4EE5\u540E\u6240\u6709\u7684\u7B2C\u4E09\u65B9\u5206\u5E03\u5F0F\u9501 library \u53EF\u4EE5\u4F11\u606F\u4E86\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&gt; set lock:codehole true ex 5 nx</span></span>
<span class="line"><span style="color:#A6ACCD;">OK</span></span>
<span class="line"><span style="color:#A6ACCD;">... do something critical ...</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; del lock:codehole</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4E0A\u9762\u8FD9\u4E2A\u6307\u4EE4\u5C31\u662F setnx \u548C expire \u7EC4\u5408\u5728\u4E00\u8D77\u7684\u539F\u5B50\u6307\u4EE4\uFF0C\u5B83\u5C31\u662F\u5206\u5E03\u5F0F\u9501\u7684\u5965\u4E49\u6240\u5728\u3002</p><h2 id="\u8D85\u65F6\u95EE\u9898" tabindex="-1">\u8D85\u65F6\u95EE\u9898 <a class="header-anchor" href="#\u8D85\u65F6\u95EE\u9898" aria-hidden="true">#</a></h2><p>Redis \u7684\u5206\u5E03\u5F0F\u9501\u4E0D\u80FD\u89E3\u51B3\u8D85\u65F6\u95EE\u9898\uFF0C\u5982\u679C\u5728\u52A0\u9501\u548C\u91CA\u653E\u9501\u4E4B\u95F4\u7684\u903B\u8F91\u6267\u884C\u7684\u592A\u957F\uFF0C\u4EE5\u81F3\u4E8E\u8D85\u51FA\u4E86\u9501\u7684\u8D85\u65F6\u9650\u5236\uFF0C\u5C31\u4F1A\u51FA\u73B0\u95EE\u9898\u3002\u56E0\u4E3A\u8FD9\u65F6\u5019\u7B2C\u4E00\u4E2A\u7EBF\u7A0B\u6301\u6709\u7684\u9501\u8FC7\u671F\u4E86\uFF0C\u4E34\u754C\u533A\u7684\u903B\u8F91\u8FD8\u6CA1\u6709\u6267\u884C\u5B8C\uFF0C\u8FD9\u4E2A\u65F6\u5019\u7B2C\u4E8C\u4E2A\u7EBF\u7A0B\u5C31\u63D0\u524D\u91CD\u65B0\u6301\u6709\u4E86\u8FD9\u628A\u9501\uFF0C\u5BFC\u81F4\u4E34\u754C\u533A\u4EE3\u7801\u4E0D\u80FD\u5F97\u5230\u4E25\u683C\u7684\u4E32\u884C\u6267\u884C\u3002</p><p>\u4E3A\u4E86\u907F\u514D\u8FD9\u4E2A\u95EE\u9898\uFF0CRedis \u5206\u5E03\u5F0F\u9501\u4E0D\u8981\u7528\u4E8E\u8F83\u957F\u65F6\u95F4\u7684\u4EFB\u52A1\u3002\u5982\u679C\u771F\u7684\u5076\u5C14\u51FA\u73B0\u4E86\uFF0C\u6570\u636E\u51FA\u73B0\u7684\u5C0F\u6CE2\u9519\u4E71\u53EF\u80FD\u9700\u8981\u4EBA\u5DE5\u4ECB\u5165\u89E3\u51B3\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">tag = random.nextint()  # \u968F\u673A\u6570</span></span>
<span class="line"><span style="color:#A6ACCD;">if redis.set(key, tag, nx=True, ex=5):</span></span>
<span class="line"><span style="color:#A6ACCD;">    do_something()</span></span>
<span class="line"><span style="color:#A6ACCD;">    redis.delifequals(key, tag)  # \u5047\u60F3\u7684 delifequals \u6307\u4EE4</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6709\u4E00\u4E2A\u7A0D\u5FAE\u5B89\u5168\u4E00\u70B9\u7684\u65B9\u6848\u662F\u4E3A set \u6307\u4EE4\u7684 value \u53C2\u6570\u8BBE\u7F6E\u4E3A\u4E00\u4E2A\u968F\u673A\u6570\uFF0C\u91CA\u653E\u9501\u65F6\u5148\u5339\u914D\u968F\u673A\u6570\u662F\u5426\u4E00\u81F4\uFF0C\u7136\u540E\u518D\u5220\u9664 key\uFF0C\u8FD9\u662F\u4E3A\u4E86\u786E\u4FDD\u5F53\u524D\u7EBF\u7A0B\u5360\u6709\u7684\u9501\u4E0D\u4F1A\u88AB\u5176\u5B83\u7EBF\u7A0B\u91CA\u653E\uFF0C\u9664\u975E\u8FD9\u4E2A\u9501\u662F\u8FC7\u671F\u4E86\u88AB\u670D\u52A1\u5668\u81EA\u52A8\u91CA\u653E\u7684\u3002 \u4F46\u662F\u5339\u914D value \u548C\u5220\u9664 key \u4E0D\u662F\u4E00\u4E2A\u539F\u5B50\u64CD\u4F5C\uFF0CRedis \u4E5F\u6CA1\u6709\u63D0\u4F9B\u7C7B\u4F3C\u4E8E<code>delifequals</code>\u8FD9\u6837\u7684\u6307\u4EE4\uFF0C\u8FD9\u5C31\u9700\u8981\u4F7F\u7528 Lua \u811A\u672C\u6765\u5904\u7406\u4E86\uFF0C\u56E0\u4E3A Lua \u811A\u672C\u53EF\u4EE5\u4FDD\u8BC1\u8FDE\u7EED\u591A\u4E2A\u6307\u4EE4\u7684\u539F\u5B50\u6027\u6267\u884C\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;"># delifequals</span></span>
<span class="line"><span style="color:#A6ACCD;">if redis.call(&quot;get&quot;,KEYS[1]) == ARGV[1] then</span></span>
<span class="line"><span style="color:#A6ACCD;">    return redis.call(&quot;del&quot;,KEYS[1])</span></span>
<span class="line"><span style="color:#A6ACCD;">else</span></span>
<span class="line"><span style="color:#A6ACCD;">    return 0</span></span>
<span class="line"><span style="color:#A6ACCD;">end</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4F46\u662F\u8FD9\u4E5F\u4E0D\u662F\u4E00\u4E2A\u5B8C\u7F8E\u7684\u65B9\u6848\uFF0C\u5B83\u53EA\u662F\u76F8\u5BF9\u5B89\u5168\u4E00\u70B9\uFF0C\u56E0\u4E3A\u5982\u679C\u771F\u7684\u8D85\u65F6\u4E86\uFF0C\u5F53\u524D\u7EBF\u7A0B\u7684\u903B\u8F91\u6CA1\u6709\u6267\u884C\u5B8C\uFF0C\u5176\u5B83\u7EBF\u7A0B\u4E5F\u4F1A\u4E58\u865A\u800C\u5165\u3002</p><h2 id="\u53EF\u91CD\u5165\u6027" tabindex="-1">\u53EF\u91CD\u5165\u6027 <a class="header-anchor" href="#\u53EF\u91CD\u5165\u6027" aria-hidden="true">#</a></h2><p>\u53EF\u91CD\u5165\u6027\u662F\u6307\u7EBF\u7A0B\u5728\u6301\u6709\u9501\u7684\u60C5\u51B5\u4E0B\u518D\u6B21\u8BF7\u6C42\u52A0\u9501\uFF0C\u5982\u679C\u4E00\u4E2A\u9501\u652F\u6301\u540C\u4E00\u4E2A\u7EBF\u7A0B\u7684\u591A\u6B21\u52A0\u9501\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u9501\u5C31\u662F\u53EF\u91CD\u5165\u7684\u3002\u6BD4\u5982 Java \u8BED\u8A00\u91CC\u6709\u4E2A ReentrantLock \u5C31\u662F\u53EF\u91CD\u5165\u9501\u3002Redis \u5206\u5E03\u5F0F\u9501\u5982\u679C\u8981\u652F\u6301\u53EF\u91CD\u5165\uFF0C\u9700\u8981\u5BF9\u5BA2\u6237\u7AEF\u7684 set \u65B9\u6CD5\u8FDB\u884C\u5305\u88C5\uFF0C\u4F7F\u7528\u7EBF\u7A0B\u7684 Threadlocal \u53D8\u91CF\u5B58\u50A8\u5F53\u524D\u6301\u6709\u9501\u7684\u8BA1\u6570\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;"># -*- coding: utf-8</span></span>
<span class="line"><span style="color:#A6ACCD;">import redis</span></span>
<span class="line"><span style="color:#A6ACCD;">import threading</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">locks = threading.local()</span></span>
<span class="line"><span style="color:#A6ACCD;">locks.redis = {}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">def key_for(user_id):</span></span>
<span class="line"><span style="color:#A6ACCD;">    return &quot;account_{}&quot;.format(user_id)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">def _lock(client, key):</span></span>
<span class="line"><span style="color:#A6ACCD;">    return bool(client.set(key, True, nx=True, ex=5))</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">def _unlock(client, key):</span></span>
<span class="line"><span style="color:#A6ACCD;">    client.delete(key)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">def lock(client, user_id):</span></span>
<span class="line"><span style="color:#A6ACCD;">    key = key_for(user_id)</span></span>
<span class="line"><span style="color:#A6ACCD;">    if key in locks.redis:</span></span>
<span class="line"><span style="color:#A6ACCD;">        locks.redis[key] += 1</span></span>
<span class="line"><span style="color:#A6ACCD;">        return True</span></span>
<span class="line"><span style="color:#A6ACCD;">    ok = _lock(client, key)</span></span>
<span class="line"><span style="color:#A6ACCD;">    if not ok:</span></span>
<span class="line"><span style="color:#A6ACCD;">        return False</span></span>
<span class="line"><span style="color:#A6ACCD;">    locks.redis[key] = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">    return True</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">def unlock(client, user_id):</span></span>
<span class="line"><span style="color:#A6ACCD;">    key = key_for(user_id)</span></span>
<span class="line"><span style="color:#A6ACCD;">    if key in locks.redis:</span></span>
<span class="line"><span style="color:#A6ACCD;">        locks.redis[key] -= 1</span></span>
<span class="line"><span style="color:#A6ACCD;">        if locks.redis[key] &lt;= 0:</span></span>
<span class="line"><span style="color:#A6ACCD;">            del locks.redis[key]</span></span>
<span class="line"><span style="color:#A6ACCD;">            self._unlock(key)</span></span>
<span class="line"><span style="color:#A6ACCD;">        return True</span></span>
<span class="line"><span style="color:#A6ACCD;">    return False</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">client = redis.StrictRedis()</span></span>
<span class="line"><span style="color:#A6ACCD;">print &quot;lock&quot;, lock(client, &quot;codehole&quot;)</span></span>
<span class="line"><span style="color:#A6ACCD;">print &quot;lock&quot;, lock(client, &quot;codehole&quot;)</span></span>
<span class="line"><span style="color:#A6ACCD;">print &quot;unlock&quot;, unlock(client, &quot;codehole&quot;)</span></span>
<span class="line"><span style="color:#A6ACCD;">print &quot;unlock&quot;, unlock(client, &quot;codehole&quot;)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4EE5\u4E0A\u8FD8\u4E0D\u662F\u53EF\u91CD\u5165\u9501\u7684\u5168\u90E8\uFF0C\u7CBE\u786E\u4E00\u70B9\u8FD8\u9700\u8981\u8003\u8651\u5185\u5B58\u9501\u8BA1\u6570\u7684\u8FC7\u671F\u65F6\u95F4\uFF0C\u4EE3\u7801\u590D\u6742\u5EA6\u5C06\u4F1A\u7EE7\u7EED\u5347\u9AD8\u3002\u8001\u94B1\u4E0D\u63A8\u8350\u4F7F\u7528\u53EF\u91CD\u5165\u9501\uFF0C\u5B83\u52A0\u91CD\u4E86\u5BA2\u6237\u7AEF\u7684\u590D\u6742\u6027\uFF0C\u5728\u7F16\u5199\u4E1A\u52A1\u65B9\u6CD5\u65F6\u6CE8\u610F\u5728\u903B\u8F91\u7ED3\u6784\u4E0A\u8FDB\u884C\u8C03\u6574\u5B8C\u5168\u53EF\u4EE5\u4E0D\u4F7F\u7528\u53EF\u91CD\u5165\u9501\u3002\u4E0B\u9762\u662F Java \u7248\u672C\u7684\u53EF\u91CD\u5165\u9501\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">public class RedisWithReentrantLock {</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  private ThreadLocal&lt;Map&lt;String, Integer&gt;&gt; lockers = new ThreadLocal&lt;&gt;();</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  private Jedis jedis;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  public RedisWithReentrantLock(Jedis jedis) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    this.jedis = jedis;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  private boolean _lock(String key) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return jedis.set(key, &quot;&quot;, &quot;nx&quot;, &quot;ex&quot;, 5L) != null;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  private void _unlock(String key) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    jedis.del(key);</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  private Map&lt;String, Integer&gt; currentLockers() {</span></span>
<span class="line"><span style="color:#A6ACCD;">    Map&lt;String, Integer&gt; refs = lockers.get();</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (refs != null) {</span></span>
<span class="line"><span style="color:#A6ACCD;">      return refs;</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    lockers.set(new HashMap&lt;&gt;());</span></span>
<span class="line"><span style="color:#A6ACCD;">    return lockers.get();</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  public boolean lock(String key) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    Map&lt;String, Integer&gt; refs = currentLockers();</span></span>
<span class="line"><span style="color:#A6ACCD;">    Integer refCnt = refs.get(key);</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (refCnt != null) {</span></span>
<span class="line"><span style="color:#A6ACCD;">      refs.put(key, refCnt + 1);</span></span>
<span class="line"><span style="color:#A6ACCD;">      return true;</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    boolean ok = this._lock(key);</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (!ok) {</span></span>
<span class="line"><span style="color:#A6ACCD;">      return false;</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    refs.put(key, 1);</span></span>
<span class="line"><span style="color:#A6ACCD;">    return true;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  public boolean unlock(String key) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    Map&lt;String, Integer&gt; refs = currentLockers();</span></span>
<span class="line"><span style="color:#A6ACCD;">    Integer refCnt = refs.get(key);</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (refCnt == null) {</span></span>
<span class="line"><span style="color:#A6ACCD;">      return false;</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    refCnt -= 1;</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (refCnt &gt; 0) {</span></span>
<span class="line"><span style="color:#A6ACCD;">      refs.put(key, refCnt);</span></span>
<span class="line"><span style="color:#A6ACCD;">    } else {</span></span>
<span class="line"><span style="color:#A6ACCD;">      refs.remove(key);</span></span>
<span class="line"><span style="color:#A6ACCD;">      this._unlock(key);</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    return true;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  public static void main(String[] args) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    Jedis jedis = new Jedis();</span></span>
<span class="line"><span style="color:#A6ACCD;">    RedisWithReentrantLock redis = new RedisWithReentrantLock(jedis);</span></span>
<span class="line"><span style="color:#A6ACCD;">    System.out.println(redis.lock(&quot;codehole&quot;));</span></span>
<span class="line"><span style="color:#A6ACCD;">    System.out.println(redis.lock(&quot;codehole&quot;));</span></span>
<span class="line"><span style="color:#A6ACCD;">    System.out.println(redis.unlock(&quot;codehole&quot;));</span></span>
<span class="line"><span style="color:#A6ACCD;">    System.out.println(redis.unlock(&quot;codehole&quot;));</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8DDF Python \u7248\u672C\u533A\u522B\u4E0D\u5927\uFF0C\u4E5F\u662F\u57FA\u4E8E ThreadLocal \u548C\u5F15\u7528\u8BA1\u6570\u3002</p><p>\u4EE5\u4E0A\u8FD8\u4E0D\u662F\u5206\u5E03\u5F0F\u9501\u7684\u5168\u90E8\uFF0C\u5728\u5C0F\u518C\u7684\u62D3\u5C55\u7BC7<a href="https://juejin.cn/book/6844733724618129422/section/6844733724727181326" target="_blank" rel="noreferrer">\u300A\u62FE\u9057\u6F0F\u8865 \u2014\u2014 \u518D\u8C08\u5206\u5E03\u5F0F\u9501\u300B</a>\uFF0C\u6211\u4EEC\u8FD8\u4F1A\u7EE7\u7EED\u5BF9\u5206\u5E03\u5F0F\u9501\u505A\u8FDB\u4E00\u6B65\u7684\u6DF1\u5165\u7406\u89E3\u3002</p><h2 id="\u601D\u8003\u9898" tabindex="-1">\u601D\u8003\u9898 <a class="header-anchor" href="#\u601D\u8003\u9898" aria-hidden="true">#</a></h2><ol><li>Review \u4E0B\u4F60\u81EA\u5DF1\u7684\u9879\u76EE\u4EE3\u7801\u4E2D\u7684\u5206\u5E03\u5F0F\u9501\uFF0C\u5B83\u7684\u4F7F\u7528\u65B9\u5F0F\u662F\u5426\u6807\u51C6\u6B63\u786E\uFF1F</li><li>\u5982\u679C\u4F60\u8FD8\u6CA1\u7528\u8FC7\u5206\u5E03\u5F0F\u9501\uFF0C\u60F3\u60F3\u81EA\u5DF1\u7684\u9879\u76EE\u4E2D\u662F\u5426\u53EF\u4EE5\u7528\u4E0A\uFF1F</li></ol>`,37),o=[p];function c(t,r,i,C,A,y){return a(),n("div",null,o)}const D=s(e,[["render",c]]);export{u as __pageData,D as default};
