import{_ as s,c as e,o as a,d as n}from"./app.c4f45da1.js";const m=JSON.parse('{"title":"\u62D3\u5C55 5-\u4F18\u80DC\u52A3\u6C70 \u2014\u2014 LRU","description":"","frontmatter":{},"headers":[{"level":2,"title":"LRU \u7B97\u6CD5","slug":"lru-\u7B97\u6CD5","link":"#lru-\u7B97\u6CD5","children":[]},{"level":2,"title":"\u8FD1\u4F3C LRU \u7B97\u6CD5","slug":"\u8FD1\u4F3C-lru-\u7B97\u6CD5","link":"#\u8FD1\u4F3C-lru-\u7B97\u6CD5","children":[]},{"level":2,"title":"\u6269\u5C55\u9605\u8BFB","slug":"\u6269\u5C55\u9605\u8BFB","link":"#\u6269\u5C55\u9605\u8BFB","children":[]},{"level":2,"title":"\u601D\u8003 & \u4F5C\u4E1A","slug":"\u601D\u8003-\u4F5C\u4E1A","link":"#\u601D\u8003-\u4F5C\u4E1A","children":[]}],"relativePath":"\u5176\u4ED6/Redis \u6DF1\u5EA6\u5386\u9669\uFF1A\u6838\u5FC3\u539F\u7406\u4E0E\u5E94\u7528\u5B9E\u8DF5/\u62D3\u5C55 5-\u4F18\u80DC\u52A3\u6C70 \u2014\u2014 LRU.md"}'),l={name:"\u5176\u4ED6/Redis \u6DF1\u5EA6\u5386\u9669\uFF1A\u6838\u5FC3\u539F\u7406\u4E0E\u5E94\u7528\u5B9E\u8DF5/\u62D3\u5C55 5-\u4F18\u80DC\u52A3\u6C70 \u2014\u2014 LRU.md"},p=n(`<h1 id="\u62D3\u5C55-5-\u4F18\u80DC\u52A3\u6C70-\u2014\u2014-lru" tabindex="-1">\u62D3\u5C55 5-\u4F18\u80DC\u52A3\u6C70 \u2014\u2014 LRU <a class="header-anchor" href="#\u62D3\u5C55-5-\u4F18\u80DC\u52A3\u6C70-\u2014\u2014-lru" aria-hidden="true">#</a></h1><hr><h1 id="\u62D3\u5C55-5\uFF1A\u4F18\u80DC\u52A3\u6C70-\u2014\u2014-lru" tabindex="-1">\u62D3\u5C55 5\uFF1A\u4F18\u80DC\u52A3\u6C70 \u2014\u2014 LRU <a class="header-anchor" href="#\u62D3\u5C55-5\uFF1A\u4F18\u80DC\u52A3\u6C70-\u2014\u2014-lru" aria-hidden="true">#</a></h1><p>\u5F53 Redis \u5185\u5B58\u8D85\u51FA\u7269\u7406\u5185\u5B58\u9650\u5236\u65F6\uFF0C\u5185\u5B58\u7684\u6570\u636E\u4F1A\u5F00\u59CB\u548C\u78C1\u76D8\u4EA7\u751F\u9891\u7E41\u7684\u4EA4\u6362 (swap)\u3002\u4EA4\u6362\u4F1A\u8BA9 Redis \u7684\u6027\u80FD\u6025\u5267\u4E0B\u964D\uFF0C\u5BF9\u4E8E\u8BBF\u95EE\u91CF\u6BD4\u8F83\u9891\u7E41\u7684 Redis \u6765\u8BF4\uFF0C\u8FD9\u6837\u9F9F\u901F\u7684\u5B58\u53D6\u6548\u7387\u57FA\u672C\u4E0A\u7B49\u4E8E\u4E0D\u53EF\u7528\u3002</p><p>\u5728\u751F\u4EA7\u73AF\u5883\u4E2D\u6211\u4EEC\u662F\u4E0D\u5141\u8BB8 Redis \u51FA\u73B0\u4EA4\u6362\u884C\u4E3A\u7684\uFF0C\u4E3A\u4E86\u9650\u5236\u6700\u5927\u4F7F\u7528\u5185\u5B58\uFF0CRedis \u63D0\u4F9B\u4E86\u914D\u7F6E\u53C2\u6570 <code>maxmemory</code> \u6765\u9650\u5236\u5185\u5B58\u8D85\u51FA\u671F\u671B\u5927\u5C0F\u3002</p><p>\u5F53\u5B9E\u9645\u5185\u5B58\u8D85\u51FA <code>maxmemory</code> \u65F6\uFF0CRedis \u63D0\u4F9B\u4E86\u51E0\u79CD\u53EF\u9009\u7B56\u7565 (maxmemory-policy) \u6765\u8BA9\u7528\u6237\u81EA\u5DF1\u51B3\u5B9A\u8BE5\u5982\u4F55\u817E\u51FA\u65B0\u7684\u7A7A\u95F4\u4EE5\u7EE7\u7EED\u63D0\u4F9B\u8BFB\u5199\u670D\u52A1\u3002</p><p><strong>noeviction</strong> \u4E0D\u4F1A\u7EE7\u7EED\u670D\u52A1\u5199\u8BF7\u6C42 (DEL \u8BF7\u6C42\u53EF\u4EE5\u7EE7\u7EED\u670D\u52A1)\uFF0C\u8BFB\u8BF7\u6C42\u53EF\u4EE5\u7EE7\u7EED\u8FDB\u884C\u3002\u8FD9\u6837\u53EF\u4EE5\u4FDD\u8BC1\u4E0D\u4F1A\u4E22\u5931\u6570\u636E\uFF0C\u4F46\u662F\u4F1A\u8BA9\u7EBF\u4E0A\u7684\u4E1A\u52A1\u4E0D\u80FD\u6301\u7EED\u8FDB\u884C\u3002\u8FD9\u662F\u9ED8\u8BA4\u7684\u6DD8\u6C70\u7B56\u7565\u3002</p><p><strong>volatile-lru</strong> \u5C1D\u8BD5\u6DD8\u6C70\u8BBE\u7F6E\u4E86\u8FC7\u671F\u65F6\u95F4\u7684 key\uFF0C\u6700\u5C11\u4F7F\u7528\u7684 key \u4F18\u5148\u88AB\u6DD8\u6C70\u3002\u6CA1\u6709\u8BBE\u7F6E\u8FC7\u671F\u65F6\u95F4\u7684 key \u4E0D\u4F1A\u88AB\u6DD8\u6C70\uFF0C\u8FD9\u6837\u53EF\u4EE5\u4FDD\u8BC1\u9700\u8981\u6301\u4E45\u5316\u7684\u6570\u636E\u4E0D\u4F1A\u7A81\u7136\u4E22\u5931\u3002</p><p><strong>volatile-ttl</strong> \u8DDF\u4E0A\u9762\u4E00\u6837\uFF0C\u9664\u4E86\u6DD8\u6C70\u7684\u7B56\u7565\u4E0D\u662F LRU\uFF0C\u800C\u662F key \u7684\u5269\u4F59\u5BFF\u547D ttl \u7684\u503C\uFF0Cttl \u8D8A\u5C0F\u8D8A\u4F18\u5148\u88AB\u6DD8\u6C70\u3002</p><p><strong>volatile-random</strong> \u8DDF\u4E0A\u9762\u4E00\u6837\uFF0C\u4E0D\u8FC7\u6DD8\u6C70\u7684 key \u662F\u8FC7\u671F key \u96C6\u5408\u4E2D\u968F\u673A\u7684 key\u3002</p><p><strong>allkeys-lru</strong> \u533A\u522B\u4E8E volatile-lru\uFF0C\u8FD9\u4E2A\u7B56\u7565\u8981\u6DD8\u6C70\u7684 key \u5BF9\u8C61\u662F\u5168\u4F53\u7684 key \u96C6\u5408\uFF0C\u800C\u4E0D\u53EA\u662F\u8FC7\u671F\u7684 key \u96C6\u5408\u3002\u8FD9\u610F\u5473\u7740\u6CA1\u6709\u8BBE\u7F6E\u8FC7\u671F\u65F6\u95F4\u7684 key \u4E5F\u4F1A\u88AB\u6DD8\u6C70\u3002</p><p><strong>allkeys-random</strong> \u8DDF\u4E0A\u9762\u4E00\u6837\uFF0C\u4E0D\u8FC7\u6DD8\u6C70\u7684\u7B56\u7565\u662F\u968F\u673A\u7684 key\u3002</p><p>volatile-xxx \u7B56\u7565\u53EA\u4F1A\u9488\u5BF9\u5E26\u8FC7\u671F\u65F6\u95F4\u7684 key \u8FDB\u884C\u6DD8\u6C70\uFF0Callkeys-xxx \u7B56\u7565\u4F1A\u5BF9\u6240\u6709\u7684 key \u8FDB\u884C\u6DD8\u6C70\u3002\u5982\u679C\u4F60\u53EA\u662F\u62FF Redis \u505A\u7F13\u5B58\uFF0C\u90A3\u5E94\u8BE5\u4F7F\u7528 allkeys-xxx\uFF0C\u5BA2\u6237\u7AEF\u5199\u7F13\u5B58\u65F6\u4E0D\u5FC5\u643A\u5E26\u8FC7\u671F\u65F6\u95F4\u3002\u5982\u679C\u4F60\u8FD8\u60F3\u540C\u65F6\u4F7F\u7528 Redis \u7684\u6301\u4E45\u5316\u529F\u80FD\uFF0C\u90A3\u5C31\u4F7F\u7528 volatile-xxx \u7B56\u7565\uFF0C\u8FD9\u6837\u53EF\u4EE5\u4FDD\u7559\u6CA1\u6709\u8BBE\u7F6E\u8FC7\u671F\u65F6\u95F4\u7684 key\uFF0C\u5B83\u4EEC\u662F\u6C38\u4E45\u7684 key \u4E0D\u4F1A\u88AB LRU \u7B97\u6CD5\u6DD8\u6C70\u3002</p><h2 id="lru-\u7B97\u6CD5" tabindex="-1">LRU \u7B97\u6CD5 <a class="header-anchor" href="#lru-\u7B97\u6CD5" aria-hidden="true">#</a></h2><p>\u5B9E\u73B0 LRU \u7B97\u6CD5\u9664\u4E86\u9700\u8981 key/value \u5B57\u5178\u5916\uFF0C\u8FD8\u9700\u8981\u9644\u52A0\u4E00\u4E2A\u94FE\u8868\uFF0C\u94FE\u8868\u4E2D\u7684\u5143\u7D20\u6309\u7167\u4E00\u5B9A\u7684\u987A\u5E8F\u8FDB\u884C\u6392\u5217\u3002\u5F53\u7A7A\u95F4\u6EE1\u7684\u65F6\u5019\uFF0C\u4F1A\u8E22\u6389\u94FE\u8868\u5C3E\u90E8\u7684\u5143\u7D20\u3002\u5F53\u5B57\u5178\u7684\u67D0\u4E2A\u5143\u7D20\u88AB\u8BBF\u95EE\u65F6\uFF0C\u5B83\u5728\u94FE\u8868\u4E2D\u7684\u4F4D\u7F6E\u4F1A\u88AB\u79FB\u52A8\u5230\u8868\u5934\u3002\u6240\u4EE5\u94FE\u8868\u7684\u5143\u7D20\u6392\u5217\u987A\u5E8F\u5C31\u662F\u5143\u7D20\u6700\u8FD1\u88AB\u8BBF\u95EE\u7684\u65F6\u95F4\u987A\u5E8F\u3002</p><p>\u4F4D\u4E8E\u94FE\u8868\u5C3E\u90E8\u7684\u5143\u7D20\u5C31\u662F\u4E0D\u88AB\u91CD\u7528\u7684\u5143\u7D20\uFF0C\u6240\u4EE5\u4F1A\u88AB\u8E22\u6389\u3002\u4F4D\u4E8E\u8868\u5934\u7684\u5143\u7D20\u5C31\u662F\u6700\u8FD1\u521A\u521A\u88AB\u4EBA\u7528\u8FC7\u7684\u5143\u7D20\uFF0C\u6240\u4EE5\u6682\u65F6\u4E0D\u4F1A\u88AB\u8E22\u3002</p><p>\u4E0B\u9762\u6211\u4EEC\u4F7F\u7528 Python \u7684 OrderedDict(\u53CC\u5411\u94FE\u8868 + \u5B57\u5178) \u6765\u5B9E\u73B0\u4E00\u4E2A\u7B80\u5355\u7684 LRU \u7B97\u6CD5\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">from collections import OrderedDict</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">class LRUDict(OrderedDict):</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">    def __init__(self, capacity):</span></span>
<span class="line"><span style="color:#A6ACCD;">        self.capacity = capacity</span></span>
<span class="line"><span style="color:#A6ACCD;">        self.items = OrderedDict()</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">    def __setitem__(self, key, value):</span></span>
<span class="line"><span style="color:#A6ACCD;">        old_value = self.items.get(key)</span></span>
<span class="line"><span style="color:#A6ACCD;">        if old_value is not None:</span></span>
<span class="line"><span style="color:#A6ACCD;">            self.items.pop(key)</span></span>
<span class="line"><span style="color:#A6ACCD;">            self.items[key] = value</span></span>
<span class="line"><span style="color:#A6ACCD;">        elif len(self.items) &lt; self.capacity:</span></span>
<span class="line"><span style="color:#A6ACCD;">            self.items[key] = value</span></span>
<span class="line"><span style="color:#A6ACCD;">        else:</span></span>
<span class="line"><span style="color:#A6ACCD;">            self.items.popitem(last=True)</span></span>
<span class="line"><span style="color:#A6ACCD;">            self.items[key] = value</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">    def __getitem__(self, key):</span></span>
<span class="line"><span style="color:#A6ACCD;">        value = self.items.get(key)</span></span>
<span class="line"><span style="color:#A6ACCD;">        if value is not None:</span></span>
<span class="line"><span style="color:#A6ACCD;">            self.items.pop(key)</span></span>
<span class="line"><span style="color:#A6ACCD;">            self.items[key] = value</span></span>
<span class="line"><span style="color:#A6ACCD;">        return value</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">    def __repr__(self):</span></span>
<span class="line"><span style="color:#A6ACCD;">        return repr(self.items)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">d = LRUDict(10)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">for i in range(15):</span></span>
<span class="line"><span style="color:#A6ACCD;">    d[i] = i</span></span>
<span class="line"><span style="color:#A6ACCD;">print d</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="\u8FD1\u4F3C-lru-\u7B97\u6CD5" tabindex="-1">\u8FD1\u4F3C LRU \u7B97\u6CD5 <a class="header-anchor" href="#\u8FD1\u4F3C-lru-\u7B97\u6CD5" aria-hidden="true">#</a></h2><p>Redis \u4F7F\u7528\u7684\u662F\u4E00\u79CD\u8FD1\u4F3C LRU \u7B97\u6CD5\uFF0C\u5B83\u8DDF LRU \u7B97\u6CD5\u8FD8\u4E0D\u592A\u4E00\u6837\u3002\u4E4B\u6240\u4EE5\u4E0D\u4F7F\u7528 LRU \u7B97\u6CD5\uFF0C\u662F\u56E0\u4E3A\u9700\u8981\u6D88\u8017\u5927\u91CF\u7684\u989D\u5916\u7684\u5185\u5B58\uFF0C\u9700\u8981\u5BF9\u73B0\u6709\u7684\u6570\u636E\u7ED3\u6784\u8FDB\u884C\u8F83\u5927\u7684\u6539\u9020\u3002\u8FD1\u4F3C LRU \u7B97\u6CD5\u5219\u5F88\u7B80\u5355\uFF0C\u5728\u73B0\u6709\u6570\u636E\u7ED3\u6784\u7684\u57FA\u7840\u4E0A\u4F7F\u7528\u968F\u673A\u91C7\u6837\u6CD5\u6765\u6DD8\u6C70\u5143\u7D20\uFF0C\u80FD\u8FBE\u5230\u548C LRU \u7B97\u6CD5\u975E\u5E38\u8FD1\u4F3C\u7684\u6548\u679C\u3002Redis \u4E3A\u5B9E\u73B0\u8FD1\u4F3C LRU \u7B97\u6CD5\uFF0C\u5B83\u7ED9\u6BCF\u4E2A key \u589E\u52A0\u4E86\u4E00\u4E2A\u989D\u5916\u7684\u5C0F\u5B57\u6BB5\uFF0C\u8FD9\u4E2A\u5B57\u6BB5\u7684\u957F\u5EA6\u662F 24 \u4E2A bit\uFF0C\u4E5F\u5C31\u662F\u6700\u540E\u4E00\u6B21\u88AB\u8BBF\u95EE\u7684\u65F6\u95F4\u6233\u3002</p><p>\u4E0A\u4E00\u8282\u63D0\u5230\u5904\u7406 key \u8FC7\u671F\u65B9\u5F0F\u5206\u4E3A\u96C6\u4E2D\u5904\u7406\u548C\u61D2\u60F0\u5904\u7406\uFF0CLRU \u6DD8\u6C70\u4E0D\u4E00\u6837\uFF0C\u5B83\u7684\u5904\u7406\u65B9\u5F0F\u53EA\u6709\u61D2\u60F0\u5904\u7406\u3002\u5F53 Redis \u6267\u884C\u5199\u64CD\u4F5C\u65F6\uFF0C\u53D1\u73B0\u5185\u5B58\u8D85\u51FA maxmemory\uFF0C\u5C31\u4F1A\u6267\u884C\u4E00\u6B21 LRU \u6DD8\u6C70\u7B97\u6CD5\u3002\u8FD9\u4E2A\u7B97\u6CD5\u4E5F\u5F88\u7B80\u5355\uFF0C\u5C31\u662F\u968F\u673A\u91C7\u6837\u51FA 5(\u53EF\u4EE5\u914D\u7F6E) \u4E2A key\uFF0C\u7136\u540E\u6DD8\u6C70\u6389\u6700\u65E7\u7684 key\uFF0C\u5982\u679C\u6DD8\u6C70\u540E\u5185\u5B58\u8FD8\u662F\u8D85\u51FA maxmemory\uFF0C\u90A3\u5C31\u7EE7\u7EED\u968F\u673A\u91C7\u6837\u6DD8\u6C70\uFF0C\u76F4\u5230\u5185\u5B58\u4F4E\u4E8E maxmemory \u4E3A\u6B62\u3002</p><p>\u5982\u4F55\u91C7\u6837\u5C31\u662F\u770B maxmemory-policy \u7684\u914D\u7F6E\uFF0C\u5982\u679C\u662F allkeys \u5C31\u662F\u4ECE\u6240\u6709\u7684 key \u5B57\u5178\u4E2D\u968F\u673A\uFF0C\u5982\u679C\u662F volatile \u5C31\u4ECE\u5E26\u8FC7\u671F\u65F6\u95F4\u7684 key \u5B57\u5178\u4E2D\u968F\u673A\u3002\u6BCF\u6B21\u91C7\u6837\u591A\u5C11\u4E2A key \u770B\u7684\u662F maxmemory_samples \u7684\u914D\u7F6E\uFF0C\u9ED8\u8BA4\u4E3A 5\u3002</p><p>\u4E0B\u9762\u662F\u968F\u673A LRU \u7B97\u6CD5\u548C\u4E25\u683C LRU \u7B97\u6CD5\u7684\u6548\u679C\u5BF9\u6BD4\u56FE\uFF1A</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2018/7/17/164a60daf989f5e2~tplv-t2oaga2asx-image.image" alt=""></p><p>\u56FE\u4E2D\u7EFF\u8272\u90E8\u5206\u662F\u65B0\u52A0\u5165\u7684 key\uFF0C\u6DF1\u7070\u8272\u90E8\u5206\u662F\u8001\u65E7\u7684 key\uFF0C\u6D45\u7070\u8272\u90E8\u5206\u662F\u901A\u8FC7 LRU \u7B97\u6CD5\u6DD8\u6C70\u6389\u7684 key\u3002\u4ECE\u56FE\u4E2D\u53EF\u4EE5\u770B\u51FA\u91C7\u6837\u6570\u91CF\u8D8A\u5927\uFF0C\u8FD1\u4F3C LRU \u7B97\u6CD5\u7684\u6548\u679C\u8D8A\u63A5\u8FD1\u4E25\u683C LRU \u7B97\u6CD5\u3002\u540C\u65F6 Redis3.0 \u5728\u7B97\u6CD5\u4E2D\u589E\u52A0\u4E86\u6DD8\u6C70\u6C60\uFF0C\u8FDB\u4E00\u6B65\u63D0\u5347\u4E86\u8FD1\u4F3C LRU \u7B97\u6CD5\u7684\u6548\u679C\u3002</p><p>\u6DD8\u6C70\u6C60\u662F\u4E00\u4E2A\u6570\u7EC4\uFF0C\u5B83\u7684\u5927\u5C0F\u662F maxmemory_samples\uFF0C\u5728\u6BCF\u4E00\u6B21\u6DD8\u6C70\u5FAA\u73AF\u4E2D\uFF0C\u65B0\u968F\u673A\u51FA\u6765\u7684 key \u5217\u8868\u4F1A\u548C\u6DD8\u6C70\u6C60\u4E2D\u7684 key \u5217\u8868\u8FDB\u884C\u878D\u5408\uFF0C\u6DD8\u6C70\u6389\u6700\u65E7\u7684\u4E00\u4E2A key \u4E4B\u540E\uFF0C\u4FDD\u7559\u5269\u4F59\u8F83\u65E7\u7684 key \u5217\u8868\u653E\u5165\u6DD8\u6C70\u6C60\u4E2D\u7559\u5F85\u4E0B\u4E00\u4E2A\u5FAA\u73AF\u3002</p><h2 id="\u6269\u5C55\u9605\u8BFB" tabindex="-1">\u6269\u5C55\u9605\u8BFB <a class="header-anchor" href="#\u6269\u5C55\u9605\u8BFB" aria-hidden="true">#</a></h2><ul><li><a href="https://yq.aliyun.com/articles/63034" target="_blank" rel="noreferrer">\u300ARedis \u4F5C\u4E3A LRU Cache \u7684\u5B9E\u73B0\u300B</a></li><li><a href="https://blog.csdn.net/mysqldba23/article/details/68482894" target="_blank" rel="noreferrer">\u300ARedis LRU \u5B9E\u73B0\u7B56\u7565\u300B</a></li></ul><h2 id="\u601D\u8003-\u4F5C\u4E1A" tabindex="-1">\u601D\u8003 &amp; \u4F5C\u4E1A <a class="header-anchor" href="#\u601D\u8003-\u4F5C\u4E1A" aria-hidden="true">#</a></h2><ol><li>\u5982\u679C\u4F60\u662F Java \u7528\u6237\uFF0C\u8BD5\u4E00\u8BD5\u7528 LinkedHashMap \u5B9E\u73B0\u4E00\u4E2A LRU \u5B57\u5178\u3002</li><li>\u5982\u679C\u4F60\u662F Golang \u7528\u6237\uFF0C\u9605\u8BFB\u4E00\u4E0B <a href="https://github.com/hashicorp/golang-lru" target="_blank" rel="noreferrer">golang-lru</a> \u7684\u6E90\u7801\u3002</li></ol>`,30),t=[p];function r(o,i,c,y,d,A){return a(),e("div",null,t)}const _=s(l,[["render",r]]);export{m as __pageData,_ as default};
