import{_ as s,c as n,o as a,d as l}from"./app.e0e4ea3a.js";const y=JSON.parse('{"title":"\u6E90\u7801 8-\u7CBE\u76CA\u6C42\u7CBE \u2014\u2014 LFU vs LRU","description":"","frontmatter":{},"headers":[{"level":2,"title":"Redis \u5BF9\u8C61\u7684\u70ED\u5EA6","slug":"redis-\u5BF9\u8C61\u7684\u70ED\u5EA6","link":"#redis-\u5BF9\u8C61\u7684\u70ED\u5EA6","children":[]},{"level":2,"title":"LRU \u6A21\u5F0F","slug":"lru-\u6A21\u5F0F","link":"#lru-\u6A21\u5F0F","children":[]},{"level":2,"title":"LFU \u6A21\u5F0F","slug":"lfu-\u6A21\u5F0F","link":"#lfu-\u6A21\u5F0F","children":[]},{"level":2,"title":"\u4E3A\u4EC0\u4E48 Redis \u8981\u7F13\u5B58\u7CFB\u7EDF\u65F6\u95F4\u6233\uFF1F","slug":"\u4E3A\u4EC0\u4E48-redis-\u8981\u7F13\u5B58\u7CFB\u7EDF\u65F6\u95F4\u6233\uFF1F","link":"#\u4E3A\u4EC0\u4E48-redis-\u8981\u7F13\u5B58\u7CFB\u7EDF\u65F6\u95F4\u6233\uFF1F","children":[]},{"level":2,"title":"redis \u4E3A\u4EC0\u4E48\u5728\u83B7\u53D6 lruclock \u65F6\u4F7F\u7528\u539F\u5B50\u64CD\u4F5C\uFF1F","slug":"redis-\u4E3A\u4EC0\u4E48\u5728\u83B7\u53D6-lruclock-\u65F6\u4F7F\u7528\u539F\u5B50\u64CD\u4F5C\uFF1F","link":"#redis-\u4E3A\u4EC0\u4E48\u5728\u83B7\u53D6-lruclock-\u65F6\u4F7F\u7528\u539F\u5B50\u64CD\u4F5C\uFF1F","children":[]},{"level":2,"title":"\u5982\u4F55\u6253\u5F00 LFU \u6A21\u5F0F\uFF1F","slug":"\u5982\u4F55\u6253\u5F00-lfu-\u6A21\u5F0F\uFF1F","link":"#\u5982\u4F55\u6253\u5F00-lfu-\u6A21\u5F0F\uFF1F","children":[]},{"level":2,"title":"\u601D\u8003\u9898","slug":"\u601D\u8003\u9898","link":"#\u601D\u8003\u9898","children":[]}],"relativePath":"\u5176\u4ED6/Redis \u6DF1\u5EA6\u5386\u9669\uFF1A\u6838\u5FC3\u539F\u7406\u4E0E\u5E94\u7528\u5B9E\u8DF5/\u6E90\u7801 8-\u7CBE\u76CA\u6C42\u7CBE \u2014\u2014 LFU vs LRU.md"}'),e={name:"\u5176\u4ED6/Redis \u6DF1\u5EA6\u5386\u9669\uFF1A\u6838\u5FC3\u539F\u7406\u4E0E\u5E94\u7528\u5B9E\u8DF5/\u6E90\u7801 8-\u7CBE\u76CA\u6C42\u7CBE \u2014\u2014 LFU vs LRU.md"},p=l(`<h1 id="\u6E90\u7801-8-\u7CBE\u76CA\u6C42\u7CBE-\u2014\u2014-lfu-vs-lru" tabindex="-1">\u6E90\u7801 8-\u7CBE\u76CA\u6C42\u7CBE \u2014\u2014 LFU vs LRU <a class="header-anchor" href="#\u6E90\u7801-8-\u7CBE\u76CA\u6C42\u7CBE-\u2014\u2014-lfu-vs-lru" aria-hidden="true">#</a></h1><hr><h1 id="\u6E90\u7801-8\uFF1A\u7CBE\u76CA\u6C42\u7CBE-\u2014\u2014-lfu-vs-lru" tabindex="-1">\u6E90\u7801 8\uFF1A\u7CBE\u76CA\u6C42\u7CBE \u2014\u2014 LFU vs LRU <a class="header-anchor" href="#\u6E90\u7801-8\uFF1A\u7CBE\u76CA\u6C42\u7CBE-\u2014\u2014-lfu-vs-lru" aria-hidden="true">#</a></h1><p>\u5728\u7B2C 27 \u5C0F\u8282\uFF0C\u6211\u4EEC\u8BB2\u5230\u4E86 Redis \u7684 LRU \u6A21\u5F0F\uFF0C\u5B83\u53EF\u4EE5\u6709\u6548\u7684\u63A7\u5236 Redis \u5360\u7528\u5185\u5B58\u5927\u5C0F\uFF0C\u5C06\u51B7\u6570\u636E\u4ECE\u5185\u5B58\u4E2D\u6DD8\u6C70\u51FA\u53BB\u3002Antirez \u5728 Redis 4.0 \u91CC\u5F15\u5165\u4E86\u4E00\u4E2A\u65B0\u7684\u6DD8\u6C70\u7B56\u7565 \u2014\u2014 LFU \u6A21\u5F0F\uFF0C\u4F5C\u8005\u8BA4\u4E3A\u5B83\u6BD4 LRU \u66F4\u52A0\u4F18\u79C0\u3002</p><p>LFU \u7684\u5168\u79F0\u662F<code>Least Frequently Used</code>\uFF0C\u8868\u793A\u6309\u6700\u8FD1\u7684\u8BBF\u95EE\u9891\u7387\u8FDB\u884C\u6DD8\u6C70\uFF0C\u5B83\u6BD4 LRU \u66F4\u52A0\u7CBE\u51C6\u5730\u8868\u793A\u4E86\u4E00\u4E2A key \u88AB\u8BBF\u95EE\u7684\u70ED\u5EA6\u3002</p><p>\u5982\u679C\u4E00\u4E2A key \u957F\u65F6\u95F4\u4E0D\u88AB\u8BBF\u95EE\uFF0C\u53EA\u662F\u521A\u521A\u5076\u7136\u88AB\u7528\u6237\u8BBF\u95EE\u4E86\u4E00\u4E0B\uFF0C\u90A3\u4E48\u5728\u4F7F\u7528 LRU \u7B97\u6CD5\u4E0B\u5B83\u662F\u4E0D\u5BB9\u6613\u88AB\u6DD8\u6C70\u7684\uFF0C\u56E0\u4E3A LRU \u7B97\u6CD5\u8BA4\u4E3A\u5F53\u524D\u8FD9\u4E2A key \u662F\u5F88\u70ED\u7684\u3002\u800C LFU \u662F\u9700\u8981\u8FFD\u8E2A\u6700\u8FD1\u4E00\u6BB5\u65F6\u95F4\u7684\u8BBF\u95EE\u9891\u7387\uFF0C\u5982\u679C\u67D0\u4E2A key \u53EA\u662F\u5076\u7136\u88AB\u8BBF\u95EE\u4E00\u6B21\u662F\u4E0D\u8DB3\u4EE5\u53D8\u5F97\u5F88\u70ED\u7684\uFF0C\u5B83\u9700\u8981\u5728\u8FD1\u671F\u4E00\u6BB5\u65F6\u95F4\u5185\u88AB\u8BBF\u95EE\u5F88\u591A\u6B21\u624D\u6709\u673A\u4F1A\u88AB\u8BA4\u4E3A\u5F88\u70ED\u3002</p><h2 id="redis-\u5BF9\u8C61\u7684\u70ED\u5EA6" tabindex="-1">Redis \u5BF9\u8C61\u7684\u70ED\u5EA6 <a class="header-anchor" href="#redis-\u5BF9\u8C61\u7684\u70ED\u5EA6" aria-hidden="true">#</a></h2><p>Redis \u7684\u6240\u6709\u5BF9\u8C61\u7ED3\u6784\u5934\u4E2D\u90FD\u6709\u4E00\u4E2A 24bit \u7684\u5B57\u6BB5\uFF0C\u8FD9\u4E2A\u5B57\u6BB5\u7528\u6765\u8BB0\u5F55\u5BF9\u8C61\u7684\u300C\u70ED\u5EA6\u300D\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// redis \u7684\u5BF9\u8C61\u5934</span></span>
<span class="line"><span style="color:#A6ACCD;">typedef struct redisObject {</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned type:4; // \u5BF9\u8C61\u7C7B\u578B\u5982 zset/set/hash \u7B49\u7B49</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned encoding:4; // \u5BF9\u8C61\u7F16\u7801\u5982 ziplist/intset/skiplist \u7B49\u7B49</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned lru:24; // \u5BF9\u8C61\u7684\u300C\u70ED\u5EA6\u300D</span></span>
<span class="line"><span style="color:#A6ACCD;">    int refcount; // \u5F15\u7528\u8BA1\u6570</span></span>
<span class="line"><span style="color:#A6ACCD;">    void *ptr; // \u5BF9\u8C61\u7684 body</span></span>
<span class="line"><span style="color:#A6ACCD;">} robj;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="lru-\u6A21\u5F0F" tabindex="-1">LRU \u6A21\u5F0F <a class="header-anchor" href="#lru-\u6A21\u5F0F" aria-hidden="true">#</a></h2><p>\u5728 LRU \u6A21\u5F0F\u4E0B\uFF0Clru \u5B57\u6BB5\u5B58\u50A8\u7684\u662F Redis \u65F6\u949F<code>server.lruclock</code>\uFF0CRedis \u65F6\u949F\u662F\u4E00\u4E2A 24bit \u7684\u6574\u6570\uFF0C\u9ED8\u8BA4\u662F Unix \u65F6\u95F4\u6233\u5BF9 2^24 \u53D6\u6A21\u7684\u7ED3\u679C\uFF0C\u5927\u7EA6 97 \u5929\u6E05\u96F6\u4E00\u6B21\u3002\u5F53\u67D0\u4E2A key \u88AB\u8BBF\u95EE\u4E00\u6B21\uFF0C\u5B83\u7684\u5BF9\u8C61\u5934\u7684 lru \u5B57\u6BB5\u503C\u5C31\u4F1A\u88AB\u66F4\u65B0\u4E3A<code>server.lruclock</code>\u3002</p><p>\u9ED8\u8BA4 Redis \u65F6\u949F\u503C\u6BCF\u6BEB\u79D2\u66F4\u65B0\u4E00\u6B21\uFF0C\u5728\u5B9A\u65F6\u4EFB\u52A1<code>serverCron</code>\u91CC\u4E3B\u52A8\u8BBE\u7F6E\u3002Redis \u7684\u5F88\u591A\u5B9A\u65F6\u4EFB\u52A1\u90FD\u662F\u5728<code>serverCron</code>\u91CC\u9762\u5B8C\u6210\u7684\uFF0C\u6BD4\u5982\u5927\u578B hash \u8868\u7684\u6E10\u8FDB\u5F0F\u8FC1\u79FB\u3001\u8FC7\u671F key \u7684\u4E3B\u52A8\u6DD8\u6C70\u3001\u89E6\u53D1 bgsave\u3001bgaofrewrite \u7B49\u7B49\u3002</p><p>\u5982\u679C<code>server.lruclock</code>\u6CA1\u6709\u6298\u8FD4 (\u5BF9 2^24 \u53D6\u6A21)\uFF0C\u5B83\u5C31\u662F\u4E00\u76F4\u9012\u589E\u7684\uFF0C\u8FD9\u610F\u5473\u7740\u5BF9\u8C61\u7684 LRU \u5B57\u6BB5\u4E0D\u4F1A\u8D85\u8FC7<code>server.lruclock</code>\u7684\u503C\u3002\u5982\u679C\u8D85\u8FC7\u4E86\uFF0C\u8BF4\u660E<code>server.lruclock</code>\u6298\u8FD4\u4E86\u3002\u901A\u8FC7\u8FD9\u4E2A\u903B\u8F91\u5C31\u53EF\u4EE5\u7CBE\u51C6\u8BA1\u7B97\u51FA\u5BF9\u8C61\u591A\u957F\u65F6\u95F4\u6CA1\u6709\u88AB\u8BBF\u95EE\u2014\u2014\u5BF9\u8C61\u7684\u7A7A\u95F2\u65F6\u95F4\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2018/8/6/1650e756e1ce8b9a~tplv-t2oaga2asx-image.image" alt=""></p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u8BA1\u7B97\u5BF9\u8C61\u7684\u7A7A\u95F2\u65F6\u95F4\uFF0C\u4E5F\u5C31\u662F\u6CA1\u6709\u88AB\u8BBF\u95EE\u7684\u65F6\u95F4\uFF0C\u8FD4\u56DE\u7ED3\u679C\u662F\u6BEB\u79D2</span></span>
<span class="line"><span style="color:#A6ACCD;">unsigned long long estimateObjectIdleTime(robj *o) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned long long lruclock = LRU_CLOCK(); // \u83B7\u53D6 redis \u65F6\u949F\uFF0C\u4E5F\u5C31\u662F server.lruclock \u7684\u503C</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (lruclock &gt;= o-&gt;lru) {</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u6B63\u5E38\u9012\u589E</span></span>
<span class="line"><span style="color:#A6ACCD;">        return (lruclock - o-&gt;lru) * LRU_CLOCK_RESOLUTION; // LRU_CLOCK_RESOLUTION \u9ED8\u8BA4\u662F 1000</span></span>
<span class="line"><span style="color:#A6ACCD;">    } else {</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u6298\u8FD4\u4E86</span></span>
<span class="line"><span style="color:#A6ACCD;">        return (lruclock + (LRU_CLOCK_MAX - o-&gt;lru)) * // LRU_CLOCK_MAX \u662F 2^24-1</span></span>
<span class="line"><span style="color:#A6ACCD;">                    LRU_CLOCK_RESOLUTION;</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6709\u4E86\u5BF9\u8C61\u7684\u7A7A\u95F2\u65F6\u95F4\uFF0C\u5C31\u53EF\u4EE5\u76F8\u4E92\u4E4B\u95F4\u8FDB\u884C\u6BD4\u8F83\u8C01\u65B0\u8C01\u65E7\uFF0C\u968F\u673A LRU \u7B97\u6CD5\u9760\u7684\u5C31\u662F\u6BD4\u8F83\u5BF9\u8C61\u7684\u7A7A\u95F2\u65F6\u95F4\u6765\u51B3\u5B9A\u8C01\u8BE5\u88AB\u6DD8\u6C70\u4E86\u3002</p><h2 id="lfu-\u6A21\u5F0F" tabindex="-1">LFU \u6A21\u5F0F <a class="header-anchor" href="#lfu-\u6A21\u5F0F" aria-hidden="true">#</a></h2><p>\u5728 LFU \u6A21\u5F0F\u4E0B\uFF0Clru \u5B57\u6BB5 24 \u4E2A bit \u7528\u6765\u5B58\u50A8\u4E24\u4E2A\u503C\uFF0C\u5206\u522B\u662F<code>ldt(last decrement time)</code>\u548C<code>logc(logistic counter)</code>\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2018/8/6/1650e8f382f31205~tplv-t2oaga2asx-image.image" alt=""></p><p>logc \u662F 8 \u4E2A bit\uFF0C\u7528\u6765\u5B58\u50A8\u8BBF\u95EE\u9891\u6B21\uFF0C\u56E0\u4E3A 8 \u4E2A bit \u80FD\u8868\u793A\u7684\u6700\u5927\u6574\u6570\u503C\u4E3A 255\uFF0C\u5B58\u50A8\u9891\u6B21\u80AF\u5B9A\u8FDC\u8FDC\u4E0D\u591F\uFF0C\u6240\u4EE5\u8FD9 8 \u4E2A bit \u5B58\u50A8\u7684\u662F\u9891\u6B21\u7684\u5BF9\u6570\u503C\uFF0C\u5E76\u4E14\u8FD9\u4E2A\u503C\u8FD8\u4F1A\u968F\u65F6\u95F4\u8870\u51CF\u3002\u5982\u679C\u5B83\u7684\u503C\u6BD4\u8F83\u5C0F\uFF0C\u90A3\u4E48\u5C31\u5F88\u5BB9\u6613\u88AB\u56DE\u6536\u3002\u4E3A\u4E86\u786E\u4FDD\u65B0\u521B\u5EFA\u7684\u5BF9\u8C61\u4E0D\u88AB\u56DE\u6536\uFF0C\u65B0\u5BF9\u8C61\u7684\u8FD9 8 \u4E2A bit \u4F1A\u521D\u59CB\u5316\u4E3A\u4E00\u4E2A\u5927\u4E8E\u96F6\u7684\u503C\uFF0C\u9ED8\u8BA4\u662F<code>LFU_INIT_VAL=5</code>\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2018/8/6/1650e9c135644d19~tplv-t2oaga2asx-image.image" alt=""></p><p>ldt \u662F 16 \u4E2A\u4F4D\uFF0C\u7528\u6765\u5B58\u50A8\u4E0A\u4E00\u6B21 logc \u7684\u66F4\u65B0\u65F6\u95F4\uFF0C\u56E0\u4E3A\u53EA\u6709 16 \u4F4D\uFF0C\u6240\u4EE5\u7CBE\u5EA6\u4E0D\u53EF\u80FD\u5F88\u9AD8\u3002\u5B83\u53D6\u7684\u662F\u5206\u949F\u65F6\u95F4\u6233\u5BF9 2^16 \u8FDB\u884C\u53D6\u6A21\uFF0C\u5927\u7EA6\u6BCF\u9694 45 \u5929\u5C31\u4F1A\u6298\u8FD4\u3002\u540C LRU \u6A21\u5F0F\u4E00\u6837\uFF0C\u6211\u4EEC\u4E5F\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E2A\u903B\u8F91\u8BA1\u7B97\u51FA\u5BF9\u8C61\u7684\u7A7A\u95F2\u65F6\u95F4\uFF0C\u53EA\u4E0D\u8FC7\u7CBE\u5EA6\u662F\u5206\u949F\u7EA7\u522B\u7684\u3002\u56FE\u4E2D\u7684 server.unixtime \u662F\u5F53\u524D redis \u8BB0\u5F55\u7684\u7CFB\u7EDF\u65F6\u95F4\u6233\uFF0C\u548C server.lruclock \u4E00\u6837\uFF0C\u5B83\u4E5F\u662F\u6BCF\u6BEB\u79D2\u66F4\u65B0\u4E00\u6B21\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// nowInMinutes</span></span>
<span class="line"><span style="color:#A6ACCD;">// server.unixtime \u4E3A redis \u7F13\u5B58\u7684\u7CFB\u7EDF\u65F6\u95F4\u6233</span></span>
<span class="line"><span style="color:#A6ACCD;">unsigned long LFUGetTimeInMinutes(void) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return (server.unixtime/60) &amp; 65535;</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// idle_in_minutes</span></span>
<span class="line"><span style="color:#A6ACCD;">unsigned long LFUTimeElapsed(unsigned long ldt) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned long now = LFUGetTimeInMinutes();</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (now &gt;= ldt) return now-ldt; // \u6B63\u5E38\u6BD4\u8F83</span></span>
<span class="line"><span style="color:#A6ACCD;">    return 65535-ldt+now; // \u6298\u8FD4\u6BD4\u8F83</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>ldt \u7684\u503C\u548C LRU \u6A21\u5F0F\u7684 lru \u5B57\u6BB5\u4E0D\u4E00\u6837\u7684\u662F ldt \u4E0D\u662F\u5728\u5BF9\u8C61\u88AB\u8BBF\u95EE\u65F6\u66F4\u65B0\u7684\u3002\u5B83\u5728 Redis \u7684\u6DD8\u6C70\u903B\u8F91\u8FDB\u884C\u65F6\u8FDB\u884C\u66F4\u65B0\uFF0C\u6DD8\u6C70\u903B\u8F91\u53EA\u4F1A\u5728\u5185\u5B58\u8FBE\u5230 maxmemory \u7684\u8BBE\u7F6E\u65F6\u624D\u4F1A\u89E6\u53D1\uFF0C\u5728\u6BCF\u4E00\u4E2A\u6307\u4EE4\u7684\u6267\u884C\u4E4B\u524D\u90FD\u4F1A\u89E6\u53D1\u3002\u6BCF\u6B21\u6DD8\u6C70\u90FD\u662F\u91C7\u7528\u968F\u673A\u7B56\u7565\uFF0C\u968F\u673A\u6311\u9009\u82E5\u5E72\u4E2A key\uFF0C\u66F4\u65B0\u8FD9\u4E2A key \u7684\u300C\u70ED\u5EA6\u300D\uFF0C\u6DD8\u6C70\u6389\u300C\u70ED\u5EA6\u300D\u6700\u4F4E\u7684\u3002\u56E0\u4E3A Redis \u91C7\u7528\u7684\u662F\u968F\u673A\u7B97\u6CD5\uFF0C\u5982\u679C key \u6BD4\u8F83\u591A\u7684\u8BDD\uFF0C\u90A3\u4E48 ldt \u66F4\u65B0\u7684\u53EF\u80FD\u4F1A\u6BD4\u8F83\u6162\u3002\u4E0D\u8FC7\u65E2\u7136\u5B83\u662F\u5206\u949F\u7EA7\u522B\u7684\u7CBE\u5EA6\uFF0C\u4E5F\u6CA1\u6709\u5FC5\u8981\u66F4\u65B0\u7684\u8FC7\u4E8E\u9891\u7E41\u3002</p><p>ldt \u66F4\u65B0\u7684\u540C\u65F6\u4E5F\u4F1A\u4E00\u540C\u8870\u51CF logc \u7684\u503C\uFF0C\u8870\u51CF\u4E5F\u6709\u7279\u5B9A\u7684\u7B97\u6CD5\u3002\u5B83\u5C06\u73B0\u6709\u7684 logc \u503C\u51CF\u53BB\u5BF9\u8C61\u7684\u7A7A\u95F2\u65F6\u95F4 (\u5206\u949F\u6570) \u9664\u4EE5\u4E00\u4E2A\u8870\u51CF\u7CFB\u6570\uFF0C\u9ED8\u8BA4\u8FD9\u4E2A\u8870\u51CF\u7CFB\u6570<code>lfu_decay_time</code>\u662F 1\u3002\u5982\u679C\u8FD9\u4E2A\u503C\u5927\u4E8E 1\uFF0C\u90A3\u4E48\u5C31\u4F1A\u8870\u51CF\u7684\u6BD4\u8F83\u6162\u3002\u5982\u679C\u5B83\u7B49\u4E8E\u96F6\uFF0C\u90A3\u5C31\u8868\u793A\u4E0D\u8870\u51CF\uFF0C\u5B83\u662F\u53EF\u4EE5\u901A\u8FC7\u914D\u7F6E\u53C2\u6570<code>lfu-decay-time</code>\u8FDB\u884C\u914D\u7F6E\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u8870\u51CF logc</span></span>
<span class="line"><span style="color:#A6ACCD;">unsigned long LFUDecrAndReturn(robj *o) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned long ldt = o-&gt;lru &gt;&gt; 8; // \u524D 16bit</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned long counter = o-&gt;lru &amp; 255; // \u540E 8bit \u4E3A logc</span></span>
<span class="line"><span style="color:#A6ACCD;">    // num_periods \u4E3A\u5373\u5C06\u8870\u51CF\u7684\u6570\u91CF</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned long num_periods = server.lfu_decay_time ? LFUTimeElapsed(ldt) / server.lfu_decay_time : 0;</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (num_periods)</span></span>
<span class="line"><span style="color:#A6ACCD;">        counter = (num_periods &gt; counter) ? 0 : counter - num_periods;</span></span>
<span class="line"><span style="color:#A6ACCD;">    return counter;</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>logc \u7684\u66F4\u65B0\u548C LRU \u6A21\u5F0F\u7684 lru \u5B57\u6BB5\u4E00\u6837\uFF0C\u90FD\u4F1A\u5728 key \u6BCF\u6B21\u88AB\u8BBF\u95EE\u7684\u65F6\u5019\u66F4\u65B0\uFF0C\u53EA\u4E0D\u8FC7\u5B83\u7684\u66F4\u65B0\u4E0D\u662F\u7B80\u5355\u7684<code>+1</code>\uFF0C\u800C\u662F\u91C7\u7528\u6982\u7387\u6CD5\u8FDB\u884C\u9012\u589E\uFF0C\u56E0\u4E3A logc \u5B58\u50A8\u7684\u662F\u8BBF\u95EE\u8BA1\u6570\u7684\u5BF9\u6570\u503C\uFF0C\u4E0D\u80FD\u76F4\u63A5<code>+1</code>\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">/* Logarithmically increment a counter. The greater is the current counter value</span></span>
<span class="line"><span style="color:#A6ACCD;"> * the less likely is that it gets really implemented. Saturate it at 255. */</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u5BF9\u6570\u9012\u589E\u8BA1\u6570\u503C</span></span>
<span class="line"><span style="color:#A6ACCD;">uint8_t LFULogIncr(uint8_t counter) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (counter == 255) return 255; // \u5230\u6700\u5927\u503C\u4E86\uFF0C\u4E0D\u80FD\u5728\u589E\u52A0\u4E86</span></span>
<span class="line"><span style="color:#A6ACCD;">    double baseval = counter - LFU_INIT_VAL; // \u51CF\u53BB\u65B0\u5BF9\u8C61\u521D\u59CB\u5316\u7684\u57FA\u6570\u503C (LFU_INIT_VAL \u9ED8\u8BA4\u662F 5)</span></span>
<span class="line"><span style="color:#A6ACCD;">    // baseval \u5982\u679C\u5C0F\u4E8E\u96F6\uFF0C\u8BF4\u660E\u8FD9\u4E2A\u5BF9\u8C61\u5FEB\u4E0D\u884C\u4E86\uFF0C\u4E0D\u8FC7\u672C\u6B21 incr \u5C06\u4F1A\u5EF6\u957F\u5B83\u7684\u5BFF\u547D</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (baseval &lt; 0) baseval = 0; </span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u5F53\u524D\u8BA1\u6570\u8D8A\u5927\uFF0C\u60F3\u8981 +1 \u5C31\u8D8A\u56F0\u96BE</span></span>
<span class="line"><span style="color:#A6ACCD;">    // lfu_log_factor \u4E3A\u56F0\u96BE\u7CFB\u6570\uFF0C\u9ED8\u8BA4\u662F 10</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u5F53 baseval \u7279\u522B\u5927\u65F6\uFF0C\u6700\u5927\u662F (255-5)\uFF0Cp \u503C\u4F1A\u975E\u5E38\u5C0F\uFF0C\u5F88\u96BE\u4F1A\u8D70\u5230 counter++ \u8FD9\u4E00\u6B65</span></span>
<span class="line"><span style="color:#A6ACCD;">    // p \u5C31\u662F counter \u901A\u5F80 [+1] \u6743\u529B\u7684\u95E8\u7F1D\uFF0Cbaseval \u8D8A\u5927\uFF0C\u8FD9\u4E2A\u95E8\u7F1D\u8D8A\u7A84\uFF0C\u901A\u8FC7\u5C31\u8D8A\u8270\u96BE</span></span>
<span class="line"><span style="color:#A6ACCD;">    double p = 1.0/(baseval*server.lfu_log_factor+1);</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u5F00\u59CB\u968F\u673A\u770B\u770B\u80FD\u4E0D\u80FD\u4ECE\u95E8\u7F1D\u6324\u8FDB\u53BB</span></span>
<span class="line"><span style="color:#A6ACCD;">    double r = (double)rand()/RAND_MAX; // 0 &lt; r &lt; 1</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (r &lt; p) counter++;</span></span>
<span class="line"><span style="color:#A6ACCD;">    return counter;</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="\u4E3A\u4EC0\u4E48-redis-\u8981\u7F13\u5B58\u7CFB\u7EDF\u65F6\u95F4\u6233\uFF1F" tabindex="-1">\u4E3A\u4EC0\u4E48 Redis \u8981\u7F13\u5B58\u7CFB\u7EDF\u65F6\u95F4\u6233\uFF1F <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48-redis-\u8981\u7F13\u5B58\u7CFB\u7EDF\u65F6\u95F4\u6233\uFF1F" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u5E73\u65F6\u4F7F\u7528\u7CFB\u7EDF\u65F6\u95F4\u6233\u65F6\uFF0C\u5E38\u5E38\u662F\u4E0D\u5047\u601D\u7D22\u5730\u4F7F\u7528<code>System.currentTimeInMillis</code>\u6216\u8005<code>time.time()</code>\u6765\u83B7\u53D6\u7CFB\u7EDF\u7684\u6BEB\u79D2\u65F6\u95F4\u6233\u3002Redis \u4E0D\u80FD\u8FD9\u6837\uFF0C\u56E0\u4E3A\u6BCF\u4E00\u6B21\u83B7\u53D6\u7CFB\u7EDF\u65F6\u95F4\u6233\u90FD\u662F\u4E00\u6B21\u7CFB\u7EDF\u8C03\u7528\uFF0C\u7CFB\u7EDF\u8C03\u7528\u76F8\u5BF9\u6765\u8BF4\u662F\u6BD4\u8F83\u8D39\u65F6\u95F4\u7684\uFF0C\u4F5C\u4E3A\u5355\u7EBF\u7A0B\u7684 Redis \u8868\u793A\u627F\u53D7\u4E0D\u8D77\uFF0C\u6240\u4EE5\u5B83\u9700\u8981\u5BF9\u65F6\u95F4\u8FDB\u884C\u7F13\u5B58\uFF0C\u83B7\u53D6\u65F6\u95F4\u90FD\u76F4\u63A5\u4ECE\u7F13\u5B58\u4E2D\u76F4\u63A5\u62FF\u3002</p><h2 id="redis-\u4E3A\u4EC0\u4E48\u5728\u83B7\u53D6-lruclock-\u65F6\u4F7F\u7528\u539F\u5B50\u64CD\u4F5C\uFF1F" tabindex="-1">redis \u4E3A\u4EC0\u4E48\u5728\u83B7\u53D6 lruclock \u65F6\u4F7F\u7528\u539F\u5B50\u64CD\u4F5C\uFF1F <a class="header-anchor" href="#redis-\u4E3A\u4EC0\u4E48\u5728\u83B7\u53D6-lruclock-\u65F6\u4F7F\u7528\u539F\u5B50\u64CD\u4F5C\uFF1F" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u77E5\u9053 Redis \u662F\u5355\u7EBF\u7A0B\u7684\uFF0C\u90A3\u4E3A\u4EC0\u4E48 lruclock \u8981\u4F7F\u7528\u539F\u5B50\u64CD\u4F5C atomicGet \u6765\u83B7\u53D6\u5462\uFF1F</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">unsigned int LRU_CLOCK(void) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    unsigned int lruclock;</span></span>
<span class="line"><span style="color:#A6ACCD;">    if (1000/server.hz &lt;= LRU_CLOCK_RESOLUTION) {</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u8FD9\u91CC\u539F\u5B50\u64CD\u4F5C\uFF0C\u901A\u5E38\u4F1A\u8D70\u8FD9\u91CC\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u6CE8\u610F\u8FD9\u91CC</span></span>
<span class="line"><span style="color:#A6ACCD;">        atomicGet(server.lruclock,lruclock);  </span></span>
<span class="line"><span style="color:#A6ACCD;">    } else {</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u76F4\u63A5\u901A\u8FC7\u7CFB\u7EDF\u8C03\u7528\u83B7\u53D6\u65F6\u95F4\u6233\uFF0Chz \u914D\u7F6E\u7684\u592A\u4F4E (\u4E00\u822C\u4E0D\u4F1A\u8FD9\u4E48\u5E72)\uFF0Clruclock \u66F4\u65B0\u4E0D\u53CA\u65F6\uFF0C\u9700\u8981\u5B9E\u65F6\u83B7\u53D6\u7CFB\u7EDF\u65F6\u95F4\u6233</span></span>
<span class="line"><span style="color:#A6ACCD;">        lruclock = getLRUClock(); </span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">    return lruclock;</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u56E0\u4E3A Redis \u5B9E\u9645\u4E0A\u5E76\u4E0D\u662F\u5355\u7EBF\u7A0B\uFF0C\u5B83\u80CC\u540E\u8FD8\u6709\u51E0\u4E2A\u5F02\u6B65\u7EBF\u7A0B\u4E5F\u5728\u9ED8\u9ED8\u5DE5\u4F5C\u3002\u8FD9\u51E0\u4E2A\u7EBF\u7A0B\u4E5F\u8981\u8BBF\u95EE Redis \u65F6\u949F\uFF0C\u6240\u4EE5 lruclock \u5B57\u6BB5\u662F\u9700\u8981\u652F\u6301\u591A\u7EBF\u7A0B\u8BFB\u5199\u7684\u3002\u4F7F\u7528 atomic \u8BFB\u5199\u80FD\u4FDD\u8BC1\u591A\u7EBF\u7A0B lruclock \u6570\u636E\u7684\u4E00\u81F4\u6027\u3002</p><h2 id="\u5982\u4F55\u6253\u5F00-lfu-\u6A21\u5F0F\uFF1F" tabindex="-1">\u5982\u4F55\u6253\u5F00 LFU \u6A21\u5F0F\uFF1F <a class="header-anchor" href="#\u5982\u4F55\u6253\u5F00-lfu-\u6A21\u5F0F\uFF1F" aria-hidden="true">#</a></h2><p>Redis 4.0 \u7ED9\u6DD8\u6C70\u7B56\u7565\u914D\u7F6E\u53C2\u6570<code>maxmemory-policy</code>\u589E\u52A0\u4E86 2 \u4E2A\u9009\u9879\uFF0C\u5206\u522B\u662F volatile-lfu \u548C allkeys-lfu\uFF0C\u5206\u522B\u662F\u5BF9\u5E26\u8FC7\u671F\u65F6\u95F4\u7684 key \u8FDB\u884C lfu \u6DD8\u6C70\u4EE5\u53CA\u5BF9\u6240\u6709\u7684 key \u6267\u884C lfu \u6DD8\u6C70\u7B97\u6CD5\u3002\u6253\u5F00\u4E86\u8FD9\u4E2A\u9009\u9879\u4E4B\u540E\uFF0C\u5C31\u53EF\u4EE5\u4F7F\u7528 <code>object freq</code>\u6307\u4EE4\u83B7\u53D6\u5BF9\u8C61\u7684 lfu \u8BA1\u6570\u503C\u4E86\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&gt; config set maxmemory-policy allkeys-lfu</span></span>
<span class="line"><span style="color:#A6ACCD;">OK</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; set codehole yeahyeahyeah</span></span>
<span class="line"><span style="color:#A6ACCD;">OK</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u83B7\u53D6\u8BA1\u6570\u503C\uFF0C\u521D\u59CB\u5316\u4E3A LFU_INIT_VAL=5</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; object freq codehole</span></span>
<span class="line"><span style="color:#A6ACCD;">(integer) 5</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u8BBF\u95EE\u4E00\u6B21</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; get codehole</span></span>
<span class="line"><span style="color:#A6ACCD;">&quot;yeahyeahyeah&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u8BA1\u6570\u503C\u589E\u52A0\u4E86</span></span>
<span class="line"><span style="color:#A6ACCD;">&gt; object freq codehole</span></span>
<span class="line"><span style="color:#A6ACCD;">(integer) 6</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="\u601D\u8003\u9898" tabindex="-1">\u601D\u8003\u9898 <a class="header-anchor" href="#\u601D\u8003\u9898" aria-hidden="true">#</a></h2><ol><li>\u4F60\u80FD\u5C1D\u8BD5\u4F7F\u7528 py \u6216\u8005 Java \u5199\u4E00\u4E2A\u7B80\u5355\u7684 LFU \u7B97\u6CD5\u4E48\uFF1F</li><li>\u5982\u679C\u4E00\u5F00\u59CB\u4F7F\u7528 LRU \u6A21\u5F0F\uFF0C\u7A81\u7136\u6539\u53D8\u914D\u7F6E\u53D8\u6210\u4E86 LFU \u6A21\u5F0F\uFF0C\u60F3\u8C61\u4E00\u4E0B Redis \u5BF9\u8C61\u5934\u7684 lru \u5B57\u6BB5\u503C\uFF0C\u4F1A\u5BF9\u73B0\u6709\u7684\u5BF9\u8C61\u4EA7\u751F\u4EC0\u4E48\u5F71\u54CD\uFF1F</li></ol>`,39),o=[p];function c(r,t,i,d,C,A){return a(),n("div",null,o)}const g=s(e,[["render",c]]);export{y as __pageData,g as default};
