import{_ as a,c as s,o as t,d as e}from"./app.e5528619.js";const v=JSON.parse('{"title":"Canvas \u7684\u5E94\u7528\u573A\u666F","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7ED8\u5236\u56FE\u8868","slug":"\u7ED8\u5236\u56FE\u8868","link":"#\u7ED8\u5236\u56FE\u8868","children":[]},{"level":2,"title":"\u5C0F\u6E38\u620F","slug":"\u5C0F\u6E38\u620F","link":"#\u5C0F\u6E38\u620F","children":[]},{"level":2,"title":"\u6D3B\u52A8\u9875\u9762","slug":"\u6D3B\u52A8\u9875\u9762","link":"#\u6D3B\u52A8\u9875\u9762","children":[]},{"level":2,"title":"\u5C0F\u7279\u6548","slug":"\u5C0F\u7279\u6548","link":"#\u5C0F\u7279\u6548","children":[]},{"level":2,"title":"\u70AB\u9177\u80CC\u666F","slug":"\u70AB\u9177\u80CC\u666F","link":"#\u70AB\u9177\u80CC\u666F","children":[]}],"relativePath":"\u5176\u4ED6/\u5982\u4F55\u4F7F\u7528 Canvas \u5236\u4F5C\u51FA\u70AB\u9177\u7684\u7F51\u9875\u80CC\u666F\u7279\u6548/Canvas \u7684\u5E94\u7528\u573A\u666F.md"}'),p={name:"\u5176\u4ED6/\u5982\u4F55\u4F7F\u7528 Canvas \u5236\u4F5C\u51FA\u70AB\u9177\u7684\u7F51\u9875\u80CC\u666F\u7279\u6548/Canvas \u7684\u5E94\u7528\u573A\u666F.md"},i=e('<h1 id="canvas-\u7684\u5E94\u7528\u573A\u666F" tabindex="-1">Canvas \u7684\u5E94\u7528\u573A\u666F <a class="header-anchor" href="#canvas-\u7684\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a></h1><hr><h1 id="canvas-\u7684\u5E94\u7528\u573A\u666F-1" tabindex="-1">Canvas \u7684\u5E94\u7528\u573A\u666F <a class="header-anchor" href="#canvas-\u7684\u5E94\u7528\u573A\u666F-1" aria-hidden="true">#</a></h1><p>\u7ECF\u8FC7\u7B2C 1 \u8282\u7684\u4ECB\u7ECD\uFF0C\u4F60\u5E94\u8BE5\u4E86\u89E3</p><ul><li>Canvas \u7684\u4ECB\u7ECD</li><li>Canvas \u63D0\u51FA\u7684\u80CC\u666F</li><li>Canvas \u548C\u5176\u4ED6\u5728 Web \u4E2D\u663E\u793A\u56FE\u50CF\u7684\u6280\u672F\u7684\u533A\u522B</li></ul><p>\u4F60\u5E94\u8BE5\u5DF2\u7ECF\u660E\u767D Canvas \u7A76\u7ADF\u662F\u4EC0\u4E48\u548C Canvas \u7684\u5927\u81F4\u7528\u9014\u3002(\u3065\uFF61\u25D5\u203F\u203F\u25D5\uFF61)\u3065</p><p>\u8FD9\u4E00\u8282\u5C06\u4E3A\u4F60\u5C55\u793A 30+ \u4E2A Canvas \u5B9E\u4F8B\uFF0C\u8BA9\u4F60\u611F\u53D7\u4E0B Canvas \u7684\u5F3A\u5927\u4F5C\u7528\u3002</p><h2 id="\u7ED8\u5236\u56FE\u8868" tabindex="-1">\u7ED8\u5236\u56FE\u8868 <a class="header-anchor" href="#\u7ED8\u5236\u56FE\u8868" aria-hidden="true">#</a></h2><p>\u7ED8\u5236\u56FE\u8868\u5E94\u8BE5\u662F Canvas \u6700\u4E3A<strong>\u5B9E\u7528</strong>\u7684\u529F\u80FD\u4E4B\u4E00\u4E86\u5427(\u0E51\u2022\u0300\u3142\u2022\u0301)\u0648\u2727</p><p>\u56E0\u4E3A Canvas \u901A\u8FC7 JavaScript \u53EF\u4EE5\u52A8\u6001\u4F20\u5165\u53C2\u6570\u7ED8\u5236\u56FE\u5F62\uFF0C\u6240\u4EE5\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528 Canvas \u4F5C\u4E3A\u5BB9\u5668\uFF0C\u901A\u8FC7 JavaScript \u52A8\u6001\u4F20\u5165\u7684\u53C2\u6570\u5C06\u6570\u636E\u4EE5\u56FE\u8868\u7684\u5F62\u5F0F\u663E\u793A\u51FA\u6765\u3002</p><p>\u4E0D\u4EC5\u663E\u793A\u66F4\u4E3A\u65B9\u4FBF\uFF0C\u800C\u4E14\u4FEE\u6539\u6570\u636E\u4E5F\u540C\u6837\u7684\u7B80\u5355\u3002\u540C\u65F6\u4E5F\u53EF\u4EE5\u6709\u4E00\u4E9B\u7B80\u5355\u7684\u52A8\u753B\u548C\u4EA4\u4E92\u6548\u679C\uFF0C\u5BF9\u4E8E\u53EF\u89C6\u5316\u7684\u6570\u636E\u5C55\u793A\u66F4\u4E3A\u53CB\u597D\u3002</p><p>\u8FD9\u4E9B\u90FD\u662F\u4F20\u7EDF\u7684 png/jpg \u9759\u6001\u663E\u793A\u56FE\u7247\u6240\u4E0D\u80FD\u6BD4\u62DF\u7684\u3002</p><p>\u73B0\u5728\u7684\u4E00\u4E9B\u6570\u636E\u53EF\u89C6\u5316\u7684 js \u5E93\uFF08\u5982 ECharts\uFF09\u5927\u90E8\u5206\u90FD\u662F\u4F7F\u7528 Canvas \u5B9E\u73B0\u7684\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe27895fb5fb70~tplv-t2oaga2asx-image.image" alt="ECharts \u5B98\u7F51\u793A\u4F8B"></p><h2 id="\u5C0F\u6E38\u620F" tabindex="-1">\u5C0F\u6E38\u620F <a class="header-anchor" href="#\u5C0F\u6E38\u620F" aria-hidden="true">#</a></h2><p>\u5982\u4ECA\u4EBA\u4EEC\u4F7F\u7528\u624B\u673A\u7684\u9891\u7387\u8D8A\u6765\u8D8A\u9AD8\uFF0C\u56E0\u6B64\u7528\u6D4F\u89C8\u5668\u6253\u5F00\u7F51\u5740\u5C31\u53EF\u4EE5\u73A9\u7684\u6E38\u620F\u8D8A\u6765\u8D8A\u53D7\u5230\u5F00\u53D1\u8005\u548C\u7528\u6237\u7684\u559C\u7231\u3002</p><p>\u800C Canvas \u56E0\u5176\u72EC\u7279\u7684\u6027\u8D28\u53EF\u4EE5\u8BF4\u662F Web \u6E38\u620F\u7684\u4E0D\u4E8C\u4E4B\u9009\uFF0C\u57FA\u672C\u4E0A\u6240\u6709\u7684 HTML5 \u6E38\u620F\u5F15\u64CE\u90FD\u662F\u57FA\u4E8E Canvas \u5F00\u53D1\u7684\u3002\u90A3\u4E48\u4E3A\u4EC0\u4E48\u4F1A\u4F7F\u7528 Canvas \u6765\u5F00\u53D1\u6E38\u620F\u5462\uFF1F</p><p>\u9996\u5148\u662F\u56E0\u4E3A Canvas \u4E0D\u9700\u8981\u501F\u52A9\u4EFB\u4F55\u63D2\u4EF6\u5C31\u53EF\u4EE5\u5728\u7F51\u9875\u4E2D\u7ED8\u56FE\u3002\u5E76\u4E14\u5176\u5F3A\u5927\u7684\u7ED8\u56FE API \u53EF\u4EE5\u64CD\u7EB5\u9875\u9762\u4E0A\u7684\u6BCF\u4E00\u4E2A\u5143\u7D20\u3002</p><p>\u4E0B\u9762\u6211\u4EEC\u6765\u6B23\u8D4F\u4E00\u4E0B\u7528 Canvas \u5236\u4F5C\u7684\u51E0\u6B3E\u7B80\u5355\u7684\u5C0F\u6E38\u620F~</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe27a59790d64c~tplv-t2oaga2asx-image.image" alt="\u8D5B\u8F66\u5C0F\u6E38\u620F"></p><p>\u8FD9\u662F\u4E00\u6B3E\u5C0F\u578B\u7684\u8D5B\u8F66\u6E38\u620F\uFF0C\u53EF\u4EE5\u4F7F\u7528\u952E\u76D8\u7684\u65B9\u5411\u952E\u6765\u63A7\u5236\u8D5B\u8F66\u7684\u884C\u9A76\u65B9\u5411\uFF0C\u6CA1\u6709\u6309\u952E\u64CD\u4F5C\uFF0C\u901F\u5EA6\u5C31\u4F1A\u53D8\u6210 0\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe27b6ba8f06b5~tplv-t2oaga2asx-image.image" alt="\u4E94\u5B50\u68CB\u5C0F\u6E38\u620F"></p><p>\u8FD9\u662F\u4E00\u6B3E\u4E94\u5B50\u68CB\u7684\u5C0F\u6E38\u620F\uFF0C\u6A21\u62DF\u4E94\u5B50\u68CB\u6E38\u620F\u89C4\u5219\uFF0C\u6E38\u620F\u5F00\u59CB\u65F6\u4E00\u65B9\u5148\u8D70\uFF0C\u7136\u540E\u53E6\u4E00\u65B9\u518D\u8D70\uFF0C\u4F9D\u6B21\u5FAA\u73AF\uFF0C\u76F4\u81F3\u4E00\u65B9\u6709\u8FDE\u6210\u4E00\u6761\u7EBF\u7684\u4E94\u4E2A\u68CB\u5B50\uFF0C\u4F1A\u81EA\u52A8\u5224\u65AD\u8F93\u8D62\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe27eb875ad23e~tplv-t2oaga2asx-image.image" alt="\u4FC4\u7F57\u65AF\u65B9\u5757"></p><p>\u5C31\u8FDE\u7ECF\u5178\u7684\u4FC4\u7F57\u65AF\u65B9\u5757\u6E38\u620F\u4E5F\u53EF\u4EE5\u4F7F\u7528 Canvas \u6765\u5236\u4F5C\u3002\u4E5F\u662F\u9700\u8981\u901A\u8FC7\u952E\u76D8\u7684\u65B9\u5411\u952E\u6765\u63A7\u5236\u65B9\u5757\uFF0C\u5DE6\u53F3\u65B9\u5411\u952E\u662F\u63A7\u5236\u65B9\u5757\u79FB\u52A8\u7684\u65B9\u5411\uFF0C\u4E0B\u952E\u662F\u52A0\u901F\u4E0B\u843D\uFF0C\u4E0A\u952E\u662F\u7FFB\u8F6C\u65B9\u5757\uFF0C\u76F4\u81F3\u67D0\u4E00\u884C\u5B8C\u5168\u88AB\u586B\u6EE1\u5C31\u88AB\u6D88\u9664\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe28005f725c9a~tplv-t2oaga2asx-image.image" alt="\u4F60\u753B\u6211\u731C\u5C0F\u6E38\u620F"></p><p>\u8FD8\u6709\u524D\u4E00\u6BB5\u633A\u706B\u7684\u4F60\u753B\u6211\u731C\u5C0F\u6E38\u620F\u4E5F\u53EF\u4EE5\u901A\u8FC7 Canvas \u6765\u5B9E\u73B0\u3002\u4FDD\u5B58\u4E86\u9F20\u6807\u7684\u8F68\u8FF9\uFF0C\u8FD8\u6709\u6E05\u9664\u5C4F\u5E55\u548C\u6A61\u76AE\u64E6\u7B49\u529F\u80FD\u3002\u4E5F\u53EF\u4EE5\u5C06\u5176\u4FDD\u5B58\u4E3A\u4E00\u5F20\u56FE\u7247\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe274320d11c8b~tplv-t2oaga2asx-image.image" alt="\u8FF7\u5BAB\u5C0F\u7403\u5C0F\u6E38\u620F"></p><p>\u624B\u6B8B\u515A\uFF0C\u8FD9\u4E2A gif \u5F55\u5236\u4E86\u597D\u51E0\u904D(\u0CA5_\u0CA5)\u3002 \u8FD9\u662F\u4E00\u6B3E\u8FF7\u5BAB\u7C7B\u7684\u5C0F\u6E38\u620F\uFF0C\u540C\u6837\u662F\u901A\u8FC7\u952E\u76D8\u7684\u65B9\u5411\u952E\u6765\u63A7\u5236\u5C0F\u7403\u7684\u79FB\u52A8\uFF0C\u6700\u7EC8\u5230\u8FBE\u67D0\u4E2A\u5730\u70B9\u5C31\u4F1A\u5224\u5B9A\u901A\u8FC7\u6E38\u620F\u3002</p><h2 id="\u6D3B\u52A8\u9875\u9762" tabindex="-1">\u6D3B\u52A8\u9875\u9762 <a class="header-anchor" href="#\u6D3B\u52A8\u9875\u9762" aria-hidden="true">#</a></h2><p>\u76F8\u4FE1\u5F88\u591A\u7684\u8425\u9500\u6D3B\u52A8\u5927\u5BB6\u90FD\u505A\u8FC7\uFF0CCanvas \u4E5F\u53EF\u4EE5\u5199\u6D3B\u52A8\u9875\u9762\u54E6~(\u0E51\u2022\u0300\u3142\u2022\u0301)\u0648\uFF0C\u2727\u8FD9\u662F\u5F88\u591A\u516C\u53F8\u7684\u8425\u9500\u7B56\u7565~</p><p>\u4F8B\u5982\uFF1A <img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe28125880f46e~tplv-t2oaga2asx-image.image" alt="\u8F6C\u76D8\u62BD\u5956\u6D3B\u52A8"></p><p>\u5F88\u5E38\u89C1\u7684\u6D3B\u52A8\u9875\u9762\uFF0C\u67D0\u5B9D\u5E97\u94FA\u7684\u5E38\u89C1\u5957\u8DEF\uFF0C\u6A21\u62DF\u8F6C\u76D8\u62BD\u5956\u3002\u70B9\u51FB\u6309\u94AE\uFF0C\u8F6C\u76D8\u8F6C\u52A8\uFF0C\u7136\u540E\u8F6C\u76D8\u505C\u6B62\uFF0C\u6307\u9488\u843D\u5728\u54EA\u4E2A\u533A\u57DF\u5C31\u63D0\u793A\u4E2D\u5956\u7684\u5956\u54C1\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe2817fd861e29~tplv-t2oaga2asx-image.image" alt="\u522E\u522E\u4E50\u62BD\u5956\u6D3B\u52A8"></p><p>\u53E6\u4E00\u4E2A\u6BD4\u8F83\u5E38\u89C1\u7684\u6D3B\u52A8\u9875\u9762\u662F\u522E\u522E\u4E50\u7684\u9875\u9762\uFF0C\u5728\u522E\u522E\u4E50\u7684\u533A\u57DF\uFF0C\u9F20\u6807\u4F1A\u663E\u793A\u4E3A\u786C\u5E01\u7684\u5F62\u72B6\uFF0C\u7136\u540E\u6309\u4F4F\u9F20\u6807\u5E76\u62D6\u52A8\uFF0C\u7ECF\u8FC7\u533A\u57DF\u5C31\u4F1A\u663E\u793A\u51FA\u6700\u5E95\u5C42\u7684\u56FE\u7247\uFF0C\u540C\u65F6\u4E0A\u5C42\u56FE\u7247\u6D88\u5931\u3002\u6A21\u62DF\u522E\u522E\u4E50\u6548\u679C\uFF0C\u522E\u5F00\u4E00\u5B9A\u6BD4\u4F8B\u9762\u79EF\u4E4B\u540E\u201C\u522E\u5956\u201D\u5B8C\u6210\uFF0C\u8FD4\u56DE\u56DE\u8C03\u51FD\u6570\u3002</p><h2 id="\u5C0F\u7279\u6548" tabindex="-1">\u5C0F\u7279\u6548 <a class="header-anchor" href="#\u5C0F\u7279\u6548" aria-hidden="true">#</a></h2><p>Canvas \u8FD8\u53EF\u4EE5\u505A\u4E00\u4E9B\u5C0F\u7279\u6548\u54E6\uFF0C\u8FD9\u4E9B\u5C0F\u7279\u6548\u53EF\u4EE5\u88C5\u9970\u4F60 \u7684\u7F51\u7AD9\uFF0C\u4F7F\u5B83\u53D8\u5F97\u66F4\u52A0\u7CBE\u81F4~</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe283202248787~tplv-t2oaga2asx-image.image" alt="\u7EB8\u7247\u4E0B\u843D\u7279\u6548"></p><p>\u8FD9\u4E2A\u7279\u6548\u4E5F\u53EF\u4EE5\u5728\u6D3B\u52A8\u9875\u9762\u4E2D\u7528\u5230\u3002\u903C\u771F\u7684\u6A21\u62DF\u4E86\u7EB8\u7247\u4E0B\u843D\u7684\u8FC7\u7A0B\uFF0C\u968F\u673A\u51FA\u73B0\u7684\u7EB8\u7247\u6709\u968F\u673A\u751F\u6210\u7684\u989C\u8272\uFF0C\u7136\u540E\u6A21\u62DF\u91CD\u529B\u4E0B\u843D\u8FC7\u7A0B\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe284d4b99886a~tplv-t2oaga2asx-image.image" alt="\u7C92\u5B50\u7EC4\u5408\u6587\u5B57\u7279\u6548"></p><p>\u8FD9\u4E2A\u5176\u5B9E\u662F\u5F88\u4E0D\u9519\u7684\uFF0C\u53EA\u4E0D\u8FC7 gif \u56FE\u7247\u770B\u5F97\u4E0D\u662F\u7279\u522B\u6E05\u695A\u3002\u968F\u673A\u751F\u6210\u7684\u5927\u5C0F\u968F\u673A\u7684\u5706\u5F62\u6216\u65B9\u5F62\u7C92\u5B50\u7EC4\u6210\u6307\u5B9A\u7684\u6587\u5B57\uFF0C\u7C92\u5B50\u4F1A\u81EA\u52A8\u7F29\u653E\uFF0C\u7ED9\u4EBA\u95EA\u70C1\u7684\u611F\u89C9\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe2862d7603a24~tplv-t2oaga2asx-image.image" alt="\u6570\u5B57\u65F6\u949F\u7279\u6548"></p><p>\u8FD9\u4E2A\u7279\u6548\u4E5F\u662F\u7531\u7C92\u5B50\u7EC4\u5408\u6210\u6587\u5B57\u7684\u7279\u6548\uFF0C\u53EA\u4E0D\u8FC7\u6587\u5B57\u7684\u5185\u5BB9\u662F\u5F53\u524D\u7684\u65F6\u95F4\uFF0C\u56E0\u4E3A\u662F gif \u56FE\uFF0C\u6240\u4EE5\u5927\u5BB6\u770B\u4E0D\u5230\u548C\u9F20\u6807\u7684\u4EA4\u4E92\uFF0C\u8FD9\u91CC\u7C92\u5B50\u6563\u5F00\u662F\u56E0\u4E3A\u6211\u9F20\u6807\u8FDB\u884C\u4E86\u70B9\u51FB\uFF0C\u5C31\u662F\u70B9\u51FB\u5C4F\u5E55\uFF0C\u7C92\u5B50\u5C31\u4F1A\u6563\u5F00\uFF0C\u70B9\u51FB\u7684\u65F6\u95F4\u8D8A\u957F\uFF0C\u7C92\u5B50\u5C31\u8D8A\u5206\u6563\u3002\u5F53\u677E\u5F00\u9F20\u6807\u4E4B\u540E\uFF0C\u6240\u6709\u7684\u7C92\u5B50\u5C31\u53C8\u5168\u90E8\u56DE\u5230\u539F\u4F4D~</p><p>\u8BD5\u60F3\u4E00\u4E0B\uFF0C\u5982\u679C\u4F60\u7684\u4E2A\u4EBA\u7F51\u7AD9\u4E2D\u7684\u5F53\u524D\u65F6\u95F4\u4F7F\u7528\u4E86\u8FD9\u6837\u4E00\u4E2A\u5C0F\u7279\u6548\uFF0C\u662F\u4E0D\u662F\u4F1A\u8BA9\u8BBF\u5BA2\u89C9\u5F97\u5F88\u6709\u8DA3\u5462~\u0669(\u0E51&gt;\u25E1&lt;\u0E51)\u06F6</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/16/15fc364e6522d39d~tplv-t2oaga2asx-image.image" alt="loading \u7279\u6548"></p><p>\u540C\u6837\u7684\u5C0F\u7279\u6548\u8FD8\u6709\u8FD9\u4E2A loading\uFF0C\u4F7F\u7528 canvas \u5236\u4F5C\u4E00\u4E2A\u8FD9\u6837\u7684 loading\uFF0C\u4E5F\u4F1A\u7ED9\u4F60\u7684\u7F51\u7AD9\u589E\u6DFB\u4EAE\u70B9\u3002</p><h2 id="\u70AB\u9177\u80CC\u666F" tabindex="-1">\u70AB\u9177\u80CC\u666F <a class="header-anchor" href="#\u70AB\u9177\u80CC\u666F" aria-hidden="true">#</a></h2><p>\u4E0A\u8FF0\u51E0\u79CD\u5E94\u7528\u573A\u666F\u90FD\u53EA\u662F\u5E26\u5927\u5BB6\u4E86\u89E3\u4E00\u4E0B\uFF0C\u4E0D\u505A\u8FC7\u591A\u7684\u4ECB\u7ECD\uFF0C\u5F53\u7136\u4E5F\u8FD8\u6709\u5176\u4ED6\u7684\u5E94\u7528\u573A\u666F\uFF0C\u8FD9\u91CC\u4E5F\u4E0D\u8FC7\u591A\u7684\u4ECB\u7ECD\uFF0C\u4E0B\u9762\u6765\u4ECB\u7ECD\u4E0B\u672C\u5C0F\u518C\u4E3B\u8981\u7ED9\u5927\u5BB6\u5206\u6790\u7684\u6548\u679C\uFF1A\u70AB\u9177\u80CC\u666F\u7279\u6548\u3002</p><p>\u56E0\u4E3A Canvas \u7684\u7279\u6027\uFF0C\u6240\u4EE5\u5982\u679C\u4F60\u7684\u7F51\u7AD9\u60F3\u8981\u4E00\u4E2A\u70AB\u9177\u7684\u80CC\u666F\uFF0C\u90A3\u4E48 Canvas \u65E0\u7591\u662F\u6700\u597D\u7684\u9009\u62E9\uFF0C\u8BA9\u6211\u4EEC\u4E00\u8D77\u6765\u6B23\u8D4F\u4E00\u4E0B canvas \u53EF\u4EE5\u505A\u51FA\u54EA\u4E9B\u70AB\u9177\u7684\u80CC\u666F\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe28f9d8ee1449~tplv-t2oaga2asx-image.image" alt="3D \u7EBF\u6761"></p><p>\u8FD9\u4E2A\u7279\u6548\u662F\u4F7F\u7528 three.js \u548C Canvas \u5236\u4F5C\u51FA\u7684 3D \u7EBF\u6761\uFF0C\u968F\u673A\u751F\u6210\u7684\u7EBF\u6761\u6784\u5EFA\u6210 3D \u7684\u7ACB\u4F53\u7A7A\u95F4\uFF0C\u8FD8\u6709\u548C\u9F20\u6807\u7684\u4EA4\u4E92\uFF0C\u9F20\u6807\u7684\u79FB\u52A8\u4F1A\u4F7F\u5F97 3D \u7A7A\u95F4\u79FB\u52A8\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe2a6812d8a174~tplv-t2oaga2asx-image.image" alt="\u6C14\u6CE1\u80CC\u666F"></p><p>\u8FD9\u4E2A\u6C14\u6CE1\u80CC\u666F\u4E5F\u662F\u5F88\u8D5E\u7684\uFF0C\u968F\u673A\u751F\u6210\u900F\u660E\u5EA6\u4E0D\u540C\u7684\u6C14\u6CE1\uFF0C\u6C14\u6CE1\u4E0D\u65AD\u79FB\u52A8\uFF0C\u6E10\u53D8\u8272\u7684\u80CC\u666F\u4E5F\u5728\u4E0D\u65AD\u53D8\u6362\uFF0C\u7ED9\u4EBA\u4EE5\u68A6\u5E7B\u7684\u611F\u89C9\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe2aa2fc8c1e32~tplv-t2oaga2asx-image.image" alt="\u68F1\u89D2\u80CC\u666F"></p><p>\u8FD9\u4E2A\u7684\u53D8\u6362\u9891\u7387\u5176\u5B9E\u6CA1\u6709\u8FD9\u4E48\u5FEB\uFF0C\u4E24\u6B21\u53D8\u6362\u4E2D\u95F4\u662F\u6709\u51E0\u79D2\u949F\u7684\u505C\u7559\u65F6\u95F4\u7684\uFF0C\u56E0\u4E3A\u5236\u4F5C\u7684 gif \u5927\u5C0F\u6709\u8981\u6C42\uFF0C\u6240\u4EE5\u4E2D\u95F4\u7684\u7ED9\u622A\u6389\u4E86\uFF0C\u53EA\u4FDD\u7559\u53D8\u6362\u7684\u90E8\u5206\u3002</p><p>\u8FD9\u4E2A\u7279\u6548\u7ED9\u4EBA\u7684\u611F\u89C9\u662F\u548C\u94BB\u77F3\u8868\u9762\u7C7B\u4F3C\u7684\u83F1\u89D2\u611F\u89C9\uFF0C\u7136\u540E\u80CC\u666F\u4E5F\u662F\u4F7F\u7528\u7684\u6E10\u53D8\u7684\u989C\u8272\u3002\u4E00\u5B9A\u65F6\u95F4\u5C31\u4F1A\u53D8\u6362\u4E00\u6B21\u3002QQ \u7684\u767B\u5F55\u6846\u5C31\u662F\u548C\u8FD9\u4E2A\u7C7B\u4F3C\u7684\u7279\u6548\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe2b9426054ef9~tplv-t2oaga2asx-image.image" alt="\u4EE3\u7801\u96E8\u80CC\u666F"></p><p>\u8FD9\u4E2A\u80CC\u666F\u662F\u9ED1\u5BA2\u5E1D\u56FD\u7684\u4EE3\u7801\u96E8\u7279\u6548\uFF0C\u53EF\u80FD\u7537\u7A0B\u5E8F\u5458\u4F1A\u6BD4\u8F83\u559C\u6B22\uFF0C\u4EE3\u7801\u4ECE\u4E0A\u5411\u4E0B\u4E0B\u843D\u7684\u6548\u679C\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe2bd9b769d71e~tplv-t2oaga2asx-image.image" alt="\u661F\u7A7A\u8FDE\u7EBF\u80CC\u666F"></p><p>\u6211\u4E2A\u4EBA\u662F\u5F88\u559C\u6B22\u8FD9\u4E2A\u7279\u6548\u7684\uFF0C\u524D\u4E00\u6BB5\u65F6\u95F4\u4E5F\u5C06\u5176\u4F5C\u4E3A\u6211\u7684\u4E2A\u4EBA\u535A\u5BA2\u7684\u80CC\u666F\uFF0C\u8BE5\u7279\u6548\u7684\u80CC\u666F\u662F\u4E00\u4E2A\u6E10\u53D8\u8272\uFF0C\u968F\u673A\u751F\u6210\u7684\u201C\u5C0F\u661F\u661F\u201D\u4F1A\u4ECE\u4E0B\u5411\u4E0A\u79FB\u52A8\uFF0C\u6700\u91CD\u8981\u7684\u662F\u548C\u9F20\u6807\u6709\u4E92\u52A8\u3002\u9F20\u6807\u7ECF\u8FC7\u7684\u5730\u65B9\u4F1A\u4EA7\u751F\u201C\u661F\u661F\u201D\u5E76\u81EA\u52A8\u548C\u5176\u4ED6\u7684\u661F\u661F\u8FDE\u6210\u7EBF\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe2bfc68ac5a1f~tplv-t2oaga2asx-image.image" alt="\u6D41\u661F\u96E8\u7279\u6548"></p><p>\u8FD9\u4E2A\u7279\u6548\u4E5F\u662F\u5F88\u4E0D\u9519\u7684\uFF0C\u6A21\u62DF\u661F\u7A7A\uFF0C\u968F\u673A\u751F\u6210\u7684 \u201C\u661F\u661F\u201D \u4F1A\u6709\u4F4D\u79FB\uFF0C\u900F\u660E\u5EA6\u4E5F\u5728\u4E0D\u65AD\u53D8\u5316\uFF0C\u4F1A\u6709 \u201C\u6D41\u661F\u201D \u968F\u673A\u4ECE\u4E0A\u5411\u4E0B\u6389\u843D\uFF0C\u5E76\u4F1A\u6709\u5C0F\u5C3E\u5DF4\u7684\u6548\u679C~</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/22/15fe2c1e63c319bf~tplv-t2oaga2asx-image.image" alt="\u7EBF\u6761\u5708\u5708\u6EDA\u52A8\u80CC\u666F"></p><p>\u8FD9\u4E2A\u7279\u6548\u662F\u4EE5\u9ED1\u8272\u4E3A\u80CC\u666F\uFF0C\u5F69\u8272\u7684\u968F\u673A\u5B9E\u5FC3 + \u7A7A\u5FC3\u5706\u5708\u6784\u6210\uFF0C\u6240\u6709\u7684\u5708\u5708\u7528\u76F4\u7EBF\u76F8\u8FDE\uFF0C\u5E76\u4E14\u4E00\u76F4\u79FB\u52A8\u3002</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2017/11/23/15fe78e69c06436c~tplv-t2oaga2asx-image.image" alt="\u76F8\u4EA4\u7EBF\u7279\u6548"> \u8FD9\u4E2A\u76F8\u4EA4\u7EBF\u7279\u6548\u5176\u5B9E\u4E5F\u5F88\u7B80\u5355\uFF0C\u968F\u673A\u751F\u6210\u7684\u7EBF\u6761\u5728\u79FB\u52A8\uFF0C\u76F8\u4EA4\u7684\u5730\u65B9\u4E3A\u5C0F\u5706\u70B9\u3002</p><p>\u5E26\u5927\u5BB6\u6B23\u8D4F\u5B8C\u8FD9\u4E48\u591A\u7684\u7279\u6548\u4E4B\u540E\uFF0C\u4F60\u662F\u4E0D\u662F\u5DF2\u7ECF\u60F3\u4F7F\u7528 Canvas \u6765\u88C5\u9970\u4F60\u7684\u4E2A\u4EBA\u7F51\u7AD9\u4E86\u5462\uFF1F\u5FC3\u52A8\u4E0D\u5982\u884C\u52A8\uFF0C\u4E0B\u9762\u6765\u5E26\u5927\u5BB6\u4E00\u8D77\u4ECE\u96F6\u5F00\u59CB\uFF0C\u4E00\u70B9\u4E00\u70B9\u5206\u6790\u600E\u4E48\u5236\u4F5C\u5C5E\u4E8E\u4F60\u81EA\u5DF1\u7684\u70AB\u9177\u7F51\u9875\u80CC\u666F\u7279\u6548~\u03B5==(\u3065\u2032\u25BD`)\u3065</p><p><strong>\u5212\u91CD\u70B9\uFF1A\u7B2C 3 \u8282\u7684\u6700\u540E\u7ED9\u51FA\u4E86\u672C\u5C0F\u518C\u4E2D\u5168\u90E8\u7279\u6548\u7684\u6E90\u7801~\xA0(\uFF61\u2665\u203F\u2665\uFF61)</strong> ha</p>',67),g=[i];function o(c,l,n,r,m,d){return t(),s("div",null,g)}const f=a(p,[["render",o]]);export{v as __pageData,f as default};
