import{_ as s,c as n,o as a,d as e}from"./app.e5528619.js";const D=JSON.parse('{"title":"\u57FA\u7840\u7BC7 1-React \u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6","description":"","frontmatter":{},"headers":[{"level":2,"title":"JSX \u8BED\u6CD5","slug":"jsx-\u8BED\u6CD5","link":"#jsx-\u8BED\u6CD5","children":[]},{"level":2,"title":"React \u7EC4\u4EF6","slug":"react-\u7EC4\u4EF6","link":"#react-\u7EC4\u4EF6","children":[]},{"level":2,"title":"Props \u4E0E State","slug":"props-\u4E0E-state","link":"#props-\u4E0E-state","children":[{"level":3,"title":"props","slug":"props","link":"#props","children":[]},{"level":3,"title":"state","slug":"state","link":"#state","children":[]}]},{"level":2,"title":"\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F","slug":"\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F","link":"#\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F","children":[]},{"level":2,"title":"\u5C0F\u7ED3","slug":"\u5C0F\u7ED3","link":"#\u5C0F\u7ED3","children":[]},{"level":2,"title":"\u53C2\u8003\u8D44\u6599","slug":"\u53C2\u8003\u8D44\u6599","link":"#\u53C2\u8003\u8D44\u6599","children":[]}],"relativePath":"\u5C0F\u7A0B\u5E8F/Taro \u591A\u7AEF\u5F00\u53D1\u5B9E\u73B0\u539F\u7406\u4E0E\u9879\u76EE\u5B9E\u6218/\u57FA\u7840\u7BC7 1-React \u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6.md"}'),l={name:"\u5C0F\u7A0B\u5E8F/Taro \u591A\u7AEF\u5F00\u53D1\u5B9E\u73B0\u539F\u7406\u4E0E\u9879\u76EE\u5B9E\u6218/\u57FA\u7840\u7BC7 1-React \u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6.md"},p=e(`<h1 id="\u57FA\u7840\u7BC7-1-react-\u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6" tabindex="-1">\u57FA\u7840\u7BC7 1-React \u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6 <a class="header-anchor" href="#\u57FA\u7840\u7BC7-1-react-\u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6" aria-hidden="true">#</a></h1><hr><h1 id="react-\u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6" tabindex="-1">React \u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6 <a class="header-anchor" href="#react-\u6838\u5FC3\u8BED\u6CD5\u521D\u8BC6" aria-hidden="true">#</a></h1><p>\u4E0A\u7BC7\u6587\u7AE0\u63D0\u5230 React \u3001 Angular \u3001 Vue \u4E09\u5927\u6846\u67B6\u5927\u884C\u5176\u9053\uFF0C\u524D\u7AEF\u5F00\u53D1\u4E5F\u53D8\u5F97\u8D8A\u6765\u8D8A\u89C4\u8303\u5316\uFF0C\u5DE5\u7A0B\u5316\uFF0C\u6211\u4EEC\u5E38\u8BF4\u7684\u524D\u7AEF\uFF0C\u4E5F\u4E0D\u4EC5\u4EC5\u5C40\u9650\u4E8E H5 \u548C\u7F51\u9875\u7AEF\uFF0C\u800C\u662F\u5EF6\u4F38\u5230\u4E86\u5C0F\u7A0B\u5E8F\u3001\u79FB\u52A8\u7AEF\u3001\u684C\u9762\u7AEF\u7B49\u3002</p><p>\u5728\u6574\u4E2A\u524D\u7AEF\u4F53\u7CFB\u53D1\u5C55\u7684\u8FC7\u7A0B\u4E2D\uFF0CReact \u8BED\u6CD5\u4F53\u7CFB\u8D77\u5230\u4E86\u81F3\u5173\u91CD\u8981\u7684\u4F5C\u7528\uFF0CTaro \u4E5F\u6B63\u662F\u57FA\u4E8E React \u8BED\u6CD5\u6765\u8FDB\u884C\u5F00\u53D1\u3002\u672C\u7AE0\u6211\u4EEC\u9010\u4E00\u4ECB\u7ECD\u4E00\u4E0B React \u8BED\u6CD5\u4F53\u7CFB\u3002</p><blockquote><p>\u6CE8\uFF1A\u76EE\u524D Taro \u6240\u652F\u6301\u7684 React \u8BED\u6CD5\u7248\u672C\u662F\u4EE5 React15 \u4E3A\u57FA\u7840\u7684\uFF0C\u6240\u4EE5\u672C\u7BC7\u6587\u7AE0\u8BF4\u7684\u751F\u547D\u5468\u671F\uFF0C API \u7B49\u90FD\u662F\u57FA\u4E8E React15 \u7684\u7248\u672C\u3002\u800C\u60F3\u4E86\u89E3\u66F4\u591A\u5173\u4E8E React16 \u7248\u672C\u7684\u53D8\u66F4\u60C5\u51B5\uFF0C\u53EF\u5230<a href="https://reactjs.org/" target="_blank" rel="noreferrer">\u5B98\u7F51</a>\u67E5\u9605\u3002</p></blockquote><h2 id="jsx-\u8BED\u6CD5" tabindex="-1">JSX \u8BED\u6CD5 <a class="header-anchor" href="#jsx-\u8BED\u6CD5" aria-hidden="true">#</a></h2><p>JSX \u662F React \u7684\u6838\u5FC3\u7EC4\u6210\u90E8\u5206\uFF0CReact \u8BA4\u4E3A\u7EC4\u4EF6\u5316\u624D\u662F\u6B63\u786E\u7684\u4EE3\u7801\u5206\u79BB\u65B9\u5F0F\uFF0C\u5B83\u8981\u6BD4\u6A21\u677F\u4E0E\u7EC4\u4EF6\u903B\u8F91\u5206\u5F00\u7684\u65B9\u5F0F\u66F4\u597D\uFF0C\u6240\u4EE5\u5C31\u6709\u4E86 JSX \u8BED\u6CD5\u3002\u5B83\u628A HTML \u6A21\u677F\u76F4\u63A5\u5D4C\u5165\u5230 JS \u4EE3\u7801\u91CC\u9762\uFF0C\u8FD9\u6837\u5C31\u505A\u5230\u4E86\u6A21\u677F\u548C\u7EC4\u4EF6\u5173\u8054\u3002JSX \u5141\u8BB8\u5728 JS \u4E2D\u76F4\u63A5\u4F7F\u7528 XML \u6807\u8BB0\u7684\u65B9\u5F0F\u6765\u58F0\u660E\u754C\u9762\uFF0C\u521D\u6B21\u770B\u8D77\u6765\uFF0C\u53EF\u80FD\u4F1A\u6709\u70B9\u7CC5\u6742\u7684\u611F\u89C9\uFF0C\u4E0D\u592A\u9002\u5E94\u3002\u4F46\u4E60\u60EF\u4E86\u4E4B\u540E\uFF0C\u4E5F\u6CA1\u6709\u90A3\u4E48\u522B\u626D\uFF0C\u800C\u4E14\u5F00\u53D1\u8D77\u6765\u8FD8\u86EE\u65B9\u4FBF\u7684\u3002</p><p>\u4E3E\u4E2A Taro \u91CC\u7684\u4F8B\u5B50\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u53EA\u770B render \u51FD\u6570\uFF0C\u7EDD\u5927\u591A\u6570\u60C5\u51B5\u4E0B render \u51FD\u6570\u91CC\u90FD\u4F1A\u6709 XML \u6807\u8BB0</span></span>
<span class="line"><span style="color:#A6ACCD;">render () {</span></span>
<span class="line"><span style="color:#A6ACCD;">  return (</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;View className=&#39;index&#39;&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">      &lt;View className=&#39;title&#39;&gt;{this.state.title}&lt;/View&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">      &lt;View className=&#39;content&#39;&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        {this.state.list.map(item =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">          return (</span></span>
<span class="line"><span style="color:#A6ACCD;">            &lt;View className=&#39;item&#39;&gt;{item}&lt;/View&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">          )</span></span>
<span class="line"><span style="color:#A6ACCD;">        })}</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;Button className=&#39;add&#39; onClick={this.add}&gt;\u6DFB\u52A0&lt;/Button&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">      &lt;/View&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    &lt;/View&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  )</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u53EF\u4EE5\u89C1\u5230\uFF0C<code>render</code> \u51FD\u6570\u8FD4\u56DE\u4E86\u4E00\u4E9B\u7528\u62EC\u53F7\u5305\u4F4F\u7684 XML \u7ED3\u6784\u7684\u754C\u9762\u63CF\u8FF0\uFF0C\u8FD9\u5176\u5B9E\u5C31\u662F\u8BE5\u7EC4\u4EF6\u7684\u754C\u9762\u63CF\u8FF0\u3002\u91CC\u9762\u7684\u5199\u6CD5\u548C HTML \u5E76\u6CA1\u6709\u591A\u5927\u7684\u5DEE\u522B\u3002\u4E0D\u540C\u7684\u5730\u65B9\u4E3B\u8981\u662F\u53EF\u4EE5\u5728\u91CC\u9762\u8FDB\u884C\u4E8B\u4EF6\u7ED1\u5B9A\uFF0C\u8868\u8FBE\u53D8\u91CF\uFF0C\u5B9E\u73B0\u7B80\u5355 JS \u903B\u8F91\u7B49\uFF0C<strong>\u5373\u5728 JS \u91CC\u5199 HTML</strong>\u3002\u800C\u53D8\u91CF\u3001\u7B80\u5355 JS \u903B\u8F91\u90FD\u662F\u9700\u8981\u7528 <code>{}</code> \u5305\u88F9\u8D77\u6765\u3002\u53E6\u5916 HTML \u7684 <code>class</code> \u5C5E\u6027\u56E0\u4E3A\u662F Javascript \u7684\u4FDD\u7559\u5B57\uFF0C\u6240\u4EE5\u9700\u8981\u5199\u6210 <code>className</code>\u3002</p><p>\u5B9E\u9645\u4E0A\uFF0C\u4E0A\u8FF0 JSX \u4EE3\u7801\uFF0C\u6700\u7EC8\u90FD\u4F1A\u901A\u8FC7 JSX \u7F16\u8BD1\u5668\uFF08\u73B0\u5728\u4E3B\u8981\u90FD\u662F <a href="https://www.babeljs.cn/" target="_blank" rel="noreferrer">babel</a>\uFF09\uFF0C\u8F6C\u6362\u4E3A JS \u4EE3\u7801\u3002\u4F8B\u5982\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// JSX \u8BED\u6CD5</span></span>
<span class="line"><span style="color:#A6ACCD;">const num = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">const hello = &lt;div className=&#39;test&#39;&gt;{num}&lt;/div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u7F16\u8BD1\u540E\u7684 JS</span></span>
<span class="line"><span style="color:#A6ACCD;">const num = 1</span></span>
<span class="line"><span style="color:#A6ACCD;">const hello = React.createElement(&#39;div&#39;, {className:&#39;test&#39;}, num)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8FD9\u91CC\u7528\u7684\u662F React \u7684\u4F8B\u5B50\uFF08<a href="https://reactjs.org/docs/react-api.html#createelement" target="_blank" rel="noreferrer">React.createElement</a> \u662F React \u91CC\u7684\u4E00\u4E2A\u6838\u5FC3\u51FD\u6570\uFF0C\u7528\u6765\u751F\u6210\u865A\u62DF DOM )\uFF0CTaro \u4E2D\u4E5F\u4F1A\u6709\u7C7B\u4F3C\u7684\u5904\u7406\u3002\u7B80\u8A00\u4E4B\uFF0CJSX \u5176\u5B9E\u662F\u4E00\u79CD\u8BED\u6CD5\u7CD6\uFF0C\u6700\u7EC8\u4F1A\u8F6C\u5316\u4E3A\u751F\u6210\u865A\u62DF DOM \u7684 JS \u4EE3\u7801\u3002\u4E4B\u6240\u4EE5\u6709\u8FD9\u79CD\u8BED\u6CD5\uFF0C\u6211\u4E2A\u4EBA\u7684\u7406\u89E3\u662F\u53EF\u4EE5\u8BA9\u4F60\u66F4\u81EA\u7531\uFF0C\u66F4\u5F00\u653E\uFF0C\u7528 JS \u7684\u601D\u7EF4\u6765\u5904\u7406\u754C\u9762\u4E0E\u6570\u636E\u4E4B\u95F4\u7684\u5173\u7CFB\uFF0C\u5C06\u754C\u9762\u62BD\u8C61\u51FA\u6765\uFF0C\u4E0D\u518D\u53D7\u90A3\u4E9B HTML \u7684\u675F\u7F1A\u3002</p><p>\u7136\u800C\uFF0C\u5728 JSX \u91CC\u4F7F\u7528 JS \u662F\u6709\u9650\u5236\u7684\uFF0C\u53EA\u80FD\u4F7F\u7528\u4E00\u4E9B\u8868\u8FBE\u5F0F\uFF0C\u4E0D\u80FD\u5B9A\u4E49\u53D8\u91CF\uFF0C\u4F7F\u7528 <code>if/else</code> \u7B49\uFF0C\u4F60\u53EF\u4EE5\u7528\u63D0\u524D\u5B9A\u4E49\u53D8\u91CF\uFF1B\u7528\u4E09\u5143\u8868\u8FBE\u5F0F\u6765\u8FBE\u5230\u540C\u6837\u7684\u6548\u679C\u3002</p><p>\u5217\u8868\u6E32\u67D3\uFF0C\u4E00\u822C\u662F\u7528\u6570\u7EC4\u7684 <code>map</code> \u51FD\u6570\u3002\u6B63\u5982\u4E0A\u9762\u7684\u4F8B\u5B50\uFF0C\u628A\u9700\u8981\u5217\u8868\u6E32\u67D3\u7684\u6570\u636E\u4F7F\u7528 <code>map</code> \u51FD\u6570\uFF0C\u8FD4\u56DE\u6240\u9700\u8981\u7684 JSX \u4EE3\u7801\u3002\u800C\u5728\u4E8B\u4EF6\u7ED1\u5B9A\u4E0A\uFF0C\u4F7F\u7528 on + \u4E8B\u4EF6\u540D\u79F0\uFF0C\u8FD9\u70B9\u548C\u6211\u4EEC\u7406\u89E3\u7684\u5E76\u65E0\u5927\u5F02\u3002</p><h2 id="react-\u7EC4\u4EF6" tabindex="-1">React \u7EC4\u4EF6 <a class="header-anchor" href="#react-\u7EC4\u4EF6" aria-hidden="true">#</a></h2><p>\u7EC4\u4EF6\u5316\uFF0C\u662F React \u7684\u53E6\u4E00\u5927\u7279\u70B9\u3002\u987E\u540D\u601D\u4E49\uFF0C\u7EC4\u4EF6\u5316\u5C31\u662F\u628A\u6574\u4E2A\u9875\u9762\u5206\u6210\u5927\u5927\u5C0F\u5C0F\u7684\u5404\u4E2A\u7EC4\u4EF6\uFF08component\uFF09\uFF0C\u7136\u540E\u50CF\u63D2\u5165\u666E\u901A HTML \u6807\u7B7E\u4E00\u6837\uFF0C\u5728\u9875\u9762\u4E2D\u63D2\u5165\u8FD9\u4E2A\u7EC4\u4EF6\u3002\u7EC4\u4EF6\u4E4B\u95F4\u53EF\u80FD\u4E92\u76F8\u5173\u8054\uFF0C\u4E5F\u53EF\u80FD\u4E92\u76F8\u72EC\u7ACB\uFF0C\u8FD9\u53D6\u51B3\u4E8E\u4E1A\u52A1\u903B\u8F91\u3002\u8FD9\u6837\u505A\u4E3B\u8981\u8FD8\u662F\u4E3A\u4E86\u65B9\u4FBF\u7BA1\u7406\uFF0C\u628A\u9875\u9762\u7684\u7EF4\u5EA6\u964D\u5230\u7EC4\u4EF6\u7684\u7EF4\u5EA6\uFF0C\u7EF4\u5EA6\u8D8A\u5C0F\uFF0C\u7BA1\u7406\u8D77\u6765\u5C31\u8D8A\u7CBE\u786E\uFF0C\u540C\u65F6\uFF0C\u7ECF\u8FC7\u62BD\u8C61\u4E4B\u540E\uFF0C\u76F8\u540C\u7684\u7EC4\u4EF6\u8FD8\u53EF\u4EE5\u590D\u7528\uFF0C\u63D0\u9AD8\u4E86\u5DE5\u4F5C\u6548\u7387\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">class Demo extends Component {</span></span>
<span class="line"><span style="color:#A6ACCD;">  // ...</span></span>
<span class="line"><span style="color:#A6ACCD;">  render () {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return &lt;View className=&#39;test&#39;&gt;{this.state.num}&lt;/View&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8FD9\u91CC\u9700\u8981\u6CE8\u610F\uFF0C\u7EC4\u4EF6\u7C7B\u7684\u7B2C\u4E00\u4E2A\u5B57\u6BCD\u5FC5\u987B\u5927\u5199\uFF0C\u5426\u5219\u4F1A\u62A5\u9519\uFF0C\u6BD4\u5982\u8FD9\u91CC\u7684 <code>Demo</code> \u4E0D\u80FD\u5199\u6210 <code>demo</code>\u3002\u53E6\u5916\uFF0C\u7EC4\u4EF6\u7C7B\u53EA\u80FD\u5305\u542B\u4E00\u4E2A\u9876\u5C42\u6807\u7B7E\uFF0C\u5426\u5219\u4E5F\u4F1A\u62A5\u9519\u3002</p><p>\u6BCF\u4E2A React \u7EC4\u4EF6\u90FD\u4F1A\u6709\u4E00\u4E2A <code>render</code> \u51FD\u6570\uFF0C\u7528\u4E8E\u8FD4\u56DE\u8BE5\u7EC4\u4EF6\u7684 JSX \u7247\u6BB5\uFF0C\u4EE3\u8868\u7740\u8BE5\u7EC4\u4EF6\u7684\u754C\u9762\u7ED3\u6784\u3002\u540C\u65F6\uFF0C\u4E5F\u53EF\u4EE5\u5728 <code>render</code> \u51FD\u6570\u91CC\u8FD4\u56DE\u53E6\u4E00\u4E2A\u7EC4\u4EF6\uFF0C\u5373\u7EC4\u4EF6\u4E4B\u95F4\u7684\u76F8\u4E92\u5D4C\u5957\uFF0C\u5982\u4E0B\u9762\u6240\u793A\uFF0C<code>Demo</code> \u7EC4\u4EF6\u7684 <code>render</code> \u51FD\u6570\u8FD4\u56DE\u4E86\u4E00\u4E2A <code>Nav</code> \u7EC4\u4EF6\u548C\u4E00\u4E9B JSX \u7247\u6BB5\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">import Nav from &#39;./nav.js&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">class Demo extends React.Component {</span></span>
<span class="line"><span style="color:#A6ACCD;">  // ...</span></span>
<span class="line"><span style="color:#A6ACCD;">  render () {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return (</span></span>
<span class="line"><span style="color:#A6ACCD;">      &lt;div className=&#39;test&#39;&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;Nav /&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;div className=&#39;bar&#39;&gt;{this.state.num}&lt;/div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">      &lt;/div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    )</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="props-\u4E0E-state" tabindex="-1">Props \u4E0E State <a class="header-anchor" href="#props-\u4E0E-state" aria-hidden="true">#</a></h2><p><code>props</code> \u4E0E <code>state</code> \u662F <code>React</code> \u7EC4\u4EF6\u4E2D\u6700\u4E3A\u91CD\u8981\u7684\u4E24\u4E2A\u72B6\u6001\u3002</p><h3 id="props" tabindex="-1">props <a class="header-anchor" href="#props" aria-hidden="true">#</a></h3><p>\u7236\u7EC4\u4EF6\u4F20\u7ED9\u5B50\u7EC4\u4EF6\u7684\u6570\u636E\uFF0C\u4F1A\u6302\u8F7D\u5728\u5B50\u7EC4\u4EF6\u7684 <code>this.props</code> \u4E0A\uFF0C\u5982\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u5B50\u7EC4\u4EF6</span></span>
<span class="line"><span style="color:#A6ACCD;">class Welcome extends React.Component {</span></span>
<span class="line"><span style="color:#A6ACCD;">  render() {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return &lt;h1&gt;Hello, {this.props.name}&lt;/h1&gt;;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u7236\u7EC4\u4EF6</span></span>
<span class="line"><span style="color:#A6ACCD;">class Demo extends React.Component {</span></span>
<span class="line"><span style="color:#A6ACCD;">  render () {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return &lt;Welcome name=&#39;aotu,taro!&#39; /&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u6700\u7EC8\u9875\u9762\u4E0A\u4F1A\u6E32\u67D3\u51FA &lt;h1&gt;Hello, aotu,taro!&lt;/h1&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u53EF\u4EE5\u89C1\u5230\uFF0C<code>Demo</code> \u7EC4\u4EF6\u5728\u8C03\u7528 <code>Welcome</code> \u7EC4\u4EF6\u65F6\uFF0C\u4F20\u5165\u4E86 <code>name=&#39;aotu,taro!&#39;</code> \u7684 <code>props</code>\uFF0C\u6240\u4EE5\u5B50\u7EC4\u4EF6\u91CC\u5C31\u80FD\u8BBF\u95EE\u5230 <code>this.props.name</code> \u5C5E\u6027\u3002\u6BD4\u8F83\u91CD\u8981\u7684\u4E00\u70B9\u662F\uFF0C\u7EC4\u4EF6\u4E2D\u5982\u679C\u6536\u5230\u4E86\u65B0\u7684 <code>props</code>\uFF0C\u5C31\u4F1A<strong>\u91CD\u65B0\u6267\u884C\u4E00\u6B21 <code>render</code> \u51FD\u6570\uFF0C\u4E5F\u5C31\u662F\u91CD\u65B0\u6E32\u67D3\u4E00\u904D</strong>\u3002</p><p>\u4E0A\u8FF0\u53EA\u662F\u5173\u4E8E <code>props</code> \u7684\u6700\u57FA\u7840\u7684\u7528\u6CD5\uFF0C\u9664\u6B64\u4E4B\u5916\uFF0C\u8FD8\u6709 <code>this.props.children</code>\u3001\u9AD8\u9636\u7EC4\u4EF6\u7B49\u6BD4\u8F83\u9AD8\u7EA7\u7684\u7528\u6CD5\uFF0C\u5728\u8FD9\u91CC\u5C31\u4E0D\u4E00\u4E00\u53D9\u8FF0\u4E86\u3002</p><h3 id="state" tabindex="-1">state <a class="header-anchor" href="#state" aria-hidden="true">#</a></h3><p><code>state</code> \u4E0E <code>props</code> \u4E0D\u540C\uFF0C\u662F\u5C5E\u4E8E\u7EC4\u4EF6\u81EA\u5DF1\u5185\u90E8\u7684\u6570\u636E\u72B6\u6001\uFF0C\u4E00\u822C\u5728 <code>constructor</code> \u6784\u9020\u51FD\u6570\u91CC\u521D\u59CB\u5316\u5B9A\u4E49 <code>state</code>\uFF0C\u4F8B\u5982\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">class Welcome extends React.Component {</span></span>
<span class="line"><span style="color:#A6ACCD;">  constructor(props) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    super(props);</span></span>
<span class="line"><span style="color:#A6ACCD;">    this.state = {name: &#39;aotu,taro!&#39;};</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">  render() {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return &lt;h1&gt;Hello, {this.state.name}&lt;/h1&gt;;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u53EF\u4EE5\u89C1\u5230\uFF0C\u5728\u6784\u9020\u51FD\u6570\u91CC\u521D\u59CB\u5316\u4E00\u4E2A <code>state</code> \u5BF9\u8C61\uFF0C\u7136\u540E\u5728 <code>render</code> \u51FD\u6570\u91CC\u6E32\u67D3\u51FA\u6765\u3002</p><p>\u503C\u5F97\u6CE8\u610F\u7684\u662F\uFF0C\u5F53 <code>state</code> \u9700\u8981\u53D8\u5316\u65F6\uFF0C\u662F\u4E0D\u5141\u8BB8\u968F\u4FBF\u66F4\u6539\u7684\uFF0C\u9700\u8981\u8C03\u7528 <code>this.setState</code> \u6765\u8FDB\u884C\u66F4\u6539\uFF0C\u5426\u5219\u89C6\u56FE\u6CA1\u6CD5\u8FDB\u884C\u66F4\u65B0\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u9519\u8BEF</span></span>
<span class="line"><span style="color:#A6ACCD;">this.state.name = &#39;hey!hey!hey!&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// \u6B63\u786E</span></span>
<span class="line"><span style="color:#A6ACCD;">this.setState({name: &#39;hey!hey!hey!&#39;})</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8FD9\u4E0E Vue \u7B49\u4E00\u4E9B\u53EF\u4EE5\u5728\u6570\u636E\u53D8\u66F4\u65F6\u81EA\u52A8\u54CD\u5E94\u89C6\u56FE\u53D8\u5316\u7684\u6846\u67B6\u4E0D\u540C\uFF0CReact \u7684\u6570\u636E\u53CA\u89C6\u56FE\u53D8\u5316\u662F\u9700\u8981\u4E3B\u52A8\u8C03\u7528 <code>setState</code> \u51FD\u6570\u6765\u89E6\u53D1\u7684\uFF0C\u5177\u4F53\u539F\u56E0\u4E0E\u6846\u67B6\u5B9E\u73B0\u7684\u539F\u7406\u6709\u5173\uFF0C\u8FD9\u91CC\u5C31\u4E0D\u7EC6\u8BF4\u4E86\u3002\u5F53\u8C03\u7528 <code>setState</code> \u51FD\u6570\u65F6\uFF0C\u6700\u7EC8\u7EC4\u4EF6\u4F1A\u6267\u884C <code>render</code> \u51FD\u6570\uFF0C\u91CD\u65B0\u518D\u6E32\u67D3\u4E00\u904D\u3002</p><p>\u6240\u4EE5\u4E00\u822C\u6765\u8BF4\uFF0C\u6BD4\u8F83\u597D\u7684\u505A\u6CD5\u662F<strong>\u53EA\u628A\u8DDF\u7EC4\u4EF6\u5185\u90E8\u89C6\u56FE\u6709\u5173\u8054\u7684\u6570\u636E\uFF0C\u53D8\u91CF\u653E\u5728 <code>state</code> \u91CC\u9762</strong>\uFF0C\u4EE5\u6B64\u907F\u514D\u4E0D\u5FC5\u8981\u7684\u6E32\u67D3\u3002</p><h2 id="\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F" tabindex="-1">\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F <a class="header-anchor" href="#\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F" aria-hidden="true">#</a></h2><p>\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F\uFF0C\u6307\u7684\u662F\u4E00\u4E2A <code>React</code> \u7EC4\u4EF6\u4ECE\u6302\u8F7D\uFF0C\u66F4\u65B0\uFF0C\u9500\u6BC1\u8FC7\u7A0B\u4E2D\u4F1A\u6267\u884C\u7684\u751F\u547D\u94A9\u5B50\u51FD\u6570\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">class Clock extends React.Component {</span></span>
<span class="line"><span style="color:#A6ACCD;">  constructor(props) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    super(props);</span></span>
<span class="line"><span style="color:#A6ACCD;">    this.state = {date: new Date()};</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  componentWillMount() {}</span></span>
<span class="line"><span style="color:#A6ACCD;"> </span></span>
<span class="line"><span style="color:#A6ACCD;">  componentDidMount() {}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  componentWillUpdate(nextProps, nextState) {}</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span></span>
<span class="line"><span style="color:#A6ACCD;">  componentWillReceiveProps(nextProps) {}  </span></span>
<span class="line"><span style="color:#A6ACCD;">  </span></span>
<span class="line"><span style="color:#A6ACCD;">  componentDidUpdate(prevProps, prevState) {}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  shouldComponentUpdate(nextProps, nextState) {}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  componentWillUnmount() {}</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span></span>
<span class="line"><span style="color:#A6ACCD;">  render() {</span></span>
<span class="line"><span style="color:#A6ACCD;">    return (</span></span>
<span class="line"><span style="color:#A6ACCD;">      &lt;div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;h1&gt;Hello, world!&lt;/h1&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">        &lt;h2&gt;It is {this.state.date.toLocaleTimeString()}.&lt;/h2&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">      &lt;/div&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    );</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4E0A\u8FF0\u7EC4\u4EF6\u4E2D\u57FA\u672C\u628A\u6240\u6709\u751F\u547D\u5468\u671F\u90FD\u5217\u51FA\u6765\u4E86\uFF0C\u4E0B\u9762\u7B80\u5355\u4E00\u4E00\u53D9\u8FF0\u4E0B</p><ul><li><p><strong>constructor</strong>\uFF0C\u987E\u540D\u601D\u4E49\uFF0C\u7EC4\u4EF6\u7684\u6784\u9020\u51FD\u6570\u3002\u4E00\u822C\u4F1A\u5728\u8FD9\u91CC\u8FDB\u884C <code>state</code> \u7684\u521D\u59CB\u5316\uFF0C\u4E8B\u4EF6\u7684\u7ED1\u5B9A\u7B49\u7B49</p></li><li><p><strong>componentWillMount</strong>\uFF0C\u662F\u5F53\u7EC4\u4EF6\u5728\u8FDB\u884C\u6302\u8F7D\u64CD\u4F5C\u524D\uFF0C\u6267\u884C\u7684\u51FD\u6570\uFF0C\u4E00\u822C\u7D27\u8DDF\u7740 <code>constructor</code> \u51FD\u6570\u540E\u6267\u884C</p></li><li><p><strong>componentDidMount</strong>\uFF0C\u662F\u5F53\u7EC4\u4EF6\u6302\u8F7D\u5728 dom \u8282\u70B9\u540E\u6267\u884C\u3002\u4E00\u822C\u4F1A\u5728\u8FD9\u91CC\u6267\u884C\u4E00\u4E9B\u5F02\u6B65\u6570\u636E\u7684\u62C9\u53D6\u7B49\u52A8\u4F5C</p></li><li><p><strong>shouldComponentUpdate</strong>\uFF0C\u8FD4\u56DE <code>false</code> \u65F6\uFF0C\u7EC4\u4EF6\u5C06\u4E0D\u4F1A\u8FDB\u884C\u66F4\u65B0\uFF0C\u53EF\u7528\u4E8E\u6E32\u67D3\u4F18\u5316</p></li><li><p><strong>componentWillReceiveProps</strong>\uFF0C\u5F53\u7EC4\u4EF6\u6536\u5230\u65B0\u7684 <code>props</code> \u65F6\u4F1A\u6267\u884C\u7684\u51FD\u6570\uFF0C\u4F20\u5165\u7684\u53C2\u6570\u5C31\u662F <code>nextProps</code> \uFF0C\u4F60\u53EF\u4EE5\u5728\u8FD9\u91CC\u6839\u636E\u65B0\u7684 <code>props</code> \u6765\u6267\u884C\u4E00\u4E9B\u76F8\u5173\u7684\u64CD\u4F5C\uFF0C\u4F8B\u5982\u67D0\u4E9B\u529F\u80FD\u521D\u59CB\u5316\u7B49</p></li><li><p><strong>componentWillUpdate</strong>\uFF0C\u5F53\u7EC4\u4EF6\u5728\u8FDB\u884C\u66F4\u65B0\u4E4B\u524D\uFF0C\u4F1A\u6267\u884C\u7684\u51FD\u6570</p></li><li><p><strong>componentDidUpdate</strong>\uFF0C\u5F53\u7EC4\u4EF6\u5B8C\u6210\u66F4\u65B0\u65F6\uFF0C\u4F1A\u6267\u884C\u7684\u51FD\u6570\uFF0C\u4F20\u5165\u4E24\u4E2A\u53C2\u6570\u662F <code>prevProps</code> \u3001<code>prevState</code></p></li><li><p><strong>componentWillUnmount</strong>\uFF0C\u5F53\u7EC4\u4EF6\u51C6\u5907\u9500\u6BC1\u65F6\u6267\u884C\u3002\u5728\u8FD9\u91CC\u4E00\u822C\u53EF\u4EE5\u6267\u884C\u4E00\u4E9B\u56DE\u6536\u7684\u5DE5\u4F5C\uFF0C\u4F8B\u5982 <code>clearInterval(this.timer)</code> \u8FD9\u79CD\u5BF9\u5B9A\u65F6\u5668\u7684\u56DE\u6536\u64CD\u4F5C</p></li></ul><h2 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3 <a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a></h2><p>\u672C\u6587\u4ECE <strong>JSX \u8BED\u6CD5</strong>\u3001<strong>React \u7EC4\u4EF6</strong>\u3001<strong>Props \u4E0E State</strong>\u3001<strong>\u751F\u547D\u5468\u671F</strong>\u8FD9\u56DB\u4E2A\u65B9\u9762\u7B80\u5355\u4ECB\u7ECD\u4E86 <a href="https://reactjs.org/" target="_blank" rel="noreferrer">React</a> \u8BED\u6CD5\u4F53\u7CFB\u7684\u6838\u5FC3\u90E8\u5206\uFF0C\u6574\u4F53\u800C\u8A00\u90FD\u662F\u4E00\u4E9B\u6BD4\u8F83\u57FA\u7840\u7684\u90E8\u5206\u3002\u800C\u66F4\u8BE6\u7EC6\uFF0C\u66F4\u591A\u5177\u4F53\u7684\u5F00\u53D1\u6280\u5DE7\uFF0C\u968F\u7740\u5C0F\u518C\u7684\u6DF1\u5165\u6211\u4EEC\u5728\u540E\u7EED\u6D89\u53CA\u5230\u7684\u7AE0\u8282\u4F1A\u7EE7\u7EED\u4ECB\u7ECD\u3002</p><p>\u9700\u8981\u8BF4\u660E\u7684\u662F\uFF0C\u7531\u4E8E\u5FAE\u4FE1\u5C0F\u7A0B\u5E8F\u7684\u9650\u5236\uFF0CReact \u4E2D\u67D0\u4E9B\u5199\u6CD5\u548C\u7279\u6027\u5728 Taro \u4E2D\u8FD8\u672A\u80FD\u5B9E\u73B0\uFF0C\u540E\u7EED\u5C06\u4F1A\u9010\u6E10\u5B8C\u5584\u3002\u5DEE\u5F02\u7684\u90E8\u5206\u53EF\u4EE5\u5728<a href="https://juejin.cn/book/6844733744830480397/section/6844733744922755085" target="_blank" rel="noreferrer">\u300A\u57FA\u7840\u7BC7 4\uFF1ATaro \u5F00\u53D1\u8BF4\u660E\u4E0E\u6CE8\u610F\u4E8B\u9879\u300B</a>\u7AE0\u8282\u91CC\u67E5\u770B\u3002</p><h2 id="\u53C2\u8003\u8D44\u6599" tabindex="-1">\u53C2\u8003\u8D44\u6599 <a class="header-anchor" href="#\u53C2\u8003\u8D44\u6599" aria-hidden="true">#</a></h2><ul><li><a href="https://reactjs.org/" target="_blank" rel="noreferrer">Reactjs Docs</a></li></ul>`,47),t=[p];function o(c,r,i,d,C,A){return a(),n("div",null,t)}const y=s(l,[["render",o]]);export{D as __pageData,y as default};
