import{_ as e,c,o as t,d as o}from"./app.c4f45da1.js";const g=JSON.parse('{"title":"\u7ED3\u675F\u8BED-\u672A\u6765\u5C55\u671B","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u6846\u67B6","slug":"\u6846\u67B6","link":"#\u6846\u67B6","children":[]},{"level":3,"title":"","slug":"","link":"#","children":[]},{"level":3,"title":"","slug":"-1","link":"#-1","children":[]},{"level":3,"title":"\u6784\u5EFA\u5DE5\u5177","slug":"\u6784\u5EFA\u5DE5\u5177","link":"#\u6784\u5EFA\u5DE5\u5177","children":[]},{"level":3,"title":"\u79FB\u52A8\u7AEF\u548C\u684C\u9762\u7AEF","slug":"\u79FB\u52A8\u7AEF\u548C\u684C\u9762\u7AEF","link":"#\u79FB\u52A8\u7AEF\u548C\u684C\u9762\u7AEF","children":[]},{"level":3,"title":"\u5C55\u671B 2022 \u6280\u672F\u8D8B\u52BF","slug":"\u5C55\u671B-2022-\u6280\u672F\u8D8B\u52BF","link":"#\u5C55\u671B-2022-\u6280\u672F\u8D8B\u52BF","children":[]}],"relativePath":"\u5DE5\u7A0B\u5316/\u521D\u63A2\u524D\u7AEF\u5DE5\u7A0B\u5316/\u7ED3\u675F\u8BED-\u672A\u6765\u5C55\u671B.md"}'),a={name:"\u5DE5\u7A0B\u5316/\u521D\u63A2\u524D\u7AEF\u5DE5\u7A0B\u5316/\u7ED3\u675F\u8BED-\u672A\u6765\u5C55\u671B.md"},r=o('<h1 id="\u7ED3\u675F\u8BED-\u672A\u6765\u5C55\u671B" tabindex="-1">\u7ED3\u675F\u8BED-\u672A\u6765\u5C55\u671B <a class="header-anchor" href="#\u7ED3\u675F\u8BED-\u672A\u6765\u5C55\u671B" aria-hidden="true">#</a></h1><hr><p>\u611F\u8C22\u5927\u5BB6\u53EF\u4EE5\u9605\u8BFB\u5230\u8FD9\u91CC\uFF0C\u611F\u8C22\u6BCF\u4E00\u4F4D\u82B1\u65F6\u95F4\u9605\u8BFB\u672C\u5C0F\u518C\u7684\u4F60\uFF0C\u611F\u8C22\u4F60\u4EEC\u7684\u966A\u4F34\uFF0C\u97A0\u8EAC.png\u3002</p><p>\u5C0F\u518C\u5230\u8FD9\u91CC\u5C31\u63A5\u8FD1\u5C3E\u58F0\u4E86\uFF0C\u6211\u4EEC\u5148\u6765\u7528\u4E00\u4E2A\u8111\u56FE\u6765\u56DE\u987E\u4E0B\u672C\u5C0F\u518C\u7684\u5185\u5BB9\uFF1A</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/24b9d619b6d442cda8787f66eb14259d~tplv-k3u1fbpfcp-zoom-1.image" alt=""></p><p>\u5728\u5C0F\u518C\u7684\u7ED3\u5C3E\uFF0C\u6211\u4EEC\u4E5F\u4E00\u8D77\u56DE\u987E\u4E00\u4E0B 2021 \u5E74\u524D\u7AEF\u5DE5\u7A0B\u5316\u53D1\u5C55\u7684\u73B0\u72B6\u548C\u5C55\u671B\u4E00\u4E0B 2022 \u7684\u53D1\u5C55\u8D8B\u52BF\u3002</p><h3 id="\u6846\u67B6" tabindex="-1">\u6846\u67B6 <a class="header-anchor" href="#\u6846\u67B6" aria-hidden="true">#</a></h3><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a891280f1ad145b8ac238c691bbca646~tplv-k3u1fbpfcp-zoom-1.image" alt=""></p><p>\u5728\u6846\u67B6\u53D1\u5C55\u65B9\u9762\uFF0C\u5728 <a href="https://risingstars.js.org/2021/zh#section-all" target="_blank" rel="noreferrer">2021 \u7684 Best of JS \u6570\u636E\u663E\u793A</a>\uFF0CReact\u3001Vue \u4ECD\u7136\u4F4D\u5217\u7B2C\u4E00\u548C\u7B2C\u4E8C\uFF0C\u51FA\u4EBA\u610F\u6599\u7684\u662F <code>Svelte</code> \u4E00\u4E3E\u8D85\u8FC7 <code>Angular</code> \u5360\u636E\u4E86\u6392\u540D\u7B2C\u4E09\u7684\u4F4D\u7F6E\u3002</p><p>\u6211\u4EEC\u6765\u56DE\u987E\u4E0B 2021 \u5E74\u8FD9\u4E09\u5927\u6846\u67B6\u6709\u54EA\u4E9B\u91CD\u5927\u4E8B\u4EF6\uFF1A</p><h4 id="react-18" tabindex="-1">React 18 <a class="header-anchor" href="#react-18" aria-hidden="true">#</a></h4><p>React \u5728 2021 \u5E74\u4E0B\u534A\u5E74\u5B8C\u6210\u4E86 18 \u7248\u672C\u7684 <code>Alpha</code>\u3001<code>Beta</code> \u7248\u672C\u7684\u53D1\u5E03\u3002\u5176\u4E2D\uFF0C\u4E3B\u8981\u6709\u4E09\u5927\u65B0\u7279\u6027\uFF1A\u5F00\u7BB1\u5373\u7528\u7684\u6539\u8FDB\uFF08\u5982 <a href="https://github.com/reactwg/react-18/discussions/21" target="_blank" rel="noreferrer">automatic batching</a> )\uFF0C<strong>Concurrent</strong> <strong>API</strong> \u4EE5\u53CA\u5185\u7F6E\u652F\u6301\u4E86 <code>React.lazy</code> \u7684 <a href="https://github.com/reactwg/react-18/discussions/37" target="_blank" rel="noreferrer">\u5168\u65B0 SSR \u67B6\u6784</a>\u3002</p><p><a href="https://github.com/reactwg/react-18/discussions/21" target="_blank" rel="noreferrer"><strong>Automatic batching</strong></a> <strong>(\u6279\u5904\u7406\uFF09</strong></p><p>React 18 \u901A\u8FC7\u9ED8\u8BA4\u6267\u884C\u66F4\u591A\u7684 <code>batching</code> \u6765\u6539\u5584\u6027\u80FD\u95EE\u9898\uFF0C<code>batching</code> \u6307\u7684\u662F\u4F1A\u5C06\u56DE\u8C03\u51FD\u6570\u4E2D\u591A\u4E2A <code>setState</code> \u4E8B\u4EF6\u5408\u5E76\u4E3A\u4E00\u6B21\u6E32\u67D3\uFF0C\u5728\u4F7F\u7528\u7684\u65F6\u5019\uFF0C\u53EA\u9700\u8981\u5C06 <code>ReactDom.render</code> \u66FF\u6362\u4E3A <code>ReactDom.createRoot</code> \u5C31\u53EF\u4EE5\u4F7F\u7528\u8BE5\u7279\u6027\u3002</p><p><strong>Concurrent API</strong> :</p><blockquote><p>\u7B80\u5355\u6765\u8BF4\uFF0C<code>Concurrent Mode</code> \u5C31\u662F\u4E00\u79CD\u53EF\u4E2D\u65AD\u6E32\u67D3\u7684\u8BBE\u8BA1\u67B6\u6784\u3002\u4EC0\u4E48\u65F6\u5019\u4E2D\u65AD\u6E32\u67D3\u5462\uFF1F\u5F53\u4E00\u4E2A\u66F4\u9AD8\u4F18\u5148\u7EA7\u6E32\u67D3\u5230\u6765\u65F6\uFF0C\u901A\u8FC7\u653E\u5F03\u5F53\u524D\u7684\u6E32\u67D3\uFF0C\u7ACB\u5373\u6267\u884C\u66F4\u9AD8\u4F18\u5148\u7EA7\u7684\u6E32\u67D3\uFF0C\u6362\u6765\u89C6\u89C9\u4E0A\u66F4\u5FEB\u7684\u54CD\u5E94\u901F\u5EA6\u3002</p></blockquote><p>\u5176\u4E2D\u4E3B\u8981\u7684\u51E0\u4E2A API \u5982\u4E0B</p><ul><li><a href="https://github.com/reactwg/react-18/discussions/41" target="_blank" rel="noreferrer">startTransition</a>\uFF1A\u88AB <code>startTransiton</code> \u5305\u88F9\u7684 <code>setState</code> \u89E6\u53D1\u7684\u6E32\u67D3\u88AB\u6807\u8BB0\u4E3A\u4E0D\u7D27\u6025\u6E32\u67D3\uFF0C\u53EF\u80FD\u4F1A\u88AB\u4F18\u5148\u7EA7\u66F4\u9AD8\u7684\u6E32\u67D3\u62A2\u5360\u3002</li><li><code>useTranstion</code>\uFF1A\u5141\u8BB8\u7EC4\u4EF6\u5728\u5207\u6362\u5230\u4E0B\u4E00\u4E2A\u754C\u9762\u4E4B\u524D\u7B49\u5230\u5185\u5BB9\u52A0\u8F7D\uFF0C\u4ECE\u800C\u907F\u514D\u4E0D\u5FC5\u8981\u7684\u52A0\u8F7D\u8FC7\u7A0B\u3002</li><li><code>useDeferredValue</code>\uFF1A\u8BA9\u4F60\u53EF\u4EE5\u5EF6\u8FDF\u6E32\u67D3\u9875\u9762\u4E2D\u4E0D\u91CD\u8981\u7684\u90E8\u5206</li></ul><p><strong>Concurrent \u7279\u6027\u662F\u53EF\u9009\u7684\uFF0C\u53EF\u4EE5\u9010\u6B65\u4F7F\u7528\u3002</strong> \u4F60\u4E5F\u53EF\u4EE5\u5728\u5E94\u7528\u7684\u4E00\u5C0F\u90E8\u5206\u5F00\u59CB\u4F7F\u7528\u8FD9\u4E9B\u529F\u80FD\uFF0C\u800C\u4E0D\u9700\u8981\u5728\u6574\u4E2A\u5E94\u7528\u7A0B\u5E8F\u4E2D\u90FD\u4F7F\u7528\u3002</p><p><strong>\u65B0\u7684 Suspense SSR \u67B6\u6784</strong> <code>React 18</code> \u63D0\u4F9B\u4E86<a href="https://github.com/reactwg/react-18/discussions/37" target="_blank" rel="noreferrer"><strong>\u5168\u65B0 SSR \u67B6\u6784</strong></a> <strong>\uFF0C</strong> \u5728\u4F20\u7EDF\u7684 <code>SSR</code> \u67B6\u6784\u4E2D\uFF0C\u6BCF\u4E00\u6B65\u90FD\u4F9D\u8D56\u4E0A\u4E00\u6B65\u7684\u7ED3\u679C\uFF0C\u5176\u4E2D\u67D0\u4E00\u6B65\u51FA\u73B0\u95EE\u9898\u5C31\u4F1A\u5F71\u54CD\u6027\u80FD\u95EE\u9898\u3002<code>React 18</code> \u4F7F\u7528 <code>Suspense</code> \u53EF\u4EE5\u4ECE\u4EE5\u4E0B\u4E24\u65B9\u9762\u89E3\u51B3\uFF1A</p><ul><li>\u6D41\u5F0F\u6E32\u67D3\uFF1A\u4F7F\u7528\u5168\u65B0\u7684 <code>pipeToNodeWritable</code> API \u642D\u5EFA\u4ECE <code>server</code> \u7AEF\u5230 <code>client</code> \u7684\u6D41\u5F0F\u6E32\u67D3\u901A\u9053\uFF0C\u80FD\u591F\u5C3D\u65E9\u7684\u53D1\u9001 HTML\uFF0C\u800C\u4E0D\u662F\u7B49\u5230\u6574\u4F53\u90FD Ready \u4E4B\u540E\u518D\u4F7F\u7528 <code>renderToString</code> \u4E00\u6B21\u6027\u8F93\u51FA\u3002</li><li>\u9009\u62E9\u6027 <code>hydration</code>\uFF1A\u53EF\u4EE5\u5728 HTML \u548C JS \u5B8C\u5168\u4E0B\u8F7D\u4E4B\u524D\uFF0C\u5C3D\u65E9\u5F00\u59CB\u8FDB\u884C <code>hydration</code>\u3002\u80FD\u591F\u52A0\u5FEB\u5E94\u7528\u52A0\u8F7D\u5E76\u7F29\u77ED\u4EA4\u4E92\u65F6\u95F4\u3002</li></ul><p>\u8FD9\u4E9B\u529F\u80FD\u4E4B\u6240\u4EE5\u80FD\u591F\u5B9E\u73B0\uFF0C\u8981\u5F52\u529F\u4E8E <code>React 18</code> \u65B0\u52A0\u5165\u7684\u53EF\u9009\u7684 \u201C\u5E76\u53D1\u6E32\u67D3\uFF08<code>concurrent rendering</code>\uFF09\u201D \u673A\u5236\u3002\u8FD9\u4F7F\u5F97 React \u53EF\u4EE5\u540C\u65F6\u51C6\u5907\u591A\u4E2A\u7248\u672C\u7684 UI\u3002\u8FD9\u4E2A\u673A\u5236\u4E3B\u8981\u53D1\u751F\u5728\u5E55\u540E\uFF0C\u4F46\u5B83\u4E3A React \u89E3\u9501\u4E86\u975E\u5E38\u591A\u65B0\u7684\u53EF\u80FD\u6027\uFF0C\u6765\u5E2E\u52A9\u4F60\u63D0\u9AD8\u4F60\u5E94\u7528\u7A0B\u5E8F\u7684\u5B9E\u9645\u4E0E\u611F\u77E5\u6027\u80FD\u3002</p><p>React 18 \u91C7\u7528\u6E10\u8FDB\u5F0F\u66F4\u65B0\uFF0C\u5176\u4E2D\u7684\u5E76\u53D1\u6027\u662F\u53EF\u9009\u529F\u80FD\uFF0C\u6240\u4EE5\u5E76\u4E0D\u4F1A\u7ACB\u523B\u5BF9\u7EC4\u4EF6\u884C\u4E3A\u5E26\u6765\u4EFB\u4F55\u660E\u663E\u7684\u7834\u574F\u6027\u53D8\u5316\u3002<strong>\u4F60\u51E0\u4E4E\u4E0D\u9700\u8981\u5BF9\u5E94\u7528\u7A0B\u5E8F\u4E2D\u7684\u4EE3\u7801\u8FDB\u884C\u4EFB\u4F55\u6539\u52A8\u5C31\u53EF\u4EE5\u76F4\u63A5\u5347\u7EA7\u5230 React 18\uFF0C\u5E76\u4E0D\u4F1A\u6BD4\u4EE5\u5F80\u7684 React \u7248\u672C\u5347\u7EA7\u8981\u56F0\u96BE\u3002</strong></p><h4 id="vue-3" tabindex="-1">Vue 3 <a class="header-anchor" href="#vue-3" aria-hidden="true">#</a></h4><p>Vue \u56E2\u961F\u5B98\u65B9\u5728 2022 \u5E74 1 \u6708 20 \u65E5\u5BA3\u5E03\uFF1A\u201C<a href="https://blog.vuejs.org/posts/vue-3-as-the-new-default.html" target="_blank" rel="noreferrer">Vue 3 \u5728 2 \u6708 7 \u65E5\u4F5C\u4E3A\u65B0\u7684\u9ED8\u8BA4\u7248\u672C</a>\u201D\uFF0C\u8FD9\u6837\u6807\u5FD7\u7740 Vue3 \u5DF2\u7ECF\u8FDB\u5165\u7A33\u5B9A\u7248\u672C\u3002</p><p><img src="https://p9-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7376740e7cf34e75932cbc3949af568c~tplv-k3u1fbpfcp-watermark.image?" alt="image.png"> <code>Vue 3</code> \u7684\u65B0\u7279\u6027\u60F3\u5FC5\u5927\u5BB6\u90FD\u5DF2\u7ECF\u4E86\u89E3\u7684\u5DEE\u4E0D\u591A\u4E86\u3002\u8FD9\u91CC\u7B80\u5355\u8BF4\u4E0B\uFF1A</p><p>\u5728\u6027\u80FD\u4E0A\uFF0C<code>Vue3</code> \u6574\u4F53\u6BD4 <code>Vue2</code> \u5FEB\u4E86 2\u500D\uFF0C\u4E3B\u8981\u662F\u56E0\u4E3A\u91CD\u5199\u4E86 <code>virtual DOM</code>\uFF0C<code>SSR</code> \u63D0\u901F\u3001<code>update</code> \u6027\u80FD\u7684\u63D0\u9AD8\u548C\u8FD0\u884C\u65F6\u7F16\u8BD1\u3002</p><p>Vue3 \u6838\u5FC3\u7684 API \u90FD\u652F\u6301\u4E86 <code>Tree-shaking</code>\uFF0C\u8FD9\u4E9B API \u901A\u8FC7\u5305\u5F15\u5165\u7684\u65B9\u5F0F\u800C\u4E0D\u662F\u76F4\u63A5\u5728\u5B9E\u4F8B\u5316\u65F6\u6CE8\u5165\uFF0C\u53EA\u4F1A\u5BF9\u4F7F\u7528\u5230\u7684\u529F\u80FD\u6216\u7279\u6027\u8FDB\u884C\u6253\u5305\uFF08\u6309\u9700\u6253\u5305\uFF09\uFF0C\u8FD9\u610F\u5473\u7740\u66F4\u591A\u7684\u529F\u80FD\u548C\u66F4\u5C0F\u7684\u4F53\u79EF\u3002</p><p>Vue3 \u4E00\u5927\u4EAE\u70B9\u5C31\u662F <code>Composition API</code>\uFF0C\u4F7F\u7528\u7EAF\u51FD\u6570\u5206\u9694\u590D\u7528\u4EE3\u7801\uFF0C\u548C <code>React</code> \u4E2D\u7684 <code>hooks</code> \u7684\u6982\u5FF5\u5F88\u76F8\u4F3C\uFF0C\u80FD\u591F\u66F4\u597D\u7684\u590D\u7528\u548C\u7EC4\u7EC7\u4EE3\u7801\uFF0C\u8FD8\u6709\u66F4\u597D\u7684\u7C7B\u578B\u63A8\u5BFC\u3002</p><p>\u65B0\u589E <code>Fragment</code>\u3001<code>Teleport</code>\u3001<code>Suspense</code> \u4E09\u4E2A\u7EC4\u4EF6\u3002<code>Fragment</code> \u53EF\u4EE5\u521B\u5EFA\u65E0\u610F\u4E49\u7684\u5305\u88F9\u7EC4\u4EF6\u3001<code>Teleport</code> \u53EF\u4EE5\u5C06\u5B50\u8282\u70B9\u6E32\u67D3\u5230\u5B58\u5728\u4E8E\u7236\u7EC4\u4EF6\u4EE5\u5916\u7684 DOM \u8282\u70B9\uFF0C<code>Suspense</code> \u8BA9\u4F60\u7684\u7EC4\u4EF6\u5728\u6E32\u67D3\u4E4B\u524D\u8FDB\u884C\u201C\u7B49\u5F85\u201D\uFF0C\u5E76\u5728\u7B49\u5F85\u65F6\u663E\u793A <code>fallback</code> \u7684\u5185\u5BB9\u3002</p><p>\u540C\u65F6 Vue \u56E2\u961F\u4E5F\u5728\u5176\u4ED6\u524D\u7AEF\u5F00\u53D1\u8BBE\u65BD\u4E2D\u6709\u6240\u521B\u65B0\u7684\u7A81\u7834\uFF0C\u6BD4\u5982\u5927\u706B\u7684 Vite \u4EE5\u53CA Vitest \u6D4B\u8BD5\u5DE5\u5177\uFF0C\u65E5\u540E\u8FD8\u6709\u4EC0\u4E48\u65B0\u4EA7\u54C1\u6211\u4EEC\u4E5F\u53EF\u4EE5\u6709\u6240\u671F\u5F85\u3002</p><h4 id="svelte" tabindex="-1">Svelte <a class="header-anchor" href="#svelte" aria-hidden="true">#</a></h4><p>\u6211\u4EEC\u5728 <a href="https://2021.stateofjs.com/zh-Hans/libraries/front-end-frameworks" target="_blank" rel="noreferrer">2021 state of JS</a> \u4E2D\uFF0C\u53EF\u4EE5\u770B\u5230\u4ECE\u6EE1\u610F\u5EA6\u3001\u5173\u6CE8\u5EA6\u3001\u4F7F\u7528\u5EA6\u548C\u8BA4\u77E5\u5EA6 4 \u4E2A\u5C42\u9762\u4E0A\u90FD\u53EF\u4EE5\u770B\u5230 Svelte \u5448\u73B0\u9010\u6B65\u4E0A\u5347\u7684\u8D8B\u52BF\u3002</p><h3 id="" tabindex="-1"><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/18bdd91b90174dc6b611a06e33ee4358~tplv-k3u1fbpfcp-zoom-1.image" alt=""> <a class="header-anchor" href="#" aria-hidden="true">#</a></h3><h3 id="-1" tabindex="-1"><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/b1b09d66770d4af487c3f8651814e8c6~tplv-k3u1fbpfcp-zoom-1.image" alt=""> <a class="header-anchor" href="#-1" aria-hidden="true">#</a></h3><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/b6c25fee9eee4e2583ecad43d9c16064~tplv-k3u1fbpfcp-zoom-1.image" alt=""></p><p><code>Svelte</code> \u7684\u6838\u5FC3\u601D\u60F3\u662F\u901A\u8FC7\u9759\u6001\u7F16\u8BD1\u51CF\u5C11\u6846\u67B6\u8FD0\u884C\u65F6\u7684\u4EE3\u7801\u91CF\u3002\u5F00\u53D1\u8005\u7684\u559C\u7231\u4E3B\u8981\u6E90\u4E8E <code>Svelte</code> \u6709\u7B80\u6D01\u7684\u8BED\u6CD5\u5E76\u4E14\u5B66\u4E60\u6210\u672C\u4F4E\u3001\u4E0A\u624B\u5FEB\u3001\u4F53\u79EF\u5C0F\uFF0C\u538B\u7F29\u540E\u4F53\u79EF\u53EA\u6709 1.6KB\uFF0C\u5728\u5F00\u53D1\u5C0F\u578B\u9879\u76EE\u4E0A\u5177\u6709\u4F18\u52BF\u3002\u4E0D\u4F7F\u7528 <code>virtual DOM</code>\uFF0C\u4E5F\u5C31\u6CA1\u6709\u4E86\u76F8\u5E94\u7684 <code>diff/patch</code>\uFF0C\u5728\u67D0\u4E9B\u60C5\u51B5\u4E0B\u6027\u80FD\u66F4\u4F18\u3002</p><p>\u4F46\u662F\u5176\u4E5F\u56E0\u4E3A\u76EE\u524D\u4F01\u4E1A\u7EA7\u7684\u5927\u578B\u9879\u76EE\u7684\u652F\u6301\u7A0B\u5EA6\u8FD8\u7F3A\u4E4F\u5B9E\u8DF5\uFF0C\u6CA1\u6709\u4E00\u4E2A\u6807\u6746\u578B\u9879\u76EE\uFF0C\u6240\u4EE5\u5176\u5728\u5927\u578B\u9879\u76EE\u4E2D\u7684\u6027\u80FD\u4F18\u52BF\u8FD8\u6CA1\u6709\u5F97\u5230\u5B9E\u8DF5\u8BC1\u660E\u3002\u4F46\u662F\u5404\u5927\u516C\u53F8\u5728\u65B0\u4E1A\u52A1\u3001\u5C0F\u578B\u9879\u76EE\u573A\u666F\u4E0B\u7684\u5C1D\u8BD5\u8FD8\u662F\u4E0D\u5C11\u7684\u3002\u5E76\u4E14\u5176\u751F\u6001\u4E5F\u5728\u9010\u6B65\u5B8C\u5584\u4E2D\uFF0C\u8DDD\u79BB React \u548C Vue \u8FD8\u662F\u6709\u4E00\u5B9A\u5DEE\u8DDD\u7684\u3002</p><p>\u540C\u65F6\uFF0C<code>SvelteKit</code> \u4E5F\u5728 2021 \u5E74\u8FCE\u6765\u4E86 <code>beta</code> \u7248\u672C\uFF0C<code>SvelteKit</code> \u53EF\u4EE5\u770B\u505A\u662F <code>Svelte</code> \u7684 <a href="https://nextjs.org/" target="_blank" rel="noreferrer">Next</a>\u3002\u662F\u4E00\u4E2A\u4F7F\u7528 <code>Svelte</code> \u6784\u5EFA\u5E94\u7528\u7A0B\u5E8F\u7684\u6846\u67B6\uFF0C\u5305\u62EC\u670D\u52A1\u5668\u7AEF\u6E32\u67D3\u3001\u8DEF\u7531\u3001JS \u548C CSS \u4EE3\u7801\u62C6\u5206\u3001\u4E0D\u540C\u65E0\u670D\u52A1\u5668\u5E73\u53F0\u7684\u9002\u914D\u5668\u7B49\u7B49\uFF0C\u5E76\u4E14\u5176\u5728\u6700\u65B0\u7248\u672C\u4E2D\u7528 Vite \u66FF\u4EE3 <code>Snowpack</code>\uFF08\u8FD9\u91CC\u4E5F\u53EF\u4EE5\u770B\u51FA Vite \u7684\u706B\u7206\u7A0B\u5EA6\uFF09\u3002</p><p>\u5728\u672A\u6765\uFF0C<code>Svelte</code> \u4ECD\u6709\u5F88\u591A\u503C\u5F97\u671F\u5F85\u7684\u5730\u65B9\uFF0C\u8BA9\u6211\u4EEC\u62ED\u76EE\u4EE5\u5F85\u5427\uFF0C\u63A8\u8350\u6CA1\u6709\u7528\u8FC7\u7684\u540C\u5B66\u53EF\u4EE5\u5C1D\u8BD5\u4E00\u4E0B~~</p><h3 id="\u6784\u5EFA\u5DE5\u5177" tabindex="-1">\u6784\u5EFA\u5DE5\u5177 <a class="header-anchor" href="#\u6784\u5EFA\u5DE5\u5177" aria-hidden="true">#</a></h3><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/431f20f10cca45c19ea029ffe4b85eae~tplv-k3u1fbpfcp-zoom-1.image" alt=""></p><p>\u5728\u6784\u5EFA\u5DE5\u5177\u4E2D\uFF0C<strong>\u5BF9\u539F\u751F ES \u6A21\u5757\u7684\u63A5\u7EB3\u4ECD\u5728\u7EE7\u7EED</strong>\uFF0CVite \u52BF\u5934\u96BE\u6321\u3002\u540C\u65F6\uFF0C<strong>\u51FA\u4E8E\u5BF9\u6027\u80FD\u7684\u8003\u8651\uFF0C\u8D8A\u6765\u8D8A\u591A\u7684\u524D\u7AEF\u5DE5\u5177\u5F00\u59CB\u7528\u5176\u4ED6\u8BED\u8A00 (Rust\u3001Go) \u6765\u6784\u5EFA\u3002</strong> \u6BD4\u5982 SWC\u3001esbuild\u3002<code>Rust Is The Future of JavaScript Infrastucture</code>\uFF0CJS \u56E0\u4E3A\u5176\u6027\u80FD\u95EE\u9898\u4E00\u76F4\u9971\u53D7\u8BDF\u75C5\u3002\u524D\u7AEF\u7684\u57FA\u7840\u8BBE\u65BD\u6BD4\u5982\u6784\u5EFA\u5DE5\u5177\u5374\u662F\u5BF9\u6027\u80FD\u8981\u6C42\u5341\u5206\u9AD8\u3002Rust \u81EA\u521B\u5EFA\u4EE5\u6765\u5C31\u4EE5\u5B83\u7684\u5B89\u5168\u6027\u3001\u6027\u80FD\u3001\u73B0\u4EE3\u5316\u7684\u8BED\u6CD5\u5438\u5F15\u4E86\u5927\u6279\u7684\u5F00\u53D1\u8005\u3002</p><p>\u4ECE\u4E0A\u9762\u6EE1\u610F\u5EA6\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\uFF0C<strong>esm \u548C esbuild \u5C06\u4F1A\u662F\u672A\u6765\u7684\u8D8B\u52BF</strong>\u3002\u867D\u7136\u76EE\u524D\u4F7F\u7528\u91CF\u4E0A\u6765\u8BF4 <code>webpack</code> \u65E0\u7591\u8FD8\u662F\u6392\u5728\u7B2C\u4E00\u4F4D\u7684\uFF0C\u4F46\u662F <code>esbuild</code> \u5728\u901F\u5EA6\u4E0A\u8868\u73B0\u7684\u5B9E\u5728\u662F\u592A\u4F18\u79C0\uFF08\u5982\u4E0B\u56FE\u6240\u793A\u7684\u6570\u636E\uFF0C\u4F7F\u7528 <code>webpack5</code> \u9700\u8981 <code>41s</code> \u7684\u65F6\u95F4\uFF0C\u4F7F\u7528 <code>esbuild</code> \u53EA\u9700\u8981 0.3s\uFF09\uFF0C\u53EF\u4EE5\u5BF9\u5176\u5C0F\u5C0F\u7684\u671F\u5F85\u4E00\u4E0B~</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/112e2f82e6f0486091d25513f8f66e60~tplv-k3u1fbpfcp-zoom-1.image" alt=""></p><p>Vite \u5B8C\u7F8E\u7684\u7EE7\u627F\u4E86 <code>esmbuild</code> \u7684 \u201C\u5FEB\u201D\uFF0C\u5176\u5173\u6CE8\u5EA6\u548C\u6EE1\u610F\u5EA6\u53CD\u9988\u90FD\u5F88\u4E0D\u9519\u3002\uFF08\u5173\u4E8E Vite \u7684\u4ECB\u7ECD\u5728\u524D\u9762\u7684\u7AE0\u8282\u4E2D\u5DF2\u7ECF\u6709\u6BD4\u8F83\u8BE6\u7EC6\u7684\u4ECB\u7ECD\u4E86\uFF0C\u8FD9\u91CC\u5C31\u4E0D\u518D\u8D58\u8FF0\u4E86\uFF09\u3002</p><p><a href="https://links.jianshu.com/go?to=https%3A%2F%2Fgithub.com%2Fswc-project%2Fswc" target="_blank" rel="noreferrer">SWC</a> \u662F\u4E00\u4E2A\u7528 Rust \u5199\u7684\u9AD8\u6027\u80FD TypeScript / JavaScript \u8F6C\u8BD1\u5668\uFF0C\u7C7B\u4F3C\u4E8E <code>babel</code>\u3002\u5BF9\u6BD4 <code>babel</code>\uFF0C\u5B98\u65B9\u5BA3\u79F0\uFF0C<code>SWC</code> \u5728\u5355\u7EBF\u7A0B\u4E0A\u6BD4 <code>Babel</code> \u5FEB 20\u500D\uFF0C\u5728\u56DB\u6838\u4E0A\u5FEB 70 \u500D\u3002\u4F46\u662F\u5BF9\u6BD4 <code>babel</code> \u7684 <code>plugin</code> \u4F53\u7CFB\uFF0C<code>SWC</code> \u7684\u751F\u6001\u8FD8\u6709\u5F85\u589E\u5F3A\uFF0C<br><a href="https://swc.rs/docs/migrating-from-babel" target="_blank" rel="noreferrer">Comparison with babel</a>\u3002</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7897186ff3c343bcb948b55dc34a36ce~tplv-k3u1fbpfcp-zoom-1.image" alt=""></p><h3 id="\u79FB\u52A8\u7AEF\u548C\u684C\u9762\u7AEF" tabindex="-1">\u79FB\u52A8\u7AEF\u548C\u684C\u9762\u7AEF <a class="header-anchor" href="#\u79FB\u52A8\u7AEF\u548C\u684C\u9762\u7AEF" aria-hidden="true">#</a></h3><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a2507f3b2f02464397716944c171e786~tplv-k3u1fbpfcp-zoom-1.image" alt=""></p><p>\u8DE8\u7AEF\u5F00\u53D1\u5728\u73B0\u5728\u5DF2\u7ECF\u662F\u4E00\u4E2A\u6301\u7EED\u5DF2\u4E45\u7684\u8BDD\u9898\u4E86\uFF0C\u4EA7\u54C1\u4E5F\u4E00\u5E74\u53C8\u4E00\u5E74\u7684\u63A8\u9648\u51FA\u65B0\uFF0C\u57FA\u672C\u4E0A\u6240\u6709\u7684\u4E92\u8054\u7F51\u5927\u5382\u90FD\u53C2\u4E0E\u5176\u4E2D\uFF0C\u6BD4\u5982 Cordov\u3001Ionic\u3001React Native\u3001Flutter \u7B49\u7B49 \u4F46\u81F3\u4ECA\u4E3A\u6B62\u4ECD\u7136\u6CA1\u6709\u4E00\u4E2A\u5B8C\u7F8E\u7684\u89E3\u51B3\u65B9\u6848\u3002</p><p>\u8FD1\u5E74\u6765\uFF0C\u5404\u5927\u8DE8\u7AEF\u6846\u67B6\u90FD\u7EB7\u7EB7\u63A8\u51FA\u4E86\u5176\u684C\u9762\u7AEF\uFF0C\u6BD4\u5982 <code>React Native Desktop</code>\u3001<code>Flutter Desktop</code>\uFF0C\u53EF\u89C1\u672A\u6765\u51E0\u5E74\u684C\u9762\u7AEF\u4E5F\u662F\u524D\u7AEF\u53D1\u5C55\u7684\u4E00\u5927\u8D8B\u52BF\u3002</p><p>\u5728\u684C\u9762\u7AEF\u9886\u57DF\uFF0C\u867D\u7136\u76EE\u524D <code>Electorn</code> \u7684\u4F7F\u7528\u5EA6\u8FD8\u5728\u9996\u4F4D\uFF0C\u4F46\u662F\u5176\u7F3A\u70B9\u4E5F\u5F88\u660E\u663E\uFF1A\u6027\u80FD\u95EE\u9898 \u548C \u5305\u5C3A\u5BF8\u592A\u5927\u30022021 \u65B0\u63A8\u51FA\u7684 <strong>Tauri</strong> \u5C31\u5F88\u597D\u7684\u89E3\u51B3\u4E86\u8FD9\u4E2A\u95EE\u9898\u56E0\u6B64\u9971\u53D7\u5173\u6CE8\uFF0C\u6F5C\u529B\u4E0D\u5BB9\u5C0F\u89D1\u3002<code>Tauri</code> \u5728\u6253\u5305\u5927\u5C0F\u548C\u5185\u5BB9\u5360\u7528\u4E0A\u90FD\u6709\u5F88\u5927\u7684\u63D0\u5347\uFF0C\u539F\u59CB <code>Tauri</code> \u5E94\u7528\u7A0B\u5E8F\u7684\u6253\u5305\u5927\u5C0F\u5C0F\u4E8E 3 MB\uFF0C\u6BD4 <code>Electron</code> \u7684\u5927\u5C0F\u5C0F140 MB\u3002<strong>\u5185\u5B58\u5360\u7528</strong>\u5C0F\u4E8E\u4F7F\u7528\u76F8\u540C\u4EE3\u7801\u5E93\u6784\u5EFA\u7684 <code>Electron</code> \u5E94\u7528\u7A0B\u5E8F\u7684\u5927\u5C0F\u7684\u4E00\u534A\u3002</p><h3 id="\u5C55\u671B-2022-\u6280\u672F\u8D8B\u52BF" tabindex="-1">\u5C55\u671B 2022 \u6280\u672F\u8D8B\u52BF <a class="header-anchor" href="#\u5C55\u671B-2022-\u6280\u672F\u8D8B\u52BF" aria-hidden="true">#</a></h3><ul><li>\u5728\u6846\u67B6\u5C42\u9762\u4E0A\uFF0CReact \u548C Vue \u4ECD\u7136\u4F1A\u7A33\u6B65\u524D\u8FDB\uFF0C\u91CD\u70B9\u8FD8\u662F\u56F4\u7ED5\u7740\u7528\u6237\u4F53\u9A8C\u548C\u5F00\u53D1\u8005\u4F53\u9A8C\u4E24\u5757\u3002\u800C\u540E\u6765\u8005\u5C45\u4E0A\u7684 Svelte \u80FD\u53D1\u5C55\u5230\u4EC0\u4E48\u7A0B\u5EA6\u6211\u4EEC\u62ED\u76EE\u4EE5\u5F85\u3002</li><li>\u5728\u6784\u5EFA\u5DE5\u5177\u5C42\u9762\u4E0A\uFF0Cesm \u548C esbuild \u5C06\u4F1A\u662F\u672A\u6765\u7684\u8D8B\u52BF\uFF0C\u4E0D\u77E5\u9053\u5176\u80FD\u5426\u64BC\u52A8 webpack \u7684\u5730\u4F4D\u3002\u540C\u65F6\uFF0C\u51FA\u4E8E\u6027\u80FD\u65B9\u9762\u7684\u8003\u8651\uFF0C\u4F7F\u7528 Rust\u3001Go \u7B49\u5176\u4ED6\u8BED\u8A00\u4E5F\u5C06\u662F\u524D\u7AEF\u6784\u5EFA\u5DE5\u5177\u7684\u53D1\u5C55\u8D8B\u52BF\u3002</li><li>\u5404\u5927\u5382\u90FD\u5728\u79EF\u6781\u63A2\u7D22\u4F4E\u4EE3\u7801\u5E73\u53F0\u548C\u642D\u5EFA\u5DE5\u5177\uFF0C\u4F46\u662F\u4ECD\u672A\u6709\u4E00\u4E2A\u5DE8\u65E0\u9738\u7684\u4EA7\u54C1\u51FA\u73B0\u3002\u8FD9\u4E5F\u662F\u4E92\u8054\u7F51\u884C\u4E1A\u8D70\u5411\u5DE5\u4E1A\u5316\u548C\u667A\u80FD\u5316\u7684\u5FC5\u7ECF\u9014\u5F84\u3002</li><li>\u5404\u5927\u5382\u5BF9\u684C\u9762\u7AEF\u7684\u5173\u6CE8\u7A0B\u5EA6\u4E0D\u65AD\u4E0A\u5347\uFF0C\u7EB7\u7EB7\u63A8\u51FA\u5176\u684C\u9762\u7AEF\uFF0C\u524D\u7AEF\u5728\u684C\u9762\u7AEF\u7684\u53D1\u5C55\u4E5F\u662F\u4E00\u5927\u8D8B\u52BF\u3002</li><li>\u968F\u7740\u4E92\u8054\u7F51\u548C\u9879\u76EE\u7684\u53D1\u5C55\uFF0C\u5DE8\u77F3\u9879\u76EE\u4F1A\u8D8A\u6765\u8D8A\u591A\uFF0C\u9879\u76EE\u62C6\u5206\u5FC5\u7136\u4E5F\u662F\u4E00\u5927\u8D8B\u52BF\uFF0C\u5FAE\u524D\u7AEF\u4E5F\u4F1A\u6709\u66F4\u591A\u6210\u719F\u7684\u65B9\u6848\u843D\u5730\u3002</li></ul><h1 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h1><p>\u524D\u7AEF\u5DE5\u7A0B\u5316\u662F\u4E00\u4E2A\u5F88\u5927\u7684\u9886\u57DF\uFF0C\u4F1A\u6D89\u53CA\u5230\u6211\u4EEC\u5E73\u65F6\u5F00\u53D1\u7684\u65B9\u65B9\u9762\u9762\u3002\u6B63\u5F0F\u56E0\u4E3A\u5176\u5185\u5BB9\u7E41\u591A\uFF0C\u5176\u4E2D\u7684\u6BCF\u4E00\u90E8\u5206\u6DF1\u5165\u7814\u7A76\u90FD\u53EF\u4EE5\u518D\u53E6\u5916\u5F00\u4E00\u4E2A\u5C0F\u518C\u3002\u6240\u4EE5\u672C\u5C0F\u518C\u4E5F\u662F\u5E26\u5927\u5BB6\u201C\u521D\u63A2\u201D\u524D\u7AEF\u5DE5\u7A0B\u5316\uFF0C\u4E3B\u8981\u5E26\u5927\u5BB6\u4ECE\u5F00\u53D1\u3001\u6784\u5EFA\u3001\u90E8\u7F72\u4E09\u4E2A\u5927\u65B9\u9762\u4ECB\u7ECD\u6211\u4EEC\u5982\u4F55\u4F7F\u7528\u5DE5\u7A0B\u5316\u7684\u624B\u6BB5\u7ED9\u524D\u7AEF\u5F00\u53D1\u63D0\u6548\u3002\u5E0C\u671B\u4F60\u901A\u8FC7\u672C\u5C0F\u518C\uFF0C\u53EF\u4EE5\u5BF9\u524D\u7AEF\u5DE5\u7A0B\u5316\u6709\u4E00\u4E2A\u7CFB\u7EDF\u7684\u8BA4\u8BC6\uFF0C\u53EF\u4EE5\u5C06\u672C\u5C0F\u518C\u7684\u5185\u5BB9\u5E94\u7528\u5230\u5E73\u65F6\u7684\u5F00\u53D1\u4E2D\u3002 \u5982\u679C\u5927\u5BB6\u5BF9\u5176\u4E2D\u7684\u67D0\u4E00\u90E8\u5206\u7279\u522B\u611F\u5174\u8DA3\u53EF\u4EE5\u7559\u8A00\u544A\u8BC9\u6211~~</p><p>\u540C\u65F6\u5E0C\u671B\u6211\u4EEC\u5E73\u65F6\u5728\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u4E0D\u8981\u53EA\u5173\u6CE8\u4E8E\u4E1A\u52A1\u4EE3\u7801\u3001\u53EA\u5173\u6CE8\u4E8E\u600E\u4E48\u4F7F\u7528 API\u3002API \u6709\u5343\u5343\u4E07\uFF0C\u662F\u80CC\u4E0D\u5B8C\u7684\uFF0C\u6211\u4EEC\u53EA\u6709\u5C06\u539F\u7406\u641E\u6E05\u695A\u4E86\u624D\u53EF\u4EE5\u4E8B\u534A\u529F\u500D\u3002</p><p>\u8BFB\u5B8C\u4E86\u672C\u5C0F\u518C\u5E0C\u671B\u5927\u5BB6\u80FD\u591F\u5728\u524D\u7AEF\u5DE5\u7A0B\u5316\u4E0A\u6709\u6240\u6536\u83B7\uFF0C\u629B\u7816\u5F15\u7389\uFF0C\u5E0C\u671B\u5927\u5BB6\u53EF\u4EE5\u5728\u524D\u7AEF\u5DE5\u7A0B\u5316\u65B9\u9762\u6709\u81EA\u5DF1\u7684\u5EFA\u6811\u751A\u81F3\u5F15\u9886\u524D\u7AEF\u53D1\u5C55(\u3065\uFF61\u25D5\u203F\u203F\u25D5\uFF61)\u3065\uFF01</p>',59),d=[r];function i(p,s,n,l,h,u){return t(),c("div",null,d)}const b=e(a,[["render",i]]);export{g as __pageData,b as default};
