import{_ as e,c as a,o as s,d as n}from"./app.e0e4ea3a.js";const u=JSON.parse('{"title":"Vue \u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u751F\u547D\u5468\u671F\u94A9\u5B50\u51FD\u6570","slug":"\u751F\u547D\u5468\u671F\u94A9\u5B50\u51FD\u6570","link":"#\u751F\u547D\u5468\u671F\u94A9\u5B50\u51FD\u6570","children":[]},{"level":2,"title":"\u7EC4\u4EF6\u901A\u4FE1","slug":"\u7EC4\u4EF6\u901A\u4FE1","link":"#\u7EC4\u4EF6\u901A\u4FE1","children":[{"level":3,"title":"\u7236\u5B50\u901A\u4FE1","slug":"\u7236\u5B50\u901A\u4FE1","link":"#\u7236\u5B50\u901A\u4FE1","children":[]},{"level":3,"title":"\u5144\u5F1F\u7EC4\u4EF6\u901A\u4FE1","slug":"\u5144\u5F1F\u7EC4\u4EF6\u901A\u4FE1","link":"#\u5144\u5F1F\u7EC4\u4EF6\u901A\u4FE1","children":[]},{"level":3,"title":"\u8DE8\u591A\u5C42\u6B21\u7EC4\u4EF6\u901A\u4FE1","slug":"\u8DE8\u591A\u5C42\u6B21\u7EC4\u4EF6\u901A\u4FE1","link":"#\u8DE8\u591A\u5C42\u6B21\u7EC4\u4EF6\u901A\u4FE1","children":[]},{"level":3,"title":"\u4EFB\u610F\u7EC4\u4EF6","slug":"\u4EFB\u610F\u7EC4\u4EF6","link":"#\u4EFB\u610F\u7EC4\u4EF6","children":[]}]},{"level":2,"title":"extend \u80FD\u505A\u4EC0\u4E48","slug":"extend-\u80FD\u505A\u4EC0\u4E48","link":"#extend-\u80FD\u505A\u4EC0\u4E48","children":[]},{"level":2,"title":"mixin \u548C mixins \u533A\u522B","slug":"mixin-\u548C-mixins-\u533A\u522B","link":"#mixin-\u548C-mixins-\u533A\u522B","children":[]},{"level":2,"title":"computed \u548C watch \u533A\u522B","slug":"computed-\u548C-watch-\u533A\u522B","link":"#computed-\u548C-watch-\u533A\u522B","children":[]},{"level":2,"title":"keep-alive \u7EC4\u4EF6\u6709\u4EC0\u4E48\u4F5C\u7528","slug":"keep-alive-\u7EC4\u4EF6\u6709\u4EC0\u4E48\u4F5C\u7528","link":"#keep-alive-\u7EC4\u4EF6\u6709\u4EC0\u4E48\u4F5C\u7528","children":[]},{"level":2,"title":"v-show \u4E0E v-if \u533A\u522B","slug":"v-show-\u4E0E-v-if-\u533A\u522B","link":"#v-show-\u4E0E-v-if-\u533A\u522B","children":[]},{"level":2,"title":"\u7EC4\u4EF6\u4E2D data \u4EC0\u4E48\u65F6\u5019\u53EF\u4EE5\u4F7F\u7528\u5BF9\u8C61","slug":"\u7EC4\u4EF6\u4E2D-data-\u4EC0\u4E48\u65F6\u5019\u53EF\u4EE5\u4F7F\u7528\u5BF9\u8C61","link":"#\u7EC4\u4EF6\u4E2D-data-\u4EC0\u4E48\u65F6\u5019\u53EF\u4EE5\u4F7F\u7528\u5BF9\u8C61","children":[]}],"relativePath":"\u9762\u8BD5/\u524D\u7AEF\u9762\u8BD5\u4E4B\u9053/Vue \u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9.md"}'),l={name:"\u9762\u8BD5/\u524D\u7AEF\u9762\u8BD5\u4E4B\u9053/Vue \u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9.md"},p=n(`<h1 id="vue-\u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9" tabindex="-1">Vue \u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9 <a class="header-anchor" href="#vue-\u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9" aria-hidden="true">#</a></h1><hr><h1 id="vue-\u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9-1" tabindex="-1">Vue \u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9 <a class="header-anchor" href="#vue-\u5E38\u8003\u57FA\u7840\u77E5\u8BC6\u70B9-1" aria-hidden="true">#</a></h1><p>\u8FD9\u4E00\u7AE0\u8282\u6211\u4EEC\u5C06\u6765\u5B66\u4E60 Vue \u7684\u4E00\u4E9B\u7ECF\u5E38\u8003\u5230\u7684\u57FA\u7840\u77E5\u8BC6\u70B9\u3002</p><h2 id="\u751F\u547D\u5468\u671F\u94A9\u5B50\u51FD\u6570" tabindex="-1">\u751F\u547D\u5468\u671F\u94A9\u5B50\u51FD\u6570 <a class="header-anchor" href="#\u751F\u547D\u5468\u671F\u94A9\u5B50\u51FD\u6570" aria-hidden="true">#</a></h2><p>\u5728 <code>beforeCreate</code> \u94A9\u5B50\u51FD\u6570\u8C03\u7528\u7684\u65F6\u5019\uFF0C\u662F\u83B7\u53D6\u4E0D\u5230 <code>props</code> \u6216\u8005 <code>data</code> \u4E2D\u7684\u6570\u636E\u7684\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u6570\u636E\u7684\u521D\u59CB\u5316\u90FD\u5728 <code>initState</code> \u4E2D\u3002</p><p>\u7136\u540E\u4F1A\u6267\u884C <code>created</code> \u94A9\u5B50\u51FD\u6570\uFF0C\u5728\u8FD9\u4E00\u6B65\u7684\u65F6\u5019\u5DF2\u7ECF\u53EF\u4EE5\u8BBF\u95EE\u5230\u4E4B\u524D\u4E0D\u80FD\u8BBF\u95EE\u5230\u7684\u6570\u636E\uFF0C\u4F46\u662F\u8FD9\u65F6\u5019\u7EC4\u4EF6\u8FD8\u6CA1\u88AB\u6302\u8F7D\uFF0C\u6240\u4EE5\u662F\u770B\u4E0D\u5230\u7684\u3002</p><p>\u63A5\u4E0B\u6765\u4F1A\u5148\u6267\u884C <code>beforeMount</code> \u94A9\u5B50\u51FD\u6570\uFF0C\u5F00\u59CB\u521B\u5EFA VDOM\uFF0C\u6700\u540E\u6267\u884C <code>mounted</code> \u94A9\u5B50\uFF0C\u5E76\u5C06 VDOM \u6E32\u67D3\u4E3A\u771F\u5B9E DOM \u5E76\u4E14\u6E32\u67D3\u6570\u636E\u3002\u7EC4\u4EF6\u4E2D\u5982\u679C\u6709\u5B50\u7EC4\u4EF6\u7684\u8BDD\uFF0C\u4F1A\u9012\u5F52\u6302\u8F7D\u5B50\u7EC4\u4EF6\uFF0C\u53EA\u6709\u5F53\u6240\u6709\u5B50\u7EC4\u4EF6\u5168\u90E8\u6302\u8F7D\u5B8C\u6BD5\uFF0C\u624D\u4F1A\u6267\u884C\u6839\u7EC4\u4EF6\u7684\u6302\u8F7D\u94A9\u5B50\u3002</p><p>\u63A5\u4E0B\u6765\u662F\u6570\u636E\u66F4\u65B0\u65F6\u4F1A\u8C03\u7528\u7684\u94A9\u5B50\u51FD\u6570 <code>beforeUpdate</code> \u548C <code>updated</code>\uFF0C\u8FD9\u4E24\u4E2A\u94A9\u5B50\u51FD\u6570\u6CA1\u4EC0\u4E48\u597D\u8BF4\u7684\uFF0C\u5C31\u662F\u5206\u522B\u5728\u6570\u636E\u66F4\u65B0\u524D\u548C\u66F4\u65B0\u540E\u4F1A\u8C03\u7528\u3002</p><p>\u53E6\u5916\u8FD8\u6709 <code>keep-alive</code> \u72EC\u6709\u7684\u751F\u547D\u5468\u671F\uFF0C\u5206\u522B\u4E3A <code>activated</code> \u548C <code>deactivated</code> \u3002\u7528 <code>keep-alive</code> \u5305\u88F9\u7684\u7EC4\u4EF6\u5728\u5207\u6362\u65F6\u4E0D\u4F1A\u8FDB\u884C\u9500\u6BC1\uFF0C\u800C\u662F\u7F13\u5B58\u5230\u5185\u5B58\u4E2D\u5E76\u6267\u884C <code>deactivated</code> \u94A9\u5B50\u51FD\u6570\uFF0C\u547D\u4E2D\u7F13\u5B58\u6E32\u67D3\u540E\u4F1A\u6267\u884C <code>actived</code> \u94A9\u5B50\u51FD\u6570\u3002</p><p>\u6700\u540E\u5C31\u662F\u9500\u6BC1\u7EC4\u4EF6\u7684\u94A9\u5B50\u51FD\u6570 <code>beforeDestroy</code> \u548C <code>destroyed</code>\u3002\u524D\u8005\u9002\u5408\u79FB\u9664\u4E8B\u4EF6\u3001\u5B9A\u65F6\u5668\u7B49\u7B49\uFF0C\u5426\u5219\u53EF\u80FD\u4F1A\u5F15\u8D77\u5185\u5B58\u6CC4\u9732\u7684\u95EE\u9898\u3002\u7136\u540E\u8FDB\u884C\u4E00\u7CFB\u5217\u7684\u9500\u6BC1\u64CD\u4F5C\uFF0C\u5982\u679C\u6709\u5B50\u7EC4\u4EF6\u7684\u8BDD\uFF0C\u4E5F\u4F1A\u9012\u5F52\u9500\u6BC1\u5B50\u7EC4\u4EF6\uFF0C\u6240\u6709\u5B50\u7EC4\u4EF6\u90FD\u9500\u6BC1\u5B8C\u6BD5\u540E\u624D\u4F1A\u6267\u884C\u6839\u7EC4\u4EF6\u7684 <code>destroyed</code> \u94A9\u5B50\u51FD\u6570\u3002</p><h2 id="\u7EC4\u4EF6\u901A\u4FE1" tabindex="-1">\u7EC4\u4EF6\u901A\u4FE1 <a class="header-anchor" href="#\u7EC4\u4EF6\u901A\u4FE1" aria-hidden="true">#</a></h2><p>\u7EC4\u4EF6\u901A\u4FE1\u4E00\u822C\u5206\u4E3A\u4EE5\u4E0B\u51E0\u79CD\u60C5\u51B5\uFF1A</p><ul><li>\u7236\u5B50\u7EC4\u4EF6\u901A\u4FE1</li><li>\u5144\u5F1F\u7EC4\u4EF6\u901A\u4FE1</li><li>\u8DE8\u591A\u5C42\u7EA7\u7EC4\u4EF6\u901A\u4FE1</li><li>\u4EFB\u610F\u7EC4\u4EF6</li></ul><p>\u5BF9\u4E8E\u4EE5\u4E0A\u6BCF\u79CD\u60C5\u51B5\u90FD\u6709\u591A\u79CD\u65B9\u5F0F\u53BB\u5B9E\u73B0\uFF0C\u63A5\u4E0B\u6765\u5C31\u6765\u5B66\u4E60\u4E0B\u5982\u4F55\u5B9E\u73B0\u3002</p><h3 id="\u7236\u5B50\u901A\u4FE1" tabindex="-1">\u7236\u5B50\u901A\u4FE1 <a class="header-anchor" href="#\u7236\u5B50\u901A\u4FE1" aria-hidden="true">#</a></h3><p>\u7236\u7EC4\u4EF6\u901A\u8FC7 <code>props</code> \u4F20\u9012\u6570\u636E\u7ED9\u5B50\u7EC4\u4EF6\uFF0C\u5B50\u7EC4\u4EF6\u901A\u8FC7 <code>emit</code> \u53D1\u9001\u4E8B\u4EF6\u4F20\u9012\u6570\u636E\u7ED9\u7236\u7EC4\u4EF6\uFF0C\u8FD9\u4E24\u79CD\u65B9\u5F0F\u662F\u6700\u5E38\u7528\u7684\u7236\u5B50\u901A\u4FE1\u5B9E\u73B0\u529E\u6CD5\u3002</p><p>\u8FD9\u79CD\u7236\u5B50\u901A\u4FE1\u65B9\u5F0F\u4E5F\u5C31\u662F\u5178\u578B\u7684\u5355\u5411\u6570\u636E\u6D41\uFF0C\u7236\u7EC4\u4EF6\u901A\u8FC7 <code>props</code> \u4F20\u9012\u6570\u636E\uFF0C\u5B50\u7EC4\u4EF6\u4E0D\u80FD\u76F4\u63A5\u4FEE\u6539 <code>props</code>\uFF0C \u800C\u662F\u5FC5\u987B\u901A\u8FC7\u53D1\u9001\u4E8B\u4EF6\u7684\u65B9\u5F0F\u544A\u77E5\u7236\u7EC4\u4EF6\u4FEE\u6539\u6570\u636E\u3002</p><p>\u53E6\u5916\u8FD9\u4E24\u79CD\u65B9\u5F0F\u8FD8\u53EF\u4EE5\u4F7F\u7528\u8BED\u6CD5\u7CD6 <code>v-model</code> \u6765\u76F4\u63A5\u5B9E\u73B0\uFF0C\u56E0\u4E3A <code>v-model</code> \u9ED8\u8BA4\u4F1A\u89E3\u6790\u6210\u540D\u4E3A <code>value</code> \u7684 <code>prop</code> \u548C\u540D\u4E3A <code>input</code> \u7684\u4E8B\u4EF6\u3002\u8FD9\u79CD\u8BED\u6CD5\u7CD6\u7684\u65B9\u5F0F\u662F\u5178\u578B\u7684\u53CC\u5411\u7ED1\u5B9A\uFF0C\u5E38\u7528\u4E8E UI \u63A7\u4EF6\u4E0A\uFF0C\u4F46\u662F\u7A76\u5176\u6839\u672C\uFF0C\u8FD8\u662F\u901A\u8FC7\u4E8B\u4EF6\u7684\u65B9\u6CD5\u8BA9\u7236\u7EC4\u4EF6\u4FEE\u6539\u6570\u636E\u3002</p><p>\u5F53\u7136\u6211\u4EEC\u8FD8\u53EF\u4EE5\u901A\u8FC7\u8BBF\u95EE <code>$parent</code> \u6216\u8005 <code>$children</code> \u5BF9\u8C61\u6765\u8BBF\u95EE\u7EC4\u4EF6\u5B9E\u4F8B\u4E2D\u7684\u65B9\u6CD5\u548C\u6570\u636E\u3002</p><p>\u53E6\u5916\u5982\u679C\u4F60\u4F7F\u7528 Vue 2.3 \u53CA\u4EE5\u4E0A\u7248\u672C\u7684\u8BDD\u8FD8\u53EF\u4EE5\u4F7F\u7528 <code>$listeners</code> \u548C <code>.sync</code> \u8FD9\u4E24\u4E2A\u5C5E\u6027\u3002</p><p><code>$listeners</code> \u5C5E\u6027\u4F1A\u5C06\u7236\u7EC4\u4EF6\u4E2D\u7684 (\u4E0D\u542B <code>.native</code> \u4FEE\u9970\u5668\u7684) <code>v-on</code> \u4E8B\u4EF6\u76D1\u542C\u5668\u4F20\u9012\u7ED9\u5B50\u7EC4\u4EF6\uFF0C\u5B50\u7EC4\u4EF6\u53EF\u4EE5\u901A\u8FC7\u8BBF\u95EE <code>$listeners</code> \u6765\u81EA\u5B9A\u4E49\u76D1\u542C\u5668\u3002</p><p><code>.sync</code> \u5C5E\u6027\u662F\u4E2A\u8BED\u6CD5\u7CD6\uFF0C\u53EF\u4EE5\u5F88\u7B80\u5355\u7684\u5B9E\u73B0\u5B50\u7EC4\u4EF6\u4E0E\u7236\u7EC4\u4EF6\u901A\u4FE1</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&lt;!--\u7236\u7EC4\u4EF6\u4E2D--&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;input :value.sync=&quot;value&quot; /&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;!--\u4EE5\u4E0A\u5199\u6CD5\u7B49\u540C\u4E8E--&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;input :value=&quot;value&quot; @update:value=&quot;v =&gt; value = v&quot;&gt;&lt;/comp&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;!--\u5B50\u7EC4\u4EF6\u4E2D--&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;script&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  this.$emit(&#39;update:value&#39;, 1)</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;/script&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h3 id="\u5144\u5F1F\u7EC4\u4EF6\u901A\u4FE1" tabindex="-1">\u5144\u5F1F\u7EC4\u4EF6\u901A\u4FE1 <a class="header-anchor" href="#\u5144\u5F1F\u7EC4\u4EF6\u901A\u4FE1" aria-hidden="true">#</a></h3><p>\u5BF9\u4E8E\u8FD9\u79CD\u60C5\u51B5\u53EF\u4EE5\u901A\u8FC7\u67E5\u627E\u7236\u7EC4\u4EF6\u4E2D\u7684\u5B50\u7EC4\u4EF6\u5B9E\u73B0\uFF0C\u4E5F\u5C31\u662F <code>this.$parent.$children</code>\uFF0C\u5728 <code>$children</code> \u4E2D\u53EF\u4EE5\u901A\u8FC7\u7EC4\u4EF6 <code>name</code> \u67E5\u8BE2\u5230\u9700\u8981\u7684\u7EC4\u4EF6\u5B9E\u4F8B\uFF0C\u7136\u540E\u8FDB\u884C\u901A\u4FE1\u3002</p><h3 id="\u8DE8\u591A\u5C42\u6B21\u7EC4\u4EF6\u901A\u4FE1" tabindex="-1">\u8DE8\u591A\u5C42\u6B21\u7EC4\u4EF6\u901A\u4FE1 <a class="header-anchor" href="#\u8DE8\u591A\u5C42\u6B21\u7EC4\u4EF6\u901A\u4FE1" aria-hidden="true">#</a></h3><p>\u5BF9\u4E8E\u8FD9\u79CD\u60C5\u51B5\u53EF\u4EE5\u4F7F\u7528 Vue 2.2 \u65B0\u589E\u7684 API <code>provide / inject</code>\uFF0C\u867D\u7136\u6587\u6863\u4E2D\u4E0D\u63A8\u8350\u76F4\u63A5\u4F7F\u7528\u5728\u4E1A\u52A1\u4E2D\uFF0C\u4F46\u662F\u5982\u679C\u7528\u5F97\u597D\u7684\u8BDD\u8FD8\u662F\u5F88\u6709\u7528\u7684\u3002</p><p>\u5047\u8BBE\u6709\u7236\u7EC4\u4EF6 A\uFF0C\u7136\u540E\u6709\u4E00\u4E2A\u8DE8\u591A\u5C42\u7EA7\u7684\u5B50\u7EC4\u4EF6 B</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u7236\u7EC4\u4EF6 A</span></span>
<span class="line"><span style="color:#A6ACCD;">export default {</span></span>
<span class="line"><span style="color:#A6ACCD;">  provide: {</span></span>
<span class="line"><span style="color:#A6ACCD;">    data: 1</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u5B50\u7EC4\u4EF6 B</span></span>
<span class="line"><span style="color:#A6ACCD;">export default {</span></span>
<span class="line"><span style="color:#A6ACCD;">  inject: [&#39;data&#39;],</span></span>
<span class="line"><span style="color:#A6ACCD;">  mounted() {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u65E0\u8BBA\u8DE8\u51E0\u5C42\u90FD\u80FD\u83B7\u5F97\u7236\u7EC4\u4EF6\u7684 data \u5C5E\u6027</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(this.data) // =&gt; 1</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h3 id="\u4EFB\u610F\u7EC4\u4EF6" tabindex="-1">\u4EFB\u610F\u7EC4\u4EF6 <a class="header-anchor" href="#\u4EFB\u610F\u7EC4\u4EF6" aria-hidden="true">#</a></h3><p>\u8FD9\u79CD\u65B9\u5F0F\u53EF\u4EE5\u901A\u8FC7 Vuex \u6216\u8005 Event Bus \u89E3\u51B3\uFF0C\u53E6\u5916\u5982\u679C\u4F60\u4E0D\u6015\u9EBB\u70E6\u7684\u8BDD\uFF0C\u53EF\u4EE5\u4F7F\u7528\u8FD9\u79CD\u65B9\u5F0F\u89E3\u51B3\u4E0A\u8FF0\u6240\u6709\u7684\u901A\u4FE1\u60C5\u51B5</p><h2 id="extend-\u80FD\u505A\u4EC0\u4E48" tabindex="-1">extend \u80FD\u505A\u4EC0\u4E48 <a class="header-anchor" href="#extend-\u80FD\u505A\u4EC0\u4E48" aria-hidden="true">#</a></h2><p>\u8FD9\u4E2A API \u5F88\u5C11\u7528\u5230\uFF0C\u4F5C\u7528\u662F\u6269\u5C55\u7EC4\u4EF6\u751F\u6210\u4E00\u4E2A\u6784\u9020\u5668\uFF0C\u901A\u5E38\u4F1A\u4E0E <code>$mount</code> \u4E00\u8D77\u4F7F\u7528\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u521B\u5EFA\u7EC4\u4EF6\u6784\u9020\u5668</span></span>
<span class="line"><span style="color:#A6ACCD;">let Component = Vue.extend({</span></span>
<span class="line"><span style="color:#A6ACCD;">  template: &#39;&lt;div&gt;test&lt;/div&gt;&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u6302\u8F7D\u5230 #app \u4E0A</span></span>
<span class="line"><span style="color:#A6ACCD;">new Component().$mount(&#39;#app&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u9664\u4E86\u4E0A\u9762\u7684\u65B9\u5F0F\uFF0C\u8FD8\u53EF\u4EE5\u7528\u6765\u6269\u5C55\u5DF2\u6709\u7684\u7EC4\u4EF6</span></span>
<span class="line"><span style="color:#A6ACCD;">let SuperComponent = Vue.extend(Component)</span></span>
<span class="line"><span style="color:#A6ACCD;">new SuperComponent({</span></span>
<span class="line"><span style="color:#A6ACCD;">    created() {</span></span>
<span class="line"><span style="color:#A6ACCD;">        console.log(1)</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;">new SuperComponent().$mount(&#39;#app&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="mixin-\u548C-mixins-\u533A\u522B" tabindex="-1">mixin \u548C mixins \u533A\u522B <a class="header-anchor" href="#mixin-\u548C-mixins-\u533A\u522B" aria-hidden="true">#</a></h2><p><code>mixin</code> \u7528\u4E8E\u5168\u5C40\u6DF7\u5165\uFF0C\u4F1A\u5F71\u54CD\u5230\u6BCF\u4E2A\u7EC4\u4EF6\u5B9E\u4F8B\uFF0C\u901A\u5E38\u63D2\u4EF6\u90FD\u662F\u8FD9\u6837\u505A\u521D\u59CB\u5316\u7684\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">Vue.mixin({</span></span>
<span class="line"><span style="color:#A6ACCD;">    beforeCreate() {</span></span>
<span class="line"><span style="color:#A6ACCD;">        // ...\u903B\u8F91</span></span>
<span class="line"><span style="color:#A6ACCD;">        // \u8FD9\u79CD\u65B9\u5F0F\u4F1A\u5F71\u54CD\u5230\u6BCF\u4E2A\u7EC4\u4EF6\u7684 beforeCreate \u94A9\u5B50\u51FD\u6570</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u867D\u7136\u6587\u6863\u4E0D\u5EFA\u8BAE\u6211\u4EEC\u5728\u5E94\u7528\u4E2D\u76F4\u63A5\u4F7F\u7528 <code>mixin</code>\uFF0C\u4F46\u662F\u5982\u679C\u4E0D\u6EE5\u7528\u7684\u8BDD\u4E5F\u662F\u5F88\u6709\u5E2E\u52A9\u7684\uFF0C\u6BD4\u5982\u53EF\u4EE5\u5168\u5C40\u6DF7\u5165\u5C01\u88C5\u597D\u7684 <code>ajax</code> \u6216\u8005\u4E00\u4E9B\u5DE5\u5177\u51FD\u6570\u7B49\u7B49\u3002</p><p><code>mixins</code> \u5E94\u8BE5\u662F\u6211\u4EEC\u6700\u5E38\u4F7F\u7528\u7684\u6269\u5C55\u7EC4\u4EF6\u7684\u65B9\u5F0F\u4E86\u3002\u5982\u679C\u591A\u4E2A\u7EC4\u4EF6\u4E2D\u6709\u76F8\u540C\u7684\u4E1A\u52A1\u903B\u8F91\uFF0C\u5C31\u53EF\u4EE5\u5C06\u8FD9\u4E9B\u903B\u8F91\u5265\u79BB\u51FA\u6765\uFF0C\u901A\u8FC7 <code>mixins</code> \u6DF7\u5165\u4EE3\u7801\uFF0C\u6BD4\u5982\u4E0A\u62C9\u4E0B\u62C9\u52A0\u8F7D\u6570\u636E\u8FD9\u79CD\u903B\u8F91\u7B49\u7B49\u3002</p><p>\u53E6\u5916\u9700\u8981\u6CE8\u610F\u7684\u662F <code>mixins</code> \u6DF7\u5165\u7684\u94A9\u5B50\u51FD\u6570\u4F1A\u5148\u4E8E\u7EC4\u4EF6\u5185\u7684\u94A9\u5B50\u51FD\u6570\u6267\u884C\uFF0C\u5E76\u4E14\u5728\u9047\u5230\u540C\u540D\u9009\u9879\u7684\u65F6\u5019\u4E5F\u4F1A\u6709\u9009\u62E9\u6027\u7684\u8FDB\u884C\u5408\u5E76\uFF0C\u5177\u4F53\u53EF\u4EE5\u9605\u8BFB <a href="https://cn.vuejs.org/v2/guide/mixins.html" target="_blank" rel="noreferrer">\u6587\u6863</a>\u3002</p><h2 id="computed-\u548C-watch-\u533A\u522B" tabindex="-1">computed \u548C watch \u533A\u522B <a class="header-anchor" href="#computed-\u548C-watch-\u533A\u522B" aria-hidden="true">#</a></h2><p><code>computed</code> \u662F\u8BA1\u7B97\u5C5E\u6027\uFF0C\u4F9D\u8D56\u5176\u4ED6\u5C5E\u6027\u8BA1\u7B97\u503C\uFF0C\u5E76\u4E14 <code>computed</code> \u7684\u503C\u6709\u7F13\u5B58\uFF0C\u53EA\u6709\u5F53\u8BA1\u7B97\u503C\u53D8\u5316\u624D\u4F1A\u8FD4\u56DE\u5185\u5BB9\u3002</p><p><code>watch</code> \u76D1\u542C\u5230\u503C\u7684\u53D8\u5316\u5C31\u4F1A\u6267\u884C\u56DE\u8C03\uFF0C\u5728\u56DE\u8C03\u4E2D\u53EF\u4EE5\u8FDB\u884C\u4E00\u4E9B\u903B\u8F91\u64CD\u4F5C\u3002</p><p>\u6240\u4EE5\u4E00\u822C\u6765\u8BF4\u9700\u8981\u4F9D\u8D56\u522B\u7684\u5C5E\u6027\u6765\u52A8\u6001\u83B7\u5F97\u503C\u7684\u65F6\u5019\u53EF\u4EE5\u4F7F\u7528 <code>computed</code>\uFF0C\u5BF9\u4E8E\u76D1\u542C\u5230\u503C\u7684\u53D8\u5316\u9700\u8981\u505A\u4E00\u4E9B\u590D\u6742\u4E1A\u52A1\u903B\u8F91\u7684\u60C5\u51B5\u53EF\u4EE5\u4F7F\u7528 <code>watch</code>\u3002</p><p>\u53E6\u5916 <code>computed</code> \u548C <code>watch</code> \u8FD8\u90FD\u652F\u6301\u5BF9\u8C61\u7684\u5199\u6CD5\uFF0C\u8FD9\u79CD\u65B9\u5F0F\u77E5\u9053\u7684\u4EBA\u5E76\u4E0D\u591A\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">vm.$watch(&#39;obj&#39;, {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u6DF1\u5EA6\u904D\u5386</span></span>
<span class="line"><span style="color:#A6ACCD;">    deep: true,</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u7ACB\u5373\u89E6\u53D1</span></span>
<span class="line"><span style="color:#A6ACCD;">    immediate: true,</span></span>
<span class="line"><span style="color:#A6ACCD;">    // \u6267\u884C\u7684\u51FD\u6570</span></span>
<span class="line"><span style="color:#A6ACCD;">    handler: function(val, oldVal) {}</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;">var vm = new Vue({</span></span>
<span class="line"><span style="color:#A6ACCD;">  data: { a: 1 },</span></span>
<span class="line"><span style="color:#A6ACCD;">  computed: {</span></span>
<span class="line"><span style="color:#A6ACCD;">    aPlus: {</span></span>
<span class="line"><span style="color:#A6ACCD;">      // this.aPlus \u65F6\u89E6\u53D1</span></span>
<span class="line"><span style="color:#A6ACCD;">      get: function () {</span></span>
<span class="line"><span style="color:#A6ACCD;">        return this.a + 1</span></span>
<span class="line"><span style="color:#A6ACCD;">      },</span></span>
<span class="line"><span style="color:#A6ACCD;">      // this.aPlus = 1 \u65F6\u89E6\u53D1</span></span>
<span class="line"><span style="color:#A6ACCD;">      set: function (v) {</span></span>
<span class="line"><span style="color:#A6ACCD;">        this.a = v - 1</span></span>
<span class="line"><span style="color:#A6ACCD;">      }</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="keep-alive-\u7EC4\u4EF6\u6709\u4EC0\u4E48\u4F5C\u7528" tabindex="-1">keep-alive \u7EC4\u4EF6\u6709\u4EC0\u4E48\u4F5C\u7528 <a class="header-anchor" href="#keep-alive-\u7EC4\u4EF6\u6709\u4EC0\u4E48\u4F5C\u7528" aria-hidden="true">#</a></h2><p>\u5982\u679C\u4F60\u9700\u8981\u5728\u7EC4\u4EF6\u5207\u6362\u7684\u65F6\u5019\uFF0C\u4FDD\u5B58\u4E00\u4E9B\u7EC4\u4EF6\u7684\u72B6\u6001\u9632\u6B62\u591A\u6B21\u6E32\u67D3\uFF0C\u5C31\u53EF\u4EE5\u4F7F\u7528 <code>keep-alive</code> \u7EC4\u4EF6\u5305\u88F9\u9700\u8981\u4FDD\u5B58\u7684\u7EC4\u4EF6\u3002</p><p>\u5BF9\u4E8E <code>keep-alive</code> \u7EC4\u4EF6\u6765\u8BF4\uFF0C\u5B83\u62E5\u6709\u4E24\u4E2A\u72EC\u6709\u7684\u751F\u547D\u5468\u671F\u94A9\u5B50\u51FD\u6570\uFF0C\u5206\u522B\u4E3A <code>activated</code> \u548C <code>deactivated</code> \u3002\u7528 <code>keep-alive</code> \u5305\u88F9\u7684\u7EC4\u4EF6\u5728\u5207\u6362\u65F6\u4E0D\u4F1A\u8FDB\u884C\u9500\u6BC1\uFF0C\u800C\u662F\u7F13\u5B58\u5230\u5185\u5B58\u4E2D\u5E76\u6267\u884C <code>deactivated</code> \u94A9\u5B50\u51FD\u6570\uFF0C\u547D\u4E2D\u7F13\u5B58\u6E32\u67D3\u540E\u4F1A\u6267\u884C <code>actived</code> \u94A9\u5B50\u51FD\u6570\u3002</p><h2 id="v-show-\u4E0E-v-if-\u533A\u522B" tabindex="-1">v-show \u4E0E v-if \u533A\u522B <a class="header-anchor" href="#v-show-\u4E0E-v-if-\u533A\u522B" aria-hidden="true">#</a></h2><p><code>v-show</code> \u53EA\u662F\u5728 <code>display: none</code> \u548C <code>display: block</code> \u4E4B\u95F4\u5207\u6362\u3002\u65E0\u8BBA\u521D\u59CB\u6761\u4EF6\u662F\u4EC0\u4E48\u90FD\u4F1A\u88AB\u6E32\u67D3\u51FA\u6765\uFF0C\u540E\u9762\u53EA\u9700\u8981\u5207\u6362 CSS\uFF0CDOM \u8FD8\u662F\u4E00\u76F4\u4FDD\u7559\u7740\u7684\u3002\u6240\u4EE5\u603B\u7684\u6765\u8BF4 <code>v-show</code> \u5728\u521D\u59CB\u6E32\u67D3\u65F6\u6709\u66F4\u9AD8\u7684\u5F00\u9500\uFF0C\u4F46\u662F\u5207\u6362\u5F00\u9500\u5F88\u5C0F\uFF0C\u66F4\u9002\u5408\u4E8E\u9891\u7E41\u5207\u6362\u7684\u573A\u666F\u3002</p><p><code>v-if</code> \u7684\u8BDD\u5C31\u5F97\u8BF4\u5230 Vue \u5E95\u5C42\u7684\u7F16\u8BD1\u4E86\u3002\u5F53\u5C5E\u6027\u521D\u59CB\u4E3A <code>false</code> \u65F6\uFF0C\u7EC4\u4EF6\u5C31\u4E0D\u4F1A\u88AB\u6E32\u67D3\uFF0C\u76F4\u5230\u6761\u4EF6\u4E3A <code>true</code>\uFF0C\u5E76\u4E14\u5207\u6362\u6761\u4EF6\u65F6\u4F1A\u89E6\u53D1\u9500\u6BC1/\u6302\u8F7D\u7EC4\u4EF6\uFF0C\u6240\u4EE5\u603B\u7684\u6765\u8BF4\u5728\u5207\u6362\u65F6\u5F00\u9500\u66F4\u9AD8\uFF0C\u66F4\u9002\u5408\u4E0D\u7ECF\u5E38\u5207\u6362\u7684\u573A\u666F\u3002</p><p>\u5E76\u4E14\u57FA\u4E8E <code>v-if</code> \u7684\u8FD9\u79CD\u60F0\u6027\u6E32\u67D3\u673A\u5236\uFF0C\u53EF\u4EE5\u5728\u5FC5\u8981\u7684\u65F6\u5019\u624D\u53BB\u6E32\u67D3\u7EC4\u4EF6\uFF0C\u51CF\u5C11\u6574\u4E2A\u9875\u9762\u7684\u521D\u59CB\u6E32\u67D3\u5F00\u9500\u3002</p><h2 id="\u7EC4\u4EF6\u4E2D-data-\u4EC0\u4E48\u65F6\u5019\u53EF\u4EE5\u4F7F\u7528\u5BF9\u8C61" tabindex="-1">\u7EC4\u4EF6\u4E2D data \u4EC0\u4E48\u65F6\u5019\u53EF\u4EE5\u4F7F\u7528\u5BF9\u8C61 <a class="header-anchor" href="#\u7EC4\u4EF6\u4E2D-data-\u4EC0\u4E48\u65F6\u5019\u53EF\u4EE5\u4F7F\u7528\u5BF9\u8C61" aria-hidden="true">#</a></h2><p>\u8FD9\u9053\u9898\u76EE\u5176\u5B9E\u66F4\u591A\u8003\u7684\u662F JS \u529F\u5E95\u3002</p><p>\u7EC4\u4EF6\u590D\u7528\u65F6\u6240\u6709\u7EC4\u4EF6\u5B9E\u4F8B\u90FD\u4F1A\u5171\u4EAB <code>data</code>\uFF0C\u5982\u679C <code>data</code> \u662F\u5BF9\u8C61\u7684\u8BDD\uFF0C\u5C31\u4F1A\u9020\u6210\u4E00\u4E2A\u7EC4\u4EF6\u4FEE\u6539 <code>data</code> \u4EE5\u540E\u4F1A\u5F71\u54CD\u5230\u5176\u4ED6\u6240\u6709\u7EC4\u4EF6\uFF0C\u6240\u4EE5\u9700\u8981\u5C06 <code>data</code> \u5199\u6210\u51FD\u6570\uFF0C\u6BCF\u6B21\u7528\u5230\u5C31\u8C03\u7528\u4E00\u6B21\u51FD\u6570\u83B7\u5F97\u65B0\u7684\u6570\u636E\u3002</p><p>\u5F53\u6211\u4EEC\u4F7F\u7528 <code>new Vue()</code> \u7684\u65B9\u5F0F\u7684\u65F6\u5019\uFF0C\u65E0\u8BBA\u6211\u4EEC\u5C06 <code>data</code> \u8BBE\u7F6E\u4E3A\u5BF9\u8C61\u8FD8\u662F\u51FD\u6570\u90FD\u662F\u53EF\u4EE5\u7684\uFF0C\u56E0\u4E3A <code>new Vue()</code> \u7684\u65B9\u5F0F\u662F\u751F\u6210\u4E00\u4E2A\u6839\u7EC4\u4EF6\uFF0C\u8BE5\u7EC4\u4EF6\u4E0D\u4F1A\u590D\u7528\uFF0C\u4E5F\u5C31\u4E0D\u5B58\u5728\u5171\u4EAB <code>data</code> \u7684\u60C5\u51B5\u4E86\u3002</p><h1 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3 <a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a></h1><p>\u603B\u7684\u6765\u8BF4\u8FD9\u4E00\u7AE0\u8282\u7684\u5185\u5BB9\u66F4\u591A\u7684\u504F\u5411\u4E8E Vue \u7684\u57FA\u7840\uFF0C\u4E0B\u4E00\u7AE0\u8282\u6211\u4EEC\u5C06\u6765\u4E86\u89E3\u4E00\u4E9B\u539F\u7406\u6027\u65B9\u9762\u7684\u77E5\u8BC6\u3002</p>`,60),o=[p];function c(d,t,i,r,C,A){return s(),a("div",null,o)}const v=e(l,[["render",c]]);export{u as __pageData,v as default};
